import { 
  useAdminSession, 
  useKeyholderRewards, 
  useKeyholderSession, 
  useMultiWearer 
} from '../../hooks/keyholder';
import { KeyholderReward, KeyholderPunishment } from '../../types';

import { TaskData } from '../../types';

interface TypedKeyholderDemoProps {
  userId: string;
  isAuthReady: boolean;
  addTask: (taskData: TaskData) => Promise<void>;
  saveDataToFirestore: (data: Record<string, unknown>) => Promise<void>;
  requiredKeyholderDurationSeconds: number;
}

/**
 * Demo component showing how the new TypeScript keyholder hooks work
 * This replaces the need for 'any' types with proper TypeScript interfaces
 */
export function TypedKeyholderDemo({
  userId,
  isAuthReady,
  addTask,
  saveDataToFirestore,
  requiredKeyholderDurationSeconds,
}: TypedKeyholderDemoProps): React.JSX.Element {
  // Admin session management with proper typing
  const {
    isAdmin,
    permissions: adminPermissions,
  } = useAdminSession({ userId, isAuthReady });

  // Keyholder session management with proper typing
  const {
    keyholderSession,
    isActive: isKeyholderActive,
  } = useKeyholderSession({ userId, isAuthReady, keyholderName: 'Demo Keyholder' });

  // Rewards and punishments with proper typing
  const {
    addReward,
    addPunishment,
  } = useKeyholderRewards({
    userId,
    addTask,
    saveDataToFirestore,
    requiredKeyholderDurationSeconds,
  });

  // Multi-wearer functionality with proper typing
  const {
    session: multiWearerSession,
    wearers,
  } = useMultiWearer({ keyholderUserId: userId, isAuthReady });

  // Example of properly typed reward
  const handleAddTypedReward = async (): Promise<void> => {
    const reward: KeyholderReward = {
      type: 'time',
      timeSeconds: 3600, // 1 hour
      note: 'Good behavior reward',
    };

    try {
      await addReward(reward);
    } catch (error) {
      console.error('Error adding reward:', error);
    }
  };

  // Example of properly typed punishment
  const handleAddTypedPunishment = async (): Promise<void> => {
    const punishment: KeyholderPunishment = {
      type: 'time',
      timeSeconds: 1800, // 30 minutes
      note: 'Minor infraction',
    };

    try {
      await addPunishment(punishment);
    } catch (error) {
      console.error('Error adding punishment:', error);
    }
  };

  return (
    <div className="typed-keyholder-demo p-4 bg-gray-800 rounded-lg">
      <h3 className="text-lg font-semibold mb-4">TypeScript Keyholder Demo</h3>

      <div className="space-y-4">
        {/* Admin Session Status */}
        <div className="admin-status">
          <h4 className="font-medium">Admin Session</h4>
          <p>Status: {isAdmin ? 'Active' : 'Inactive'}</p>
          {adminPermissions && (
            <p>Permissions: {Object.keys(adminPermissions).filter(
              key => adminPermissions[key as keyof typeof adminPermissions]
            ).join(', ')}</p>
          )}
        </div>

        {/* Keyholder Session Status */}
        <div className="keyholder-status">
          <h4 className="font-medium">Keyholder Session</h4>
          <p>Status: {isKeyholderActive ? 'Active' : 'Inactive'}</p>
          {keyholderSession && (
            <p>Keyholder: {keyholderSession.keyholderName}</p>
          )}
        </div>

        {/* Multi-Wearer Status */}
        <div className="multi-wearer-status">
          <h4 className="font-medium">Multi-Wearer Session</h4>
          <p>Active: {multiWearerSession?.isActive ? 'Yes' : 'No'}</p>
          <p>Wearers: {wearers.length}</p>
        </div>

        {/* Demo Actions */}
        <div className="demo-actions space-x-2">
          <button 
            onClick={handleAddTypedReward}
            className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
          >
            Add Typed Reward
          </button>

          <button 
            onClick={handleAddTypedPunishment}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Add Typed Punishment
          </button>
        </div>
      </div>
    </div>
  );
}

/**
 * Offline Status Component
 * Shows the status of the offline queue and sync operations
 * Demonstrates the complete Firebase ↔ Dexie ↔ TanStack Query architecture
 */
import React, { useState } from "react";
import { useOfflineQueueStats } from "../../hooks/api";
import { useNotificationActions } from "../../stores";
import { FaWifi, FaWifiSlash, FaSync } from "../../utils/iconImport";

export const OfflineStatus: React.FC = () => {
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const { data: queueStats } = useOfflineQueueStats();
  const { showInfo } = useNotificationActions();

  // Listen for online/offline events
  React.useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      showInfo(
        "Back online! Syncing pending changes...",
        "Connection Restored",
      );
    };

    const handleOffline = () => {
      setIsOnline(false);
      showInfo(
        "You're offline. Changes will sync when connection is restored.",
        "Offline Mode",
      );
    };

    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
    };
  }, [showInfo]);

  const statusColor = isOnline
    ? (queueStats?.pending || 0) > 0
      ? "bg-yellow-500"
      : "bg-green-500"
    : "bg-red-500";

  const statusIcon = isOnline ? (
    queueStats?.pending ? (
      <FaSync className="animate-spin" />
    ) : (
      <FaWifi />
    )
  ) : (
    <FaWifiSlash />
  );

  return (
    <div className="fixed bottom-4 right-4 bg-white/10 backdrop-blur-sm border border-white/20 rounded-lg shadow-lg p-4 max-w-sm">
      {/* Status Header */}
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <div className={`w-3 h-3 rounded-full ${statusColor}`} />
          <span className="text-sm font-medium text-white">
            {isOnline ? "Online" : "Offline"}
          </span>
          {statusIcon}
        </div>
      </div>

      {/* Architecture Flow Indicator */}
      <div className="mt-3 pt-3 border-t border-white/10">
        <div className="text-xs font-medium text-white mb-2">
          🎯 Migration Complete
        </div>
        <div className="flex items-center justify-between text-xs">
          <span className="text-blue-400">Firebase</span>
          <span className="text-gray-400">↔</span>
          <span className="text-green-400">Dexie</span>
          <span className="text-gray-400">↔</span>
          <span className="text-purple-400">TanStack</span>
          <span className="text-gray-400">↔</span>
          <span className="text-yellow-400">UI</span>
        </div>
        <div className="text-center mt-1">
          <span className="text-xs text-gray-400">↑</span>
        </div>
        <div className="text-center">
          <span className="text-xs text-orange-400">Zustand</span>
        </div>
      </div>

      {/* Implementation Status */}
      <div className="mt-3 p-2 bg-green-500/20 rounded text-xs text-green-200">
        <strong>✅ Architecture Ready:</strong> Firebase to TanStack Query
        migration is complete and functional.
      </div>
    </div>
  );
};

import React from "react";
import {
  FaTimes,
  FaExclamationTriangle,
  FaArrowLeft,
  FaArrowRight,
} from "react-icons/fa";
import type { EmergencyUnlockReason } from "../../types/events";
import { EMERGENCY_UNLOCK_REASONS } from "../../types/events";
import { useEmergencyUnlockModal } from "../../hooks/tracker/useEmergencyUnlockModal";

interface EmergencyUnlockModalProps {
  isOpen: boolean;
  onClose: () => void;
  onEmergencyUnlock: (
    reason: EmergencyUnlockReason,
    additionalNotes?: string,
  ) => Promise<void>;
  sessionId: string;
  isProcessing?: boolean;
}

export const EmergencyUnlockModal: React.FC<EmergencyUnlockModalProps> = ({
  isOpen,
  onClose,
  onEmergencyUnlock,
  sessionId,
  isProcessing: _isProcessing = false,
}) => {
  const {
    stage,
    reason,
    customReason,
    confirmText,
    isSubmitting,
    setStage,
    setReason,
    setCustomReason,
    setConfirmText,
    handleEmergencyUnlock,
    canProceedFromReason,
    canConfirm,
    requiredText,
    confirmInputRef,
  } = useEmergencyUnlockModal({
    sessionId,
    onEmergencyUnlock: async (finalReason, additionalNotes) => {
      await onEmergencyUnlock(finalReason, additionalNotes);
      onClose();
    },
    isOpen,
  });

  if (!isOpen) return null;

  const WarningStage = () => (
    <div className="text-center">
      <FaExclamationTriangle className="text-6xl text-red-400 mx-auto mb-6" />
      <h3 className="text-xl font-bold mb-4 text-red-300">
        🚨 Emergency Unlock
      </h3>

      <div className="bg-red-900/30 border border-red-600 rounded-lg p-4 mb-6 text-left">
        <h4 className="font-semibold text-red-300 mb-2">
          ⚠️ Warning: Emergency Use Only
        </h4>
        <ul className="text-sm text-red-200 space-y-1">
          <li>• This will immediately end your current session</li>
          <li>• All session goals and restrictions will be bypassed</li>
          <li>• This action will be logged for safety and accountability</li>
          <li>• Frequent use may trigger cooldown periods</li>
          <li>• Use only in genuine emergency situations</li>
        </ul>
      </div>

      <p className="text-sm text-gray-300 mb-6">
        This feature is designed for medical emergencies, safety concerns,
        equipment malfunctions, or other urgent situations that genuinely
        require immediate unlock.
      </p>

      <div className="flex flex-col space-y-3">
        <button
          onClick={() => setStage("reason")}
          className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-3 px-4 rounded-lg transition flex items-center justify-center"
        >
          I Understand - Continue
          <FaArrowRight className="ml-2" />
        </button>
        <button
          onClick={onClose}
          className="w-full bg-gray-600 hover:bg-gray-500 text-white font-bold py-3 px-4 rounded-lg transition"
        >
          Cancel
        </button>
      </div>
    </div>
  );

  const ReasonStage = () => (
    <div>
      <div className="flex items-center mb-6">
        <button
          onClick={() => setStage("warning")}
          className="mr-3 p-1 text-gray-400 hover:text-white transition"
          aria-label="Go back"
        >
          <FaArrowLeft />
        </button>
        <h3 className="text-xl font-bold text-red-300">
          Select Emergency Reason
        </h3>
      </div>

      <p className="text-sm text-gray-300 mb-4">
        Please select the reason for your emergency unlock. This information
        helps ensure the feature is used appropriately and safely.
      </p>

      <div className="space-y-3 mb-6">
        {EMERGENCY_UNLOCK_REASONS.map((emergencyReason) => (
          <label
            key={emergencyReason}
            className={`flex items-start p-3 rounded-lg border cursor-pointer transition ${
              reason === emergencyReason
                ? "border-red-500 bg-red-900/30"
                : "border-gray-600 bg-gray-800/50 hover:bg-gray-700/50"
            }`}
          >
            <input
              type="radio"
              name="emergency-reason"
              value={emergencyReason}
              checked={reason === emergencyReason}
              onChange={(e) =>
                setReason(e.target.value as EmergencyUnlockReason)
              }
              className="mt-1 mr-3 text-red-600"
            />
            <div>
              <div className="font-medium text-white">{emergencyReason}</div>
              <div className="text-xs text-gray-400 mt-1">
                {emergencyReason === "Medical Emergency" &&
                  "Health-related emergencies requiring immediate unlock"}
                {emergencyReason === "Safety Concern" &&
                  "Physical safety or security situations"}
                {emergencyReason === "Equipment Malfunction" &&
                  "Device failure or malfunction"}
                {emergencyReason === "Urgent Situation" &&
                  "Other urgent circumstances requiring unlock"}
                {emergencyReason === "Other" &&
                  "Custom reason with additional details"}
              </div>
            </div>
          </label>
        ))}
      </div>

      {reason === "Other" && (
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-300 mb-2">
            Please specify the emergency reason:
          </label>
          <textarea
            value={customReason}
            onChange={(e) => setCustomReason(e.target.value)}
            placeholder="Describe your emergency situation..."
            rows={3}
            className="w-full p-3 rounded-lg border border-gray-600 bg-gray-800 text-white focus:border-red-500 focus:ring-1 focus:ring-red-500 resize-none"
            maxLength={500}
          />
          <div className="text-xs text-gray-400 mt-1">
            {customReason.length}/500 characters
          </div>
        </div>
      )}

      <div className="flex flex-col space-y-3">
        <button
          onClick={() => setStage("confirm")}
          disabled={!canProceedFromReason}
          className="w-full bg-red-600 hover:bg-red-700 disabled:bg-gray-600 disabled:cursor-not-allowed text-white font-bold py-3 px-4 rounded-lg transition flex items-center justify-center"
        >
          Continue to Confirmation
          <FaArrowRight className="ml-2" />
        </button>
        <button
          onClick={() => setStage("warning")}
          className="w-full bg-gray-600 hover:bg-gray-500 text-white font-bold py-3 px-4 rounded-lg transition"
        >
          Back
        </button>
      </div>
    </div>
  );

  const ConfirmationStage = () => (
    <div>
      <div className="flex items-center mb-6">
        <button
          onClick={() => setStage("reason")}
          className="mr-3 p-1 text-gray-400 hover:text-white transition"
          aria-label="Go back"
        >
          <FaArrowLeft />
        </button>
        <h3 className="text-xl font-bold text-red-300">Final Confirmation</h3>
      </div>

      <div className="bg-yellow-900/30 border border-yellow-600 rounded-lg p-4 mb-6">
        <h4 className="font-semibold text-yellow-300 mb-2">⚠️ Final Warning</h4>
        <p className="text-sm text-yellow-200 mb-3">
          You are about to perform an emergency unlock. This action:
        </p>
        <ul className="text-sm text-yellow-200 space-y-1 mb-3">
          <li>• Will immediately end session #{sessionId.slice(-8)}</li>
          <li>• Cannot be undone</li>
          <li>• Will be permanently logged</li>
          <li>
            • Reason: <strong>{reason}</strong>
          </li>
        </ul>
      </div>

      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-300 mb-2">
          Type "{requiredText}" to confirm:
        </label>
        <input
          ref={confirmInputRef}
          type="text"
          value={confirmText}
          onChange={(e) => setConfirmText(e.target.value.toUpperCase())}
          placeholder={requiredText}
          className={`w-full p-3 rounded-lg border bg-gray-800 text-white text-center font-mono text-lg tracking-widest focus:ring-2 transition ${
            confirmText === requiredText
              ? "border-green-500 focus:ring-green-500"
              : "border-gray-600 focus:ring-red-500"
          }`}
          maxLength={requiredText.length}
        />
        <div className="text-xs text-gray-400 mt-1 text-center">
          {confirmText.length}/{requiredText.length} characters
        </div>
      </div>

      <div className="flex flex-col space-y-3">
        <button
          onClick={handleEmergencyUnlock}
          disabled={!canConfirm || isSubmitting}
          className="w-full bg-red-600 hover:bg-red-700 disabled:bg-gray-600 disabled:cursor-not-allowed text-white font-bold py-3 px-4 rounded-lg transition"
        >
          {isSubmitting
            ? "Processing Emergency Unlock..."
            : "🚨 EMERGENCY UNLOCK 🚨"}
        </button>
        <button
          onClick={() => setStage("reason")}
          disabled={isSubmitting}
          className="w-full bg-gray-600 hover:bg-gray-500 disabled:bg-gray-600 text-white font-bold py-3 px-4 rounded-lg transition"
        >
          Back to Reason Selection
        </button>
      </div>
    </div>
  );

  return (
    <div className="fixed inset-0 bg-black/75 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-gradient-to-br from-gray-900 to-gray-800 max-w-md w-full rounded-xl border-2 border-red-500 shadow-2xl">
        <div className="relative p-6">
          <button
            onClick={onClose}
            disabled={isSubmitting}
            className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors disabled:cursor-not-allowed"
            aria-label="Close modal"
          >
            <FaTimes size={20} />
          </button>

          {stage === "warning" && <WarningStage />}
          {stage === "reason" && <ReasonStage />}
          {stage === "confirm" && <ConfirmationStage />}
        </div>
      </div>
    </div>
  );
};

import React from "react";
import { usePauseResumeControls } from "../../hooks/tracker/usePauseResumeControls";

// Temporary types until proper hook pattern is implemented
type EnhancedPauseReason = "Bathroom Break" | "Emergency" | "Medical" | "Other";

type PauseState = {
  canPause: boolean;
  lastPauseTime?: Date;
  nextPauseAvailable?: Date;
  cooldownRemaining?: number;
};

// PauseButton sub-component
interface PauseButtonProps {
  canPause: boolean;
  isLoading: boolean;
  onPauseClick: () => void;
  pauseButtonStyling: string;
  pauseButtonText: string;
}

const PauseButton: React.FC<PauseButtonProps> = ({
  canPause,
  isLoading,
  onPauseClick,
  pauseButtonStyling,
  pauseButtonText,
}) => (
  <div className="flex justify-center mb-8">
    <button
      type="button"
      onClick={onPauseClick}
      disabled={!canPause || isLoading}
      className={`glass-button font-bold py-3 px-6 shadow-lg transition-all duration-300 transform hover:scale-105 disabled:opacity-50 ${pauseButtonStyling}`}
    >
      ⏸️ {pauseButtonText}
    </button>
  </div>
);

// ResumeButton sub-component
interface ResumeButtonProps {
  isLoading: boolean;
  onResumeClick: () => void;
}

const ResumeButton: React.FC<ResumeButtonProps> = ({
  isLoading,
  onResumeClick,
}) => (
  <div className="flex justify-center mb-8">
    <button
      type="button"
      onClick={onResumeClick}
      disabled={isLoading}
      className="glass-button bg-gradient-to-r from-green-600/80 to-emerald-600/80 hover:from-green-500/90 hover:to-emerald-500/90 text-white font-bold py-3 px-6 shadow-lg hover:shadow-green-500/20 transition-all duration-300 transform hover:scale-105 disabled:opacity-50"
    >
      ▶️ {isLoading ? "Resuming..." : "Resume Session"}
    </button>
  </div>
);

// CooldownDisplay sub-component
interface CooldownDisplayProps {
  cooldownDisplay: string;
}

const CooldownDisplay: React.FC<CooldownDisplayProps> = ({
  cooldownDisplay,
}) => (
  <div className="text-sm text-yellow-600 mb-4 text-center">
    Next pause available in: {cooldownDisplay}
  </div>
);

// PauseModal sub-component
interface PauseModalProps {
  show: boolean;
  selectedReason: EnhancedPauseReason;
  customReason: string;
  isLoading: boolean;
  onReasonChange: (reason: EnhancedPauseReason) => void;
  onCustomReasonChange: (reason: string) => void;
  onConfirm: () => void;
  onCancel: () => void;
}

const PauseModal: React.FC<PauseModalProps> = ({
  show,
  selectedReason,
  customReason,
  isLoading,
  onReasonChange,
  onCustomReasonChange,
  onConfirm,
  onCancel,
}) => {
  if (!show) return null;

  const reasons: EnhancedPauseReason[] = [
    "Bathroom Break",
    "Emergency",
    "Medical",
    "Other",
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4">
      <div className="glass-morphism p-6 md:p-8 text-center w-full max-w-md text-gray-50 border border-yellow-700/30">
        <h3 className="text-lg md:text-xl font-bold mb-4 text-yellow-300">
          Reason for Pausing Session
        </h3>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-300 mb-2">
            Select reason:
          </label>
          <select
            value={selectedReason}
            onChange={(e) =>
              onReasonChange(e.target.value as EnhancedPauseReason)
            }
            className="w-full p-2 rounded-lg border border-yellow-600/50 bg-gray-900/50 backdrop-blur-sm text-gray-50 focus:outline-none focus:ring-2 focus:ring-yellow-500"
          >
            {reasons.map((reason) => (
              <option key={reason} value={reason}>
                {reason}
              </option>
            ))}
          </select>
        </div>

        {selectedReason === "Other" && (
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Custom reason:
            </label>
            <input
              type="text"
              value={customReason}
              onChange={(e) => onCustomReasonChange(e.target.value)}
              placeholder="Enter custom reason"
              className="w-full p-2 rounded-lg border border-yellow-600/50 bg-gray-900/50 backdrop-blur-sm text-gray-50 focus:outline-none focus:ring-2 focus:ring-yellow-500"
            />
          </div>
        )}

        <div className="flex flex-col sm:flex-row justify-around space-y-3 sm:space-y-0 sm:space-x-4">
          <button
            type="button"
            onClick={onConfirm}
            disabled={
              isLoading || (selectedReason === "Other" && !customReason.trim())
            }
            className="w-full sm:w-auto glass-button bg-gradient-to-r from-yellow-600/80 to-yellow-700/80 hover:from-yellow-500/90 hover:to-yellow-600/90 text-white font-bold py-2 px-4 transition-all duration-300 disabled:opacity-50"
          >
            {isLoading ? "Pausing..." : "Confirm Pause"}
          </button>
          <button
            type="button"
            onClick={onCancel}
            disabled={isLoading}
            className="w-full sm:w-auto glass-button bg-gradient-to-r from-gray-600/80 to-gray-700/80 hover:from-gray-500/90 hover:to-gray-600/90 text-white font-bold py-2 px-4 transition-all duration-300 disabled:opacity-50"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

interface PauseResumeButtonsProps {
  sessionId: string;
  userId: string;
  isPaused: boolean;
  pauseState?: PauseState;
  onPause?: () => void;
  onResume?: () => void;
}

export const PauseResumeButtons: React.FC<PauseResumeButtonsProps> = ({
  sessionId,
  userId,
  isPaused,
  pauseState,
  onPause,
  onResume,
}) => {
  const {
    buttonStates,
    cooldownDisplay,
    pauseButtonStyling,
    pauseButtonText,
    showPauseModal,
    selectedReason,
    customReason,
    isLoading,
    handlePauseClick,
    handleResumeClick,
    handleConfirmPause,
    handleModalCancel,
    setSelectedReason,
    setCustomReason,
  } = usePauseResumeControls({
    sessionId,
    userId,
    isPaused,
    pauseState,
    onPause,
    onResume,
  });

  return (
    <>
      {buttonStates.showResume && (
        <ResumeButton isLoading={isLoading} onResumeClick={handleResumeClick} />
      )}

      {buttonStates.showPause && (
        <PauseButton
          canPause={buttonStates.canPause}
          isLoading={isLoading}
          onPauseClick={handlePauseClick}
          pauseButtonStyling={pauseButtonStyling}
          pauseButtonText={pauseButtonText}
        />
      )}

      {buttonStates.showCooldown && (
        <CooldownDisplay cooldownDisplay={cooldownDisplay} />
      )}

      <PauseModal
        show={showPauseModal}
        selectedReason={selectedReason}
        customReason={customReason}
        isLoading={isLoading}
        onReasonChange={setSelectedReason}
        onCustomReasonChange={setCustomReason}
        onConfirm={handleConfirmPause}
        onCancel={handleModalCancel}
      />
    </>
  );
};

import React from "react";
import { FaExclamationTriangle, FaClock, FaCalendarAlt } from "react-icons/fa";
import type { DBSession } from "../../types/database";

interface SessionRecoveryModalProps {
  corruptedSession: DBSession;
  onRecover: (session: DBSession) => void;
  onDiscard: () => void;
  isRecovering?: boolean;
}

export const SessionRecoveryModal: React.FC<SessionRecoveryModalProps> = ({
  corruptedSession,
  onRecover,
  onDiscard,
  isRecovering = false,
}) => {
  const formatDuration = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  };

  const calculateEstimatedDuration = (session: DBSession): number => {
    const now = new Date();
    const startTime = new Date(session.startTime);
    const totalMs = now.getTime() - startTime.getTime();
    const totalSeconds = Math.floor(totalMs / 1000);
    return Math.max(0, totalSeconds - session.accumulatedPauseTime);
  };

  const estimatedDuration = calculateEstimatedDuration(corruptedSession);

  return (
    <div className="fixed inset-0 bg-black/75 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-xl shadow-2xl p-6 max-w-md w-full border border-yellow-500">
        <div className="text-center mb-6">
          <FaExclamationTriangle className="text-yellow-400 text-4xl mx-auto mb-3" />
          <h3 className="text-xl font-bold text-yellow-300 mb-2">
            Session Recovery Required
          </h3>
          <p className="text-sm text-gray-300">
            We detected an interrupted session that may have corrupted data.
            Would you like to attempt recovery?
          </p>
        </div>

        <div className="bg-gray-700/50 rounded-lg p-4 mb-6 space-y-3">
          <div className="flex items-center text-sm text-gray-300">
            <FaCalendarAlt className="text-blue-400 mr-2" />
            <span className="font-medium">Session Started:</span>
            <span className="ml-2">
              {new Date(corruptedSession.startTime).toLocaleString()}
            </span>
          </div>

          <div className="flex items-center text-sm text-gray-300">
            <FaClock className="text-green-400 mr-2" />
            <span className="font-medium">Estimated Duration:</span>
            <span className="ml-2">{formatDuration(estimatedDuration)}</span>
          </div>

          {corruptedSession.isPaused && (
            <div className="flex items-center text-sm text-yellow-300">
              <FaExclamationTriangle className="text-yellow-400 mr-2" />
              <span className="font-medium">Status:</span>
              <span className="ml-2">Session was paused</span>
            </div>
          )}

          {corruptedSession.accumulatedPauseTime > 0 && (
            <div className="flex items-center text-sm text-gray-300">
              <FaClock className="text-orange-400 mr-2" />
              <span className="font-medium">Total Pause Time:</span>
              <span className="ml-2">
                {formatDuration(corruptedSession.accumulatedPauseTime)}
              </span>
            </div>
          )}
        </div>

        <div className="bg-yellow-900/30 border border-yellow-600 rounded-lg p-3 mb-6">
          <p className="text-xs text-yellow-200">
            <strong>Recovery Process:</strong> We'll attempt to fix any data
            inconsistencies while preserving your session progress. Your session
            timing and pause history will be validated and corrected if
            necessary.
          </p>
        </div>

        <div className="flex flex-col sm:flex-row gap-3">
          <button
            onClick={() => onRecover(corruptedSession)}
            disabled={isRecovering}
            className="flex-1 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-800 disabled:opacity-50 text-white font-semibold py-3 px-4 rounded-lg transition flex items-center justify-center"
          >
            {isRecovering ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                Recovering...
              </>
            ) : (
              "Recover Session"
            )}
          </button>

          <button
            onClick={onDiscard}
            disabled={isRecovering}
            className="flex-1 bg-gray-600 hover:bg-gray-500 disabled:bg-gray-700 disabled:opacity-50 text-white font-semibold py-3 px-4 rounded-lg transition"
          >
            Discard Session
          </button>
        </div>

        <p className="text-xs text-gray-500 text-center mt-4">
          If recovery fails, you can always start a new session. Your session
          history will be preserved.
        </p>
      </div>
    </div>
  );
};

