/**
 * Delete task
 */
export function useDeleteTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      taskId,
      userId,
    }: {
      taskId: string;
      userId: string;
    }): Promise<void> => {
      logger.info("Deleting task", { taskId, userId });

      await taskDBService.delete(taskId);

      logger.info("Task deleted successfully", { taskId, userId });
    },
    onSuccess: (
      _: void,
      { taskId, userId }: { taskId: string; userId: string },
    ) => {
      logger.info("Task deletion successful", { taskId, userId });

      // Remove from detail cache
      queryClient.removeQueries({ queryKey: taskKeys.detail(taskId) });

      // Invalidate list queries
      queryClient.invalidateQueries({ queryKey: taskKeys.lists() });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, "pending"),
      });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, "completed"),
      });

      // Optimistically remove from cached lists
      queryClient.setQueriesData(
        { queryKey: taskKeys.list(userId) },
        (oldData: Task[] | undefined) => {
          if (!oldData) return undefined;
          return oldData.filter((task) => task.id !== taskId);
        },
      );
    },
    onError: (error, { taskId, userId }) => {
      logger.error("Task deletion failed", {
        error: error instanceof Error ? error.message : String(error),
        taskId,
        userId,
      });
    },
  });
}

/**
 * Get task statistics for dashboard
 */
export function useTaskStats(userId: string) {
  return useQuery({
    queryKey: [...taskKeys.all, "stats", userId],
    queryFn: async () => {
      logger.info("Calculating task statistics", { userId });

      const tasks = await taskDBService.findByUserId(userId);

      const stats = {
        total: tasks.length,
        pending: tasks.filter((t) => t.status === "pending").length,
        inProgress: tasks.filter((t) => t.status === "submitted").length, // Use submitted instead of in_progress
        completed: tasks.filter((t) => t.status === "completed").length,
        overdue: tasks.filter((t) => {
          if (!t.dueDate || t.status === "completed") return false;
          return new Date(t.dueDate) < new Date();
        }).length,
        byPriority: {
          high: tasks.filter((t) => t.priority === "high").length,
          medium: tasks.filter((t) => t.priority === "medium").length,
          low: tasks.filter((t) => t.priority === "low").length,
        },
        completionRate:
          tasks.length > 0
            ? Math.round(
                (tasks.filter((t) => t.status === "completed").length /
                  tasks.length) *
                  100,
              )
            : 0,
      };

      return stats;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    enabled: !!userId,
  });
}

/**
 * Hook for handling data sync conflict resolution
 */
import { useCallback, Dispatch, SetStateAction } from "react";
import type {
  DataConflict,
  ConflictResolution,
  GlobalResolutionStrategy,
  ConflictResolutionResult,
  SyncMetrics,
} from "./types/dataSync";
import { serviceLogger } from "../../utils/logging";
import {
  intelligentMerge,
  getLatestTimestampVersion,
} from "../../utils/dataSyncHelpers";

const logger = serviceLogger("useConflictResolution");

interface UseConflictResolutionProps {
  conflicts: DataConflict[];
  setConflicts: Dispatch<SetStateAction<DataConflict[]>>;
  setSyncMetrics: Dispatch<SetStateAction<SyncMetrics>>;
}

export const useConflictResolution = ({
  conflicts,
  setConflicts,
  setSyncMetrics,
}: UseConflictResolutionProps) => {
  const resolveConflict = useCallback(
    async (
      conflictId: string,
      resolution: ConflictResolution,
    ): Promise<void> => {
      try {
        logger.debug("Resolving conflict", { conflictId, resolution });

        const conflict = conflicts.find((c) => c.id === conflictId);
        if (!conflict) {
          throw new Error("Conflict not found");
        }

        // Apply resolution strategy
        let _resolvedData: Record<string, unknown>;

        switch (resolution.strategy) {
          case "local_wins":
            _resolvedData = conflict.localVersion;
            break;
          case "remote_wins":
            _resolvedData = conflict.remoteVersion;
            break;
          case "keyholder_wins":
            _resolvedData = conflict.keyholderVersion || conflict.remoteVersion;
            break;
          case "merge_intelligent":
            _resolvedData = intelligentMerge(
              conflict.localVersion,
              conflict.remoteVersion,
            );
            break;
          case "latest_timestamp":
            _resolvedData = getLatestTimestampVersion(conflict);
            break;
          default:
            throw new Error(
              `Unsupported resolution strategy: ${resolution.strategy}`,
            );
        }

        // Remove resolved conflict
        setConflicts((prev) => prev.filter((c) => c.id !== conflictId));

        // Update metrics
        setSyncMetrics((prev) => ({
          ...prev,
          conflictsResolved: prev.conflictsResolved + 1,
        }));

        logger.info("Conflict resolved successfully", {
          conflictId,
          strategy: resolution.strategy,
        });
      } catch (error) {
        logger.error("Failed to resolve conflict", { error, conflictId });
        throw error;
      }
    },
    [conflicts, setConflicts, setSyncMetrics],
  );

  const resolveAllConflicts = useCallback(
    async (
      strategy: GlobalResolutionStrategy,
    ): Promise<ConflictResolutionResult[]> => {
      try {
        logger.debug("Resolving all conflicts", {
          strategy,
          conflictCount: conflicts.length,
        });

        const results: ConflictResolutionResult[] = [];

        for (const conflict of conflicts) {
          try {
            const resolutionStrategy =
              strategy.strategyByType[conflict.type] ||
              strategy.defaultStrategy;

            await resolveConflict(conflict.id, {
              conflictId: conflict.id,
              strategy: resolutionStrategy,
              preserveHistory: true,
            });

            results.push({
              conflictId: conflict.id,
              success: true,
              appliedStrategy: resolutionStrategy,
              resultingData: {},
            });
          } catch (error) {
            results.push({
              conflictId: conflict.id,
              success: false,
              appliedStrategy: strategy.defaultStrategy,
              resultingData: {},
              error: error instanceof Error ? error.message : "Unknown error",
            });
          }
        }

        logger.info("Batch conflict resolution completed", {
          total: conflicts.length,
          successful: results.filter((r) => r.success).length,
        });

        return results;
      } catch (error) {
        logger.error("Failed to resolve all conflicts", { error });
        throw error;
      }
    },
    [conflicts, resolveConflict],
  );

  return {
    resolveConflict,
    resolveAllConflicts,
  };
};

/**
 * Enhanced Data Synchronization Hook
 * Handles multi-user data sync, relationship data, and conflict resolution
 * with proper privacy controls
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import type { SyncResult } from "../../types/database";
import { serviceLogger } from "../../utils/logging";
import {
  calculateOverallSyncQuality,
  getLastSuccessfulSync,
  getSyncInterval,
} from "../../utils/dataSyncHelpers";
import { useConflictResolution } from "./useConflictResolution";
import { useSyncBackup } from "./useSyncBackup";
import { useSyncMonitoring } from "./useSyncMonitoring";

const logger = serviceLogger("useDataSync");

// ==================== INTERFACES ====================

import type {
  SyncStatus,
  RelationshipSyncStatus,
  DataConflict,
  SyncPermissions,
  SyncMetrics,
  SyncScope,
  DataEntityType,
  RelationshipSyncResult,
} from "./types/dataSync";
import type * as _Types from "./types/dataSync";
export type * from "./types/dataSync";

export const useDataSync = (userId: string) => {
  // ==================== STATE ====================

  const [syncStatus, setSyncStatus] = useState<SyncStatus>({
    state: "idle",
    lastSync: null,
    progress: 0,
    message: "Ready to sync",
    error: null,
  });

  const [relationshipSync, setRelationshipSync] = useState<
    RelationshipSyncStatus[]
  >([]);

  const [conflicts, setConflicts] = useState<DataConflict[]>([]);

  const [syncPermissions, setSyncPermissions] = useState<SyncPermissions>({
    allowDataSharing: true,
    shareSessionData: true,
    shareGoalData: true,
    shareTaskData: true,
    shareEventData: false,
    allowRealTimeSync: false,
    syncFrequency: "moderate",
    privacyLevel: "relationship_only",
  });

  const [syncMetrics, setSyncMetrics] = useState<SyncMetrics>({
    totalSyncs: 0,
    successfulSyncs: 0,
    failedSyncs: 0,
    averageSyncTime: 0,
    dataTransferred: 0,
    conflictsResolved: 0,
    lastSuccessfulSync: null,
    reliabilityScore: 100,
  });

  const [realTimeSyncEnabled, setRealTimeSyncEnabled] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // ==================== SUB-HOOKS ====================

  const conflictResolution = useConflictResolution({
    conflicts,
    setConflicts,
    setSyncMetrics,
  });

  const syncBackup = useSyncBackup({ userId });

  const syncMonitoring = useSyncMonitoring({ conflicts, syncMetrics });

  // ==================== COMPUTED VALUES ====================

  const isSyncing = useMemo(
    () => syncStatus.state === "syncing",
    [syncStatus.state],
  );

  const hasConflicts = useMemo(() => conflicts.length > 0, [conflicts.length]);

  const syncQuality = useMemo(
    () => calculateOverallSyncQuality(relationshipSync),
    [relationshipSync],
  );

  const lastSuccessfulSync = useMemo(
    () => getLastSuccessfulSync(syncMetrics),
    [syncMetrics],
  );

  const needsAttention = useMemo(
    () =>
      conflicts.some((c) => c.priority === "high" || c.priority === "critical"),
    [conflicts],
  );

  // ==================== INITIALIZATION ====================

  useEffect(() => {
    const initializeSync = async () => {
      if (!userId) return;

      try {
        setIsLoading(true);
        setError(null);

        // Load sync state and configuration
        await Promise.all([
          loadSyncPermissions(),
          loadSyncHistory(),
          loadPendingConflicts(),
          loadRelationshipSyncStatus(),
        ]);

        // Initialize real-time sync if enabled
        if (syncPermissions.allowRealTimeSync) {
          await initializeRealTimeSync();
        }
      } catch (err) {
        logger.error("Failed to initialize data sync", { error: err });
        setError(
          err instanceof Error ? err.message : "Failed to initialize sync",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializeSync();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, syncPermissions.allowRealTimeSync]);

  // ==================== REAL-TIME SYNC ====================

  useEffect(() => {
    if (!realTimeSyncEnabled || !syncPermissions.allowRealTimeSync) return;

    const syncInterval = getSyncInterval(syncPermissions.syncFrequency);
    const interval = setInterval(() => {
      performBackgroundSync();
    }, syncInterval);

    return () => clearInterval(interval);
    // performBackgroundSync is stable (no deps), syncPermissions used for getSyncInterval
    // syncPermissions is state, not a store action
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [realTimeSyncEnabled, syncPermissions]);

  // ==================== DATA LOADING FUNCTIONS ====================

  const loadSyncPermissions = useCallback(async () => {
    try {
      // Load user's sync preferences from database
      logger.debug("Loading sync permissions", { userId });
    } catch (error) {
      logger.error("Failed to load sync permissions", { error });
    }
  }, [userId]);

  const loadSyncHistory = useCallback(async () => {
    try {
      // Load sync metrics and history
      logger.debug("Loading sync history", { userId });
    } catch (error) {
      logger.error("Failed to load sync history", { error });
    }
  }, [userId]);

  const loadPendingConflicts = useCallback(async () => {
    try {
      // Load unresolved conflicts
      setConflicts([]);
      logger.debug("Loading pending conflicts", { userId });
    } catch (error) {
      logger.error("Failed to load pending conflicts", { error });
    }
  }, [userId]);

  const loadRelationshipSyncStatus = useCallback(async () => {
    try {
      // Load sync status for all relationships
      setRelationshipSync([]);
      logger.debug("Loading relationship sync status", { userId });
    } catch (error) {
      logger.error("Failed to load relationship sync status", { error });
    }
  }, [userId]);

  // ==================== MANUAL SYNC OPERATIONS ====================

  const forceSyncAll = useCallback(async (): Promise<SyncResult> => {
    try {
      logger.debug("Starting force sync all", { userId });

      setSyncStatus({
        state: "syncing",
        lastSync: null,
        progress: 0,
        message: "Starting synchronization...",
        error: null,
      });

      // Simulate sync process
      for (let i = 0; i <= 100; i += 20) {
        setSyncStatus((prev) => ({
          ...prev,
          progress: i,
          message: `Syncing... ${i}%`,
        }));
        await new Promise((resolve) => setTimeout(resolve, 200));
      }

      const result: SyncResult = {
        success: true,
        operations: {
          uploaded: 15,
          downloaded: 8,
          conflicts: 2,
        },
        conflicts: [],
        timestamp: new Date(),
      };

      setSyncStatus({
        state: "completed",
        lastSync: new Date(),
        progress: 100,
        message: "Sync completed successfully",
        error: null,
      });

      // Update metrics
      setSyncMetrics((prev) => ({
        ...prev,
        totalSyncs: prev.totalSyncs + 1,
        successfulSyncs: prev.successfulSyncs + 1,
        lastSuccessfulSync: new Date(),
      }));

      logger.info("Force sync completed successfully", { result });
      return result;
    } catch (error) {
      logger.error("Force sync failed", { error });

      setSyncStatus({
        state: "error",
        lastSync: null,
        progress: 0,
        message: "Sync failed",
        error: error instanceof Error ? error.message : "Unknown error",
      });

      setSyncMetrics((prev) => ({
        ...prev,
        totalSyncs: prev.totalSyncs + 1,
        failedSyncs: prev.failedSyncs + 1,
      }));

      throw error;
    }
  }, [userId]);

  const syncRelationshipData = useCallback(
    async (relationshipId: string): Promise<RelationshipSyncResult> => {
      try {
        logger.debug("Syncing relationship data", { relationshipId });

        const startTime = Date.now();

        // Find relationship sync status
        const relationshipStatus = relationshipSync.find(
          (rs) => rs.relationshipId === relationshipId,
        );
        if (!relationshipStatus) {
          throw new Error("Relationship not found");
        }

        // Perform relationship-specific sync
        const result: RelationshipSyncResult = {
          relationshipId,
          success: true,
          syncedCollections: ["sessions", "goals", "tasks"],
          conflictsFound: 1,
          conflictsResolved: 0,
          metrics: {
            duration: Date.now() - startTime,
            itemsSynced: 12,
            bytesTransferred: 4096,
          },
        };

        // Update relationship sync status
        setRelationshipSync((prev) =>
          prev.map((rs) =>
            rs.relationshipId === relationshipId
              ? { ...rs, lastSync: new Date() }
              : rs,
          ),
        );

        logger.info("Relationship sync completed", { relationshipId, result });
        return result;
      } catch (error) {
        logger.error("Relationship sync failed", { error, relationshipId });

        return {
          relationshipId,
          success: false,
          syncedCollections: [],
          conflictsFound: 0,
          conflictsResolved: 0,
          error: error instanceof Error ? error.message : "Unknown error",
          metrics: {
            duration: 0,
            itemsSynced: 0,
            bytesTransferred: 0,
          },
        };
      }
    },
    [relationshipSync],
  );

  // ==================== PRIVACY AND PERMISSIONS ====================

  const updateSyncPermissions = useCallback(
    async (permissions: Partial<SyncPermissions>): Promise<void> => {
      try {
        logger.debug("Updating sync permissions", { permissions });

        const updatedPermissions = { ...syncPermissions, ...permissions };
        setSyncPermissions(updatedPermissions);

        // Update real-time sync based on new permissions
        if (
          updatedPermissions.allowRealTimeSync !==
          syncPermissions.allowRealTimeSync
        ) {
          if (updatedPermissions.allowRealTimeSync) {
            await initializeRealTimeSync();
          } else {
            setRealTimeSyncEnabled(false);
          }
        }

        logger.info("Sync permissions updated successfully");
      } catch (error) {
        logger.error("Failed to update sync permissions", { error });
        throw error;
      }
    },
    [syncPermissions, initializeRealTimeSync],
  );

  const configureSyncScope = useCallback(
    async (scope: SyncScope): Promise<void> => {
      try {
        logger.debug("Configuring sync scope", { scope });
        // Configure what data gets synced
        logger.info("Sync scope configured successfully");
      } catch (error) {
        logger.error("Failed to configure sync scope", { error });
        throw error;
      }
    },
    [],
  );

  // ==================== REAL-TIME SYNC ====================

  const enableRealtimeSync = useCallback(
    (entityTypes: DataEntityType[]): void => {
      try {
        logger.debug("Enabling realtime sync", { entityTypes });
        setRealTimeSyncEnabled(true);
        // Set up WebSocket connections or other real-time mechanisms
        logger.info("Realtime sync enabled successfully");
      } catch (error) {
        logger.error("Failed to enable realtime sync", { error });
      }
    },
    [],
  );

  const disableRealtimeSync = useCallback((): void => {
    try {
      logger.debug("Disabling realtime sync");
      setRealTimeSyncEnabled(false);
      // Clean up real-time connections
      logger.info("Realtime sync disabled successfully");
    } catch (error) {
      logger.error("Failed to disable realtime sync", { error });
    }
  }, []);

  // ==================== PRIVATE HELPER FUNCTIONS ====================

  const initializeRealTimeSync = useCallback(async (): Promise<void> => {
    try {
      logger.debug("Initializing real-time sync");
      setRealTimeSyncEnabled(true);
      // Set up WebSocket connections, event listeners, etc.
    } catch (error) {
      logger.error("Failed to initialize real-time sync", { error });
    }
  }, []);

  const performBackgroundSync = useCallback(async (): Promise<void> => {
    try {
      // Perform lightweight background sync
      logger.debug("Performing background sync");
    } catch (error) {
      logger.error("Background sync failed", { error });
    }
  }, []);

  // ==================== RETURN HOOK INTERFACE ====================

  return {
    // Sync state
    syncStatus,
    relationshipSync,
    conflicts,
    syncMetrics,

    // Manual sync operations
    forceSyncAll,
    syncRelationshipData,

    // Conflict resolution (from useConflictResolution hook)
    resolveConflict: conflictResolution.resolveConflict,
    resolveAllConflicts: conflictResolution.resolveAllConflicts,

    // Privacy and permissions
    updateSyncPermissions,
    configureSyncScope,

    // Real-time sync
    enableRealtimeSync,
    disableRealtimeSync,

    // Backup and recovery (from useSyncBackup hook)
    createBackup: syncBackup.createBackup,
    restoreFromBackup: syncBackup.restoreFromBackup,

    // Monitoring (from useSyncMonitoring hook)
    getSyncHealth: syncMonitoring.getSyncHealth,
    getSyncHistory: syncMonitoring.getSyncHistory,

    // Computed values
    isSyncing,
    hasConflicts,
    syncQuality,
    lastSuccessfulSync,
    needsAttention,

    // Loading states
    isLoading,
    error,
  };
};

/**
 * Comprehensive Statistics Hook
 * Provides analytics and insights for both users and keyholders
 * with appropriate privacy controls
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import { serviceLogger } from "../../utils/logging";
import {
  calculateImprovementScore,
  calculateConsistencyRating,
  calculateOverallProgress,
  calculateKeyholderSatisfaction,
} from "../../utils/statisticsHelpers";

const logger = serviceLogger("useStatistics");

// ==================== INTERFACES ====================

// Re-export all types from the types file
export type * from "./types/statistics";

import type {
  SessionStatistics,
  GoalStatistics,
  AchievementStatistics,
  ComparativeStatistics,
  SharedStatistics,
  PredictiveAnalytics,
  RecommendationEngine,
  TimePeriod,
  PeriodStatistics,
  MonthlyTrends,
  WeeklyBreakdown,
  ComparisonResult,
  BenchmarkData,
  KeyholderDashboardStats,
  RelationshipComparisonStats,
  PredictiveInsights,
  Recommendation,
  StatisticsExport,
  ExportFormat,
} from "./types/statistics";

// ==================== HOOK IMPLEMENTATION ====================

export const useStatistics = (userId: string, relationshipId?: string) => {
  // ==================== STATE ====================

  const [sessionStats, setSessionStats] = useState<SessionStatistics>({
    totalSessionTime: 0,
    averageSessionLength: 0,
    longestSession: 0,
    shortestSession: 0,
    sessionsThisWeek: 0,
    sessionsThisMonth: 0,
    sessionFrequency: {
      daily: 0,
      weekly: 0,
      monthly: 0,
      trend: "stable",
    },
    completionRate: 0,
    goalAchievementRate: 0,
    satisfactionRating: 0,
    trends: [],
    streaks: {
      current: 0,
      longest: 0,
      type: "session_consistency",
    },
  });

  const [goalStats, setGoalStats] = useState<GoalStatistics>({
    totalGoals: 0,
    completedGoals: 0,
    activeGoals: 0,
    completionRate: 0,
    averageCompletionTime: 0,
    mostCommonGoalTypes: [],
    hardestGoalTypes: [],
    goalStreaks: {
      current: 0,
      longest: 0,
      type: "goal_completion",
    },
  });

  const [achievementStats, setAchievementStats] =
    useState<AchievementStatistics>({
      totalAchievements: 0,
      recentAchievements: [],
      achievementsByCategory: [],
      rareAchievements: [],
      achievementPoints: 0,
      percentileRank: 0,
    });

  const [comparativeStats, setComparativeStats] =
    useState<ComparativeStatistics>({
      userPercentile: 50,
      averageUserStats: sessionStats,
      personalBest: {
        longestSession: 0,
        mostGoalsInSession: 0,
        longestStreak: 0,
        highestSatisfactionRating: 0,
        bestMonth: {
          month: "",
          year: 0,
          totalTime: 0,
          sessionCount: 0,
        },
      },
      improvements: {
        sessionLength: { improvement: 0, timeframe: "month" },
        consistency: { improvement: 0, timeframe: "month" },
        goalCompletion: { improvement: 0, timeframe: "month" },
      },
    });

  const [sharedStats, setSharedStats] = useState<SharedStatistics>({
    allowedMetrics: [],
    keyholderView: {
      sessionOverview: {
        totalSessions: 0,
        averageDuration: 0,
        lastSessionDate: new Date(),
      },
      goalProgress: {
        activeGoals: 0,
        completionRate: 0,
      },
      behaviorPatterns: {
        consistency: 0,
        pauseFrequency: 0,
        improvementTrend: "stable",
      },
      allowedInsights: [],
    },
    lastSharedAt: new Date(),
    sharingLevel: "basic",
  });

  const [_predictiveAnalytics, setPredictiveAnalytics] =
    useState<PredictiveAnalytics>({
      nextSessionPrediction: {
        suggestedDuration: 0,
        successProbability: 0,
        optimalStartTime: new Date(),
        riskFactors: [],
      },
      goalRecommendations: [],
      improvementOpportunities: [],
      trendPredictions: [],
    });

  const [_recommendations, setRecommendations] = useState<RecommendationEngine>(
    {
      sessionRecommendations: [],
      goalRecommendations: [],
      behaviorInsights: [],
      personalizedTips: [],
    },
  );

  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // ==================== COMPUTED VALUES ====================

  const improvementScore = useMemo(
    () => calculateImprovementScore(sessionStats.trends),
    [sessionStats.trends],
  );

  const consistencyRating = useMemo(
    () => calculateConsistencyRating(sessionStats),
    [sessionStats],
  );

  const overallProgress = useMemo(
    () => calculateOverallProgress(goalStats),
    [goalStats],
  );

  const keyholderSatisfaction = useMemo(
    () => calculateKeyholderSatisfaction(sharedStats),
    [sharedStats],
  );

  // ==================== INITIALIZATION ====================

  useEffect(() => {
    const initializeStatistics = async () => {
      if (!userId) return;

      try {
        setIsLoading(true);
        setError(null);

        // Load all statistics data
        await Promise.all([
          loadSessionStatistics(),
          loadGoalStatistics(),
          loadAchievementStatistics(),
          loadComparativeStatistics(),
          loadSharedStatistics(),
          loadPredictiveAnalytics(),
          loadRecommendations(),
        ]);
      } catch (err) {
        logger.error("Failed to initialize statistics", { error: err });
        setError(
          err instanceof Error
            ? err.message
            : "Failed to initialize statistics",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializeStatistics();
    // Callback functions are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, relationshipId]);

  // ==================== DATA LOADING FUNCTIONS ====================

  const loadSessionStatistics = useCallback(async () => {
    try {
      // This would integrate with your session database service
      // Calculate session statistics from historical data
      const mockStats: SessionStatistics = {
        totalSessionTime: 7200, // 2 hours
        averageSessionLength: 3600, // 1 hour
        longestSession: 7200,
        shortestSession: 1800,
        sessionsThisWeek: 3,
        sessionsThisMonth: 12,
        sessionFrequency: {
          daily: 0.4,
          weekly: 3,
          monthly: 12,
          trend: "stable",
        },
        completionRate: 85,
        goalAchievementRate: 75,
        satisfactionRating: 4.2,
        trends: [],
        streaks: {
          current: 5,
          longest: 12,
          type: "session_consistency",
        },
      };

      setSessionStats(mockStats);
    } catch (error) {
      logger.error("Failed to load session statistics", { error });
    }
  }, []); // userId not actually used in mock implementation

  const loadGoalStatistics = useCallback(async () => {
    try {
      // Calculate goal statistics from historical goal data
      setGoalStats({
        totalGoals: 25,
        completedGoals: 20,
        activeGoals: 3,
        completionRate: 80,
        averageCompletionTime: 48, // hours
        mostCommonGoalTypes: [],
        hardestGoalTypes: [],
        goalStreaks: {
          current: 3,
          longest: 8,
          type: "goal_completion",
        },
      });
    } catch (error) {
      logger.error("Failed to load goal statistics", { error });
    }
  }, []); // userId not actually used in mock implementation

  const loadAchievementStatistics = useCallback(async () => {
    try {
      // Load achievement data
      setAchievementStats({
        totalAchievements: 15,
        recentAchievements: [],
        achievementsByCategory: [],
        rareAchievements: [],
        achievementPoints: 1500,
        percentileRank: 75,
      });
    } catch (error) {
      logger.error("Failed to load achievement statistics", { error });
    }
  }, []);

  const loadComparativeStatistics = useCallback(async () => {
    try {
      // Load comparative data (anonymized benchmarks)
      setComparativeStats((prev) => ({
        ...prev,
        userPercentile: 72,
      }));
    } catch (error) {
      logger.error("Failed to load comparative statistics", { error });
    }
  }, []); // sessionStats not actually used for computation

  const loadSharedStatistics = useCallback(async () => {
    try {
      if (!relationshipId) return;

      // Load keyholder-specific statistics view
      const keyholderView: KeyholderStatisticsView = {
        sessionOverview: {
          totalSessions: sessionStats.sessionsThisMonth,
          averageDuration: sessionStats.averageSessionLength,
          lastSessionDate: new Date(),
        },
        goalProgress: {
          activeGoals: goalStats.activeGoals,
          completionRate: goalStats.completionRate,
        },
        behaviorPatterns: {
          consistency: consistencyRating,
          pauseFrequency: 0.2,
          improvementTrend: "improving",
        },
        allowedInsights: ["session_duration", "goal_completion"],
      };

      setSharedStats((prev) => ({
        ...prev,
        keyholderView,
      }));
    } catch (error) {
      logger.error("Failed to load shared statistics", { error });
    }
  }, [
    relationshipId,
    consistencyRating,
    sessionStats.sessionsThisMonth,
    sessionStats.averageSessionLength,
    goalStats.activeGoals,
    goalStats.completionRate,
  ]);

  const loadPredictiveAnalytics = useCallback(async () => {
    try {
      // Generate predictive insights based on historical data
      setPredictiveAnalytics({
        nextSessionPrediction: {
          suggestedDuration: sessionStats.averageSessionLength * 1.1,
          successProbability: 85,
          optimalStartTime: new Date(),
          riskFactors: [],
        },
        goalRecommendations: [],
        improvementOpportunities: [],
        trendPredictions: [],
      });
    } catch (error) {
      logger.error("Failed to load predictive analytics", { error });
    }
  }, [sessionStats.averageSessionLength]);

  const loadRecommendations = useCallback(async () => {
    try {
      // Generate personalized recommendations
      setRecommendations({
        sessionRecommendations: [],
        goalRecommendations: [],
        behaviorInsights: [],
        personalizedTips: [],
      });
    } catch (error) {
      logger.error("Failed to load recommendations", { error });
    }
  }, []); // Static data for recommendations

  // ==================== TIME-BASED QUERIES ====================

  const getStatsForPeriod = useCallback(
    (period: TimePeriod): PeriodStatistics => {
      // Filter and calculate statistics for specific time period
      return {
        period,
        sessionCount: 0,
        totalTime: 0,
        goalCompletionRate: 0,
        averageSatisfaction: 0,
      };
    },
    [],
  );

  const getMonthlyTrends = useCallback((_months: number): MonthlyTrends => {
    // Calculate monthly trends for specified number of months
    return {
      months: [],
      overallTrend: "stable",
    };
  }, []);

  const getWeeklyBreakdown = useCallback((): WeeklyBreakdown => {
    // Analyze sessions by day of week
    return {
      weekdays: [],
      weekendVsWeekday: {
        weekday: {
          period: { start: new Date(), end: new Date(), label: "Weekday" },
          sessionCount: 0,
          totalTime: 0,
          goalCompletionRate: 0,
          averageSatisfaction: 0,
        },
        weekend: {
          period: { start: new Date(), end: new Date(), label: "Weekend" },
          sessionCount: 0,
          totalTime: 0,
          goalCompletionRate: 0,
          averageSatisfaction: 0,
        },
      },
    };
  }, []);

  // ==================== COMPARATIVE ANALYSIS ====================

  const compareWithPrevious = useCallback(
    (_period: TimePeriod): ComparisonResult => {
      // Compare current metrics with previous period
      return {
        metric: "session_duration",
        currentValue: sessionStats.averageSessionLength,
        previousValue: sessionStats.averageSessionLength * 0.9,
        change: sessionStats.averageSessionLength * 0.1,
        changePercentage: 10,
        trend: "improving",
      };
    },
    [sessionStats],
  );

  const getBenchmarkComparisons = useCallback((): BenchmarkData[] => {
    // Compare user metrics with anonymized community benchmarks
    return [
      {
        userValue: sessionStats.averageSessionLength,
        averageValue: 3000, // 50 minutes average
        percentile: 75,
        category: "session_duration",
      },
    ];
  }, [sessionStats]);

  // ==================== KEYHOLDER FEATURES ====================

  const getKeyholderDashboard = useCallback((): KeyholderDashboardStats => {
    return {
      submissiveOverview: {
        name: "Submissive",
        consistencyScore: consistencyRating,
        improvementTrend: "improving",
        lastActiveDate: new Date(),
      },
      sessionSummary: {
        thisWeek: sessionStats.sessionsThisWeek,
        thisMonth: sessionStats.sessionsThisMonth,
        averageDuration: sessionStats.averageSessionLength,
        completionRate: sessionStats.completionRate,
      },
      goalTracking: {
        activeGoals: [],
        completionRate: goalStats.completionRate,
        upcomingDeadlines: [],
      },
      behaviorInsights: {
        strengths: [],
        areasForImprovement: [],
        recommendations: [],
      },
    };
  }, [
    consistencyRating,
    sessionStats.sessionsThisWeek,
    sessionStats.sessionsThisMonth,
    sessionStats.averageSessionLength,
    sessionStats.completionRate,
    goalStats.completionRate,
  ]);

  const getRelationshipComparison =
    useCallback((): RelationshipComparisonStats => {
      return {
        relationshipDuration: 90, // days
        sharedSessions: sessionStats.sessionsThisMonth,
        collaborationScore: 85,
        satisfactionTrend: "improving",
        milestones: [],
      };
    }, [sessionStats.sessionsThisMonth]);

  // ==================== PREDICTIVE ANALYTICS ====================

  const getPredictiveInsights = useCallback((): PredictiveInsights => {
    return {
      nextSessionSuccess: {
        probability: 85,
        factors: ["consistent schedule", "appropriate goal setting"],
      },
      goalAchievementLikelihood: [],
      riskAssessment: {
        burnoutRisk: "low",
        consistencyRisk: "low",
        factors: [],
      },
    };
  }, []);

  const getRecommendations = useCallback((): Recommendation[] => {
    return [
      {
        id: "rec1",
        type: "session",
        title: "Optimize Session Timing",
        description:
          "Consider starting sessions earlier in the day for better completion rates",
        priority: "medium",
        expectedImpact: "Improved session completion rate",
        actionRequired: "Adjust session start time",
      },
    ];
  }, []);

  // ==================== EXPORT AND SHARING ====================

  const exportStatistics = useCallback(
    async (format: ExportFormat): Promise<StatisticsExport> => {
      try {
        logger.debug("Exporting statistics", { format, userId });

        const exportData = {
          sessionStats,
          goalStats,
          achievementStats,
          exportedAt: new Date(),
        };

        return {
          format,
          data: exportData,
          generatedAt: new Date(),
          fileSize: JSON.stringify(exportData).length,
          downloadUrl: "https://example.com/download/stats",
        };
      } catch (error) {
        logger.error("Failed to export statistics", { error });
        throw error;
      }
    },
    [userId, sessionStats, goalStats, achievementStats],
  );

  const shareWithKeyholder = useCallback(
    async (statTypes: StatisticType[]): Promise<void> => {
      try {
        if (!relationshipId) {
          throw new Error("No keyholder relationship found");
        }

        logger.debug("Sharing statistics with keyholder", { statTypes });

        setSharedStats((prev) => ({
          ...prev,
          allowedMetrics: statTypes,
          lastSharedAt: new Date(),
        }));

        logger.info("Statistics shared with keyholder successfully");
      } catch (error) {
        logger.error("Failed to share statistics with keyholder", { error });
        throw error;
      }
    },
    [relationshipId],
  );

  // ==================== RETURN HOOK INTERFACE ====================

  return {
    // Statistics
    sessionStats,
    goalStats,
    achievementStats,
    comparativeStats,

    // Time-based queries
    getStatsForPeriod,
    getMonthlyTrends,
    getWeeklyBreakdown,

    // Comparative analysis
    compareWithPrevious,
    getBenchmarkComparisons,

    // Keyholder features
    getKeyholderDashboard,
    getRelationshipComparison,

    // Predictive analytics
    getPredictiveInsights,
    getRecommendations,

    // Export and sharing
    exportStatistics,
    shareWithKeyholder,

    // Computed values
    improvementScore,
    consistencyRating,
    overallProgress,
    keyholderSatisfaction,

    // Loading states
    isLoading,
    error,
  };
};

