export interface UseDisplaySettingsReturn {
  displaySettings: DisplaySettings;
  isLoading: boolean;
  updateTheme: (theme: "light" | "dark" | "auto") => Promise<void>;
  updateLanguage: (language: string) => Promise<void>;
  updateDateFormat: (format: string) => Promise<void>;
  updateTimeFormat: (format: "12h" | "24h") => Promise<void>;
  resetToDefaults: () => Promise<void>;
  isUpdating: boolean;
  error: Error | null;
  previewSettings: (settings: Partial<DisplaySettings>) => void;
  clearPreview: () => void;
}

const defaultSettings: DisplaySettings = {
  theme: "auto",
  language: "en",
  dateFormat: "MM/DD/YYYY",
  timeFormat: "12h",
};

export function useDisplaySettings(): UseDisplaySettingsReturn {
  const [displaySettings, setDisplaySettings] =
    useState<DisplaySettings>(defaultSettings);
  const [isLoading, setIsLoading] = useState(true);
  const [isUpdating, setIsUpdating] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [_previewedSettings, _setPreviewedSettings] =
    useState<Partial<DisplaySettings> | null>(null);

  const updateTheme = useCallback(
    async (theme: "light" | "dark" | "auto"): Promise<void> => {
      setIsUpdating(true);
      setError(null);
      try {
        await new Promise((resolve) => setTimeout(resolve, 100));
        setDisplaySettings((prev) => ({ ...prev, theme }));
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to update theme");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  const updateLanguage = useCallback(
    async (language: string): Promise<void> => {
      setIsUpdating(true);
      setError(null);
      try {
        await new Promise((resolve) => setTimeout(resolve, 100));
        setDisplaySettings((prev) => ({ ...prev, language }));
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to update language");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  const updateDateFormat = useCallback(
    async (dateFormat: string): Promise<void> => {
      setIsUpdating(true);
      setError(null);
      try {
        await new Promise((resolve) => setTimeout(resolve, 100));
        setDisplaySettings((prev) => ({ ...prev, dateFormat }));
      } catch (err) {
        const error =
          err instanceof Error
            ? err
            : new Error("Failed to update date format");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  const updateTimeFormat = useCallback(
    async (timeFormat: "12h" | "24h"): Promise<void> => {
      setIsUpdating(true);
      setError(null);
      try {
        await new Promise((resolve) => setTimeout(resolve, 100));
        setDisplaySettings((prev) => ({ ...prev, timeFormat }));
      } catch (err) {
        const error =
          err instanceof Error
            ? err
            : new Error("Failed to update time format");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  const resetToDefaults = useCallback(async (): Promise<void> => {
    setIsUpdating(true);
    setError(null);
    try {
      await new Promise((resolve) => setTimeout(resolve, 100));
      setDisplaySettings(defaultSettings);
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to reset settings");
      setError(error);
      throw error;
    } finally {
      setIsUpdating(false);
    }
  }, []);

  const previewSettings = useCallback((settings: Partial<DisplaySettings>) => {
    setPreviewedSettings(settings);
  }, []);

  const clearPreview = useCallback(() => {
    setPreviewedSettings(null);
  }, []);

  useState(() => {
    setTimeout(() => setIsLoading(false), 100);
  });

  return {
    displaySettings,
    isLoading,
    updateTheme,
    updateLanguage,
    updateDateFormat,
    updateTimeFormat,
    resetToDefaults,
    isUpdating,
    error,
    previewSettings,
    clearPreview,
  };
}

/**
 * useGameification Hook - Enhanced Gamification System
 *
 * Comprehensive gamification system beyond basic achievements, including challenges,
 * leaderboards, seasons, and social features.
 */

import { useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  PlayerProfile,
  Challenge,
  Leaderboard,
  Season,
  SocialGameFeatures,
  Badge,
  ExperienceSource,
  LevelResult,
  LevelUpResult,
  ChallengeCompletion,
  LeaderboardRank,
  FriendComparison,
  SeasonalReward,
  PlayerTitle,
  ChallengeDifficulty,
  ChallengeType,
  LeaderboardCategory,
  LeaderboardPeriod,
  BadgeCategory,
  ExperienceEvent,
} from "../../types/gamification";
import { logger } from "../../utils/logging";

// Storage keys
const STORAGE_KEYS = {
  PLAYER_PROFILE: "chastity-gamification-profile",
  CHALLENGES: "chastity-gamification-challenges",
  LEADERBOARDS: "chastity-gamification-leaderboards",
  SOCIAL_FEATURES: "chastity-gamification-social",
  EXPERIENCE_HISTORY: "chastity-gamification-experience",
};

// Experience values by source
const _EXPERIENCE_VALUES = {
  [ExperienceSource.SESSION_COMPLETE]: 100,
  [ExperienceSource.CHALLENGE_COMPLETE]: 250,
  [ExperienceSource.MILESTONE_REACHED]: 150,
  [ExperienceSource.BEHAVIOR_IMPROVEMENT]: 75,
  [ExperienceSource.DAILY_CHECK_IN]: 25,
  [ExperienceSource.SOCIAL_INTERACTION]: 50,
  [ExperienceSource.GOAL_ACHIEVEMENT]: 200,
};

// Level thresholds
const LEVEL_THRESHOLDS = [
  0, 100, 250, 500, 1000, 1750, 2750, 4000, 5500, 7500, 10000, 13000, 16500,
  20500, 25000, 30000, 35500, 41500, 48000, 55000, 62500,
];

// Default player profile
const DEFAULT_PLAYER_PROFILE: PlayerProfile = {
  level: 1,
  experience: 0,
  experienceToNext: 100,
  title: PlayerTitle.NOVICE,
  badges: [],
  stats: {
    totalExperience: 0,
    challengesCompleted: 0,
    currentStreak: 0,
    longestStreak: 0,
    totalDuration: 0,
    badgesEarned: 0,
    leaderboardRank: {
      [LeaderboardCategory.EXPERIENCE]: 0,
      [LeaderboardCategory.DURATION]: 0,
      [LeaderboardCategory.CHALLENGES]: 0,
      [LeaderboardCategory.STREAKS]: 0,
      [LeaderboardCategory.SOCIAL]: 0,
    },
    socialConnections: 0,
    achievementPoints: 0,
  },
  preferences: {
    showLevel: true,
    showBadges: true,
    participateInLeaderboards: true,
    allowSocialFeatures: true,
    notificationSettings: {
      levelUp: true,
      badgeEarned: true,
      challengeComplete: true,
      leaderboardUpdate: false,
      socialActivity: true,
      seasonalEvents: true,
    },
  },
  joinedAt: new Date(),
  lastActive: new Date(),
};

// Sample challenges
const SAMPLE_CHALLENGES: Challenge[] = [
  {
    id: "challenge-streak-7",
    type: ChallengeType.DURATION,
    name: "7-Day Streak",
    description: "Maintain a 7-day consecutive streak",
    difficulty: ChallengeDifficulty.BEGINNER,
    requirements: [
      {
        id: "req-1",
        type: "duration",
        description: "Complete 7 consecutive days",
        targetValue: 7,
        currentValue: 0,
        completed: false,
      },
    ],
    rewards: [
      {
        type: "experience",
        value: 500,
        description: "500 XP",
        claimed: false,
      },
      {
        type: "badge",
        value: 1,
        description: "Streak Master Badge",
        claimed: false,
      },
    ],
    startDate: new Date(),
    endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000),
    progress: {
      percentage: 0,
      requirementsCompleted: 0,
      totalRequirements: 1,
      lastUpdated: new Date(),
      milestones: [],
    },
    isCompleted: false,
    participants: 247,
    isPublic: true,
  },
];

/**
 * Enhanced Gamification Hook
 */
export const useGameification = (userId: string) => {
  const queryClient = useQueryClient();

  // Get player profile
  const { data: playerProfile = DEFAULT_PLAYER_PROFILE } =
    useQuery<PlayerProfile>({
      queryKey: ["gamification", "profile", userId],
      queryFn: () => {
        const stored = localStorage.getItem(STORAGE_KEYS.PLAYER_PROFILE);
        return stored
          ? { ...DEFAULT_PLAYER_PROFILE, ...JSON.parse(stored) }
          : DEFAULT_PLAYER_PROFILE;
      },
      enabled: Boolean(userId),
      staleTime: 30 * 1000,
    });

  // Get active challenges
  const { data: activeChallenges = [] } = useQuery<Challenge[]>({
    queryKey: ["gamification", "challenges", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.CHALLENGES);
      const userChallenges = stored ? JSON.parse(stored) : [];
      return [...SAMPLE_CHALLENGES, ...userChallenges].filter(
        (c) => !c.isCompleted,
      );
    },
    enabled: Boolean(userId),
    staleTime: 60 * 1000,
  });

  // Get leaderboards
  const { data: leaderboards = [] } = useQuery<Leaderboard[]>({
    queryKey: ["gamification", "leaderboards"],
    queryFn: async () => {
      // Simulate leaderboard data
      return generateSampleLeaderboards();
    },
    staleTime: 5 * 60 * 1000,
    refetchInterval: 10 * 60 * 1000,
  });

  // Get current season
  const { data: currentSeason } = useQuery<Season | null>({
    queryKey: ["gamification", "season"],
    queryFn: () => {
      return {
        id: "season-winter-2024",
        name: "Winter Challenge 2024",
        description:
          "Embrace the cold season with special winter-themed challenges",
        theme: "winter",
        startDate: new Date("2024-12-01"),
        endDate: new Date("2024-02-28"),
        rewards: generateSeasonalRewards(),
        challenges: ["winter-endurance", "cold-discipline"],
        leaderboards: ["winter-champions"],
        isActive: true,
      };
    },
    staleTime: 60 * 60 * 1000, // 1 hour
  });

  // Get social features
  const { data: socialFeatures } = useQuery<SocialGameFeatures>({
    queryKey: ["gamification", "social", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.SOCIAL_FEATURES);
      return stored
        ? JSON.parse(stored)
        : {
            friends: [],
            pendingRequests: [],
            recentActivity: [],
            groups: [],
            comparisons: [],
          };
    },
    enabled: Boolean(userId) && playerProfile.preferences.allowSocialFeatures,
    staleTime: 2 * 60 * 1000,
  });

  // Get experience history
  const { data: experienceHistory = [] } = useQuery<ExperienceEvent[]>({
    queryKey: ["gamification", "experience", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.EXPERIENCE_HISTORY);
      return stored ? JSON.parse(stored) : [];
    },
    enabled: Boolean(userId),
    staleTime: 60 * 1000,
  });

  // Accept challenge mutation
  const acceptChallengeMutation = useMutation({
    mutationFn: async (challengeId: string) => {
      const challenge = activeChallenges.find((c) => c.id === challengeId);
      if (!challenge) throw new Error("Challenge not found");

      logger.info("Challenge accepted", { challengeId, userId });

      // In a real implementation, this would register the user for the challenge
      await new Promise((resolve) => setTimeout(resolve, 500));
    },
  });

  // Complete challenge mutation
  const completeChallengeMutation = useMutation({
    mutationFn: async (challengeId: string): Promise<ChallengeCompletion> => {
      const challenge = activeChallenges.find((c) => c.id === challengeId);
      if (!challenge) throw new Error("Challenge not found");

      logger.info("Challenge completed", { challengeId, userId });

      // Mark challenge as completed
      const updatedChallenges = activeChallenges.map((c) =>
        c.id === challengeId
          ? {
              ...c,
              isCompleted: true,
              progress: { ...c.progress, percentage: 100 },
            }
          : c,
      );
      localStorage.setItem(
        STORAGE_KEYS.CHALLENGES,
        JSON.stringify(updatedChallenges),
      );

      // Calculate rewards
      const experienceGained = challenge.rewards.reduce(
        (total, reward) =>
          reward.type === "experience" ? total + reward.value : total,
        0,
      );

      // Add experience
      const levelResult = await addExperienceInternal(
        experienceGained,
        ExperienceSource.CHALLENGE_COMPLETE,
      );

      // Create completion result
      const completion: ChallengeCompletion = {
        challengeId,
        completedAt: new Date(),
        rewards: challenge.rewards,
        experience: experienceGained,
        newBadges: challenge.rewards
          .filter((r) => r.type === "badge")
          .map((r) => createBadgeFromReward(r)),
        levelUp: levelResult.leveledUp
          ? {
              newLevel: levelResult.newLevel,
              rewards: [],
              unlockedFeatures: [],
            }
          : undefined,
      };

      // Update stats
      const updatedProfile = {
        ...playerProfile,
        stats: {
          ...playerProfile.stats,
          challengesCompleted: playerProfile.stats.challengesCompleted + 1,
          totalExperience:
            playerProfile.stats.totalExperience + experienceGained,
        },
      };
      localStorage.setItem(
        STORAGE_KEYS.PLAYER_PROFILE,
        JSON.stringify(updatedProfile),
      );
      queryClient.setQueryData(
        ["gamification", "profile", userId],
        updatedProfile,
      );

      return completion;
    },
  });

  // Add experience mutation
  const addExperienceMutation = useMutation({
    mutationFn: async ({
      amount,
      source,
    }: {
      amount: number;
      source: ExperienceSource;
    }): Promise<LevelResult> => {
      return addExperienceInternal(amount, source);
    },
  });

  // Internal add experience function
  const addExperienceInternal = async (
    amount: number,
    source: ExperienceSource,
  ): Promise<LevelResult> => {
    const oldLevel = playerProfile.level;
    const newExperience = playerProfile.experience + amount;

    // Calculate new level
    let newLevel = oldLevel;
    for (let i = 0; i < LEVEL_THRESHOLDS.length; i++) {
      if (newExperience >= LEVEL_THRESHOLDS[i]) {
        newLevel = i + 1;
      } else {
        break;
      }
    }

    const experienceToNext =
      newLevel < LEVEL_THRESHOLDS.length
        ? LEVEL_THRESHOLDS[newLevel] - newExperience
        : 0;

    // Create experience event
    const experienceEvent: ExperienceEvent = {
      id: `exp-${Date.now()}`,
      source,
      amount,
      description: `Gained ${amount} XP from ${source}`,
      timestamp: new Date(),
    };

    // Update experience history
    const updatedHistory = [experienceEvent, ...experienceHistory].slice(
      0,
      100,
    );
    localStorage.setItem(
      STORAGE_KEYS.EXPERIENCE_HISTORY,
      JSON.stringify(updatedHistory),
    );
    queryClient.setQueryData(
      ["gamification", "experience", userId],
      updatedHistory,
    );

    // Update profile
    const updatedProfile = {
      ...playerProfile,
      level: newLevel,
      experience: newExperience,
      experienceToNext,
      stats: {
        ...playerProfile.stats,
        totalExperience: playerProfile.stats.totalExperience + amount,
      },
      lastActive: new Date(),
    };

    localStorage.setItem(
      STORAGE_KEYS.PLAYER_PROFILE,
      JSON.stringify(updatedProfile),
    );
    queryClient.setQueryData(
      ["gamification", "profile", userId],
      updatedProfile,
    );

    logger.info("Experience added", {
      amount,
      source,
      oldLevel,
      newLevel,
      userId,
    });

    return {
      leveledUp: newLevel > oldLevel,
      oldLevel,
      newLevel,
      experience: amount,
    };
  };

  // Check level up
  const checkLevelUp = useCallback(async (): Promise<LevelUpResult | null> => {
    const _currentLevelThreshold =
      LEVEL_THRESHOLDS[playerProfile.level - 1] || 0;
    const nextLevelThreshold =
      LEVEL_THRESHOLDS[playerProfile.level] || Infinity;

    if (playerProfile.experience >= nextLevelThreshold) {
      const newLevel = playerProfile.level + 1;
      const rewards = generateLevelRewards(newLevel);

      return {
        newLevel,
        rewards,
        newTitle: getPlayerTitle(newLevel),
        unlockedFeatures: getUnlockedFeatures(newLevel),
      };
    }

    return null;
  }, [playerProfile]);

  // Get leaderboard rank
  const getLeaderboardRank = useCallback(
    async (leaderboardId: string): Promise<LeaderboardRank> => {
      const leaderboard = leaderboards.find((l) => l.id === leaderboardId);
      if (!leaderboard) throw new Error("Leaderboard not found");

      // Find user's rank (simulated)
      const userRank =
        Math.floor(Math.random() * leaderboard.totalParticipants) + 1;
      const percentile =
        ((leaderboard.totalParticipants - userRank) /
          leaderboard.totalParticipants) *
        100;

      return {
        category: leaderboard.category,
        period: leaderboard.period,
        rank: userRank,
        totalParticipants: leaderboard.totalParticipants,
        percentile,
        value: playerProfile.stats.totalExperience,
      };
    },
    [leaderboards, playerProfile],
  );

  // Compare with friends
  const compareWithFriends = useCallback(async (): Promise<
    FriendComparison[]
  > => {
    if (!socialFeatures?.friends) return [];

    return socialFeatures.friends.map((friend) => ({
      friendId: friend.userId,
      friendName: friend.displayName,
      categories: [
        {
          category: "Level",
          playerValue: playerProfile.level,
          friendValue: friend.level,
          difference: playerProfile.level - friend.level,
          status:
            playerProfile.level > friend.level
              ? "ahead"
              : playerProfile.level < friend.level
                ? "behind"
                : "tied",
        },
        {
          category: "Experience",
          playerValue: playerProfile.experience,
          friendValue: Math.floor(Math.random() * 10000),
          difference: 0,
          status: "tied",
        },
      ],
      overallComparison: "ahead",
    }));
  }, [socialFeatures, playerProfile]);

  // Send challenge to friend
  const sendChallenge = useCallback(
    async (friendId: string, challengeId: string) => {
      logger.info("Challenge sent to friend", {
        friendId,
        challengeId,
        userId,
      });
      // In a real implementation, this would create a challenge invitation
      await new Promise((resolve) => setTimeout(resolve, 500));
    },
    [userId],
  );

  // Get seasonal rewards
  const getSeasonalRewards = useCallback(async (): Promise<
    SeasonalReward[]
  > => {
    return currentSeason?.rewards || [];
  }, [currentSeason]);

  // Claim seasonal reward
  const claimSeasonalReward = useCallback(
    async (rewardId: string) => {
      logger.info("Seasonal reward claimed", { rewardId, userId });
      // In a real implementation, this would claim the reward and update user data
      await new Promise((resolve) => setTimeout(resolve, 500));
    },
    [userId],
  );

  // Helper functions
  const generateSampleLeaderboards = (): Leaderboard[] => [
    {
      id: "exp-weekly",
      category: LeaderboardCategory.EXPERIENCE,
      period: LeaderboardPeriod.WEEKLY,
      name: "Weekly Experience Leaders",
      description: "Top experience earners this week",
      entries: Array.from({ length: 10 }, (_, i) => ({
        rank: i + 1,
        userId: `user-${i}`,
        displayName: `Player ${i + 1}`,
        value: Math.floor(Math.random() * 5000),
        change: Math.floor(Math.random() * 10) - 5,
        badge: i < 3 ? ["gold", "silver", "bronze"][i] : undefined,
      })),
      lastUpdated: new Date(),
      totalParticipants: 1247,
    },
  ];

  const generateSeasonalRewards = (): SeasonalReward[] => [
    {
      id: "winter-badge",
      name: "Winter Warrior",
      description: "Complete 10 challenges during winter season",
      type: "badge",
      requirement: {
        type: "challenges",
        value: 10,
        description: "Complete 10 challenges",
      },
      claimed: false,
      exclusive: true,
    },
  ];

  const createBadgeFromReward = (reward: ChallengeReward): Badge => ({
    id: `badge-${reward.value}`,
    name: reward.description,
    description: reward.description,
    category: BadgeCategory.ACHIEVEMENT,
    iconUrl: "/badges/achievement.png",
    rarity: "common",
    earnedAt: new Date(),
    requirements: [],
    hidden: false,
  });

  const generateLevelRewards = (level: number) => [
    {
      type: "badge" as const,
      value: `level-${level}`,
      description: `Level ${level} Achievement Badge`,
    },
  ];

  const getPlayerTitle = (level: number): PlayerTitle => {
    if (level >= 20) return PlayerTitle.LEGEND;
    if (level >= 15) return PlayerTitle.GRANDMASTER;
    if (level >= 12) return PlayerTitle.MASTER;
    if (level >= 10) return PlayerTitle.EXPERT;
    if (level >= 8) return PlayerTitle.ADEPT;
    if (level >= 6) return PlayerTitle.PRACTITIONER;
    if (level >= 4) return PlayerTitle.APPRENTICE;
    return PlayerTitle.NOVICE;
  };

  const getUnlockedFeatures = (level: number): string[] => {
    const features = [];
    if (level >= 3) features.push("Custom Challenges");
    if (level >= 5) features.push("Friend System");
    if (level >= 8) features.push("Group Challenges");
    if (level >= 10) features.push("Advanced Analytics");
    return features;
  };

  // Computed properties
  const currentLevel = playerProfile.level;
  const progressToNext =
    playerProfile.experienceToNext > 0
      ? ((playerProfile.experience -
          (LEVEL_THRESHOLDS[playerProfile.level - 1] || 0)) /
          ((LEVEL_THRESHOLDS[playerProfile.level] ||
            playerProfile.experienceToNext) -
            (LEVEL_THRESHOLDS[playerProfile.level - 1] || 0))) *
        100
      : 100;

  const activeChallengeCount = activeChallenges.length;
  const completedChallengesThisWeek = playerProfile.stats.challengesCompleted; // Simplified

  const rank =
    leaderboards.length > 0
      ? leaderboards[0].entries.findIndex((e) => e.userId === userId) + 1 || 0
      : 0;

  const hasUnclaimedRewards =
    currentSeason?.rewards.some((r) => !r.claimed) || false;

  return {
    // Player state
    playerProfile,
    activeChallenges,
    leaderboards,
    currentSeason,
    socialFeatures,
    experienceHistory,

    // Challenge management
    acceptChallenge: acceptChallengeMutation.mutate,
    completeChallenge: completeChallengeMutation.mutate,
    getChallengeProgress: (challengeId: string) => {
      const challenge = activeChallenges.find((c) => c.id === challengeId);
      return challenge?.progress;
    },

    // Experience and leveling
    addExperience: addExperienceMutation.mutate,
    checkLevelUp,

    // Leaderboard features
    getLeaderboardRank,
    joinLeaderboard: async (leaderboardId: string) => {
      logger.info("Joined leaderboard", { leaderboardId, userId });
    },
    leaveLeaderboard: async (leaderboardId: string) => {
      logger.info("Left leaderboard", { leaderboardId, userId });
    },

    // Social features
    compareWithFriends,
    sendChallenge,

    // Seasonal events
    getSeasonalRewards,
    claimSeasonalReward,

    // Loading states
    isAcceptingChallenge: acceptChallengeMutation.isPending,
    isCompletingChallenge: completeChallengeMutation.isPending,
    isAddingExperience: addExperienceMutation.isPending,

    // Results
    lastChallengeCompletion: completeChallengeMutation.data,
    lastLevelResult: addExperienceMutation.data,

    // Computed properties
    currentLevel,
    progressToNext,
    activeChallengeCount,
    completedChallengesThisWeek,
    rank,
    hasUnclaimedRewards,

    // Quick stats
    totalExperience: playerProfile.stats.totalExperience,
    totalBadges: playerProfile.badges.length,
    currentStreak: playerProfile.stats.currentStreak,

    // Errors
    error:
      acceptChallengeMutation.error ||
      completeChallengeMutation.error ||
      addExperienceMutation.error,
  };
};

/**
 * useGoals Hook - Enhanced Goal System
 *
 * Advanced goal system with AI recommendations, collaborative goals, and
 * comprehensive progress analytics.
 */

import { useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  EnhancedGoal,
  CollaborativeGoal,
  GoalRecommendation,
  GoalAnalytics,
  GoalTemplate,
  CreateGoalRequest,
  GoalUpdate,
  OptimizedGoalPlan,
  CollaborationInvite,
  GoalInsights,
  GoalPredictions,
  CompletionTrends,
  GoalType,
  GoalCategory,
  GoalDifficulty,
  GoalStatus,
} from "../../types/goals";
import { logger } from "../../utils/logging";
import {
  getGoalInsights,
  getPredictiveAnalytics as getPredictiveAnalyticsHelper,
  getCompletionTrends as getCompletionTrendsHelper,
} from "../../utils/goalsHelpers";

// Enhanced goal state

import type * as _Types from "./types/Goals";
export type * from "./types/Goals";

export const useGoals = (userId?: string, relationshipId?: string) => {
  const queryClient = useQueryClient();

  // Get personal goals
  const { data: personalGoals = [] } = useQuery<EnhancedGoal[]>({
    queryKey: ["goals", "personal", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.PERSONAL_GOALS);
      return stored ? JSON.parse(stored) : [];
    },
    enabled: Boolean(userId),
    staleTime: 30 * 1000,
  });

  // Get collaborative goals
  const { data: collaborativeGoals = [] } = useQuery<CollaborativeGoal[]>({
    queryKey: ["goals", "collaborative", userId, relationshipId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.COLLABORATIVE_GOALS);
      return stored ? JSON.parse(stored) : [];
    },
    enabled: Boolean(userId) && Boolean(relationshipId),
    staleTime: 30 * 1000,
  });

  // Get goal templates
  const { data: goalTemplates = DEFAULT_TEMPLATES } = useQuery<GoalTemplate[]>({
    queryKey: ["goals", "templates"],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.GOAL_TEMPLATES);
      return stored
        ? [...DEFAULT_TEMPLATES, ...JSON.parse(stored)]
        : DEFAULT_TEMPLATES;
    },
    staleTime: 5 * 60 * 1000,
  });

  // Get AI recommendations
  const { data: recommendedGoals = [] } = useQuery<GoalRecommendation[]>({
    queryKey: ["goals", "recommendations", userId],
    queryFn: async () => {
      // Simulate AI recommendation generation
      await new Promise((resolve) => setTimeout(resolve, 800));

      return generateSmartRecommendations(personalGoals, collaborativeGoals);
    },
    enabled:
      Boolean(userId) &&
      (personalGoals.length > 0 || collaborativeGoals.length > 0),
    staleTime: 10 * 60 * 1000, // 10 minutes
    refetchInterval: 30 * 60 * 1000, // 30 minutes
  });

  // Get goal analytics
  const { data: goalAnalytics } = useQuery<GoalAnalytics>({
    queryKey: ["goals", "analytics", userId],
    queryFn: () => calculateGoalAnalytics(personalGoals, collaborativeGoals),
    enabled:
      Boolean(userId) &&
      (personalGoals.length > 0 || collaborativeGoals.length > 0),
    staleTime: 5 * 60 * 1000,
  });

  // Create goal mutation
  const createGoalMutation = useMutation({
    mutationFn: async (request: CreateGoalRequest) => {
      const newGoal: EnhancedGoal = {
        id: `goal-${Date.now()}`,
        type: request.type,
        category: request.category,
        title: request.title,
        description: request.description,
        target: request.target,
        progress: {
          current: 0,
          target: request.target.value,
          percentage: 0,
          status: GoalStatus.ACTIVE,
          milestones:
            request.milestones?.map((m) => ({
              ...m,
              id: `milestone-${Date.now()}-${Math.random()}`,
              achieved: false,
            })) || [],
          lastUpdated: new Date(),
        },
        milestones:
          request.milestones?.map((m) => ({
            ...m,
            id: `milestone-${Date.now()}-${Math.random()}`,
            achieved: false,
          })) || [],
        aiGenerated: false,
        difficulty: request.difficulty,
        estimatedCompletion: calculateEstimatedCompletion(
          request.difficulty,
          request.target,
        ),
        createdAt: new Date(),
        tags: request.tags || [],
        isPublic: request.isPublic || false,
      };

      const updated = [...personalGoals, newGoal];
      localStorage.setItem(
        STORAGE_KEYS.PERSONAL_GOALS,
        JSON.stringify(updated),
      );
      queryClient.setQueryData(["goals", "personal", userId], updated);

      logger.info("Goal created", { goalId: newGoal.id, title: newGoal.title });
      return newGoal;
    },
  });

  // Update goal mutation
  const updateGoalMutation = useMutation({
    mutationFn: async ({
      goalId,
      updates,
    }: {
      goalId: string;
      updates: GoalUpdate;
    }) => {
      const goalIndex = personalGoals.findIndex((g) => g.id === goalId);
      if (goalIndex === -1) throw new Error("Goal not found");

      const updatedGoal: EnhancedGoal = {
        ...personalGoals[goalIndex],
        ...updates,
      };
      const updated = [...personalGoals];
      updated[goalIndex] = updatedGoal;

      localStorage.setItem(
        STORAGE_KEYS.PERSONAL_GOALS,
        JSON.stringify(updated),
      );
      queryClient.setQueryData(["goals", "personal", userId], updated);

      logger.info("Goal updated", { goalId, updates });
      return updatedGoal;
    },
  });

  // Delete goal mutation
  const deleteGoalMutation = useMutation({
    mutationFn: async (goalId: string) => {
      const updated = personalGoals.filter((g) => g.id !== goalId);
      localStorage.setItem(
        STORAGE_KEYS.PERSONAL_GOALS,
        JSON.stringify(updated),
      );
      queryClient.setQueryData(["goals", "personal", userId], updated);

      logger.info("Goal deleted", { goalId });
    },
  });

  // Generate goal from prompt mutation
  const generateGoalFromPromptMutation = useMutation({
    mutationFn: async (prompt: string) => {
      logger.info("Generating goal from prompt", { prompt });

      // Simulate AI processing
      await new Promise((resolve) => setTimeout(resolve, 1500));

      const aiGoal: EnhancedGoal = {
        id: `ai-goal-${Date.now()}`,
        type: GoalType.DURATION,
        category: GoalCategory.CHASTITY,
        title: "AI-Generated Chastity Goal",
        description: `Generated from: "${prompt}"`,
        target: {
          type: "duration",
          value: 14,
          unit: "days",
          description: "14 days of commitment",
        },
        progress: {
          current: 0,
          target: 14,
          percentage: 0,
          status: GoalStatus.ACTIVE,
          milestones: [],
          lastUpdated: new Date(),
        },
        milestones: [
          {
            id: "ai-milestone-1",
            name: "First Week",
            description: "Complete 7 days",
            target: 7,
            achieved: false,
          },
          {
            id: "ai-milestone-2",
            name: "Final Goal",
            description: "Complete 14 days",
            target: 14,
            achieved: false,
          },
        ],
        aiGenerated: true,
        difficulty: GoalDifficulty.MEDIUM,
        estimatedCompletion: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000),
        createdAt: new Date(),
        tags: ["ai-generated"],
        isPublic: false,
      };

      return aiGoal;
    },
  });

  // Optimize goal plan mutation
  const optimizeGoalPlanMutation = useMutation({
    mutationFn: async (goalIds: string[]) => {
      const goalsToOptimize = personalGoals.filter((g) =>
        goalIds.includes(g.id),
      );

      // Simulate optimization processing
      await new Promise((resolve) => setTimeout(resolve, 1000));

      const optimizedPlan: OptimizedGoalPlan = {
        goals: goalsToOptimize,
        timeline: generateTimeline(goalsToOptimize),
        conflicts: detectConflicts(goalsToOptimize),
        recommendations: generatePlanRecommendations(goalsToOptimize),
        estimatedCompletion: new Date(Date.now() + 90 * 24 * 60 * 60 * 1000), // 90 days
      };

      return optimizedPlan;
    },
  });

  // Share goal mutation
  const shareGoalMutation = useMutation({
    mutationFn: async ({
      goalId,
      targetUserId,
    }: {
      goalId: string;
      targetUserId: string;
    }) => {
      logger.info("Sharing goal", { goalId, targetUserId });
      // In a real implementation, this would create a sharing record
      await new Promise((resolve) => setTimeout(resolve, 500));
    },
  });

  // Invite collaborator mutation
  const inviteCollaboratorMutation = useMutation({
    mutationFn: async ({
      goalId,
      targetUserId,
    }: {
      goalId: string;
      targetUserId: string;
    }) => {
      const invite: CollaborationInvite = {
        id: `invite-${Date.now()}`,
        goalId,
        inviterId: userId!,
        inviteeId: targetUserId,
        permissions: {
          canEdit: false,
          canDelete: false,
          canInviteOthers: false,
          canViewProgress: true,
          canAddMilestones: false,
        },
        createdAt: new Date(),
        expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        status: "pending",
      };

      logger.info("Collaboration invite sent", {
        inviteId: invite.id,
        goalId,
        targetUserId,
      });
      return invite;
    },
  });

  // Helper functions
  const generateSmartRecommendations = (
    personal: EnhancedGoal[],
    _collaborative: CollaborativeGoal[],
  ): GoalRecommendation[] => {
    const recommendations: GoalRecommendation[] = [];

    // Analyze completed goals to suggest similar ones
    const completedGoals = personal.filter(
      (g) => g.progress.status === GoalStatus.COMPLETED,
    );
    const activeCategories = new Set(personal.map((g) => g.category));

    // Suggest goals in successful categories
    completedGoals.forEach((goal) => {
      if (Math.random() > 0.7) {
        // 30% chance to recommend
        recommendations.push({
          id: `rec-${Date.now()}-${Math.random()}`,
          type: goal.type,
          category: goal.category,
          title: `Advanced ${goal.category} Challenge`,
          description: `Based on your success with "${goal.title}"`,
          difficulty:
            goal.difficulty === GoalDifficulty.EASY
              ? GoalDifficulty.MEDIUM
              : GoalDifficulty.HARD,
          estimatedDuration: 30,
          reasoning: `You successfully completed similar goals in the ${goal.category} category`,
          confidence: 0.8,
          similarGoals: [goal.id],
          successRate: 0.75,
        });
      }
    });

    // Suggest unexplored categories
    const allCategories = Object.values(GoalCategory);
    const unexploredCategories = allCategories.filter(
      (cat) => !activeCategories.has(cat),
    );

    unexploredCategories.forEach((category) => {
      if (Math.random() > 0.8) {
        // 20% chance
        recommendations.push({
          id: `exp-${Date.now()}-${Math.random()}`,
          type: GoalType.MILESTONE,
          category,
          title: `Explore ${category}`,
          description: `Try something new in the ${category} category`,
          difficulty: GoalDifficulty.EASY,
          estimatedDuration: 14,
          reasoning: `Diversifying goal categories can improve overall success`,
          confidence: 0.6,
          similarGoals: [],
          successRate: 0.65,
        });
      }
    });

    return recommendations.slice(0, 5); // Limit to 5 recommendations
  };

  const calculateGoalAnalytics = (
    personal: EnhancedGoal[],
    collaborative: CollaborativeGoal[],
  ): GoalAnalytics => {
    const allGoals = [...personal, ...collaborative];
    const completed = allGoals.filter(
      (g) => g.progress.status === GoalStatus.COMPLETED,
    );
    const active = allGoals.filter(
      (g) => g.progress.status === GoalStatus.ACTIVE,
    );

    const categoryDistribution = Object.values(GoalCategory).reduce(
      (acc, cat) => {
        acc[cat] = allGoals.filter((g) => g.category === cat).length;
        return acc;
      },
      {} as Record<GoalCategory, number>,
    );

    const difficultyDistribution = Object.values(GoalDifficulty).reduce(
      (acc, diff) => {
        acc[diff] = allGoals.filter((g) => g.difficulty === diff).length;
        return acc;
      },
      {} as Record<GoalDifficulty, number>,
    );

    return {
      totalGoals: allGoals.length,
      completedGoals: completed.length,
      activeGoals: active.length,
      completionRate:
        allGoals.length > 0 ? (completed.length / allGoals.length) * 100 : 0,
      averageCompletionTime: calculateAverageCompletionTime(completed),
      categoryDistribution,
      difficultyDistribution,
      monthlyProgress: generateMonthlyProgress(allGoals),
      streaks: calculateStreaks(completed),
    };
  };

  const calculateEstimatedCompletion = (
    difficulty: GoalDifficulty,
    target: { value?: number; unit?: string },
  ): Date => {
    const baseDays = target.value || 30;
    const multiplier = {
      [GoalDifficulty.EASY]: 1,
      [GoalDifficulty.MEDIUM]: 1.5,
      [GoalDifficulty.HARD]: 2,
      [GoalDifficulty.EXTREME]: 3,
    };

    return new Date(
      Date.now() + baseDays * multiplier[difficulty] * 24 * 60 * 60 * 1000,
    );
  };

  const calculateAverageCompletionTime = (
    completed: EnhancedGoal[],
  ): number => {
    if (completed.length === 0) return 0;

    const totalDays = completed.reduce((acc, goal) => {
      if (goal.completedAt && goal.startedAt) {
        return (
          acc +
          (goal.completedAt.getTime() - goal.startedAt.getTime()) /
            (24 * 60 * 60 * 1000)
        );
      }
      return acc;
    }, 0);

    return totalDays / completed.length;
  };

  const generateMonthlyProgress = (_goals: EnhancedGoal[]) => {
    // Simplified monthly progress calculation
    return Array.from({ length: 6 }, (_, i) => ({
      month: new Date(
        Date.now() - i * 30 * 24 * 60 * 60 * 1000,
      ).toLocaleDateString("en-US", { month: "short" }),
      goalsStarted: Math.floor(Math.random() * 5),
      goalsCompleted: Math.floor(Math.random() * 3),
      totalProgress: Math.floor(Math.random() * 100),
    }));
  };

  const calculateStreaks = (_completed: EnhancedGoal[]) => {
    return Object.values(GoalCategory).map((category) => ({
      category,
      currentStreak: Math.floor(Math.random() * 10),
      longestStreak: Math.floor(Math.random() * 20),
      lastGoalCompleted: new Date(
        Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000,
      ),
    }));
  };

  const generateTimeline = (goals: EnhancedGoal[]) => {
    return goals.map((goal) => ({
      date: goal.estimatedCompletion,
      goals: [goal.id],
      milestones: goal.milestones.map((m) => m.id),
      estimatedEffort: 4, // hours
    }));
  };

  const detectConflicts = (_goals: EnhancedGoal[]) => {
    // Simplified conflict detection
    return [];
  };

  const generatePlanRecommendations = (_goals: EnhancedGoal[]) => {
    return [
      {
        type: "reorder" as const,
        description: "Consider starting easier goals first to build momentum",
        impact: "Improved success rate",
        effort: "Low",
      },
    ];
  };

  // Analytics functions using helpers
  const getGoalInsightsCallback = useCallback(
    (): GoalInsights => getGoalInsights(),
    [],
  );

  const getPredictiveAnalyticsCallback = useCallback(
    (): GoalPredictions => getPredictiveAnalyticsHelper(personalGoals),
    [personalGoals],
  );

  const getCompletionTrendsCallback = useCallback(
    (): CompletionTrends => getCompletionTrendsHelper(),
    [],
  );

  return {
    // Goal state
    personalGoals,
    collaborativeGoals,
    recommendedGoals,
    goalAnalytics,
    goalTemplates,

    // Goal management
    createGoal: createGoalMutation.mutate,
    updateGoal: updateGoalMutation.mutate,
    deleteGoal: deleteGoalMutation.mutate,

    // AI features
    getSmartRecommendations: () => recommendedGoals,
    generateGoalFromPrompt: generateGoalFromPromptMutation.mutate,
    optimizeGoalPlan: optimizeGoalPlanMutation.mutate,

    // Collaboration
    shareGoal: shareGoalMutation.mutate,
    inviteCollaborator: inviteCollaboratorMutation.mutate,
    acceptCollaboration: async (inviteId: string) => {
      logger.info("Collaboration accepted", { inviteId });
    },

    // Analytics
    getGoalInsights: getGoalInsightsCallback,
    getPredictiveAnalytics: getPredictiveAnalyticsCallback,
    getCompletionTrends: getCompletionTrendsCallback,

    // Loading states
    isCreating: createGoalMutation.isPending,
    isUpdating: updateGoalMutation.isPending,
    isDeleting: deleteGoalMutation.isPending,
    isGenerating: generateGoalFromPromptMutation.isPending,
    isOptimizing: optimizeGoalPlanMutation.isPending,

    // Computed properties
    activeGoalsCount: personalGoals.filter(
      (g) => g.progress.status === GoalStatus.ACTIVE,
    ).length,
    completionRate: goalAnalytics ? goalAnalytics.completionRate : 0,
    averageCompletionTime: goalAnalytics
      ? goalAnalytics.averageCompletionTime
      : 0,
    hasCollaborativeGoals: collaborativeGoals.length > 0,
    needsAttention: personalGoals.filter(
      (g) => g.progress.status === GoalStatus.BEHIND,
    ).length,

    // Results
    lastOptimization: optimizeGoalPlanMutation.data,
    lastGeneratedGoal: generateGoalFromPromptMutation.data,

    // Errors
    error:
      createGoalMutation.error ||
      updateGoalMutation.error ||
      deleteGoalMutation.error,
  };
};

/**
 * Log Event Form Hook
 *
 * Extracts form submission logic from LogEventForm component.
 */

import { useState, useCallback } from "react";
import { Event } from "../../types/events";

export interface EventFormData {
  type: string;
  notes?: string;
  timestamp?: Date;
  category?: string;
}

export interface UseLogEventFormReturn {
  formData: EventFormData;
  setFormData: (data: Partial<EventFormData>) => void;
  resetForm: () => void;
  errors: Record<string, string>;
  isValid: boolean;
  validate: () => boolean;
  submitEvent: () => Promise<void>;
  isSubmitting: boolean;
  submitError: Error | null;
  saveDraft: () => void;
  loadDraft: () => void;
  hasDraft: boolean;
  clearDraft: () => void;
  categorySuggestions: string[];
  recentEvents: Event[];
}

const defaultFormData: EventFormData = {
  type: "",
  notes: "",
  timestamp: new Date(),
};

export function useLogEventForm(
  initialData?: Partial<EventFormData>,
): UseLogEventFormReturn {
  const [formData, setFormDataState] = useState<EventFormData>({
    ...defaultFormData,
    ...initialData,
  });
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState<Error | null>(null);
  const [hasDraft, setHasDraft] = useState(false);

  const setFormData = useCallback((data: Partial<EventFormData>) => {
    setFormDataState((prev) => ({ ...prev, ...data }));
  }, []);

  const resetForm = useCallback(() => {
    setFormDataState(defaultFormData);
    setErrors({});
  }, []);

  const validate = useCallback((): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.type) {
      newErrors.type = "Event type is required";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [formData]);

  const submitEvent = useCallback(async (): Promise<void> => {
    setIsSubmitting(true);
    setSubmitError(null);

    try {
      if (!validate()) {
        throw new Error("Validation failed");
      }

      // Mock submission
      await new Promise((resolve) => setTimeout(resolve, 100));
      resetForm();
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to submit event");
      setSubmitError(error);
      throw error;
    } finally {
      setIsSubmitting(false);
    }
  }, [validate, resetForm]);

  const saveDraft = useCallback(() => {
    localStorage.setItem("eventDraft", JSON.stringify(formData));
    setHasDraft(true);
  }, [formData]);

  const loadDraft = useCallback(() => {
    const draft = localStorage.getItem("eventDraft");
    if (draft) {
      setFormDataState(JSON.parse(draft));
    }
  }, []);

  const clearDraft = useCallback(() => {
    localStorage.removeItem("eventDraft");
    setHasDraft(false);
  }, []);

  return {
    formData,
    setFormData,
    resetForm,
    errors,
    isValid: Object.keys(errors).length === 0,
    validate,
    submitEvent,
    isSubmitting,
    submitError,
    saveDraft,
    loadDraft,
    hasDraft,
    clearDraft,
    categorySuggestions: ["Orgasm", "Edge", "Release", "Lock"],
    recentEvents: [],
  };
}

/**
 * Personal Goals Hook - Extracts goal management from PersonalGoalSection component
 */

import { useState, useCallback } from "react";

export interface PersonalGoal {
  id: string;
  title: string;
  description?: string;
  targetDuration: number; // in seconds
  currentProgress: number; // in seconds
  isCompleted: boolean;
  createdAt: Date;
  completedAt?: Date;
  reward?: string;
}

export interface CreateGoalInput {
  title: string;
  description?: string;
  targetDuration: number;
  reward?: string;
}

export interface UpdateGoalInput {
  title?: string;
  description?: string;
  targetDuration?: number;
  reward?: string;
}

export interface UsePersonalGoalsReturn {
  goals: PersonalGoal[];
  activeGoals: PersonalGoal[];
  completedGoals: PersonalGoal[];
  isLoading: boolean;
  createGoal: (goal: CreateGoalInput) => Promise<PersonalGoal>;
  updateGoal: (id: string, updates: UpdateGoalInput) => Promise<PersonalGoal>;
  deleteGoal: (id: string) => Promise<void>;
  completeGoal: (id: string) => Promise<void>;
  isCreating: boolean;
  isUpdating: boolean;
  isDeleting: boolean;
  error: Error | null;
  getGoalProgress: (goalId: string) => number;
  getGoalProjection: (goalId: string) => Date | null;
}

export function usePersonalGoals(): UsePersonalGoalsReturn {
  const [goals, setGoals] = useState<PersonalGoal[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isCreating, setIsCreating] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  const activeGoals = goals.filter((g) => !g.isCompleted);
  const completedGoals = goals.filter((g) => g.isCompleted);

  const createGoal = useCallback(
    async (goal: CreateGoalInput): Promise<PersonalGoal> => {
      setIsCreating(true);
      setError(null);
      try {
        const newGoal: PersonalGoal = {
          id: `goal-${Date.now()}`,
          title: goal.title,
          description: goal.description,
          targetDuration: goal.targetDuration,
          currentProgress: 0,
          isCompleted: false,
          createdAt: new Date(),
          reward: goal.reward,
        };
        setGoals((prev) => [...prev, newGoal]);
        return newGoal;
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to create goal");
        setError(error);
        throw error;
      } finally {
        setIsCreating(false);
      }
    },
    [],
  );

  const updateGoal = useCallback(
    async (id: string, updates: UpdateGoalInput): Promise<PersonalGoal> => {
      setIsUpdating(true);
      setError(null);
      try {
        let updatedGoal: PersonalGoal | null = null;
        setGoals((prev) =>
          prev.map((g) => {
            if (g.id === id) {
              updatedGoal = { ...g, ...updates };
              return updatedGoal;
            }
            return g;
          }),
        );
        if (!updatedGoal) throw new Error("Goal not found");
        return updatedGoal;
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to update goal");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  const deleteGoal = useCallback(async (id: string): Promise<void> => {
    setIsDeleting(true);
    setError(null);
    try {
      setGoals((prev) => prev.filter((g) => g.id !== id));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to delete goal");
      setError(error);
      throw error;
    } finally {
      setIsDeleting(false);
    }
  }, []);

  const completeGoal = useCallback(async (id: string): Promise<void> => {
    setError(null);
    try {
      setGoals((prev) =>
        prev.map((g) =>
          g.id === id
            ? { ...g, isCompleted: true, completedAt: new Date() }
            : g,
        ),
      );
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to complete goal");
      setError(error);
      throw error;
    }
  }, []);

  const getGoalProgress = useCallback(
    (goalId: string): number => {
      const goal = goals.find((g) => g.id === goalId);
      if (!goal || goal.targetDuration === 0) return 0;
      return (goal.currentProgress / goal.targetDuration) * 100;
    },
    [goals],
  );

  const getGoalProjection = useCallback(
    (goalId: string): Date | null => {
      const goal = goals.find((g) => g.id === goalId);
      if (!goal || goal.currentProgress === 0) return null;

      const remainingDuration = goal.targetDuration - goal.currentProgress;
      const daysPassed = Math.floor(
        (Date.now() - goal.createdAt.getTime()) / (1000 * 60 * 60 * 24),
      );
      const progressPerDay = goal.currentProgress / daysPassed;

      if (progressPerDay === 0) return null;

      const daysRemaining = Math.ceil(remainingDuration / progressPerDay);
      const projectedDate = new Date();
      projectedDate.setDate(projectedDate.getDate() + daysRemaining);

      return projectedDate;
    },
    [goals],
  );

  useState(() => {
    setTimeout(() => setIsLoading(false), 100);
  });

  return {
    goals,
    activeGoals,
    completedGoals,
    isLoading,
    createGoal,
    updateGoal,
    deleteGoal,
    completeGoal,
    isCreating,
    isUpdating,
    isDeleting,
    error,
    getGoalProgress,
    getGoalProjection,
  };
}

/**
 * useReporting Hook - Advanced Reporting & Analytics
 *
 * Comprehensive reporting system with custom reports, data visualization,
 * and export capabilities for detailed analysis.
 */

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { logger } from "../../utils/logging";

// Report types
export enum ReportType {
  SUMMARY = "summary",
  DETAILED = "detailed",
  ANALYTICS = "analytics",
  CUSTOM = "custom",
}

// Export formats
export enum ExportFormat {
  JSON = "json",
  CSV = "csv",
  PDF = "pdf",
  XLSX = "xlsx",
}

// Report template
export interface ReportTemplate {
  id: string;
  name: string;
  description: string;
  type: ReportType;
  parameters: ReportParameter[];
  defaultParameters: Record<string, string | number | boolean | Date>;
}

// Report parameter
export interface ReportParameter {
  name: string;
  type: "string" | "number" | "date" | "boolean" | "select";
  required: boolean;
  options?: string[];
  default?: string | number | boolean | Date;
}

// Report parameters
export interface ReportParameters {
  dateRange: {
    start: Date;
    end: Date;
  };
  includeDetails: boolean;
  groupBy?: string;
  filters: Record<string, string | number | boolean | Date>;
}

// Generated report
export interface GeneratedReport {
  id: string;
  templateId: string;
  name: string;
  parameters: ReportParameters;
  data: Record<string, unknown> | unknown[];
  generatedAt: Date;
  generatedBy: string;
  size: number;
}

// Custom report definition
export interface CustomReportDefinition {
  name: string;
  description: string;
  dataSource: string;
  fields: string[];
  filters: ReportFilter[];
  grouping?: string[];
  sorting?: ReportSort[];
}

// Report filter
export interface ReportFilter {
  field: string;
  operator: "equals" | "contains" | "greater" | "less" | "between";
  value: string | number | boolean | Date;
}

// Report sort
export interface ReportSort {
  field: string;
  direction: "asc" | "desc";
}

// Custom report
export interface CustomReport {
  id: string;
  definition: CustomReportDefinition;
  createdAt: Date;
  lastRun?: Date;
}

// Report schedule
export interface ReportSchedule {
  frequency: "daily" | "weekly" | "monthly";
  time: string;
  recipients: string[];
  format: ExportFormat;
  enabled: boolean;
}

// Scheduled report
export interface ScheduledReport {
  id: string;
  reportId: string;
  schedule: ReportSchedule;
  nextRun: Date;
  lastRun?: Date;
}

// Report export
export interface ReportExport {
  id: string;
  reportId: string;
  format: ExportFormat;
  url: string;
  expiresAt: Date;
}

// Data export
export interface DataExport {
  id: string;
  dataType: string;
  filters: DataFilter[];
  format: ExportFormat;
  url: string;
  expiresAt: Date;
}

// Data filter
export interface DataFilter {
  field: string;
  value: string | number | boolean | Date;
}

// Reporting preferences
export interface ReportingPreferences {
  defaultFormat: ExportFormat;
  autoRefresh: boolean;
  refreshInterval: number;
  maxReports: number;
}

// Default templates
const DEFAULT_TEMPLATES: ReportTemplate[] = [
  {
    id: "session-summary",
    name: "Session Summary",
    description: "Summary of chastity sessions over time",
    type: ReportType.SUMMARY,
    parameters: [
      { name: "dateRange", type: "date", required: true },
      {
        name: "includeDetails",
        type: "boolean",
        required: false,
        default: false,
      },
    ],
    defaultParameters: {
      includeDetails: false,
    },
  },
  {
    id: "achievement-progress",
    name: "Achievement Progress",
    description: "Progress on achievements and goals",
    type: ReportType.ANALYTICS,
    parameters: [
      { name: "dateRange", type: "date", required: true },
      {
        name: "category",
        type: "select",
        required: false,
        options: ["all", "duration", "behavior", "goals"],
      },
    ],
    defaultParameters: {
      category: "all",
    },
  },
  {
    id: "behavioral-analysis",
    name: "Behavioral Analysis",
    description: "Analysis of behavioral patterns and events",
    type: ReportType.DETAILED,
    parameters: [
      { name: "dateRange", type: "date", required: true },
      {
        name: "eventTypes",
        type: "select",
        required: false,
        options: ["all", "sessions", "events", "tasks"],
      },
    ],
    defaultParameters: {
      eventTypes: "all",
    },
  },
];

// Storage keys
const STORAGE_KEYS = {
  CUSTOM_REPORTS: "chastity-reports-custom",
  RECENT_REPORTS: "chastity-reports-recent",
  PREFERENCES: "chastity-reports-preferences",
};

/**
 * Advanced Reporting Hook
 */
export const useReporting = (userId?: string, _relationshipId?: string) => {
  const queryClient = useQueryClient();

  // Get available templates
  const { data: availableReports = DEFAULT_TEMPLATES } = useQuery<
    ReportTemplate[]
  >({
    queryKey: ["reports", "templates"],
    queryFn: () => DEFAULT_TEMPLATES,
    staleTime: 5 * 60 * 1000,
  });

  // Get custom reports
  const { data: customReports = [] } = useQuery<CustomReport[]>({
    queryKey: ["reports", "custom", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.CUSTOM_REPORTS);
      return stored ? JSON.parse(stored) : [];
    },
    enabled: Boolean(userId),
    staleTime: 60 * 1000,
  });

  // Get recent reports
  const { data: recentReports = [] } = useQuery<GeneratedReport[]>({
    queryKey: ["reports", "recent", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.RECENT_REPORTS);
      return stored ? JSON.parse(stored) : [];
    },
    enabled: Boolean(userId),
    staleTime: 30 * 1000,
  });

  // Get preferences
  const { data: preferences } = useQuery<ReportingPreferences>({
    queryKey: ["reports", "preferences", userId],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.PREFERENCES);
      return stored
        ? JSON.parse(stored)
        : {
            defaultFormat: ExportFormat.JSON,
            autoRefresh: false,
            refreshInterval: 300000, // 5 minutes
            maxReports: 50,
          };
    },
    enabled: Boolean(userId),
    staleTime: 5 * 60 * 1000,
  });

  // Generate report mutation
  const generateReportMutation = useMutation({
    mutationFn: async ({
      templateId,
      parameters,
    }: {
      templateId: string;
      parameters: ReportParameters;
    }) => {
      const template = availableReports.find((t) => t.id === templateId);
      if (!template) throw new Error("Template not found");

      logger.info("Generating report", { templateId, parameters });

      // Simulate report generation
      const reportData = await generateReportData(templateId, parameters);

      const report: GeneratedReport = {
        id: `report-${Date.now()}`,
        templateId,
        name: template.name,
        parameters,
        data: reportData,
        generatedAt: new Date(),
        generatedBy: userId || "anonymous",
        size: JSON.stringify(reportData).length,
      };

      // Store in recent reports
      const updated = [report, ...recentReports].slice(
        0,
        preferences?.maxReports || 50,
      );
      localStorage.setItem(
        STORAGE_KEYS.RECENT_REPORTS,
        JSON.stringify(updated),
      );
      queryClient.setQueryData(["reports", "recent", userId], updated);

      return report;
    },
  });

  // Create custom report mutation
  const createCustomReportMutation = useMutation({
    mutationFn: async (definition: CustomReportDefinition) => {
      const customReport: CustomReport = {
        id: `custom-${Date.now()}`,
        definition,
        createdAt: new Date(),
      };

      const updated = [...customReports, customReport];
      localStorage.setItem(
        STORAGE_KEYS.CUSTOM_REPORTS,
        JSON.stringify(updated),
      );
      queryClient.setQueryData(["reports", "custom", userId], updated);

      logger.info("Custom report created", { reportId: customReport.id });
      return customReport;
    },
  });

  // Schedule report mutation
  const scheduleReportMutation = useMutation({
    mutationFn: async ({
      reportId,
      schedule,
    }: {
      reportId: string;
      schedule: ReportSchedule;
    }) => {
      const scheduledReport: ScheduledReport = {
        id: `schedule-${Date.now()}`,
        reportId,
        schedule,
        nextRun: calculateNextRun(schedule),
      };

      logger.info("Report scheduled", {
        reportId,
        schedule: scheduledReport.id,
      });
      return scheduledReport;
    },
  });

  // Export report mutation
  const exportReportMutation = useMutation({
    mutationFn: async ({
      reportId,
      format,
    }: {
      reportId: string;
      format: ExportFormat;
    }) => {
      const report = recentReports.find((r) => r.id === reportId);
      if (!report) throw new Error("Report not found");

      const exportData = await exportReportData(report, format);

      const exportResult: ReportExport = {
        id: `export-${Date.now()}`,
        reportId,
        format,
        url: exportData.url,
        expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours
      };

      logger.info("Report exported", {
        reportId,
        format,
        exportId: exportResult.id,
      });
      return exportResult;
    },
  });

  // Export raw data mutation
  const exportRawDataMutation = useMutation({
    mutationFn: async ({
      dataType,
      filters,
      format,
    }: {
      dataType: string;
      filters: DataFilter[];
      format: ExportFormat;
    }) => {
      const rawData = await getRawData(dataType, filters);
      const exportResult = await exportData(rawData, format);

      const dataExport: DataExport = {
        id: `data-export-${Date.now()}`,
        dataType,
        filters,
        format,
        url: exportResult.url,
        expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000),
      };

      logger.info("Raw data exported", {
        dataType,
        format,
        exportId: dataExport.id,
      });
      return dataExport;
    },
  });

  // Helper functions
  const generateReportData = async (
    templateId: string,
    parameters: ReportParameters,
  ) => {
    // In a real implementation, this would query actual data
    await new Promise((resolve) => setTimeout(resolve, 1000));

    return {
      summary: {
        totalSessions: Math.floor(Math.random() * 100),
        averageDuration: Math.floor(Math.random() * 72),
        longestSession: Math.floor(Math.random() * 168),
      },
      details: parameters.includeDetails ? generateDetailedData() : null,
      generatedAt: new Date().toISOString(),
    };
  };

  const generateDetailedData = () => ({
    sessions: Array.from({ length: 10 }, (_, i) => ({
      id: i + 1,
      startDate: new Date(
        Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000,
      ),
      duration: Math.floor(Math.random() * 72),
      events: Math.floor(Math.random() * 5),
    })),
  });

  const calculateNextRun = (schedule: ReportSchedule): Date => {
    const now = new Date();
    const [hours, minutes] = schedule.time.split(":").map(Number);

    const nextRun = new Date(now);
    nextRun.setHours(hours, minutes, 0, 0);

    if (nextRun <= now) {
      switch (schedule.frequency) {
        case "daily":
          nextRun.setDate(nextRun.getDate() + 1);
          break;
        case "weekly":
          nextRun.setDate(nextRun.getDate() + 7);
          break;
        case "monthly":
          nextRun.setMonth(nextRun.getMonth() + 1);
          break;
      }
    }

    return nextRun;
  };

  const exportReportData = async (
    report: GeneratedReport,
    format: ExportFormat,
  ) => {
    // Simulate export processing
    await new Promise((resolve) => setTimeout(resolve, 500));

    const blob = new Blob([JSON.stringify(report.data, null, 2)], {
      type: getContentType(format),
    });
    const url = URL.createObjectURL(blob);

    return { url };
  };

  const getRawData = async (dataType: string, filters: DataFilter[]) => {
    // Simulate data retrieval
    await new Promise((resolve) => setTimeout(resolve, 800));
    return { dataType, filters, records: [] };
  };

  const exportData = async (data: Record<string, unknown> | unknown[], format: ExportFormat) => {
    const blob = new Blob([JSON.stringify(data, null, 2)], {
      type: getContentType(format),
    });
    const url = URL.createObjectURL(blob);
    return { url };
  };

  const getContentType = (format: ExportFormat): string => {
    switch (format) {
      case ExportFormat.JSON:
        return "application/json";
      case ExportFormat.CSV:
        return "text/csv";
      case ExportFormat.PDF:
        return "application/pdf";
      case ExportFormat.XLSX:
        return "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";
      default:
        return "application/octet-stream";
    }
  };

  return {
    // Available reports and templates
    availableReports,
    customReports,
    recentReports,
    preferences,

    // Actions
    generateReport: generateReportMutation.mutate,
    createCustomReport: createCustomReportMutation.mutate,
    scheduleReport: scheduleReportMutation.mutate,
    exportReport: exportReportMutation.mutate,
    exportRawData: exportRawDataMutation.mutate,

    // Loading states
    isGenerating: generateReportMutation.isPending,
    isCreatingCustom: createCustomReportMutation.isPending,
    isScheduling: scheduleReportMutation.isPending,
    isExporting:
      exportReportMutation.isPending || exportRawDataMutation.isPending,

    // Results
    lastGeneratedReport: generateReportMutation.data,
    lastExport: exportReportMutation.data || exportRawDataMutation.data,

    // Computed properties
    totalReports: recentReports.length,
    hasScheduledReports: false, // Would check actual scheduled reports
    lastReportDate:
      recentReports.length > 0 ? recentReports[0].generatedAt : null,
    hasCustomReports: customReports.length > 0,

    // Errors
    error:
      generateReportMutation.error ||
      createCustomReportMutation.error ||
      scheduleReportMutation.error ||
      exportReportMutation.error ||
      exportRawDataMutation.error,
  };
};

/**
 * Rule Editor Hook
 *
 * Extracts rule management logic from RuleEditor component.
 */

import { useState, useCallback } from "react";

export interface Rule {
  id: string;
  title: string;
  description: string;
  enabled: boolean;
  conditions: Condition[];
  actions: RuleAction[];
}

export interface Condition {
  type: string;
  value: unknown;
}

export interface RuleAction {
  type: string;
  value: unknown;
}

export interface CreateRuleInput {
  title: string;
  description?: string;
}

export interface UpdateRuleInput {
  title?: string;
  description?: string;
  enabled?: boolean;
}

export interface ValidationResult {
  valid: boolean;
  errors: string[];
}

export interface TestResult {
  passed: boolean;
  message: string;
}

export interface UseRuleEditorReturn {
  rules: Rule[];
  currentRule: Rule | null;
  isLoading: boolean;
  createRule: (rule: CreateRuleInput) => Promise<Rule>;
  updateRule: (id: string, updates: UpdateRuleInput) => Promise<Rule>;
  deleteRule: (id: string) => Promise<void>;
  toggleRule: (id: string, enabled: boolean) => Promise<void>;
  setCurrentRule: (rule: Rule | null) => void;
  resetEditor: () => void;
  validateRule: (rule: Rule) => ValidationResult;
  isRuleValid: boolean;
  validationErrors: string[];
  testRule: (
    rule: Rule,
    testData: Record<string, unknown>,
  ) => Promise<TestResult>;
  isTesting: boolean;
  addCondition: (condition: Condition) => void;
  removeCondition: (index: number) => void;
  updateCondition: (index: number, condition: Condition) => void;
  addAction: (action: RuleAction) => void;
  removeAction: (index: number) => void;
  updateAction: (index: number, action: RuleAction) => void;
}

export function useRuleEditor(): UseRuleEditorReturn {
  const [rules, setRules] = useState<Rule[]>([]);
  const [currentRule, setCurrentRule] = useState<Rule | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTesting, setIsTesting] = useState(false);
  const [validationErrors, setValidationErrors] = useState<string[]>([]);

  const createRule = useCallback(
    async (rule: CreateRuleInput): Promise<Rule> => {
      const newRule: Rule = {
        id: `rule-${Date.now()}`,
        title: rule.title,
        description: rule.description || "",
        enabled: true,
        conditions: [],
        actions: [],
      };
      setRules((prev) => [...prev, newRule]);
      return newRule;
    },
    [],
  );

  const updateRule = useCallback(
    async (id: string, updates: UpdateRuleInput): Promise<Rule> => {
      let updatedRule: Rule | null = null;
      setRules((prev) =>
        prev.map((r) => {
          if (r.id === id) {
            updatedRule = { ...r, ...updates };
            return updatedRule;
          }
          return r;
        }),
      );
      if (!updatedRule) throw new Error("Rule not found");
      return updatedRule;
    },
    [],
  );

  const deleteRule = useCallback(async (id: string): Promise<void> => {
    setRules((prev) => prev.filter((r) => r.id !== id));
  }, []);

  const toggleRule = useCallback(
    async (id: string, enabled: boolean): Promise<void> => {
      await updateRule(id, { enabled });
    },
    [updateRule],
  );

  const resetEditor = useCallback(() => {
    setCurrentRule(null);
    setValidationErrors([]);
  }, []);

  const validateRule = useCallback((rule: Rule): ValidationResult => {
    const errors: string[] = [];
    if (!rule.title) errors.push("Title is required");
    if (rule.conditions.length === 0)
      errors.push("At least one condition is required");
    if (rule.actions.length === 0)
      errors.push("At least one action is required");
    setValidationErrors(errors);
    return { valid: errors.length === 0, errors };
  }, []);

  const testRule = useCallback(
    async (
      _rule: Rule,
      _testData: Record<string, unknown>,
    ): Promise<TestResult> => {
      setIsTesting(true);
      try {
        await new Promise((resolve) => setTimeout(resolve, 500));
        return { passed: true, message: "Rule test passed" };
      } finally {
        setIsTesting(false);
      }
    },
    [],
  );

  const addCondition = useCallback(
    (condition: Condition) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          conditions: [...currentRule.conditions, condition],
        });
      }
    },
    [currentRule],
  );

  const removeCondition = useCallback(
    (index: number) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          conditions: currentRule.conditions.filter((_, i) => i !== index),
        });
      }
    },
    [currentRule],
  );

  const updateCondition = useCallback(
    (index: number, condition: Condition) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          conditions: currentRule.conditions.map((c, i) =>
            i === index ? condition : c,
          ),
        });
      }
    },
    [currentRule],
  );

  const addAction = useCallback(
    (action: RuleAction) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          actions: [...currentRule.actions, action],
        });
      }
    },
    [currentRule],
  );

  const removeAction = useCallback(
    (index: number) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          actions: currentRule.actions.filter((_, i) => i !== index),
        });
      }
    },
    [currentRule],
  );

  const updateAction = useCallback(
    (index: number, action: RuleAction) => {
      if (currentRule) {
        setCurrentRule({
          ...currentRule,
          actions: currentRule.actions.map((a, i) =>
            i === index ? action : a,
          ),
        });
      }
    },
    [currentRule],
  );

  // Simulate initial load
  useState(() => {
    setTimeout(() => setIsLoading(false), 100);
  });

  return {
    rules,
    currentRule,
    isLoading,
    createRule,
    updateRule,
    deleteRule,
    toggleRule,
    setCurrentRule,
    resetEditor,
    validateRule,
    isRuleValid: validationErrors.length === 0,
    validationErrors,
    testRule,
    isTesting,
    addCondition,
    removeCondition,
    updateCondition,
    addAction,
    removeAction,
    updateAction,
  };
}

/**
 * Admin Dashboard Hook
 *
 * Extracts admin session logic and wearer management from AdminDashboard component.
 * Provides aggregated statistics and multi-wearer operations.
 */

import { useState, useCallback, useMemo } from "react";
import { logger } from "../../utils/logging";

// Types for admin dashboard
export interface WearerWithSession {
  id: string;
  name: string;
  email?: string;
  isActive: boolean;
  sessionStartTime?: Date;
  sessionDuration: number; // in seconds
  isPaused: boolean;
  taskCount: number;
}

export interface AdminStatistics {
  totalWearers: number;
  activeWearers: number;
  activeSessions: number;
  totalSessionTime: number; // in seconds
  averageSessionLength: number; // in seconds
  tasksCompleted: number;
  tasksPending: number;
}

export interface Activity {
  id: string;
  type: "session_start" | "session_end" | "task_completed" | "task_assigned";
  wearerId: string;
  wearerName: string;
  timestamp: Date;
  description: string;
}

export interface AdminFilter {
  showActive?: boolean;
  showInactive?: boolean;
  searchText?: string;
}

export interface UseAdminDashboardReturn {
  // Data
  wearers: WearerWithSession[];
  statistics: AdminStatistics;
  recentActivity: Activity[];
  isLoading: boolean;
  error: Error | null;

  // Actions
  pauseAllSessions: () => Promise<void>;
  resumeAllSessions: () => Promise<void>;
  endSession: (wearerId: string) => Promise<void>;
  sendBulkNotification: (message: string, wearerIds: string[]) => Promise<void>;

  // Metrics
  activeSessions: number;
  totalWearers: number;
  averageSessionLength: number;

  // Filtering
  filterBy: (filter: AdminFilter) => void;
  currentFilter: AdminFilter;

  // Real-time
  refreshData: () => Promise<void>;
  lastUpdate: Date | null;
}

/**
 * Admin Dashboard Hook
 *
 * @returns Admin dashboard interface with wearer management and statistics
 */
export function useAdminDashboard(): UseAdminDashboardReturn {
  // State
  const [wearers, setWearers] = useState<WearerWithSession[]>([]);
  const [recentActivity, setRecentActivity] = useState<Activity[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);
  const [currentFilter, setCurrentFilter] = useState<AdminFilter>({});
  const [lastUpdate, setLastUpdate] = useState<Date | null>(null);

  // Calculate statistics
  const statistics = useMemo((): AdminStatistics => {
    const activeWearers = wearers.filter((w) => w.isActive).length;
    const activeSessions = wearers.filter(
      (w) => w.isActive && w.sessionStartTime,
    ).length;
    const totalSessionTime = wearers.reduce(
      (sum, w) => sum + w.sessionDuration,
      0,
    );
    const averageSessionLength =
      activeSessions > 0 ? totalSessionTime / activeSessions : 0;

    return {
      totalWearers: wearers.length,
      activeWearers,
      activeSessions,
      totalSessionTime,
      averageSessionLength,
      tasksCompleted: wearers.reduce((sum, w) => sum + (w.taskCount || 0), 0),
      tasksPending: 0, // Would be calculated from actual task data
    };
  }, [wearers]);

  // Pause all active sessions
  const pauseAllSessions = useCallback(async (): Promise<void> => {
    setError(null);
    try {
      setWearers((prev) =>
        prev.map((w) =>
          w.isActive && !w.isPaused ? { ...w, isPaused: true } : w,
        ),
      );
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to pause sessions");
      setError(error);
      throw error;
    }
  }, []);

  // Resume all paused sessions
  const resumeAllSessions = useCallback(async (): Promise<void> => {
    setError(null);
    try {
      setWearers((prev) =>
        prev.map((w) => (w.isPaused ? { ...w, isPaused: false } : w)),
      );
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to resume sessions");
      setError(error);
      throw error;
    }
  }, []);

  // End a specific wearer's session
  const endSession = useCallback(
    async (wearerId: string): Promise<void> => {
      setError(null);
      try {
        setWearers((prev) =>
          prev.map((w) =>
            w.id === wearerId
              ? {
                  ...w,
                  isActive: false,
                  sessionStartTime: undefined,
                  isPaused: false,
                }
              : w,
          ),
        );

        // Add activity log
        const wearer = wearers.find((w) => w.id === wearerId);
        if (wearer) {
          setRecentActivity((prev) =>
            [
              {
                id: `activity-${Date.now()}`,
                type: "session_end",
                wearerId,
                wearerName: wearer.name,
                timestamp: new Date(),
                description: `Session ended for ${wearer.name}`,
              },
              ...prev,
            ].slice(0, 50),
          ); // Keep last 50 activities
        }
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to end session");
        setError(error);
        throw error;
      }
    },
    [wearers],
  );

  // Send bulk notification
  const sendBulkNotification = useCallback(
    async (message: string, wearerIds: string[]): Promise<void> => {
      setError(null);
      try {
        // Mock implementation - in production would call notification service
        logger.info(
          `Sending notification to ${wearerIds.length} wearers: ${message}`,
        );
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to send notification");
        setError(error);
        throw error;
      }
    },
    [],
  );

  // Apply filter
  const filterBy = useCallback((filter: AdminFilter) => {
    setCurrentFilter(filter);
  }, []);

  // Refresh data
  const refreshData = useCallback(async (): Promise<void> => {
    setError(null);
    try {
      // Mock implementation - in production would fetch from API/Firebase
      setLastUpdate(new Date());
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to refresh data");
      setError(error);
      throw error;
    }
  }, []);

  // Simulate initial load
  useState(() => {
    setTimeout(() => setIsLoading(false), 100);
  });

  return {
    // Data
    wearers,
    statistics,
    recentActivity,
    isLoading,
    error,

    // Actions
    pauseAllSessions,
    resumeAllSessions,
    endSession,
    sendBulkNotification,

    // Metrics
    activeSessions: statistics.activeSessions,
    totalWearers: statistics.totalWearers,
    averageSessionLength: statistics.averageSessionLength,

    // Filtering
    filterBy,
    currentFilter,

    // Real-time
    refreshData,
    lastUpdate,
  };
}

import React, { useState, useCallback } from "react";
import { serverTimestamp } from "firebase/firestore";
import { KeyholderReward, KeyholderPunishment, TaskData } from "../../types";

interface UseKeyholderRewardsProps {
  userId: string;
  addTask: (taskData: TaskData) => Promise<void>;
  saveDataToFirestore: (data: Record<string, unknown>) => Promise<void>;
  requiredKeyholderDurationSeconds: number;
}

interface UseKeyholderRewardsReturn {
  isLoading: boolean;
  error: string | null;
  addReward: (reward: KeyholderReward) => Promise<void>;
  addPunishment: (punishment: KeyholderPunishment) => Promise<void>;
  updateDuration: (newDurationSeconds: number) => Promise<void>;
  adjustTimeFromReward: (timeSeconds: number) => Promise<void>;
  adjustTimeFromPunishment: (timeSeconds: number) => Promise<void>;
}

export function useKeyholderRewards({
  userId,
  addTask,
  saveDataToFirestore,
  requiredKeyholderDurationSeconds,
}: UseKeyholderRewardsProps): UseKeyholderRewardsReturn {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const addReward = useCallback(
    async (reward: KeyholderReward) => {
      await handleAddReward({
        reward,
        userId,
        addTask,
        saveDataToFirestore,
        requiredKeyholderDurationSeconds,
        setIsLoading,
        setError,
      });
    },
    [userId, addTask, saveDataToFirestore, requiredKeyholderDurationSeconds],
  );

  const addPunishment = useCallback(
    async (punishment: KeyholderPunishment) => {
      await handleAddPunishment({
        punishment,
        userId,
        addTask,
        saveDataToFirestore,
        requiredKeyholderDurationSeconds,
        setIsLoading,
        setError,
      });
    },
    [userId, addTask, saveDataToFirestore, requiredKeyholderDurationSeconds],
  );

  const updateDuration = useCallback(
    async (newDurationSeconds: number) => {
      await handleUpdateDuration({
        newDurationSeconds,
        saveDataToFirestore,
        setIsLoading,
        setError,
      });
    },
    [saveDataToFirestore],
  );

  const adjustTimeFromReward = useCallback(
    async (timeSeconds: number) => {
      await handleAdjustTimeFromReward({
        timeSeconds,
        requiredKeyholderDurationSeconds,
        updateDuration,
        setIsLoading,
        setError,
      });
    },
    [requiredKeyholderDurationSeconds, updateDuration],
  );

  const adjustTimeFromPunishment = useCallback(
    async (timeSeconds: number) => {
      await handleAdjustTimeFromPunishment({
        timeSeconds,
        requiredKeyholderDurationSeconds,
        updateDuration,
        setIsLoading,
        setError,
      });
    },
    [requiredKeyholderDurationSeconds, updateDuration],
  );

  return {
    isLoading,
    error,
    addReward,
    addPunishment,
    updateDuration,
    adjustTimeFromReward,
    adjustTimeFromPunishment,
  };
}

// Helper functions for useKeyholderRewards
async function handleAddReward(params: {
  reward: KeyholderReward;
  userId: string;
  addTask: (taskData: TaskData) => Promise<void>;
  saveDataToFirestore: (data: Record<string, unknown>) => Promise<void>;
  requiredKeyholderDurationSeconds: number;
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  setError: React.Dispatch<React.SetStateAction<string | null>>;
}): Promise<void> {
  const {
    reward,
    userId,
    addTask,
    saveDataToFirestore,
    requiredKeyholderDurationSeconds,
    setIsLoading,
    setError,
  } = params;

  try {
    setIsLoading(true);
    setError(null);

    if (!userId || !addTask || !saveDataToFirestore) {
      throw new Error("Missing required dependencies for adding reward");
    }

    const timeToRemoveInSeconds = reward.timeSeconds || 0;

    // Update duration if time reward is given
    if (timeToRemoveInSeconds > 0) {
      const currentDuration = requiredKeyholderDurationSeconds || 0;
      const newDuration = Math.max(0, currentDuration - timeToRemoveInSeconds);
      await saveDataToFirestore({
        requiredKeyholderDurationSeconds: newDuration,
      });
    }

    // Create the standardized log entry
    const taskData: TaskData = {
      text: "Keyholder Reward",
      logType: "reward",
      sourceText: "Manually added by Keyholder",
      note: reward.note || reward.other || "",
      timeChangeSeconds: timeToRemoveInSeconds > 0 ? -timeToRemoveInSeconds : 0,
      createdAt: serverTimestamp(),
    };

    await addTask(taskData);
  } catch (err) {
    setError(err instanceof Error ? err.message : "Failed to add reward");
    throw err;
  } finally {
    setIsLoading(false);
  }
}

async function handleAddPunishment(params: {
  punishment: KeyholderPunishment;
  userId: string;
  addTask: (taskData: TaskData) => Promise<void>;
  saveDataToFirestore: (data: Record<string, unknown>) => Promise<void>;
  requiredKeyholderDurationSeconds: number;
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  setError: React.Dispatch<React.SetStateAction<string | null>>;
}): Promise<void> {
  const {
    punishment,
    userId,
    addTask,
    saveDataToFirestore,
    requiredKeyholderDurationSeconds,
    setIsLoading,
    setError,
  } = params;

  try {
    setIsLoading(true);
    setError(null);

    if (!userId || !addTask || !saveDataToFirestore) {
      throw new Error("Missing required dependencies for adding punishment");
    }

    const timeToAddInSeconds = punishment.timeSeconds || 0;

    // Update duration if time punishment is given
    if (timeToAddInSeconds > 0) {
      const currentDuration = requiredKeyholderDurationSeconds || 0;
      const newDuration = currentDuration + timeToAddInSeconds;
      await saveDataToFirestore({
        requiredKeyholderDurationSeconds: newDuration,
      });
    }

    // Create the standardized log entry
    const taskData: TaskData = {
      text: "Keyholder Punishment",
      logType: "punishment",
      sourceText: "Manually added by Keyholder",
      note: punishment.note || punishment.other || "",
      timeChangeSeconds: timeToAddInSeconds > 0 ? timeToAddInSeconds : 0,
      createdAt: serverTimestamp(),
    };

    await addTask(taskData);
  } catch (err) {
    setError(err instanceof Error ? err.message : "Failed to add punishment");
    throw err;
  } finally {
    setIsLoading(false);
  }
}

async function handleUpdateDuration(params: {
  newDurationSeconds: number;
  saveDataToFirestore: (data: Record<string, unknown>) => Promise<void>;
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  setError: React.Dispatch<React.SetStateAction<string | null>>;
}): Promise<void> {
  const { newDurationSeconds, saveDataToFirestore, setIsLoading, setError } =
    params;

  try {
    setIsLoading(true);
    setError(null);

    if (!saveDataToFirestore) {
      throw new Error("Missing saveDataToFirestore function");
    }

    if (newDurationSeconds < 0) {
      throw new Error("Duration cannot be negative");
    }

    await saveDataToFirestore({
      requiredKeyholderDurationSeconds: newDurationSeconds,
    });
  } catch (err) {
    setError(err instanceof Error ? err.message : "Failed to update duration");
    throw err;
  } finally {
    setIsLoading(false);
  }
}

async function handleAdjustTimeFromReward(params: {
  timeSeconds: number;
  requiredKeyholderDurationSeconds: number;
  updateDuration: (newDurationSeconds: number) => Promise<void>;
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  setError: React.Dispatch<React.SetStateAction<string | null>>;
}): Promise<void> {
  const {
    timeSeconds,
    requiredKeyholderDurationSeconds,
    updateDuration,
    setIsLoading,
    setError,
  } = params;

  try {
    setIsLoading(true);
    setError(null);

    if (timeSeconds <= 0) {
      throw new Error("Reward time must be positive");
    }

    const currentDuration = requiredKeyholderDurationSeconds || 0;
    const newDuration = Math.max(0, currentDuration - timeSeconds);

    await updateDuration(newDuration);
  } catch (err) {
    setError(
      err instanceof Error ? err.message : "Failed to adjust time from reward",
    );
    throw err;
  } finally {
    setIsLoading(false);
  }
}

async function handleAdjustTimeFromPunishment(params: {
  timeSeconds: number;
  requiredKeyholderDurationSeconds: number;
  updateDuration: (newDurationSeconds: number) => Promise<void>;
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  setError: React.Dispatch<React.SetStateAction<string | null>>;
}): Promise<void> {
  const {
    timeSeconds,
    requiredKeyholderDurationSeconds,
    updateDuration,
    setIsLoading,
    setError,
  } = params;

  try {
    setIsLoading(true);
    setError(null);

    if (timeSeconds <= 0) {
      throw new Error("Punishment time must be positive");
    }

    const currentDuration = requiredKeyholderDurationSeconds || 0;
    const newDuration = currentDuration + timeSeconds;

    await updateDuration(newDuration);
  } catch (err) {
    setError(
      err instanceof Error
        ? err.message
        : "Failed to adjust time from punishment",
    );
    throw err;
  } finally {
    setIsLoading(false);
  }
}

import { useState, useEffect, useCallback } from 'react';
import { doc, setDoc, onSnapshot } from 'firebase/firestore';
import { db } from '../../firebase';

/**
 * @typedef {Object} KeyholderPermissions
 * @property {boolean} canModifyDuration
 * @property {boolean} canAssignTasks
 * @property {boolean} canViewHistory
 */

/**
 * @typedef {Object} KeyholderSessionState
 * @property {boolean} isActive
 * @property {string|null} keyholderEmail
 * @property {Date|null} sessionStartTime
 * @property {KeyholderPermissions} permissions
 * @property {number} [requiredDuration]
 */

/**
 * @typedef {Object} KeyholderSessionOptions
 * @property {string|null} userId
 * @property {boolean} isAuthReady
 * @property {string} [userEmail]
 */

/**
 * Hook for managing keyholder sessions
 * @param {KeyholderSessionOptions} options
 * @returns {Object}
 */
export const useKeyholderSession = ({ userId, isAuthReady }) => {
  const [sessionState, setSessionState] = useState({
    isActive: false,
    keyholderEmail: null,
    sessionStartTime: null,
    permissions: {
      canModifyDuration: false,
      canAssignTasks: false,
      canViewHistory: false
    }
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const startKeyholderSession = useCallback(async (keyholderEmail, permissions = {}) => {
    if (!userId || !isAuthReady) {
      setError('User not authenticated');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const userDocRef = doc(db, 'users', userId);
      const newSessionState = {
        isActive: true,
        keyholderEmail,
        sessionStartTime: new Date(),
        permissions: {
          canModifyDuration: permissions.canModifyDuration ?? true,
          canAssignTasks: permissions.canAssignTasks ?? true,
          canViewHistory: permissions.canViewHistory ?? true
        }
      };

      await setDoc(userDocRef, { keyholderSession: newSessionState }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to start keyholder session');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady]);

  const endKeyholderSession = useCallback(async () => {
    if (!userId || !isAuthReady) {
      setError('User not authenticated');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const userDocRef = doc(db, 'users', userId);
      const endSessionState = {
        isActive: false,
        keyholderEmail: null,
        sessionStartTime: null,
        permissions: {
          canModifyDuration: false,
          canAssignTasks: false,
          canViewHistory: false
        }
      };

      await setDoc(userDocRef, { keyholderSession: endSessionState }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to end keyholder session');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady]);

  const updatePermissions = useCallback(async (newPermissions) => {
    if (!userId || !isAuthReady || !sessionState.isActive) {
      setError('No active keyholder session');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const userDocRef = doc(db, 'users', userId);
      await setDoc(userDocRef, { 
        keyholderSession: {
          ...sessionState,
          permissions: { ...sessionState.permissions, ...newPermissions }
        }
      }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update permissions');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady, sessionState]);

  useEffect(() => {
    if (!userId || !isAuthReady) {
      return;
    }

    const userDocRef = doc(db, 'users', userId);
    const unsubscribe = onSnapshot(userDocRef,
      (docSnap) => {
        if (docSnap.exists()) {
          const data = docSnap.data();
          if (data.keyholderSession) {
            setSessionState({
              ...data.keyholderSession,
              sessionStartTime: data.keyholderSession.sessionStartTime?.toDate() || null
            });
          }
        }
        setError(null);
      },
      (err) => {
        setError(err.message);
      }
    );

    return () => unsubscribe();
  }, [userId, isAuthReady]);

  return {
    sessionState,
    isLoading,
    error,
    startKeyholderSession,
    endKeyholderSession,
    updatePermissions,
    isKeyholderActive: sessionState.isActive && sessionState.keyholderEmail !== null
  };
};

import React, { useState, useEffect, useCallback } from "react";
import {
  doc,
  setDoc,
  updateDoc,
  onSnapshot,
  DocumentReference,
} from "firebase/firestore";
import { db } from "../../firebase";
import { KeyholderSession, KeyholderPermissions } from "../../types";

interface UseKeyholderSessionProps {
  userId: string;
  isAuthReady: boolean;
  keyholderName?: string;
}

interface UseKeyholderSessionReturn {
  keyholderSession: KeyholderSession | null;
  isActive: boolean;
  permissions: KeyholderPermissions | null;
  isLoading: boolean;
  error: string | null;
  startSession: (
    keyholderName: string,
    permissions: KeyholderPermissions,
  ) => Promise<void>;
  endSession: () => Promise<void>;
  updatePermissions: (
    permissions: Partial<KeyholderPermissions>,
  ) => Promise<void>;
  updateKeyholderName: (name: string) => Promise<void>;
}

const defaultPermissions: KeyholderPermissions = {
  canApproveTasks: false,
  canAddPunishments: false,
  canAddRewards: false,
  canModifyDuration: false,
  canLockControls: false,
};

export function useKeyholderSession({
  userId,
  isAuthReady,
  keyholderName,
}: UseKeyholderSessionProps): UseKeyholderSessionReturn {
  const [keyholderSession, setKeyholderSession] =
    useState<KeyholderSession | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const getKeyholderDocRef = useCallback(() => {
    if (!userId) return null;
    return doc(db, "users", userId, "keyholderSession", "current");
  }, [userId]);

  // Set up real-time listener for keyholder session
  useEffect(() => {
    if (!isAuthReady || !userId) {
      setIsLoading(false);
      setKeyholderSession(null);
      return;
    }

    const keyholderDocRef = getKeyholderDocRef();
    if (!keyholderDocRef) {
      setIsLoading(false);
      return;
    }

    setIsLoading(true);

    const unsubscribe = setupSessionListener(
      keyholderDocRef,
      keyholderName,
      setKeyholderSession,
      setError,
      setIsLoading,
    );

    return () => unsubscribe();
  }, [isAuthReady, userId, keyholderName, getKeyholderDocRef]);

  const startSession = useCallback(
    async (sessionKeyholderName: string, permissions: KeyholderPermissions) => {
      await handleStartSession(
        sessionKeyholderName,
        permissions,
        userId,
        getKeyholderDocRef,
        setError,
      );
    },
    [userId, getKeyholderDocRef],
  );

  const endSession = useCallback(async () => {
    await handleEndSession(getKeyholderDocRef, setError);
  }, [getKeyholderDocRef]);

  const updatePermissions = useCallback(
    async (newPermissions: Partial<KeyholderPermissions>) => {
      await handleUpdatePermissions(
        keyholderSession,
        newPermissions,
        getKeyholderDocRef,
        setError,
      );
    },
    [keyholderSession, getKeyholderDocRef],
  );

  const updateKeyholderName = useCallback(
    async (name: string) => {
      await handleUpdateKeyholderName(
        name,
        userId,
        getKeyholderDocRef,
        setError,
      );
    },
    [userId, getKeyholderDocRef],
  );

  return {
    keyholderSession,
    isActive: keyholderSession?.isActive || false,
    permissions: keyholderSession?.permissions || null,
    isLoading,
    error,
    startSession,
    endSession,
    updatePermissions,
    updateKeyholderName,
  };
}

// Helper functions for useKeyholderSession
function setupSessionListener(
  keyholderDocRef: DocumentReference,
  keyholderName: string | undefined,
  setKeyholderSession: React.Dispatch<
    React.SetStateAction<KeyholderSession | null>
  >,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>,
) {
  return onSnapshot(
    keyholderDocRef,
    (docSnapshot) => {
      try {
        if (docSnapshot.exists()) {
          const data = docSnapshot.data();
          const session: KeyholderSession = {
            keyholderName: data.keyholderName || keyholderName,
            isActive: data.isActive || false,
            startTime: data.startTime?.toDate(),
            endTime: data.endTime?.toDate(),
            permissions: { ...defaultPermissions, ...data.permissions },
          };
          setKeyholderSession(session);
        } else {
          setKeyholderSession(null);
        }
        setError(null);
      } catch (err) {
        setError(
          err instanceof Error ? err.message : "Failed to process session data",
        );
      } finally {
        setIsLoading(false);
      }
    },
    (err) => {
      setError(
        err instanceof Error
          ? err.message
          : "Failed to listen to session changes",
      );
      setIsLoading(false);
    },
  );
}

async function handleStartSession(
  sessionKeyholderName: string,
  permissions: KeyholderPermissions,
  userId: string,
  getKeyholderDocRef: () => DocumentReference | null,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
): Promise<void> {
  try {
    setError(null);
    const keyholderDocRef = getKeyholderDocRef();
    if (!keyholderDocRef) {
      throw new Error("No keyholder document reference available");
    }

    if (!sessionKeyholderName.trim()) {
      throw new Error("Keyholder name is required");
    }

    const sessionData = {
      keyholderName: sessionKeyholderName.trim(),
      isActive: true,
      startTime: new Date(),
      endTime: null,
      permissions,
    };

    await setDoc(keyholderDocRef, sessionData);

    // Also update the main user document with keyholder name
    const userDocRef = doc(db, "users", userId);
    await updateDoc(userDocRef, {
      keyholderName: sessionKeyholderName.trim(),
    });
  } catch (err) {
    setError(
      err instanceof Error ? err.message : "Failed to start keyholder session",
    );
    throw err;
  }
}

async function handleEndSession(
  getKeyholderDocRef: () => DocumentReference | null,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
): Promise<void> {
  try {
    setError(null);
    const keyholderDocRef = getKeyholderDocRef();
    if (!keyholderDocRef) return;

    await updateDoc(keyholderDocRef, {
      isActive: false,
      endTime: new Date(),
    });
  } catch (err) {
    setError(
      err instanceof Error ? err.message : "Failed to end keyholder session",
    );
    throw err;
  }
}

async function handleUpdatePermissions(
  keyholderSession: KeyholderSession | null,
  newPermissions: Partial<KeyholderPermissions>,
  getKeyholderDocRef: () => DocumentReference | null,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
): Promise<void> {
  try {
    setError(null);
    if (!keyholderSession || !keyholderSession.isActive) {
      throw new Error("No active keyholder session to update");
    }

    const keyholderDocRef = getKeyholderDocRef();
    if (!keyholderDocRef) return;

    const updatedPermissions = {
      ...keyholderSession.permissions,
      ...newPermissions,
    };

    await updateDoc(keyholderDocRef, {
      permissions: updatedPermissions,
    });
  } catch (err) {
    setError(
      err instanceof Error ? err.message : "Failed to update permissions",
    );
    throw err;
  }
}

async function handleUpdateKeyholderName(
  name: string,
  userId: string,
  getKeyholderDocRef: () => DocumentReference | null,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
): Promise<void> {
  try {
    setError(null);
    if (!name.trim()) {
      throw new Error("Keyholder name cannot be empty");
    }

    const keyholderDocRef = getKeyholderDocRef();
    if (!keyholderDocRef) return;

    await updateDoc(keyholderDocRef, {
      keyholderName: name.trim(),
    });

    // Also update the main user document
    const userDocRef = doc(db, "users", userId);
    await updateDoc(userDocRef, {
      keyholderName: name.trim(),
    });
  } catch (err) {
    setError(
      err instanceof Error ? err.message : "Failed to update keyholder name",
    );
    throw err;
  }
}

/**
 * useKeyholderSystem Hook
 * Unified management interface for all keyholder functionality
 * Acts as the primary entry point for keyholder operations
 */
import React, { useState, useEffect, useCallback, useMemo } from "react";
import { useAuthState } from "../../contexts";
import { useKeyholderRelationships } from "../useKeyholderRelationships";
import {
  KeyholderRelationship,
  KeyholderPermissions as _KeyholderPermissions,
} from "../../types/core";
import { Task } from "../../types";
import { InviteCode } from "../../services/database/KeyholderRelationshipDBService";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useKeyholderSystem");

// ==================== TYPES ====================

export interface KeyholderStats {
  totalSubmissives: number;
  activeRelationships: number;
  totalSessions: number;
  averageSessionDuration: number;
  totalRewardsGiven: number;
  totalPunishmentsGiven: number;
  lastActivity: Date | null;
}

export interface KeyholderStatus {
  isActiveKeyholder: boolean;
  hasPermissions: boolean;
  canCreateInvites: boolean;
  maxRelationships: number;
  currentRelationships: number;
}

export interface AdminSession {
  id: string;
  keyholderId: string;
  relationshipId: string;
  startTime: Date;
  expiresAt: Date;
  permissions: string[];
  isActive: boolean;
  lastActivity: Date;
}

export interface KeyholderSystemState {
  // Active relationships where user is keyholder
  activeRelationships: KeyholderRelationship[];
  // Current admin session if active
  adminSession: AdminSession | null;
  // Overall keyholder status and permissions
  keyholderStatus: KeyholderStatus;
  // Quick stats for dashboard
  stats: KeyholderStats;
  // Currently selected relationship for operations
  selectedRelationship: KeyholderRelationship | null;
  // Loading states
  isLoading: boolean;
  isInitialized: boolean;
  error: string | null;
}

export interface InviteOptions {
  expirationHours?: number;
  maxUses?: number;
  customNote?: string;
}

export interface BulkOperations {
  startSessions: (
    relationshipIds: string[],
    options?: { duration?: number; message?: string },
  ) => Promise<void>;
  stopSessions: (relationshipIds: string[], reason?: string) => Promise<void>;
  sendMessages: (relationshipIds: string[], message: string) => Promise<void>;
  assignTasks: (
    relationshipIds: string[],
    task: Omit<Task, "id" | "createdAt">,
  ) => Promise<void>;
}

export interface KeyholderSystemActions {
  // Relationship management
  createInviteCode: (options?: InviteOptions) => Promise<string | null>;
  acceptSubmissive: (
    inviteCode: string,
  ) => Promise<KeyholderRelationship | null>;
  removeSubmissive: (relationshipId: string) => Promise<void>;

  // Session management
  startAdminSession: (relationshipId: string) => Promise<AdminSession | null>;
  endAdminSession: () => Promise<void>;

  // Multi-user operations
  switchActiveRelationship: (relationshipId: string) => void;
  getBulkOperations: () => BulkOperations;

  // Data management
  refreshData: () => Promise<void>;
  resetError: () => void;
}

// ==================== INITIAL STATE ====================

const initialKeyholderStatus: KeyholderStatus = {
  isActiveKeyholder: false,
  hasPermissions: false,
  canCreateInvites: false,
  maxRelationships: 5, // Default limit
  currentRelationships: 0,
};

const initialStats: KeyholderStats = {
  totalSubmissives: 0,
  activeRelationships: 0,
  totalSessions: 0,
  averageSessionDuration: 0,
  totalRewardsGiven: 0,
  totalPunishmentsGiven: 0,
  lastActivity: null,
};

const initialState: KeyholderSystemState = {
  activeRelationships: [],
  adminSession: null,
  keyholderStatus: initialKeyholderStatus,
  stats: initialStats,
  selectedRelationship: null,
  isLoading: false,
  isInitialized: false,
  error: null,
};

// ==================== MAIN HOOK ====================

export const useKeyholderSystem = (keyholderId?: string) => {
  const { user } = useAuthState();
  const keyholderRelationships = useKeyholderRelationships();

  // Use provided keyholderId or fall back to current user
  const effectiveKeyholderId = keyholderId || user?.uid;

  const [state, setState] = useState<KeyholderSystemState>(initialState);

  // ==================== COMPUTED VALUES ====================

  const computedValues = useMemo(() => {
    const { activeRelationships, adminSession, keyholderStatus } = state;

    return {
      hasActiveRelationships: activeRelationships.length > 0,
      canStartAdminSession: adminSession === null,
      activeRelationshipCount: activeRelationships.length,
      hasMaxRelationships:
        keyholderStatus.currentRelationships >=
        keyholderStatus.maxRelationships,
      selectedRelationshipId: state.selectedRelationship?.id || null,
    };
  }, [state]);

  // ==================== ACTIONS ====================

  const refreshData = useCallback(async () => {
    if (!effectiveKeyholderId) return;

    setState((prev) => ({ ...prev, isLoading: true, error: null }));

    try {
      logger.debug("Refreshing keyholder system data", {
        keyholderId: effectiveKeyholderId,
      });

      // Get relationships from the existing hook
      await keyholderRelationships.loadRelationships();

      const keyholderRelationshipsList =
        keyholderRelationships.relationships.asKeyholder;

      // Calculate keyholder status and stats
      const { keyholderStatus, stats } = calculateKeyholderStatusAndStats(
        keyholderRelationshipsList,
        keyholderRelationships,
      );

      setState((prev) => ({
        ...prev,
        activeRelationships: keyholderRelationshipsList,
        keyholderStatus,
        stats,
        isLoading: false,
        isInitialized: true,
      }));

      logger.info("Keyholder system data refreshed", {
        relationshipCount: keyholderRelationshipsList.length,
        activeCount: stats.activeRelationships,
      });
    } catch (error) {
      const errorMessage =
        error instanceof Error
          ? error.message
          : "Failed to refresh keyholder data";
      setState((prev) => ({
        ...prev,
        isLoading: false,
        error: errorMessage,
      }));
      logger.error("Failed to refresh keyholder system data", {
        error: error as Error,
      });
    }
  }, [effectiveKeyholderId, keyholderRelationships]);

  const createInviteCode = useCallback(
    async (options: InviteOptions = {}): Promise<string | null> => {
      if (!effectiveKeyholderId) return null;

      try {
        logger.debug("Creating invite code", {
          keyholderId: effectiveKeyholderId,
          options,
        });

        const inviteCode = await keyholderRelationships.createInviteCode(
          options.expirationHours,
        );

        return await handleInviteCodeCreation(inviteCode, refreshData);
      } catch (error) {
        return handleInviteCodeError(error, setState);
      }
    },
    [effectiveKeyholderId, keyholderRelationships, refreshData],
  );

  const acceptSubmissive = useCallback(
    async (inviteCode: string): Promise<KeyholderRelationship | null> => {
      if (!effectiveKeyholderId) return null;

      try {
        logger.debug("Accepting submissive", {
          keyholderId: effectiveKeyholderId,
          inviteCode,
        });

        const success =
          await keyholderRelationships.acceptInviteCode(inviteCode);

        return await handleSubmissiveAcceptance(
          success,
          refreshData,
          state.activeRelationships,
        );
      } catch (error) {
        return handleSubmissiveAcceptanceError(error, setState);
      }
    },
    [
      effectiveKeyholderId,
      keyholderRelationships,
      refreshData,
      state.activeRelationships,
    ],
  );

  const removeSubmissive = useCallback(
    async (relationshipId: string): Promise<void> => {
      if (!effectiveKeyholderId) return;

      try {
        logger.debug("Removing submissive", {
          keyholderId: effectiveKeyholderId,
          relationshipId,
        });

        await keyholderRelationships.endRelationship(relationshipId);

        // Refresh data to update state
        await refreshData();

        // Clear selected relationship if it was the removed one
        if (state.selectedRelationship?.id === relationshipId) {
          setState((prev) => ({ ...prev, selectedRelationship: null }));
        }
      } catch (error) {
        logger.error("Failed to remove submissive", { error: error as Error });
        setState((prev) => ({
          ...prev,
          error:
            error instanceof Error
              ? error.message
              : "Failed to remove submissive",
        }));
      }
    },
    [
      effectiveKeyholderId,
      keyholderRelationships,
      refreshData,
      state.selectedRelationship,
    ],
  );

  const switchActiveRelationship = useCallback(
    (relationshipId: string) => {
      const relationship = state.activeRelationships.find(
        (rel) => rel.id === relationshipId,
      );
      if (relationship) {
        setState((prev) => ({ ...prev, selectedRelationship: relationship }));
        logger.debug("Switched active relationship", { relationshipId });
      }
    },
    [state.activeRelationships],
  );

  // Admin session management (placeholder implementation)
  const startAdminSession = useCallback(
    async (relationshipId: string): Promise<AdminSession | null> => {
      if (!effectiveKeyholderId) return null;

      try {
        logger.debug("Starting admin session", {
          keyholderId: effectiveKeyholderId,
          relationshipId,
        });

        // TODO: Implement actual admin session creation
        const adminSession: AdminSession = {
          id: `admin_${Date.now()}`,
          keyholderId: effectiveKeyholderId,
          relationshipId,
          startTime: new Date(),
          expiresAt: new Date(Date.now() + 30 * 60 * 1000), // 30 minutes
          permissions: ["session_control", "task_management", "rewards"], // Default permissions
          isActive: true,
          lastActivity: new Date(),
        };

        setState((prev) => ({ ...prev, adminSession }));

        logger.info("Admin session started", { sessionId: adminSession.id });
        return adminSession;
      } catch (error) {
        logger.error("Failed to start admin session", {
          error: error as Error,
        });
        setState((prev) => ({
          ...prev,
          error:
            error instanceof Error
              ? error.message
              : "Failed to start admin session",
        }));
        return null;
      }
    },
    [effectiveKeyholderId],
  );

  const endAdminSession = useCallback(async (): Promise<void> => {
    if (!state.adminSession) return;

    try {
      logger.debug("Ending admin session", {
        sessionId: state.adminSession.id,
      });

      // TODO: Implement actual admin session cleanup
      setState((prev) => ({ ...prev, adminSession: null }));

      logger.info("Admin session ended");
    } catch (error) {
      logger.error("Failed to end admin session", { error: error as Error });
      setState((prev) => ({
        ...prev,
        error:
          error instanceof Error
            ? error.message
            : "Failed to end admin session",
      }));
    }
  }, [state.adminSession]);

  const getBulkOperations = useCallback((): BulkOperations => {
    return {
      startSessions: async (
        relationshipIds: string[],
        options?: { duration?: number; message?: string },
      ) => {
        logger.debug("Bulk starting sessions", { relationshipIds, options });
        // TODO: Implement bulk session start
      },
      stopSessions: async (relationshipIds: string[], reason?: string) => {
        logger.debug("Bulk stopping sessions", { relationshipIds, reason });
        // TODO: Implement bulk session stop
      },
      sendMessages: async (relationshipIds: string[], message: string) => {
        logger.debug("Bulk sending messages", { relationshipIds, message });
        // TODO: Implement bulk messaging
      },
      assignTasks: async (
        relationshipIds: string[],
        task: Omit<Task, "id" | "createdAt">,
      ) => {
        logger.debug("Bulk assigning tasks", { relationshipIds, task });
        // TODO: Implement bulk task assignment
      },
    };
  }, []);

  const resetError = useCallback(() => {
    setState((prev) => ({ ...prev, error: null }));
  }, []);

  // ==================== EFFECTS ====================

  // Initialize data when user changes
  useEffect(() => {
    if (effectiveKeyholderId && !state.isInitialized) {
      refreshData();
    }
  }, [effectiveKeyholderId, state.isInitialized, refreshData]);

  // Auto-refresh when relationships data changes
  useEffect(() => {
    if (
      keyholderRelationships.relationships.asKeyholder.length !==
      state.activeRelationships.length
    ) {
      refreshData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [
    keyholderRelationships.relationships.asKeyholder.length,
    state.activeRelationships.length,
  ]);

  // Admin session auto-expiry
  useEffect(() => {
    if (state.adminSession && state.adminSession.expiresAt < new Date()) {
      logger.info("Admin session expired, ending session");
      endAdminSession();
    }
    // endAdminSession is stable as it only depends on state.adminSession which is already in deps
  }, [state.adminSession]);

  // ==================== RETURN ====================

  const actions: KeyholderSystemActions = {
    createInviteCode,
    acceptSubmissive,
    removeSubmissive,
    startAdminSession,
    endAdminSession,
    switchActiveRelationship,
    getBulkOperations,
    refreshData,
    resetError,
  };

  return {
    // State
    ...state,

    // Computed
    ...computedValues,

    // Actions
    ...actions,
  };
};

export type UseKeyholderSystemReturn = ReturnType<typeof useKeyholderSystem>;

// Helper functions for useKeyholderSystem
function calculateKeyholderStatusAndStats(
  keyholderRelationshipsList: KeyholderRelationship[],
  keyholderRelationships: Record<string, unknown>,
): { keyholderStatus: KeyholderStatus; stats: KeyholderStats } {
  // Calculate keyholder status
  const keyholderStatus: KeyholderStatus = {
    isActiveKeyholder: keyholderRelationshipsList.length > 0,
    hasPermissions: keyholderRelationshipsList.some(
      (rel) =>
        rel.permissions && Object.values(rel.permissions).some((perm) => perm),
    ),
    canCreateInvites: keyholderRelationships.canCreateInviteCode
      ? keyholderRelationships.canCreateInviteCode()
      : false,
    maxRelationships: 5, // Could be user-specific in the future
    currentRelationships: keyholderRelationshipsList.length,
  };

  // Calculate basic stats
  const stats: KeyholderStats = {
    totalSubmissives: keyholderRelationshipsList.length,
    activeRelationships: keyholderRelationshipsList.filter(
      (rel) => rel.status === "active",
    ).length,
    totalSessions: 0, // TODO: Calculate from session data
    averageSessionDuration: 0, // TODO: Calculate from session data
    totalRewardsGiven: 0, // TODO: Calculate from reward data
    totalPunishmentsGiven: 0, // TODO: Calculate from punishment data
    lastActivity: null, // TODO: Calculate from activity data
  };

  return { keyholderStatus, stats };
}

async function handleInviteCodeCreation(
  inviteCode: InviteCode | null,
  refreshData: () => Promise<void>,
): Promise<string | null> {
  if (inviteCode) {
    // Refresh data to update state
    await refreshData();
    return inviteCode.code;
  }
  return null;
}

function handleInviteCodeError(
  error: unknown,
  setState: React.Dispatch<React.SetStateAction<KeyholderSystemState>>,
): null {
  logger.error("Failed to create invite code", { error: error as Error });
  setState((prev) => ({
    ...prev,
    error:
      error instanceof Error ? error.message : "Failed to create invite code",
  }));
  return null;
}

async function handleSubmissiveAcceptance(
  success: boolean,
  refreshData: () => Promise<void>,
  activeRelationships: KeyholderRelationship[],
): Promise<KeyholderRelationship | null> {
  if (success) {
    // Refresh data to get the new relationship
    await refreshData();

    // Return the newest relationship (should be the one just created)
    const newestRelationship = activeRelationships.sort(
      (a, b) => b.createdAt.getTime() - a.createdAt.getTime(),
    )[0];

    return newestRelationship || null;
  }
  return null;
}

function handleSubmissiveAcceptanceError(
  error: unknown,
  setState: React.Dispatch<React.SetStateAction<KeyholderSystemState>>,
): null {
  logger.error("Failed to accept submissive", { error: error as Error });
  setState((prev) => ({
    ...prev,
    error:
      error instanceof Error ? error.message : "Failed to accept submissive",
  }));
  return null;
}

import { useState, useEffect, useCallback } from "react";
import { onSnapshot, query, where } from "firebase/firestore";
import {
  MultiWearerSession,
  Wearer,
  KeyholderPermissions,
  SessionData,
} from "../../types";
import {
  getMultiWearerCollectionRef,
  getWearersCollectionRef,
  createMultiWearerSession,
  endMultiWearerSession,
  addWearerToSession,
  removeWearerFromSession,
  updateWearerInSession,
  parseWearerData,
  parseSessionData,
} from "./multiWearerHelpers";
import { logger } from "../../utils/logging";

interface UseMultiWearerProps {
  keyholderUserId: string;
  isAuthReady: boolean;
}

interface UseMultiWearerReturn {
  session: MultiWearerSession | null;
  wearers: Wearer[];
  isLoading: boolean;
  error: string | null;
  createSession: () => Promise<void>;
  endSession: () => Promise<void>;
  addWearer: (wearerData: Omit<Wearer, "id">) => Promise<void>;
  removeWearer: (wearerId: string) => Promise<void>;
  updateWearer: (wearerId: string, updates: Partial<Wearer>) => Promise<void>;
  updateWearerPermissions: (
    wearerId: string,
    permissions: Partial<KeyholderPermissions>,
  ) => Promise<void>;
  updateWearerSession: (
    wearerId: string,
    sessionData: Partial<SessionData>,
  ) => Promise<void>;
  activateWearer: (wearerId: string) => Promise<void>;
  deactivateWearer: (wearerId: string) => Promise<void>;
}

export function useMultiWearer({
  keyholderUserId,
  isAuthReady,
}: UseMultiWearerProps): UseMultiWearerReturn {
  const [session, setSession] = useState<MultiWearerSession | null>(null);
  const [wearers, setWearers] = useState<Wearer[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Set up real-time listener for multi-wearer session
  useEffect(() => {
    if (!isAuthReady || !keyholderUserId) {
      setIsLoading(false);
      setSession(null);
      setWearers([]);
      return;
    }

    const multiWearerCollectionRef = getMultiWearerCollectionRef();
    const q = query(
      multiWearerCollectionRef,
      where("keyholderUserId", "==", keyholderUserId),
      where("isActive", "==", true),
    );

    setIsLoading(true);

    const unsubscribe = onSnapshot(
      q,
      (querySnapshot) => {
        try {
          if (!querySnapshot.empty) {
            const docSnapshot = querySnapshot.docs[0];
            const sessionData = parseSessionData(docSnapshot);
            setSession({ ...sessionData, wearers: [] });

            // Set up listener for wearers
            const wearersCollectionRef = getWearersCollectionRef(
              docSnapshot.id,
            );
            const wearersUnsubscribe = onSnapshot(
              wearersCollectionRef,
              (wearersSnapshot) => {
                const wearersData: Wearer[] =
                  wearersSnapshot.docs.map(parseWearerData);
                setWearers(wearersData);
                setSession((prev) =>
                  prev ? { ...prev, wearers: wearersData } : null,
                );
              },
            );

            return () => wearersUnsubscribe();
          } else {
            setSession(null);
            setWearers([]);
          }
          setError(null);
        } catch (err) {
          logger.error("Error processing multi-wearer session data", err);
          setError(
            err instanceof Error
              ? err.message
              : "Failed to process session data",
          );
        } finally {
          setIsLoading(false);
        }
      },
      (err) => {
        logger.error("Error listening to multi-wearer session", err);
        setError(
          err instanceof Error
            ? err.message
            : "Failed to listen to session changes",
        );
        setIsLoading(false);
      },
    );

    return () => unsubscribe();
  }, [isAuthReady, keyholderUserId]);

  const createSession = useCallback(async () => {
    try {
      setError(null);
      if (!keyholderUserId) {
        throw new Error("Keyholder user ID is required");
      }
      await createMultiWearerSession(keyholderUserId);
    } catch (err) {
      logger.error("Error creating multi-wearer session", err);
      setError(err instanceof Error ? err.message : "Failed to create session");
      throw err;
    }
  }, [keyholderUserId]);

  const endSession = useCallback(async () => {
    try {
      setError(null);
      if (!session) return;
      await endMultiWearerSession(keyholderUserId);
    } catch (err) {
      logger.error("Error ending multi-wearer session", err);
      setError(err instanceof Error ? err.message : "Failed to end session");
      throw err;
    }
  }, [session, keyholderUserId]);

  const addWearer = useCallback(
    async (wearerData: Omit<Wearer, "id">) => {
      try {
        setError(null);
        if (!session) {
          throw new Error("No active session to add wearer to");
        }
        await addWearerToSession(keyholderUserId, wearerData);
      } catch (err) {
        logger.error("Error adding wearer", err);
        setError(err instanceof Error ? err.message : "Failed to add wearer");
        throw err;
      }
    },
    [session, keyholderUserId],
  );

  const removeWearer = useCallback(
    async (wearerId: string) => {
      try {
        setError(null);
        if (!session) return;
        await removeWearerFromSession(keyholderUserId, wearerId);
      } catch (err) {
        logger.error("Error removing wearer", err);
        setError(
          err instanceof Error ? err.message : "Failed to remove wearer",
        );
        throw err;
      }
    },
    [session, keyholderUserId],
  );

  const updateWearer = useCallback(
    async (wearerId: string, updates: Partial<Wearer>) => {
      try {
        setError(null);
        if (!session) return;
        await updateWearerInSession(keyholderUserId, wearerId, updates);
      } catch (err) {
        logger.error("Error updating wearer", err);
        setError(
          err instanceof Error ? err.message : "Failed to update wearer",
        );
        throw err;
      }
    },
    [session, keyholderUserId],
  );

  const updateWearerPermissions = useCallback(
    async (wearerId: string, permissions: Partial<KeyholderPermissions>) => {
      const wearer = wearers.find((w) => w.id === wearerId);
      if (!wearer) return;

      const updatedPermissions = {
        ...wearer.keyholderPermissions,
        ...permissions,
      };
      await updateWearer(wearerId, {
        keyholderPermissions: updatedPermissions,
      });
    },
    [wearers, updateWearer],
  );

  const updateWearerSession = useCallback(
    async (wearerId: string, sessionData: Partial<SessionData>) => {
      const wearer = wearers.find((w) => w.id === wearerId);
      if (!wearer) return;

      const updatedSessionData = { ...wearer.sessionData, ...sessionData };
      await updateWearer(wearerId, { sessionData: updatedSessionData });
    },
    [wearers, updateWearer],
  );

  const activateWearer = useCallback(
    async (wearerId: string) => {
      await updateWearer(wearerId, { isActive: true });
    },
    [updateWearer],
  );

  const deactivateWearer = useCallback(
    async (wearerId: string) => {
      await updateWearer(wearerId, { isActive: false });
    },
    [updateWearer],
  );

  return {
    session,
    wearers,
    isLoading,
    error,
    createSession,
    endSession,
    addWearer,
    removeWearer,
    updateWearer,
    updateWearerPermissions,
    updateWearerSession,
    activateWearer,
    deactivateWearer,
  };
}

/**
 * Session Controls Hook
 *
 * Extracts session control UI logic from SessionControls component.
 * Handles session time modifications and control permissions.
 */

import { useState, useCallback, useMemo as _useMemo } from "react";

export interface UseSessionControlsReturn {
  // Actions
  extendSession: (minutes: number) => Promise<void>;
  reduceSession: (minutes: number) => Promise<void>;
  lockSession: () => Promise<void>;
  unlockSession: () => Promise<void>;
  setLockTimer: (duration: number) => Promise<void>;

  // Permissions
  canExtend: boolean;
  canReduce: boolean;
  canLock: boolean;
  canUnlock: boolean;

  // State
  isExtending: boolean;
  isReducing: boolean;
  isLocking: boolean;
  isUnlocking: boolean;

  // Cooldowns
  extendCooldown: number | null;
  lockCooldown: number | null;

  // Error handling
  error: Error | null;
  clearError: () => void;
}

export function useSessionControls(
  _wearerId?: string,
): UseSessionControlsReturn {
