  const [isUnlocking, setIsUnlocking] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [_extendCooldown, _setExtendCooldown] = useState<number | null>(null);
  const [_lockCooldown, _setLockCooldown] = useState<number | null>(null);

  const extendSession = useCallback(async (_minutes: number): Promise<void> => {
    setIsExtending(true);
    setError(null);
    try {
      // Mock implementation
      await new Promise((resolve) => setTimeout(resolve, 100));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to extend session");
      setError(error);
      throw error;
    } finally {
      setIsExtending(false);
    }
  }, []);

  const reduceSession = useCallback(async (_minutes: number): Promise<void> => {
    setIsReducing(true);
    setError(null);
    try {
      await new Promise((resolve) => setTimeout(resolve, 100));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to reduce session");
      setError(error);
      throw error;
    } finally {
      setIsReducing(false);
    }
  }, []);

  const lockSession = useCallback(async (): Promise<void> => {
    setIsLocking(true);
    setError(null);
    try {
      await new Promise((resolve) => setTimeout(resolve, 100));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to lock session");
      setError(error);
      throw error;
    } finally {
      setIsLocking(false);
    }
  }, []);

  const unlockSession = useCallback(async (): Promise<void> => {
    setIsUnlocking(true);
    setError(null);
    try {
      await new Promise((resolve) => setTimeout(resolve, 100));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to unlock session");
      setError(error);
      throw error;
    } finally {
      setIsUnlocking(false);
    }
  }, []);

  const setLockTimer = useCallback(async (_duration: number): Promise<void> => {
    setError(null);
    try {
      await new Promise((resolve) => setTimeout(resolve, 100));
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to set lock timer");
      setError(error);
      throw error;
    }
  }, []);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Permissions - would be calculated from actual session data
  const canExtend = true;
  const canReduce = true;
  const canLock = true;
  const canUnlock = true;

  return {
    extendSession,
    reduceSession,
    lockSession,
    unlockSession,
    setLockTimer,
    canExtend,
    canReduce,
    canLock,
    canUnlock,
    isExtending,
    isReducing,
    isLocking,
    isUnlocking,
    extendCooldown,
    lockCooldown,
    error,
    clearError,
  };
}

/**
 * Pull to Refresh Hook
 * Provides pull-to-refresh functionality for mobile devices
 */
import { useCallback, useRef, useState, useEffect } from "react";
import type { React } from "react";
import { useHapticFeedback } from "./useHapticFeedback";
import { logger } from "../../utils/logging";

interface PullToRefreshOptions {
  threshold?: number;
  maxPullDistance?: number;
  onRefresh: () => Promise<void> | void;
  disabled?: boolean;
}

interface PullToRefreshReturn {
  isRefreshing: boolean;
  isPulling: boolean;
  pullDistance: number;
  pullPercentage: number;
  onTouchStart: (e: React.TouchEvent) => void;
  onTouchMove: (e: React.TouchEvent) => void;
  onTouchEnd: (e: React.TouchEvent) => void;
  onScroll: (e: React.UIEvent) => void;
}

export const usePullToRefresh = ({
  threshold = 80,
  maxPullDistance = 120,
  onRefresh,
  disabled = false,
}: PullToRefreshOptions): PullToRefreshReturn => {
  const { light: hapticLight, success: hapticSuccess } = useHapticFeedback();

  const [isRefreshing, setIsRefreshing] = useState(false);
  const [isPulling, setIsPulling] = useState(false);
  const [pullDistance, setPullDistance] = useState(0);
  const [_scrollTop, setScrollTop] = useState(0);

  const startY = useRef<number>(0);
  const currentY = useRef<number>(0);
  const isAtTop = useRef<boolean>(true);
  const hasTriggeredHaptic = useRef<boolean>(false);

  const pullPercentage = Math.min((pullDistance / threshold) * 100, 100);

  const handleTouchStart = useCallback(
    (e: React.TouchEvent) => {
      if (disabled || isRefreshing) return;

      const touch = e.touches[0];
      if (!touch) return;

      startY.current = touch.clientY;
      currentY.current = touch.clientY;
      hasTriggeredHaptic.current = false;
    },
    [disabled, isRefreshing],
  );

  const handleTouchMove = useCallback(
    (e: React.TouchEvent) => {
      if (disabled || isRefreshing || !isAtTop.current) return;

      const touch = e.touches[0];
      if (!touch) return;

      currentY.current = touch.clientY;
      const deltaY = currentY.current - startY.current;

      if (deltaY > 0) {
        // Pulling down
        const distance = Math.min(deltaY * 0.5, maxPullDistance); // Apply resistance
        setPullDistance(distance);
        setIsPulling(distance > 10);

        // Haptic feedback when threshold is reached
        if (distance >= threshold && !hasTriggeredHaptic.current) {
          hapticLight();
          hasTriggeredHaptic.current = true;
        }

        // Prevent default scrolling when pulling
        if (deltaY > 10) {
          e.preventDefault();
        }
      } else {
        setPullDistance(0);
        setIsPulling(false);
      }
    },
    [disabled, isRefreshing, threshold, maxPullDistance, hapticLight],
  );

  const handleTouchEnd = useCallback(async () => {
    if (disabled || isRefreshing) return;

    if (pullDistance >= threshold) {
      setIsRefreshing(true);
      hapticSuccess();

      try {
        await onRefresh();
      } catch (error) {
        logger.error("Pull to refresh failed", { error }, "usePullToRefresh");
      } finally {
        setIsRefreshing(false);
      }
    }

    setPullDistance(0);
    setIsPulling(false);
    hasTriggeredHaptic.current = false;
  }, [
    disabled,
    isRefreshing,
    pullDistance,
    threshold,
    onRefresh,
    hapticSuccess,
  ]);

  const handleScroll = useCallback(
    (e: React.UIEvent) => {
      const target = e.currentTarget as HTMLElement;
      const scrollTop = target.scrollTop;
      setScrollTop(scrollTop);
      isAtTop.current = scrollTop <= 0;

      // Reset pull state when scrolling away from top
      if (scrollTop > 0 && isPulling) {
        setPullDistance(0);
        setIsPulling(false);
      }
    },
    [isPulling],
  );

  // Reset states when disabled changes
  useEffect(() => {
    if (disabled) {
      setPullDistance(0);
      setIsPulling(false);
      setIsRefreshing(false);
    }
  }, [disabled]);

  return {
    isRefreshing,
    isPulling,
    pullDistance,
    pullPercentage,
    onTouchStart: handleTouchStart,
    onTouchMove: handleTouchMove,
    onTouchEnd: handleTouchEnd,
    onScroll: handleScroll,
  };
};

/**
 * Touch Gestures Hook
 * Provides swipe, tap, and other touch interactions for mobile devices
 */
import { useCallback, useRef, useState } from "react";
import type { React } from "react";

interface TouchPoint {
  x: number;
  y: number;
  timestamp: number;
}

interface SwipeOptions {
  minDistance?: number;
  maxTime?: number;
  threshold?: number;
}

interface UseTouchGesturesReturn {
  onTouchStart: (e: React.TouchEvent) => void;
  onTouchMove: (e: React.TouchEvent) => void;
  onTouchEnd: (e: React.TouchEvent) => void;
  isSwipeActive: boolean;
}

interface TouchGestureHandlers {
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
  onTap?: () => void;
  onLongPress?: () => void;
}

// Helper function to determine if a gesture is a tap
const isTapGesture = (
  deltaX: number,
  deltaY: number,
  threshold: number,
): boolean => {
  return Math.abs(deltaX) < threshold && Math.abs(deltaY) < threshold;
};

// Helper function to determine swipe direction and trigger handler
const handleSwipeGesture = (
  deltaX: number,
  deltaY: number,
  minDistance: number,
  handlers: TouchGestureHandlers,
): void => {
  const absDeltaX = Math.abs(deltaX);
  const absDeltaY = Math.abs(deltaY);

  if (absDeltaX > absDeltaY && absDeltaX >= minDistance) {
    // Horizontal swipe
    if (deltaX > 0) {
      handlers.onSwipeRight?.();
    } else {
      handlers.onSwipeLeft?.();
    }
  } else if (absDeltaY >= minDistance) {
    // Vertical swipe
    if (deltaY > 0) {
      handlers.onSwipeDown?.();
    } else {
      handlers.onSwipeUp?.();
    }
  }
};

// Helper function to reset touch state
const resetTouchState = (
  touchStart: React.MutableRefObject<TouchPoint | null>,
  touchCurrent: React.MutableRefObject<TouchPoint | null>,
  setIsSwipeActive: (active: boolean) => void,
): void => {
  touchStart.current = null;
  touchCurrent.current = null;
  setIsSwipeActive(false);
};

export const useTouchGestures = (
  handlers: TouchGestureHandlers,
  options: SwipeOptions = {},
): UseTouchGesturesReturn => {
  const { minDistance = 50, maxTime = 1000, threshold = 30 } = options;

  const touchStart = useRef<TouchPoint | null>(null);
  const touchCurrent = useRef<TouchPoint | null>(null);
  const longPressTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const [isSwipeActive, setIsSwipeActive] = useState(false);

  const clearLongPressTimer = useCallback(() => {
    if (longPressTimer.current) {
      clearTimeout(longPressTimer.current);
      longPressTimer.current = null;
    }
  }, []);

  const handleTouchStart = useCallback(
    (e: React.TouchEvent) => {
      const touch = e.touches[0];
      if (!touch) return;

      const touchPoint: TouchPoint = {
        x: touch.clientX,
        y: touch.clientY,
        timestamp: Date.now(),
      };

      touchStart.current = touchPoint;
      touchCurrent.current = touchPoint;
      setIsSwipeActive(false);

      // Start long press timer
      if (handlers.onLongPress) {
        longPressTimer.current = setTimeout(() => {
          handlers.onLongPress?.();
        }, 500);
      }

      // Prevent default to avoid iOS bounce
      if (e.cancelable) {
        e.preventDefault();
      }
    },
    [handlers],
  );

  const handleTouchMove = useCallback(
    (e: React.TouchEvent) => {
      const touch = e.touches[0];
      if (!touch || !touchStart.current) return;

      touchCurrent.current = {
        x: touch.clientX,
        y: touch.clientY,
        timestamp: Date.now(),
      };

      const deltaX = Math.abs(touch.clientX - touchStart.current.x);
      const deltaY = Math.abs(touch.clientY - touchStart.current.y);

      // Clear long press if we've moved significantly
      if (deltaX > threshold || deltaY > threshold) {
        clearLongPressTimer();

        // Set swipe active if we've moved enough
        if (deltaX > minDistance || deltaY > minDistance) {
          setIsSwipeActive(true);
        }
      }

      // Prevent scrolling during horizontal swipes
      if (deltaX > deltaY && deltaX > threshold) {
        e.preventDefault();
      }
    },
    [minDistance, threshold, clearLongPressTimer],
  );

  const handleTouchEnd = useCallback(
    (_e: React.TouchEvent) => {
      clearLongPressTimer();

      if (!touchStart.current || !touchCurrent.current) {
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      const startPoint = touchStart.current;
      const endPoint = touchCurrent.current;
      const deltaTime = endPoint.timestamp - startPoint.timestamp;

      // Check if gesture was too slow
      if (deltaTime > maxTime) {
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      const deltaX = endPoint.x - startPoint.x;
      const deltaY = endPoint.y - startPoint.y;

      // Determine if it's a tap (small movement)
      if (isTapGesture(deltaX, deltaY, threshold)) {
        handlers.onTap?.();
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      // Handle swipe gestures
      handleSwipeGesture(deltaX, deltaY, minDistance, handlers);
      resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
    },
    [handlers, maxTime, minDistance, threshold, clearLongPressTimer],
  );

  return {
    onTouchStart: handleTouchStart,
    onTouchMove: handleTouchMove,
    onTouchEnd: handleTouchEnd,
    isSwipeActive,
  };
};

/**
 * Simplified swipe hook for common use cases
 */
export const useSwipeGestures = (
  onSwipeLeft?: () => void,
  onSwipeRight?: () => void,
  options?: SwipeOptions,
) => {
  return useTouchGestures(
    {
      onSwipeLeft,
      onSwipeRight,
    },
    options,
  );
};

/**
 * Viewport Hook
 * Provides viewport dimensions and mobile-specific viewport utilities
 */
import { useState, useEffect, useCallback } from "react";

interface ViewportSize {
  width: number;
  height: number;
  innerWidth: number;
  innerHeight: number;
}

interface ViewportInfo extends ViewportSize {
  isMobile: boolean;
  isTablet: boolean;
  isDesktop: boolean;
  isLandscape: boolean;
  isPortrait: boolean;
  safeAreaInsets: {
    top: number;
    bottom: number;
    left: number;
    right: number;
  };
}

// Breakpoints
const MOBILE_BREAKPOINT = 768;
const TABLET_BREAKPOINT = 1024;

export const useViewport = (): ViewportInfo => {
  const [viewport, setViewport] = useState<ViewportSize>(() => {
    if (typeof window === "undefined") {
      return {
        width: 0,
        height: 0,
        innerWidth: 0,
        innerHeight: 0,
      };
    }

    return {
      width: window.screen.width,
      height: window.screen.height,
      innerWidth: window.innerWidth,
      innerHeight: window.innerHeight,
    };
  });

  const [safeAreaInsets, setSafeAreaInsets] = useState({
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
  });

  const updateViewport = useCallback(() => {
    setViewport({
      width: window.screen.width,
      height: window.screen.height,
      innerWidth: window.innerWidth,
      innerHeight: window.innerHeight,
    });
  }, []);

  const updateSafeAreaInsets = useCallback(() => {
    if (typeof window === "undefined" || !window.getComputedStyle) return;

    const computedStyle = window.getComputedStyle(document.documentElement);

    setSafeAreaInsets({
      top: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-top)") || "0",
      ),
      bottom: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-bottom)") || "0",
      ),
      left: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-left)") || "0",
      ),
      right: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-right)") || "0",
      ),
    });
  }, []);

  useEffect(() => {
    if (typeof window === "undefined") return;

    updateViewport();
    updateSafeAreaInsets();

    // Handle resize events with throttling
    let timeoutId: ReturnType<typeof setTimeout>;
    const handleResize = () => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        updateViewport();
        updateSafeAreaInsets();
      }, 150);
    };

    // Handle orientation change
    const handleOrientationChange = () => {
      // Delay to account for viewport changes after orientation change
      setTimeout(() => {
        updateViewport();
        updateSafeAreaInsets();
      }, 500);
    };

    window.addEventListener("resize", handleResize, { passive: true });
    window.addEventListener("orientationchange", handleOrientationChange, {
      passive: true,
    });

    return () => {
      clearTimeout(timeoutId);
      window.removeEventListener("resize", handleResize);
      window.removeEventListener("orientationchange", handleOrientationChange);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Determine device type based on viewport width
  const isMobile = viewport.innerWidth < MOBILE_BREAKPOINT;
  const isTablet =
    viewport.innerWidth >= MOBILE_BREAKPOINT &&
    viewport.innerWidth < TABLET_BREAKPOINT;
  const isDesktop = viewport.innerWidth >= TABLET_BREAKPOINT;

  // Determine orientation
  const isLandscape = viewport.innerWidth > viewport.innerHeight;
  const isPortrait = viewport.innerHeight >= viewport.innerWidth;

  return {
    ...viewport,
    isMobile,
    isTablet,
    isDesktop,
    isLandscape,
    isPortrait,
    safeAreaInsets,
  };
};

/**
 * Notification operation helper functions
 */
import React, { useCallback } from "react";
import {
  Notification,
  NotificationState,
  NotificationType,
  NotificationPriority,
  NotificationChannelType as _NotificationChannelType,
  NotificationPreferences,
} from "../../types/realtime";
import {
  isInQuietHours,
  deliverNotification,
  saveNotification,
  updateNotificationStatus,
  updateMultipleNotificationStatus,
  deleteNotification,
  saveNotificationPreferences,
} from "./notification-utils";

// Helper function to create a new notification
export const createNotificationFactory = (
  userId: string,
  notificationState: NotificationState,
  setNotificationState: React.Dispatch<React.SetStateAction<NotificationState>>,
  maxNotifications: number,
) => {
  return useCallback(
    async (
      notification: Omit<
        Notification,
        "id" | "timestamp" | "isRead" | "userId"
      >,
    ): Promise<void> => {
      const newNotification: Notification = {
        ...notification,
        id: `notif_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        timestamp: new Date(),
        isRead: false,
        userId,
      };

      // Check if notifications are enabled globally
      if (!notificationState.preferences.globalEnabled) {
        return;
      }

      // Check category preferences - using manual find for compatibility
      let categoryPref = null;
      for (
        let i = 0;
        i < notificationState.preferences.categories.length;
        i++
      ) {
        if (
          notificationState.preferences.categories[i].category ===
          notification.type
        ) {
          categoryPref = notificationState.preferences.categories[i];
          break;
        }
      }

      if (!categoryPref?.enabled) {
        return;
      }

      // Check quiet hours
      if (isInQuietHours(notificationState.preferences.quietHours)) {
        // Queue for later delivery if not urgent
        if (notification.priority !== NotificationPriority.URGENT) {
          return;
        }
      }

      // Add to state
      setNotificationState((prev) => ({
        ...prev,
        notifications: [
          newNotification,
          ...prev.notifications.slice(0, maxNotifications - 1),
        ],
      }));

      // Deliver through enabled channels
      await deliverNotification(newNotification, categoryPref.channels);

      // Save to backend
      await saveNotification(newNotification);
    },
    [
      userId,
      notificationState.preferences,
      maxNotifications,
      setNotificationState,
    ],
  );
};

// Helper function to create mark as read function
export const createMarkAsReadFunction = (
  setNotificationState: React.Dispatch<React.SetStateAction<NotificationState>>,
) => {
  return useCallback(
    async (notificationId: string): Promise<void> => {
      setNotificationState((prev) => ({
        ...prev,
        notifications: prev.notifications.map((notif) =>
          notif.id === notificationId ? { ...notif, isRead: true } : notif,
        ),
      }));

      // Update in backend
      await updateNotificationStatus(notificationId, { isRead: true });
    },
    [setNotificationState],
  );
};

// Helper function to create mark all as read function
export const createMarkAllAsReadFunction = (
  notificationState: NotificationState,
  setNotificationState: React.Dispatch<React.SetStateAction<NotificationState>>,
) => {
  return useCallback(async (): Promise<void> => {
    const unreadIds: string[] = [];
    for (let i = 0; i < notificationState.notifications.length; i++) {
      const notif = notificationState.notifications[i];
      if (!notif.isRead) {
        unreadIds.push(notif.id);
      }
    }

    setNotificationState((prev) => ({
      ...prev,
      notifications: prev.notifications.map((notif) => ({
        ...notif,
        isRead: true,
      })),
    }));

    // Update in backend
    await updateMultipleNotificationStatus(unreadIds, { isRead: true });
  }, [notificationState.notifications, setNotificationState]);
};

// Helper function to create dismiss notification function
export const createDismissNotificationFunction = (
  setNotificationState: React.Dispatch<React.SetStateAction<NotificationState>>,
) => {
  return useCallback(
    async (notificationId: string): Promise<void> => {
      setNotificationState((prev) => ({
        ...prev,
        notifications: prev.notifications.filter(
          (notif) => notif.id !== notificationId,
        ),
      }));

      // Remove from backend
      await deleteNotification(notificationId);
    },
    [setNotificationState],
  );
};

// Helper function to create preferences update function
export const createUpdatePreferencesFunction = (
  userId: string,
  notificationState: NotificationState,
  setNotificationState: React.Dispatch<React.SetStateAction<NotificationState>>,
) => {
  return useCallback(
    async (prefs: Partial<NotificationPreferences>): Promise<void> => {
      const updatedPreferences = {
        ...notificationState.preferences,
        ...prefs,
      };

      setNotificationState((prev) => ({
        ...prev,
        preferences: updatedPreferences,
        deliveryChannels: updatedPreferences.channels,
      }));

      // Save to backend
      await saveNotificationPreferences(userId, updatedPreferences);
    },
    [userId, notificationState.preferences, setNotificationState],
  );
};

// Helper function to create convenience notification functions
export const createConvenienceNotificationFunctions = (
  addNotification: (
    notification: Omit<Notification, "id" | "timestamp" | "isRead" | "userId">,
  ) => Promise<void>,
) => {
  const showSuccess = useCallback(
    (title: string, message: string) => {
      return addNotification({
        type: NotificationType.SUCCESS,
        title,
        message,
        priority: NotificationPriority.LOW,
      });
    },
    [addNotification],
  );

  const showError = useCallback(
    (title: string, message: string) => {
      return addNotification({
        type: NotificationType.ERROR,
        title,
        message,
        priority: NotificationPriority.HIGH,
      });
    },
    [addNotification],
  );

  const showWarning = useCallback(
    (title: string, message: string) => {
      return addNotification({
        type: NotificationType.WARNING,
        title,
        message,
        priority: NotificationPriority.MEDIUM,
      });
    },
    [addNotification],
  );

  const showInfo = useCallback(
    (title: string, message: string) => {
      return addNotification({
        type: NotificationType.INFO,
        title,
        message,
        priority: NotificationPriority.LOW,
      });
    },
    [addNotification],
  );

  return { showSuccess, showError, showWarning, showInfo };
};

