/**
 * Presence operation helper functions
 */
import React, { useCallback } from "react";
import {
  PresenceState,
  UserPresence,
  PresenceStatus,
  ActivityContext,
  PresenceSubscription,
} from "../../types/realtime";
import {
  createPresenceUpdate,
  sendPresenceUpdate,
  fetchUserPresences,
} from "./presence-utils";

// Helper function to create presence update functions
export const createPresenceUpdateFunctions = (
  userId: string,
  presenceState: PresenceState,
  setPresenceState: React.Dispatch<React.SetStateAction<PresenceState>>,
) => {
  const updateOwnPresence = useCallback(
    async (
      status: PresenceStatus,
      customMessage?: string,
      currentActivity?: ActivityContext,
    ): Promise<void> => {
      const updatedPresence = createPresenceUpdate(
        presenceState.ownPresence,
        status,
        customMessage,
        currentActivity,
      );

      setPresenceState((prev) => ({
        ...prev,
        ownPresence: updatedPresence,
        userPresences: {
          ...prev.userPresences,
          [userId]: updatedPresence,
        },
      }));

      // Send presence update to backend/WebSocket
      await sendPresenceUpdate(updatedPresence);
    },
    [userId, presenceState.ownPresence, setPresenceState],
  );

  const setOnline = useCallback(
    (customMessage?: string) => {
      return updateOwnPresence(PresenceStatus.ONLINE, customMessage);
    },
    [updateOwnPresence],
  );

  const setOffline = useCallback(
    (customMessage?: string) => {
      return updateOwnPresence(PresenceStatus.OFFLINE, customMessage);
    },
    [updateOwnPresence],
  );

  const setAway = useCallback(
    (customMessage?: string) => {
      return updateOwnPresence(PresenceStatus.AWAY, customMessage);
    },
    [updateOwnPresence],
  );

  const setBusy = useCallback(
    (customMessage?: string) => {
      return updateOwnPresence(PresenceStatus.BUSY, customMessage);
    },
    [updateOwnPresence],
  );

  const setInSession = useCallback(
    (sessionStartTime?: Date) => {
      const presence: UserPresence = {
        ...presenceState.ownPresence,
        status: PresenceStatus.IN_SESSION,
        isInChastitySession: true,
        sessionStartTime: sessionStartTime || new Date(),
      };

      setPresenceState((prev) => ({
        ...prev,
        ownPresence: presence,
        userPresences: {
          ...prev.userPresences,
          [userId]: presence,
        },
      }));

      return sendPresenceUpdate(presence);
    },
    [userId, presenceState.ownPresence, setPresenceState],
  );

  const updateActivity = useCallback(
    (activity: ActivityContext) => {
      return updateOwnPresence(
        presenceState.ownPresence.status,
        presenceState.ownPresence.customMessage,
        activity,
      );
    },
    [presenceState.ownPresence, updateOwnPresence],
  );

  return {
    updateOwnPresence,
    setOnline,
    setOffline,
    setAway,
    setBusy,
    setInSession,
    updateActivity,
  };
};

// Helper function to create subscription functions
export const createSubscriptionFunctions = (
  setPresenceState: React.Dispatch<React.SetStateAction<PresenceState>>,
) => {
  const subscribeToPresence = useCallback(
    (
      userIds: string[],
      callback: (presences: UserPresence[]) => void,
    ): PresenceSubscription => {
      const subscription: PresenceSubscription = {
        userIds,
        callback,
        isActive: true,
      };

      setPresenceState((prev) => ({
        ...prev,
        subscriptions: [...prev.subscriptions, subscription],
      }));

      // Fetch initial presence data
      fetchUserPresences(userIds).then((presences) => {
        const presenceMap: Record<string, UserPresence> = {};
        for (let i = 0; i < presences.length; i++) {
          const presence = presences[i];
          presenceMap[presence.userId] = presence;
        }

        setPresenceState((prev) => ({
          ...prev,
          userPresences: {
            ...prev.userPresences,
            ...presenceMap,
          },
        }));

        callback(presences);
      });

      // Return unsubscribe function
      return {
        ...subscription,
        unsubscribe: () => {
          setPresenceState((prev) => ({
            ...prev,
            subscriptions: prev.subscriptions.filter(
              (sub) => sub !== subscription,
            ),
          }));
        },
      } as PresenceSubscription & { unsubscribe: () => void };
    },
    [setPresenceState],
  );

  return { subscribeToPresence };
};

// Helper function to create query functions
export const createQueryFunctions = (presenceState: PresenceState) => {
  const getUserPresence = useCallback(
    (targetUserId: string): UserPresence | null => {
      return presenceState.userPresences[targetUserId] || null;
    },
    [presenceState.userPresences],
  );

  const getMultipleUserPresence = useCallback(
    (userIds: string[]): UserPresence[] => {
      const result: UserPresence[] = [];
      for (let i = 0; i < userIds.length; i++) {
        const presence = presenceState.userPresences[userIds[i]];
        if (presence) {
          result.push(presence);
        }
      }
      return result;
    },
    [presenceState.userPresences],
  );

  const isUserOnline = useCallback(
    (targetUserId: string): boolean => {
      const presence = getUserPresence(targetUserId);
      return presence?.status === PresenceStatus.ONLINE;
    },
    [getUserPresence],
  );

  const isUserInSession = useCallback(
    (targetUserId: string): boolean => {
      const presence = getUserPresence(targetUserId);
      return (
        presence?.status === PresenceStatus.IN_SESSION ||
        presence?.isInChastitySession === true
      );
    },
    [getUserPresence],
  );

  const getOnlineCount = useCallback(
    (userIds: string[]): number => {
      let count = 0;
      for (let i = 0; i < userIds.length; i++) {
        const presence = presenceState.userPresences[userIds[i]];
        if (
          presence &&
          [
            PresenceStatus.ONLINE,
            PresenceStatus.BUSY,
            PresenceStatus.IN_SESSION,
          ].indexOf(presence.status) !== -1
        ) {
          count++;
        }
      }
      return count;
    },
    [presenceState.userPresences],
  );

  return {
    getUserPresence,
    getMultipleUserPresence,
    isUserOnline,
    isUserInSession,
    getOnlineCount,
  };
};

// Helper function to calculate computed values
export const calculatePresenceComputedValues = (
  userPresences: Record<string, UserPresence>,
  lastActivity: Date,
  activityTimeout: number,
) => {
  const totalUsers = Object.keys(userPresences).length;

  let onlineUsers = 0;
  let inSessionUsers = 0;

  const userPresenceValues = Object.keys(userPresences).map(
    (key) => userPresences[key],
  );

  for (let i = 0; i < userPresenceValues.length; i++) {
    const p = userPresenceValues[i];
    if (
      [
        PresenceStatus.ONLINE,
        PresenceStatus.BUSY,
        PresenceStatus.IN_SESSION,
      ].indexOf(p.status) !== -1
    ) {
      onlineUsers++;
    }

    if (p.status === PresenceStatus.IN_SESSION || p.isInChastitySession) {
      inSessionUsers++;
    }
  }

  const isActive = Date.now() - lastActivity.getTime() < activityTimeout;

  return {
    totalUsers,
    onlineUsers,
    inSessionUsers,
    lastActivity,
    isActive,
  };
};

/**
 * Realtime sync operation helper functions
 */
import React, { useCallback } from "react";
import {
  RealtimeSyncState,
  ConnectionStatus,
  SyncChannel,
  ChannelType,
  RealtimeUpdate,
  Subscription,
} from "../../types/realtime";
import {
  createWebSocketUrl,
  createSyncChannel,
  sendWebSocketMessage,
  updateSyncMetrics,
  shouldAttemptReconnection,
  createSubscription,
  notifySubscribers as _notifySubscribers,
} from "./realtimeSyncHelpers";

// Helper function to create WebSocket connection functions
export const createWebSocketFunctions = (
  userId: string,
  setSyncState: React.Dispatch<React.SetStateAction<RealtimeSyncState>>,
  wsRef: React.MutableRefObject<WebSocket | null>,
  subscriptionsRef: React.MutableRefObject<{ [key: string]: Subscription }>,
  reconnectAttemptsRef: React.MutableRefObject<number>,
  reconnectTimeoutRef: React.MutableRefObject<ReturnType<
    typeof setTimeout
  > | null>,
  heartbeatTimeoutRef: React.MutableRefObject<ReturnType<
    typeof setTimeout
  > | null>,
  connectionStartTimeRef: React.MutableRefObject<Date | null>,
  maxReconnectAttempts: number,
  reconnectInterval: number,
  heartbeatInterval: number,
) => {
  const connect = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      return; // Already connected
    }

    setSyncState((prev) => ({
      ...prev,
      connectionStatus: ConnectionStatus.CONNECTING,
    }));

    try {
      const wsUrl = createWebSocketUrl(userId);
      wsRef.current = new WebSocket(wsUrl);
      connectionStartTimeRef.current = new Date();

      wsRef.current.onopen = () => {
        // WebSocket connected
        reconnectAttemptsRef.current = 0;

        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.CONNECTED,
        }));

        // Start heartbeat
        startHeartbeat();

        // Rejoin previous channels
        // Implementation would go here
      };

      wsRef.current.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data);
          handleMessage(message);
        } catch {
          // Failed to parse WebSocket message
        }
      };

      wsRef.current.onclose = (event) => {
        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.DISCONNECTED,
        }));

        stopHeartbeat();

        if (
          shouldAttemptReconnection(
            event,
            reconnectAttemptsRef.current,
            maxReconnectAttempts,
          )
        ) {
          attemptReconnect();
        }
      };

      wsRef.current.onerror = (_error) => {
        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.ERROR,
          syncMetrics: updateSyncMetrics(prev.syncMetrics, "error"),
        }));
      };
    } catch {
      setSyncState((prev) => ({
        ...prev,
        connectionStatus: ConnectionStatus.ERROR,
      }));
    }
    // Refs (wsRef, reconnectAttemptsRef, connectionStartTimeRef, heartbeatTimeoutRef, reconnectTimeoutRef)
    // are stable and don't trigger re-renders. Functions (attemptReconnect, handleMessage, startHeartbeat,
    // stopHeartbeat) are defined later and create circular dependencies. All should be wrapped in useCallback
    // with proper dependencies to avoid stale closures.
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, maxReconnectAttempts]);

  const disconnect = useCallback(() => {
    if (wsRef.current) {
      wsRef.current.close(1000, "Intentional disconnect");
      wsRef.current = null;
    }

    stopHeartbeat();

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [stopHeartbeat]);

  const attemptReconnect = useCallback(() => {
    if (reconnectAttemptsRef.current >= maxReconnectAttempts) {
      return;
    }

    reconnectAttemptsRef.current++;

    setSyncState((prev) => ({
      ...prev,
      connectionStatus: ConnectionStatus.RECONNECTING,
    }));

    reconnectTimeoutRef.current = setTimeout(() => {
      connect();
    }, reconnectInterval);
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [connect, maxReconnectAttempts, reconnectInterval, setSyncState]);

  const startHeartbeat = useCallback(() => {
    const sendHeartbeat = () => {
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({ type: "heartbeat", timestamp: new Date().toISOString() });
        heartbeatTimeoutRef.current = setTimeout(
          sendHeartbeat,
          heartbeatInterval,
        );
      }
    };

    heartbeatTimeoutRef.current = setTimeout(sendHeartbeat, heartbeatInterval);
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [heartbeatInterval, sendMessage]);

  const stopHeartbeat = useCallback(() => {
    if (heartbeatTimeoutRef.current) {
      clearTimeout(heartbeatTimeoutRef.current);
      heartbeatTimeoutRef.current = null;
    }
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const sendMessage = useCallback(

    (message: RealtimeUpdate | Record<string, unknown>) => {
      const success = sendWebSocketMessage(wsRef.current, message, () => {
        setSyncState((prev) => ({
          ...prev,
          syncMetrics: updateSyncMetrics(prev.syncMetrics, "messageSent"),
        }));
      });
    },
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [setSyncState],
  );

  const handleMessage = useCallback(
    (message: Record<string, unknown>) => {
      setSyncState((prev) => ({
        ...prev,
        syncMetrics: updateSyncMetrics(prev.syncMetrics, "messageReceived"),
      }));

      switch (message.type) {
        case "channel_joined":
          handleChannelJoined(message);
          break;
        case "channel_left":
          handleChannelLeft(message);
          break;
        case "realtime_update":
          handleRealtimeUpdate(message);
          break;
        case "heartbeat_ack":
          // Heartbeat acknowledged
          break;
        default:
        // Unknown message type
      }
    },
    [
      setSyncState,
      handleChannelJoined,
      handleChannelLeft,
      handleRealtimeUpdate,
    ],
  );

  const handleChannelJoined = useCallback(
    (message: { channel: SyncChannel }) => {
      const channel: SyncChannel = message.channel;

      setSyncState((prev) => ({
        ...prev,
        activeChannels: [
          ...prev.activeChannels.filter((c) => c.id !== channel.id),
          channel,
        ],
      }));
    },
    [setSyncState],
  );

  const handleChannelLeft = useCallback(
    (message: { channelId: string }) => {
      const channelId = message.channelId;

      setSyncState((prev) => ({
        ...prev,
        activeChannels: prev.activeChannels.filter((c) => c.id !== channelId),
      }));
    },
    [setSyncState],
  );

  const handleRealtimeUpdate = useCallback(
    (message: { update: RealtimeUpdate }) => {
      const update: RealtimeUpdate = message.update;

      // Update local data
      setSyncState((prev) => ({
        ...prev,
        realtimeData: {
          ...prev.realtimeData,
          [update.type]: update.data,
        },
      }));

      // Notify subscribers - using object instead of Map
      const subscriptionKeys = Object.keys(subscriptionsRef.current);
      for (let i = 0; i < subscriptionKeys.length; i++) {
        const key = subscriptionKeys[i];
        const subscription = subscriptionsRef.current[key];
        if (subscription.dataType === update.type && subscription.isActive) {
          try {
            subscription.callback(update);
          } catch {
            // Error in subscription callback
          }
        }
      }
    },
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [setSyncState],
  );

  return {
    connect,
    disconnect,
    sendMessage,
    startHeartbeat,
    stopHeartbeat,
  };
};

// Helper function to create channel management functions
export const createChannelFunctions = (
  userId: string,
  sendMessage: (message: RealtimeUpdate | Record<string, unknown>) => void,
) => {
  const joinChannel = useCallback(
    async (channelId: string): Promise<void> => {
      sendMessage({
        type: "join_channel",
        channelId,
        userId,
      });
    },
    [userId, sendMessage],
  );

  const leaveChannel = useCallback(
    async (channelId: string): Promise<void> => {
      sendMessage({
        type: "leave_channel",
        channelId,
        userId,
      });
    },
    [userId, sendMessage],
  );

  const createChannel = useCallback(
    async (type: ChannelType, participants: string[]): Promise<SyncChannel> => {
      const channel = createSyncChannel(type, userId, participants);

      sendMessage({
        type: "create_channel",
        channel,
        userId,
      });

      return channel;
    },
    [userId, sendMessage],
  );

  return {
    joinChannel,
    leaveChannel,
    createChannel,
  };
};

// Helper function to create subscription functions
export const createRealtimeSubscriptionFunctions = (
  subscriptionsRef: React.MutableRefObject<{ [key: string]: Subscription }>,
) => {
  const subscribeToUpdates = useCallback(
    (
      dataType: string,
      callback: (update: RealtimeUpdate) => void,
    ): Subscription => {
      const subscription = createSubscription(dataType, callback);
      subscriptionsRef.current[subscription.id] = subscription;

      // Return unsubscribe function
      return {
        ...subscription,
        unsubscribe: () => {
          const sub = subscriptionsRef.current[subscription.id];
          if (sub) {
            sub.isActive = false;
            delete subscriptionsRef.current[subscription.id];
          }
        },
      } as Subscription & { unsubscribe: () => void };
    },
    // Refs are stable and don't need to be in deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [],
  );

  const publishUpdate = useCallback(
    async (_update: RealtimeUpdate): Promise<void> => {
      // Implementation would send the update via WebSocket
    },
    [],
  );

  return {
    subscribeToUpdates,
    publishUpdate,
  };
};

