/**
 * Timer operation helper functions
 */
import {
  LiveTimer,
  TimerStatus,
  LiveTimerState,
  TimerSubscription,
} from "../../types/realtime";

// Helper function to validate timer permissions
export const validateTimerPermissions = (
  timer: LiveTimer,
  userId: string,
  operation: string,
): void => {
  if (timer.isKeyholderControlled && timer.keyholderUserId !== userId) {
    throw new Error(`Only the keyholder can ${operation} this timer`);
  }
};

// Helper function to update timer in state
export const updateTimerInState = (
  timers: LiveTimer[],
  timerId: string,
  updates: Partial<LiveTimer>,
): LiveTimer[] => {
  return timers.map((t) => (t.id === timerId ? { ...t, ...updates } : t));
};

// Helper function to calculate timer progress
export const calculateTimerProgress = (
  timer: LiveTimer,
  now: Date,
): Partial<LiveTimer> => {
  if (timer.status !== TimerStatus.RUNNING) {
    return {};
  }

  const actualElapsed = Math.floor(
    (now.getTime() - timer.startTime.getTime() - timer.totalPauseTime) / 1000,
  );
  const remaining = Math.max(0, timer.duration - actualElapsed);

  return {
    currentTime: now,
    elapsed: actualElapsed,
    remaining,
    status: remaining <= 0 ? TimerStatus.COMPLETED : timer.status,
  };
};

// Helper function to create timer subscription
export const createTimerSubscription = (
  timerId: string,
  callback: (timer: LiveTimer) => void,
  events: string[] = ["start", "pause", "resume", "stop", "update"],
  setTimerState: React.Dispatch<React.SetStateAction<LiveTimerState>>,
  subscriptionsRef: React.MutableRefObject<Map<string, TimerSubscription>>,
): TimerSubscription => {
  const subscription: TimerSubscription = {
    timerId,
    callback,
    events,
    isActive: true,
  };

  const subscriptionId = `sub_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  subscriptionsRef.current.set(subscriptionId, subscription);

  setTimerState((prev) => ({
    ...prev,
    subscriptions: [...prev.subscriptions, subscription],
  }));

  return {
    ...subscription,
    unsubscribe: () => {
      subscriptionsRef.current.delete(subscriptionId);
      setTimerState((prev) => ({
        ...prev,
        subscriptions: prev.subscriptions.filter((sub) => sub !== subscription),
      }));
    },
  } as TimerSubscription & { unsubscribe: () => void };
};

// Helper function to calculate computed values
export const calculateComputedValues = (activeTimers: LiveTimer[]) => {
  const runningCount = activeTimers.filter(
    (t) => t.status === TimerStatus.RUNNING,
  ).length;
  const pausedCount = activeTimers.filter(
    (t) => t.status === TimerStatus.PAUSED,
  ).length;
  const completedCount = activeTimers.filter(
    (t) => t.status === TimerStatus.COMPLETED,
  ).length;

  const totalActiveTime = activeTimers
    .filter((t) => t.status === TimerStatus.RUNNING)
    .reduce((total, timer) => total + timer.elapsed, 0);

  const longestRunningTimer =
    activeTimers
      .filter((t) => t.status === TimerStatus.RUNNING)
      .sort((a, b) => b.elapsed - a.elapsed)[0] || null;

  return {
    runningCount,
    pausedCount,
    completedCount,
    totalActiveTime,
    longestRunningTimer,
  };
};

/**
 * useLiveTimer - Live Timer Synchronization Hook
 *
 * Synchronized timer updates across all devices and users with real-time progress
 * sharing and keyholder monitoring.
 */
import { useState, useEffect, useCallback, useRef, useMemo } from "react";
import {
  LiveTimerState,
  LiveTimer,
  TimerType,
  TimerStatus,
  TimerSync,
  TimerEvent,
  TimerSubscription,
} from "../../types/realtime";
import {
  validateTimerPermissions,
  updateTimerInState,
  calculateTimerProgress,
  createTimerSubscription,
  calculateComputedValues,
} from "./timer-operations";

interface UseLiveTimerOptions {
  userId: string;
  relationshipId?: string;
  syncInterval?: number; // milliseconds
  accuracyThreshold?: number; // milliseconds
}

// Helper function to create timer sync record
const createTimerSync = async (timerId: string): Promise<TimerSync> => {
  const now = new Date();
  return {
    timerId,
    lastSync: now,
    serverTime: await getServerTime(),
    clientOffset: 0,
    syncAccuracy: 1.0,
  };
};

// Helper function to create new timer
const createNewTimer = (
  userId: string,
  relationshipId: string | undefined,
  type: TimerType,
  duration: number,
  title: string,
  options?: {
    description?: string;
    canPause?: boolean;
    canStop?: boolean;
    canExtend?: boolean;
    isKeyholderControlled?: boolean;
    keyholderUserId?: string;
    sessionId?: string;
    taskId?: string;
  },
): LiveTimer => {
  const now = new Date();
  return {
    id: `timer_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    type,
    status: TimerStatus.STOPPED,
    startTime: now,
    currentTime: now,
    duration,
    elapsed: 0,
    remaining: duration,
    isPaused: false,
    totalPauseTime: 0,
    userId,
    relationshipId,
    title,
    description: options?.description,
    canPause: options?.canPause ?? true,
    canStop: options?.canStop ?? true,
    canExtend: options?.canExtend ?? false,
    isKeyholderControlled: options?.isKeyholderControlled ?? false,
    keyholderUserId: options?.keyholderUserId,
    sessionId: options?.sessionId,
    taskId: options?.taskId,
  };
};

export const useLiveTimer = (options: UseLiveTimerOptions) => {
  const {
    userId,
    relationshipId,
    syncInterval = 1000, // 1 second
    accuracyThreshold = 500, // 500ms
  } = options;

  // Timer state
  const [timerState, setTimerState] = useState<LiveTimerState>({
    activeTimers: [],
    timerSyncs: [],
    subscriptions: [],
    events: [],
  });

  // Refs for intervals and sync
  const updateIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const syncIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const subscriptionsRef = useRef<Map<string, TimerSubscription>>(new Map());

  // Create a new timer
  const createTimer = useCallback(
    async (
      type: TimerType,
      duration: number, // seconds
      title: string,
      options?: {
        description?: string;
        canPause?: boolean;
        canStop?: boolean;
        canExtend?: boolean;
        isKeyholderControlled?: boolean;
        keyholderUserId?: string;
        sessionId?: string;
        taskId?: string;
      },
    ): Promise<LiveTimer> => {
      const timer = createNewTimer(
        userId,
        relationshipId,
        type,
        duration,
        title,
        options,
      );

      setTimerState((prev) => ({
        ...prev,
        activeTimers: [...prev.activeTimers, timer],
      }));

      // Create sync record
      const sync = await createTimerSync(timer.id);
      setTimerState((prev) => ({
        ...prev,
        timerSyncs: [...prev.timerSyncs, sync],
      }));

      // Save to backend
      await saveTimer(timer);

      return timer;
    },
    [userId, relationshipId],
  );

  // Start a timer
  const startTimer = useCallback(
    async (timerId: string): Promise<void> => {
      const timer = timerState.activeTimers.find((t) => t.id === timerId);
      if (!timer) {
        throw new Error("Timer not found");
      }

      validateTimerPermissions(timer, userId, "control");

      const now = new Date();
      const updatedTimer: LiveTimer = {
        ...timer,
        status: TimerStatus.RUNNING,
        startTime: now,
        currentTime: now,
        isPaused: false,
      };

      setTimerState((prev) => ({
        ...prev,
        activeTimers: updateTimerInState(
          prev.activeTimers,
          timerId,
          updatedTimer,
        ),
      }));

      // Log event and sync
      await logTimerEvent(timerId, "start", { startTime: now });
      await syncTimer(timerId);
      notifySubscribers(updatedTimer);
    },
    [timerState.activeTimers, userId, notifySubscribers, syncTimer],
  );

  // Pause a timer
  const pauseTimer = useCallback(
    async (timerId: string): Promise<void> => {
      const timer = timerState.activeTimers.find((t) => t.id === timerId);
      if (!timer) {
        throw new Error("Timer not found");
      }

      if (!timer.canPause) {
        throw new Error("Timer cannot be paused");
      }

      validateTimerPermissions(timer, userId, "pause");

      const now = new Date();
      const updatedTimer: LiveTimer = {
        ...timer,
        status: TimerStatus.PAUSED,
        isPaused: true,
        pausedAt: now,
        currentTime: now,
      };

      setTimerState((prev) => ({
        ...prev,
        activeTimers: updateTimerInState(
          prev.activeTimers,
          timerId,
          updatedTimer,
        ),
      }));

      await logTimerEvent(timerId, "pause", { pausedAt: now });
      await syncTimer(timerId);
      notifySubscribers(updatedTimer);
    },
    [timerState.activeTimers, userId, notifySubscribers, syncTimer],
  );

  // Resume a timer
  const resumeTimer = useCallback(
    async (timerId: string): Promise<void> => {
      const timer = timerState.activeTimers.find((t) => t.id === timerId);
      if (!timer) {
        throw new Error("Timer not found");
      }

      validateTimerPermissions(timer, userId, "resume");

      const now = new Date();
      const pauseDuration = timer.pausedAt
        ? now.getTime() - timer.pausedAt.getTime()
        : 0;

      const updatedTimer: LiveTimer = {
        ...timer,
        status: TimerStatus.RUNNING,
        isPaused: false,
        pausedAt: undefined,
        totalPauseTime: timer.totalPauseTime + pauseDuration,
        currentTime: now,
      };

      setTimerState((prev) => ({
        ...prev,
        activeTimers: updateTimerInState(
          prev.activeTimers,
          timerId,
          updatedTimer,
        ),
      }));

      await logTimerEvent(timerId, "resume", { resumedAt: now, pauseDuration });
      await syncTimer(timerId);
      notifySubscribers(updatedTimer);
    },
    [timerState.activeTimers, userId, notifySubscribers, syncTimer],
  );

  // Stop a timer
  const stopTimer = useCallback(
    async (timerId: string): Promise<void> => {
      const timer = timerState.activeTimers.find((t) => t.id === timerId);
      if (!timer) {
        throw new Error("Timer not found");
      }

      if (!timer.canStop) {
        throw new Error("Timer cannot be stopped");
      }

      validateTimerPermissions(timer, userId, "stop");

      const now = new Date();
      const updatedTimer: LiveTimer = {
        ...timer,
        status: TimerStatus.STOPPED,
        endTime: now,
        currentTime: now,
        isPaused: false,
      };

      setTimerState((prev) => ({
        ...prev,
        activeTimers: updateTimerInState(
          prev.activeTimers,
          timerId,
          updatedTimer,
        ),
      }));

      await logTimerEvent(timerId, "stop", { stoppedAt: now });
      await syncTimer(timerId);
      notifySubscribers(updatedTimer);
    },
    [timerState.activeTimers, userId, notifySubscribers, syncTimer],
  );

  // Extend a timer
  const extendTimer = useCallback(
    async (timerId: string, additionalSeconds: number): Promise<void> => {
      const timer = timerState.activeTimers.find((t) => t.id === timerId);
      if (!timer) {
        throw new Error("Timer not found");
      }

      if (!timer.canExtend) {
        throw new Error("Timer cannot be extended");
      }

      validateTimerPermissions(timer, userId, "extend");

      const updatedTimer: LiveTimer = {
        ...timer,
        duration: timer.duration + additionalSeconds,
        remaining: timer.remaining + additionalSeconds,
      };

      setTimerState((prev) => ({
        ...prev,
        activeTimers: updateTimerInState(
          prev.activeTimers,
          timerId,
          updatedTimer,
        ),
      }));

      await logTimerEvent(timerId, "extend", { additionalSeconds });
      await syncTimer(timerId);
      notifySubscribers(updatedTimer);
    },
    [timerState.activeTimers, userId, notifySubscribers, syncTimer],
  );

  // Subscribe to timer updates
  const subscribeToTimer = useCallback(
    (
      timerId: string,
      callback: (timer: LiveTimer) => void,
      events: string[] = ["start", "pause", "resume", "stop", "update"],
    ): TimerSubscription => {
      return createTimerSubscription(
        timerId,
        callback,
        events,
        setTimerState,
        subscriptionsRef,
      );
    },
    [],
  );

  // Get timer by ID
  const getTimer = useCallback(
    (timerId: string): LiveTimer | null => {
      return timerState.activeTimers.find((t) => t.id === timerId) || null;
    },
    [timerState.activeTimers],
  );

  // Get timers by type
  const getTimersByType = useCallback(
    (type: TimerType): LiveTimer[] => {
      return timerState.activeTimers.filter((t) => t.type === type);
    },
    [timerState.activeTimers],
  );

  // Get running timers
  const getRunningTimers = useCallback((): LiveTimer[] => {
    return timerState.activeTimers.filter(
      (t) => t.status === TimerStatus.RUNNING,
    );
  }, [timerState.activeTimers]);

  // Update timer progress
  const updateTimerProgress = useCallback(() => {
    const now = new Date();

    setTimerState((prev) => ({
      ...prev,
      activeTimers: prev.activeTimers.map((timer) => {
        const progress = calculateTimerProgress(timer, now);
        const updatedTimer = { ...timer, ...progress };

        // Check if timer completed
        if (progress.remaining === 0 && timer.status === TimerStatus.RUNNING) {
          logTimerEvent(timer.id, "complete", { completedAt: now });
          notifySubscribers(updatedTimer);
        }

        return updatedTimer;
      }),
    }));
  }, [notifySubscribers]);

  // Sync timer with server
  const syncTimer = useCallback(
    async (timerId: string): Promise<void> => {
      try {
        const serverTime = await getServerTime();
        const now = new Date();
        const clientOffset = now.getTime() - serverTime.getTime();

        setTimerState((prev) => ({
          ...prev,
          timerSyncs: prev.timerSyncs.map((sync) =>
            sync.timerId === timerId
              ? {
                  ...sync,
                  lastSync: now,
                  serverTime,
                  clientOffset,
                  syncAccuracy:
                    Math.abs(clientOffset) < accuracyThreshold ? 1.0 : 0.5,
                }
              : sync,
          ),
        }));
      } catch {
        // Failed to sync timer
      }
    },
    [accuracyThreshold],
  );

  // Notify subscribers
  const notifySubscribers = useCallback((timer: LiveTimer) => {
    subscriptionsRef.current.forEach((subscription) => {
      if (subscription.timerId === timer.id && subscription.isActive) {
        try {
          subscription.callback(timer);
        } catch {
          // Error in subscription callback
        }
      }
    });
  }, []);

  // Helper function to sync active timers with server
  const syncActiveTimers = useCallback(async () => {
    const runningTimers = timerState.activeTimers.filter(
      (t) => t.status === TimerStatus.RUNNING,
    );

    for (const timer of runningTimers) {
      try {
        const serverTime = await getServerTime();
        const now = new Date();
        const clientOffset = now.getTime() - serverTime.getTime();

        setTimerState((prev) => ({
          ...prev,
          timerSyncs: prev.timerSyncs.map((sync) =>
            sync.timerId === timer.id
              ? {
                  ...sync,
                  lastSync: now,
                  serverTime,
                  clientOffset,
                  syncAccuracy:
                    Math.abs(clientOffset) < accuracyThreshold ? 1.0 : 0.5,
                }
              : sync,
          ),
        }));
      } catch {
        // Failed to sync timer
      }
    }
  }, [timerState.activeTimers, accuracyThreshold]);

  // Start update interval
  useEffect(() => {
    updateIntervalRef.current = setInterval(updateTimerProgress, syncInterval);

    return () => {
      if (updateIntervalRef.current) {
        clearInterval(updateIntervalRef.current);
      }
    };
    // updateTimerProgress is stable (no deps)
    // syncInterval is a number variable, not a store action
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [syncInterval]);

  // Start sync interval
  useEffect(() => {
    syncIntervalRef.current = setInterval(syncActiveTimers, syncInterval * 30); // Sync every 30 seconds

    return () => {
      if (syncIntervalRef.current) {
        clearInterval(syncIntervalRef.current);
      }
    };
    // syncActiveTimers is stable
    // syncInterval is a number variable, not a store action
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [syncInterval]);

  // Computed values
  const computedValues = useMemo(() => {
    return calculateComputedValues(timerState.activeTimers);
  }, [timerState.activeTimers]);

  return {
    // Timer state
    activeTimers: timerState.activeTimers,
    timerSyncs: timerState.timerSyncs,
    events: timerState.events,

    // Timer management
    createTimer,
    startTimer,
    pauseTimer,
    resumeTimer,
    stopTimer,
    extendTimer,

    // Timer queries
    getTimer,
    getTimersByType,
    getRunningTimers,

    // Subscription management
    subscribeToTimer,

    // Computed values
    ...computedValues,
  };
};

