export const usePresence = (options: UsePresenceOptions) => {
  const {
    userId,
    updateInterval = 30000, // 30 seconds
    activityTimeout = 300000, // 5 minutes
    autoTrackActivity = true,
  } = options;

  // Presence state
  const [presenceState, setPresenceState] = useState<PresenceState>({
    userPresences: {},
    subscriptions: [],
    ownPresence: {
      userId,
      status: PresenceStatus.OFFLINE,
      lastSeen: new Date(),
    },
  });

  // Refs for tracking activity
  const lastActivityRef = useRef<Date>(new Date());
  const presenceIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const activityTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Create operation functions using helpers
  const presenceUpdateFunctions = createPresenceUpdateFunctions(
    userId,
    presenceState,
    setPresenceState,
  );

  const subscriptionFunctions = createSubscriptionFunctions(setPresenceState);
  const queryFunctions = createQueryFunctions(presenceState);

  // Extract individual functions for easier use
  const {
    updateOwnPresence: _updateOwnPresence,
    setOnline,
    setOffline,
    setAway,
    setBusy,
    setInSession,
    updateActivity: updateActivityStatus,
  } = presenceUpdateFunctions;

  const { subscribeToPresence } = subscriptionFunctions;
  const {
    getUserPresence,
    getMultipleUserPresence,
    isUserOnline,
    isUserInSession,
    getOnlineCount,
  } = queryFunctions;

  // Update activity
  const updateActivity = useCallback(
    (activity: ActivityContext) => {
      lastActivityRef.current = new Date();
      return updateActivityStatus(activity);
    },
    [updateActivityStatus],
  );

  // Handle activity tracking
  useEffect(() => {
    if (!autoTrackActivity) return;

    const trackActivity = () => {
      lastActivityRef.current = new Date();

      // Reset activity timeout
      if (activityTimeoutRef.current) {
        clearTimeout(activityTimeoutRef.current);
      }

      // Set away status after inactivity
      activityTimeoutRef.current = setTimeout(() => {
        if (presenceState.ownPresence.status === PresenceStatus.ONLINE) {
          setAway("Away due to inactivity");
        }
      }, activityTimeout);
    };

    // Track various activity events
    const events = [
      "mousedown",
      "mousemove",
      "keypress",
      "scroll",
      "touchstart",
      "click",
    ];

    events.forEach((event) => {
      document.addEventListener(event, trackActivity, { passive: true });
    });

    return () => {
      events.forEach((event) => {
        document.removeEventListener(event, trackActivity);
      });

      if (activityTimeoutRef.current) {
        clearTimeout(activityTimeoutRef.current);
      }
    };
    // setAway is from presenceUpdateFunctions which is recreated when presenceState changes
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [autoTrackActivity, presenceState.ownPresence.status, activityTimeout]);

  // Handle presence updates
  useEffect(() => {
    const sendPeriodicUpdate = async () => {
      if (presenceState.ownPresence.status !== PresenceStatus.OFFLINE) {
        // In real implementation, send to backend/WebSocket
        // await sendPresenceUpdate(presenceState.ownPresence);
      }
    };

    presenceIntervalRef.current = setInterval(
      sendPeriodicUpdate,
      updateInterval,
    );

    return () => {
      if (presenceIntervalRef.current) {
        clearInterval(presenceIntervalRef.current);
      }
    };
    // sendPeriodicUpdate uses presenceState.ownPresence which is in deps
    // updateInterval is a config number, not a store action
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [presenceState.ownPresence, updateInterval]);

  // Handle online/offline events
  useEffect(() => {
    const handleOnline = () => {
      setOnline("Back online");
    };

    const handleOffline = () => {
      setOffline("Connection lost");
    };

    const handleVisibilityChange = () => {
      if (document.hidden) {
        setAway("Tab inactive");
      } else {
        setOnline("Tab active");
      }
    };

    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
    // setOnline, setOffline, setAway are from presenceUpdateFunctions (stable)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Initialize presence as online
  useEffect(() => {
    setOnline("Connected");

    // Set offline when component unmounts
    return () => {
      setOffline("Disconnected");
    };
    // setOnline, setOffline are from presenceUpdateFunctions (stable)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Computed values
  const computedValues = useMemo(() => {
    return calculatePresenceComputedValues(
      presenceState.userPresences,
      lastActivityRef.current,
      activityTimeout,
    );
  }, [presenceState.userPresences, activityTimeout]);

  return {
    // Presence state
    ownPresence: presenceState.ownPresence,
    userPresences: presenceState.userPresences,

    // Status management
    setOnline,
    setOffline,
    setAway,
    setBusy,
    setInSession,
    updateActivity,

    // Subscription management
    subscribeToPresence,

    // Presence queries
    getUserPresence,
    getMultipleUserPresence,
    isUserOnline,
    isUserInSession,
    getOnlineCount,

    // Computed values
    ...computedValues,
  };
};

/**
 * useRealtimeSync - Real-time Data Synchronization Hook
 *
 * Real-time data synchronization across devices and users with WebSocket integration
 * and conflict resolution.
 */
import { useState, useEffect, useCallback, useRef, useMemo } from "react";
import {
  RealtimeSyncState,
  ConnectionStatus,
  SyncChannel,
  ChannelType,
  RealtimeUpdate,
  Subscription,
  UpdateCallback,
} from "../../types/realtime";

interface WebSocketMessage {
  type: string;
  data?: unknown;
  channelId?: string;
  userId?: string;
  timestamp?: Date;
}

interface UseRealtimeSyncOptions {
  userId: string;
  autoConnect?: boolean;
  reconnectInterval?: number; // milliseconds
  heartbeatInterval?: number; // milliseconds
  maxReconnectAttempts?: number;
}

export const useRealtimeSync = (options: UseRealtimeSyncOptions) => {
  const {
    userId,
    autoConnect = true,
    reconnectInterval = 5000,
    heartbeatInterval = 30000,
    maxReconnectAttempts = 5,
  } = options;

  // Realtime sync state
  const [syncState, setSyncState] = useState<RealtimeSyncState>({
    connectionStatus: ConnectionStatus.DISCONNECTED,
    activeChannels: [],
    realtimeData: {},
    syncMetrics: {
      lastSuccessfulSync: new Date(),
      syncErrors: 0,
      messagesReceived: 0,
      messagesSent: 0,
      averageLatency: 0,
      connectionUptime: 0,
    },
  });

  // Refs for WebSocket and subscriptions
  const wsRef = useRef<WebSocket | null>(null);
  const subscriptionsRef = useRef<Map<string, Subscription>>(new Map());
  const reconnectAttemptsRef = useRef(0);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const heartbeatTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const connectionStartTimeRef = useRef<Date | null>(null);

  // Connect to WebSocket
  const connect = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      return; // Already connected
    }

    setSyncState((prev) => ({
      ...prev,
      connectionStatus: ConnectionStatus.CONNECTING,
    }));

    try {
      // In real implementation, use your WebSocket server URL
      const wsUrl =
        process.env.NODE_ENV === "development"
          ? "ws://localhost:8080/ws"
          : "wss://api.chastityos.com/ws";

      wsRef.current = new WebSocket(`${wsUrl}?userId=${userId}`);
      connectionStartTimeRef.current = new Date();

      wsRef.current.onopen = () => {
        // WebSocket connected
        reconnectAttemptsRef.current = 0;

        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.CONNECTED,
        }));

        // Start heartbeat
        startHeartbeat();

        // Rejoin previous channels
        syncState.activeChannels.forEach((channel) => {
          sendMessage({
            type: "join_channel",
            channelId: channel.id,
            userId,
          });
        });
      };

      wsRef.current.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data);
          handleMessage(message);
        } catch {
          // Failed to parse WebSocket message
        }
      };

      wsRef.current.onclose = (event) => {
        // WebSocket disconnected

        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.DISCONNECTED,
        }));

        stopHeartbeat();

        // Attempt reconnection if not intentional disconnect
        if (
          event.code !== 1000 &&
          reconnectAttemptsRef.current < maxReconnectAttempts
        ) {
          attemptReconnect();
        }
      };

      wsRef.current.onerror = (_error) => {
        // WebSocket error

        setSyncState((prev) => ({
          ...prev,
          connectionStatus: ConnectionStatus.ERROR,
          syncMetrics: {
            ...prev.syncMetrics,
            syncErrors: prev.syncMetrics.syncErrors + 1,
          },
        }));
      };
    } catch {
      // Failed to create WebSocket connection
      setSyncState((prev) => ({
        ...prev,
        connectionStatus: ConnectionStatus.ERROR,
      }));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, maxReconnectAttempts]);

  // Disconnect WebSocket
  const disconnect = useCallback(() => {
    if (wsRef.current) {
      wsRef.current.close(1000, "Intentional disconnect");
      wsRef.current = null;
    }

    stopHeartbeat();

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // stopHeartbeat is stable

  // Attempt reconnection
  const attemptReconnect = useCallback(() => {
    if (reconnectAttemptsRef.current >= maxReconnectAttempts) {
      // Max reconnection attempts reached
      return;
    }

    reconnectAttemptsRef.current++;

    setSyncState((prev) => ({
      ...prev,
      connectionStatus: ConnectionStatus.RECONNECTING,
    }));

    reconnectTimeoutRef.current = setTimeout(() => {
      // Reconnection attempt
      connect();
    }, reconnectInterval);
  }, [connect, maxReconnectAttempts, reconnectInterval]);

  // Start heartbeat
  const startHeartbeat = useCallback(() => {
    const sendHeartbeat = () => {
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({ type: "heartbeat", timestamp: new Date().toISOString() });
        heartbeatTimeoutRef.current = setTimeout(
          sendHeartbeat,
          heartbeatInterval,
        );
      }
    };

    heartbeatTimeoutRef.current = setTimeout(sendHeartbeat, heartbeatInterval);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [heartbeatInterval]); // sendMessage is stable

  // Stop heartbeat
  const stopHeartbeat = useCallback(() => {
    if (heartbeatTimeoutRef.current) {
      clearTimeout(heartbeatTimeoutRef.current);
      heartbeatTimeoutRef.current = null;
    }
  }, []);

  // Send message via WebSocket
  const sendMessage = useCallback((message: WebSocketMessage) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));

      setSyncState((prev) => ({
        ...prev,
        syncMetrics: {
          ...prev.syncMetrics,
          messagesSent: prev.syncMetrics.messagesSent + 1,
        },
      }));
    }
  }, []);

  // Handle incoming messages
  const handleMessage = useCallback((message: WebSocketMessage) => {
    setSyncState((prev) => ({
      ...prev,
      syncMetrics: {
        ...prev.syncMetrics,
        messagesReceived: prev.syncMetrics.messagesReceived + 1,
        lastSuccessfulSync: new Date(),
      },
    }));

    switch (message.type) {
      case "channel_joined":
        handleChannelJoined(message);
        break;
      case "channel_left":
        handleChannelLeft(message);
        break;
      case "realtime_update":
        handleRealtimeUpdate(message);
        break;
      case "heartbeat_ack":
        // Heartbeat acknowledged
        break;
      default:
      // Unknown message type
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // handler functions are stable

  // Handle channel joined
  const handleChannelJoined = useCallback((message: WebSocketMessage) => {
    const channel: SyncChannel = message.data as SyncChannel;

    setSyncState((prev) => ({
      ...prev,
      activeChannels: [
        ...prev.activeChannels.filter((c) => c.id !== channel.id),
        channel,
      ],
    }));
  }, []);

  // Handle channel left
  const handleChannelLeft = useCallback((message: WebSocketMessage) => {
    const channelId = (message.data as { channelId: string })?.channelId;

    setSyncState((prev) => ({
      ...prev,
      activeChannels: prev.activeChannels.filter((c) => c.id !== channelId),
    }));
  }, []);

  // Handle realtime update
  const handleRealtimeUpdate = useCallback((message: WebSocketMessage) => {
    const update: RealtimeUpdate = message.data as RealtimeUpdate;

    // Update local data
    setSyncState((prev) => ({
      ...prev,
      realtimeData: {
        ...prev.realtimeData,
        [update.type]: update.data,
      },
    }));

    // Notify subscribers
    subscriptionsRef.current.forEach((subscription, _subscriptionId) => {
      if (subscription.dataType === update.type && subscription.isActive) {
        try {
          subscription.callback(update);
        } catch {
          // Error in subscription callback
        }
      }
    });
  }, []);

  // Join a sync channel
  const joinChannel = useCallback(
    async (channelId: string): Promise<void> => {
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({
          type: "join_channel",
          channelId,
          userId,
        });
      } else {
        throw new Error("WebSocket not connected");
      }
    },
    [userId, sendMessage],
  );

  // Leave a sync channel
  const leaveChannel = useCallback(
    async (channelId: string): Promise<void> => {
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({
          type: "leave_channel",
          channelId,
          userId,
        });
      }
    },
    [userId, sendMessage],
  );

  // Create a new sync channel
  const createChannel = useCallback(
    async (type: ChannelType, participants: string[]): Promise<SyncChannel> => {
      const channel: SyncChannel = {
        id: `${type}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        type,
        participants: [userId, ...participants],
        lastActivity: new Date(),
        isActive: true,
        encryptionEnabled: type === ChannelType.RELATIONSHIP,
      };

      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({
          type: "create_channel",
          channel,
          userId,
        });
      }

      return channel;
    },
    [userId, sendMessage],
  );

  // Subscribe to realtime updates
  const subscribeToUpdates = useCallback(
    (dataType: string, callback: UpdateCallback): Subscription => {
      const subscription: Subscription = {
        id: `sub_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        dataType,
        callback,
        isActive: true,
        created: new Date(),
      };

      subscriptionsRef.current.set(subscription.id, subscription);

      // Return unsubscribe function
      return {
        ...subscription,
        unsubscribe: () => {
          const sub = subscriptionsRef.current.get(subscription.id);
          if (sub) {
            sub.isActive = false;
            subscriptionsRef.current.delete(subscription.id);
          }
        },
      } as Subscription & { unsubscribe: () => void };
    },
    [],
  );

  // Publish a realtime update
  const publishUpdate = useCallback(
    async (update: RealtimeUpdate): Promise<void> => {
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        sendMessage({
          type: "publish_update",
          update: {
            ...update,
            userId,
            timestamp: new Date(),
          },
        });
      } else {
        throw new Error("WebSocket not connected");
      }
    },
    [userId, sendMessage],
  );

  // Sync with keyholder
  const syncWithKeyholder = useCallback(
    async (relationshipId: string): Promise<void> => {
      const channelId = `relationship_${relationshipId}`;
      await joinChannel(channelId);
    },
    [joinChannel],
  );

  // Sync session data
  const syncSessionData = useCallback(
    async (sessionId: string): Promise<void> => {
      const channelId = `session_${sessionId}`;
      await joinChannel(channelId);
    },
    [joinChannel],
  );

  // Auto-connect on mount
  useEffect(() => {
    if (autoConnect && userId) {
      connect();
    }

    return () => {
      disconnect();
    };
  }, [userId, autoConnect, connect, disconnect]);

  // Update connection uptime
  useEffect(() => {
    if (
      syncState.connectionStatus === ConnectionStatus.CONNECTED &&
      connectionStartTimeRef.current
    ) {
      const updateUptime = () => {
        setSyncState((prev) => ({
          ...prev,
          syncMetrics: {
            ...prev.syncMetrics,
            connectionUptime:
              Date.now() - connectionStartTimeRef.current!.getTime(),
          },
        }));
      };

      const interval = setInterval(updateUptime, 1000);
      return () => clearInterval(interval);
    }
  }, [syncState.connectionStatus]);

  // Computed values
  const computedValues = useMemo(() => {
    const isConnected =
      syncState.connectionStatus === ConnectionStatus.CONNECTED;
    const channelCount = syncState.activeChannels.length;
    const lastSyncTime = syncState.syncMetrics.lastSuccessfulSync;
    const hasActiveSync = syncState.activeChannels.some((c) => c.isActive);

    return {
      isConnected,
      channelCount,
      lastSyncTime,
      hasActiveSync,
    };
  }, [syncState]);

  return {
    // Connection state
    connectionStatus: syncState.connectionStatus,
    activeChannels: syncState.activeChannels,
    syncMetrics: syncState.syncMetrics,

    // Connection control
    connect,
    disconnect,

    // Channel management
    joinChannel,
    leaveChannel,
    createChannel,

    // Real-time data operations
    subscribeToUpdates,
    publishUpdate,

    // Relationship-specific sync
    syncWithKeyholder,
    syncSessionData,

    // Computed values
    ...computedValues,
  };
};

/**
 * React Hook for Relationship Status Management
 * Handles session management and status updates
 */
import { useState, useEffect, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { relationshipChastityService } from "@/services/database/RelationshipChastityService";
import {
  RelationshipChastityData,
  RelationshipSession,
} from "@/types/relationships";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";

interface RelationshipStatusState extends BaseHookState {
  chastityData: RelationshipChastityData | null;
  sessions: RelationshipSession[];
}

interface RelationshipStatusActions extends BaseHookActions {
  startSession: (
    relationshipId: string,
    options?: {
      goalDuration?: number;
      isHardcoreMode?: boolean;
      notes?: string;
    },
  ) => Promise<void>;
  endSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  pauseSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  resumeSession: (relationshipId: string, sessionId: string) => Promise<void>;
  loadRelationshipData: (relationshipId: string) => Promise<void>;
}

export function useRelationshipStatus(): RelationshipStatusState &
  RelationshipStatusActions {
  const { user } = useAuthState();
  const userId = user?.uid;

  const [state, setState] = useState<RelationshipStatusState>({
    chastityData: null,
    sessions: [],
    isLoading: false,
    error: null,
  });

  const { clearError: clearErrorFn } = createBaseActions();

  const loadChastityData = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const chastityData =
          await relationshipChastityService.getChastityData(relationshipId);
        setState((prev) => ({ ...prev, chastityData }));
      },
      "load chastity data",
      setState,
    );
  }, []);

  const loadSessions = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const sessions =
          await relationshipChastityService.getSessionHistory(relationshipId);
        setState((prev) => ({ ...prev, sessions }));
      },
      "load sessions",
      setState,
    );
  }, []);

  const loadRelationshipData = useCallback(
    async (relationshipId: string) => {
      await Promise.all([
        loadChastityData(relationshipId),
        loadSessions(relationshipId),
      ]);
    },
    [loadChastityData, loadSessions],
  );

  const startSession = useCallback(
    async (
      relationshipId: string,
      options?: {
        goalDuration?: number;
        isHardcoreMode?: boolean;
        notes?: string;
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.startSession(
            relationshipId,
            userId,
            options || {},
          );
          await loadRelationshipData(relationshipId);
        },
        "start session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const endSession = useCallback(
    async (relationshipId: string, sessionId: string, reason?: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.endSession(
            relationshipId,
            sessionId,
            userId,
            reason,
          );
          await loadRelationshipData(relationshipId);
        },
        "end session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const pauseSession = useCallback(
    async (relationshipId: string, sessionId: string, reason?: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.pauseSession(
            relationshipId,
            sessionId,
            userId,
            reason,
          );
          await loadRelationshipData(relationshipId);
        },
        "pause session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const resumeSession = useCallback(
    async (relationshipId: string, sessionId: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.resumeSession(
            relationshipId,
            sessionId,
            userId,
          );
          await loadRelationshipData(relationshipId);
        },
        "resume session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  // Set up real-time listeners for active relationship data
  useEffect(() => {
    // This would be called externally when activeRelationship changes
    // The parent hook will need to manage this subscription
  }, []);

  return {
    ...state,
    startSession,
    endSession,
    pauseSession,
    resumeSession,
    loadRelationshipData,
    clearError,
  };
}

/**
 * React Hook for Relationship Task and Event Management
 * Handles creating, updating tasks and logging events
 */
import { useState, useEffect, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { relationshipChastityService } from "@/services/database/RelationshipChastityService";
import { RelationshipTask, RelationshipEvent } from "@/types/relationships";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";

interface RelationshipTasksState extends BaseHookState {
  tasks: RelationshipTask[];
  events: RelationshipEvent[];
}

interface RelationshipTasksActions extends BaseHookActions {
  createTask: (
    relationshipId: string,
    taskData: {
      text: string;
      dueDate?: Date;
      consequence?: RelationshipTask["consequence"];
    },
  ) => Promise<void>;
  updateTaskStatus: (
    relationshipId: string,
    taskId: string,
    status: RelationshipTask["status"],
    note?: string,
  ) => Promise<void>;
  logEvent: (
    relationshipId: string,
    eventData: {
      type: RelationshipEvent["type"];
      details: RelationshipEvent["details"];
      isPrivate?: boolean;
      tags?: string[];
    },
  ) => Promise<void>;
  loadRelationshipData: (relationshipId: string) => Promise<void>;
}

export function useRelationshipTasks(): RelationshipTasksState &
  RelationshipTasksActions {
  const { user } = useAuthState();
  const userId = user?.uid;

  const [state, setState] = useState<RelationshipTasksState>({
    tasks: [],
    events: [],
    isLoading: false,
    error: null,
  });

  const { clearError: clearErrorFn } = createBaseActions();

  const loadTasks = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const tasks =
          await relationshipChastityService.getTasks(relationshipId);
        setState((prev) => ({ ...prev, tasks }));
      },
      "load tasks",
      setState,
    );
  }, []);

  const loadEvents = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const events =
          await relationshipChastityService.getEvents(relationshipId);
        setState((prev) => ({ ...prev, events }));
      },
      "load events",
      setState,
    );
  }, []);

  const loadRelationshipData = useCallback(
    async (relationshipId: string) => {
      await Promise.all([
        loadTasks(relationshipId),
        loadEvents(relationshipId),
      ]);
    },
    [loadTasks, loadEvents],
  );

  const createTask = useCallback(
    async (
      relationshipId: string,
      taskData: {
        text: string;
        dueDate?: Date;
        consequence?: RelationshipTask["consequence"];
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.createTask(
            relationshipId,
            taskData,
            userId,
          );
          await loadTasks(relationshipId);
        },
        "create task",
        setState,
      );
    },
    [userId, loadTasks],
  );

  const updateTaskStatus = useCallback(
    async (
      relationshipId: string,
      taskId: string,
      status: RelationshipTask["status"],
      note?: string,
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.updateTaskStatus(
            relationshipId,
            taskId,
            status,
            userId,
            note,
          );
          await loadTasks(relationshipId);
        },
        "update task status",
        setState,
      );
    },
    [userId, loadTasks],
  );

  const logEvent = useCallback(
    async (
      relationshipId: string,
      eventData: {
        type: RelationshipEvent["type"];
        details: RelationshipEvent["details"];
        isPrivate?: boolean;
        tags?: string[];
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.logEvent(
            relationshipId,
            eventData,
            userId,
          );
          await loadEvents(relationshipId);
        },
        "log event",
        setState,
      );
    },
    [userId, loadEvents],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  // Set up real-time listeners for tasks
  useEffect(() => {
    // This would be called externally when activeRelationship changes
    // The parent hook will need to manage this subscription
  }, []);

  return {
    ...state,
    createTask,
    updateTaskStatus,
    logEvent,
    loadRelationshipData,
    clearError,
  };
}

/**
 * React Hook for Relationship Form Validation
 * Handles validation logic for relationship forms and rules
 */
import { useState, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { dataMigrationService } from "@/services/migration/DataMigrationService";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";
import type { KeyholderPermissions } from "@/types/core";

interface RelationshipValidationState extends BaseHookState {
  needsMigration: boolean;
}

interface RelationshipValidationActions extends BaseHookActions {
  migrateSingleUserData: () => Promise<void>;
  checkMigrationStatus: () => Promise<void>;
  validateRequestForm: (formData: {
    email: string;
    role: "submissive" | "keyholder";
    message?: string;
  }) => { isValid: boolean; errors: string[] };
  validatePermissionsForm: (permissions: KeyholderPermissions) => {
    isValid: boolean;
    errors: string[];
  };
}

export function useRelationshipValidation(): RelationshipValidationState &
  RelationshipValidationActions {
  const { user } = useAuthState();
  const userId = user?.uid;

  const [state, setState] = useState<RelationshipValidationState>({
    needsMigration: false,
    isLoading: false,
    error: null,
  });

  const { clearError: clearErrorFn } = createBaseActions();

  const checkMigrationStatus = useCallback(async () => {
    if (!userId) return;

    try {
      const needsMigration = await dataMigrationService.needsMigration(userId);
      setState((prev) => ({ ...prev, needsMigration }));
    } catch {
      // Silent fail for migration check - using logger instead of console
      // logger.warn("Failed to check migration status", error);
    }
  }, [userId]);

  const migrateSingleUserData = useCallback(async () => {
    if (!userId) throw new Error("User not authenticated");

    return withErrorHandling(
      async () => {
        const result = await dataMigrationService.migrateSingleUserData(userId);
        if (!result.success) {
          throw new Error(result.errors.join(", "));
        }
        setState((prev) => ({ ...prev, needsMigration: false }));
      },
      "migrate single user data",
      setState,
    );
  }, [userId]);

  const validateRequestForm = useCallback(
    (formData: {
      email: string;
      role: "submissive" | "keyholder";
      message?: string;
    }) => {
      const errors: string[] = [];

      if (!formData.email.trim()) {
        errors.push("Email is required");
      } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        errors.push("Email format is invalid");
      }

      if (!formData.role) {
        errors.push("Role is required");
      }

      if (formData.message && formData.message.length > 500) {
        errors.push("Message cannot exceed 500 characters");
      }

      return {
        isValid: errors.length === 0,
        errors,
      };
    },
    [],
  );

  const validatePermissionsForm = useCallback(
    (permissions: KeyholderPermissions) => {
      const errors: string[] = [];

      // Add specific validation rules for permissions
      if (typeof permissions !== "object" || permissions === null) {
        errors.push("Permissions must be an object");
      }

      // Add more specific validation as needed based on RelationshipPermissions type

      return {
        isValid: errors.length === 0,
        errors,
      };
    },
    [],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  return {
    ...state,
    migrateSingleUserData,
    checkMigrationStatus,
    validateRequestForm,
    validatePermissionsForm,
    clearError,
  };
}

