/**
 * useAuditLog - Comprehensive Audit System Hook
 *
 * Complete audit logging system for transparency, compliance, and security monitoring
 * across all user actions.
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import {
  AuditLogState,
  AuditEntry,
  AuditAction,
  AuditCategory,
  AuditContext,
  AuditDetails,
  AuditSeverity,
  AuditOutcome,
  AuditFilter,
  AuditSearchQuery,
  SecurityEvent,
  SecurityAuditSummary,
  ComplianceReport,
  ExportFormat,
  AuditExport,
  AuditPrivacySettings,
  CleanupResult as _CleanupResult,
} from "../../types/security";
import { PermissionContext } from "../../types/security";
import {
  getCategoryForAction,
  getClientIP,
  saveAuditEntry,
  fetchRecentAuditEntries,
  savePrivacySettings,
  applyAuditFilters,
  convertToCSV,
  generatePDF,
  calculateSecurityScore,
  generateSecurityRecommendations,
} from "./audit-utils";

interface UseAuditLogOptions {
  userId: string;
  relationshipId?: string;
  autoLogActions?: boolean;
  retentionDays?: number;
}

export const useAuditLog = (options: UseAuditLogOptions) => {
  const {
    userId,
    relationshipId,
    _autoLogActions = true,
    retentionDays = 365,
  } = options;

  // Initialize state
  const [auditState, setAuditState] = useState<AuditLogState>(() =>
    initializeAuditState(retentionDays),
  );
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Load recent audit entries on mount
  useEffect(() => {
    if (userId) {
      loadAuditEntries(
        userId,
        relationshipId,
        setAuditState,
        setError,
        setLoading,
      );
    }
  }, [userId, relationshipId]);

  // Create action handlers
  const actionHandlers = useMemo(
    () =>
      createActionHandlers(userId, relationshipId, auditState, setAuditState),
    [userId, relationshipId, auditState],
  );

  // Create query handlers
  const queryHandlers = useMemo(
    () => createQueryHandlers(auditState),
    [auditState],
  );

  // Create management handlers
  const managementHandlers = useMemo(
    () => createManagementHandlers(userId, auditState, setAuditState),
    [userId, auditState],
  );

  // Computed values
  const computedValues = useMemo(
    () => calculateComputedValues(auditState),
    [auditState],
  );

  return {
    // Audit data
    recentEntries: auditState.recentEntries,
    categories: auditState.categories,
    filters: auditState.filters,
    loading,
    error,

    // Action handlers
    ...actionHandlers,

    // Query handlers
    ...queryHandlers,

    // Management handlers
    ...managementHandlers,

    // Computed values
    ...computedValues,
  };
};

// Search helper functions
function applyTextSearch(entries: AuditEntry[], query?: string): AuditEntry[] {
  if (!query) return entries;

  const searchTerm = query.toLowerCase();
  return entries.filter(
    (entry) =>
      entry.details.description.toLowerCase().includes(searchTerm) ||
      entry.action.toLowerCase().includes(searchTerm),
  );
}

function applySearchFilters(
  entries: AuditEntry[],
  filters?: AuditFilter,
): AuditEntry[] {
  if (!filters) return entries;

  let results = entries;

  if (filters.startDate && filters.endDate) {
    results = results.filter(
      (entry) =>
        entry.timestamp >= filters.startDate! &&
        entry.timestamp <= filters.endDate!,
    );
  }

  if (filters.userId) {
    results = results.filter((entry) => entry.userId === filters.userId);
  }

  if (filters.actions && filters.actions.length > 0) {
    results = results.filter((entry) =>
      filters.actions!.includes(entry.action),
    );
  }

  if (filters.categories && filters.categories.length > 0) {
    results = results.filter((entry) =>
      filters.categories!.includes(entry.category),
    );
  }

  if (filters.severity) {
    results = results.filter((entry) => entry.severity === filters.severity);
  }

  if (filters.outcome) {
    results = results.filter((entry) => entry.outcome === filters.outcome);
  }

  return results;
}

function applySorting(
  entries: AuditEntry[],
  sortBy?: string,
  sortOrder?: "asc" | "desc",
): AuditEntry[] {
  if (!sortBy) return entries;

  return [...entries].sort((a, b) => {
    let comparison = 0;

    switch (sortBy) {
      case "timestamp":
        comparison = a.timestamp.getTime() - b.timestamp.getTime();
        break;
      case "severity":
        const severityOrder: Record<string, number> = {
          low: 0,
          medium: 1,
          high: 2,
          critical: 3,
        };
        comparison = severityOrder[a.severity] - severityOrder[b.severity];
        break;
      case "category":
        comparison = a.category.localeCompare(b.category);
        break;
    }

    return sortOrder === "desc" ? -comparison : comparison;
  });
}

function applyPagination(
  entries: AuditEntry[],
  limit?: number,
  offset?: number,
): AuditEntry[] {
  if (!limit && !offset) return entries;

  const start = offset || 0;
  const end = start + (limit || entries.length);
  return entries.slice(start, end);
}

// Initialization and handler creation helpers
function initializeAuditState(retentionDays: number): AuditLogState {
  return {
    recentEntries: [],
    categories: Object.values(AuditCategory),
    filters: {},
    exportOptions: {
      formats: ["json", "csv", "pdf"],
      maxRecords: 10000,
      includeMetadata: true,
    },
    privacySettings: {
      shareWithKeyholder: false,
      includeIPAddresses: false,
      includeUserAgent: false,
      retentionDays,
      anonymizeAfterDays: 90,
    },
  };
}

async function loadAuditEntries(
  userId: string,
  relationshipId: string | undefined,
  setAuditState: React.Dispatch<React.SetStateAction<AuditLogState>>,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
  setIsLoading: React.Dispatch<React.SetStateAction<boolean>>,
): Promise<void> {
  try {
    setIsLoading(true);
    setError(null);

    const entries = await fetchRecentAuditEntries(userId, relationshipId);

    setAuditState((prev) => ({
      ...prev,
      recentEntries: entries,
    }));
  } catch (err) {
    setError(err instanceof Error ? err.message : "Failed to load audit log");
  } finally {
    setIsLoading(false);
  }
}

function createActionHandlers(
  userId: string,
  relationshipId: string | undefined,
  auditState: AuditLogState,
  setAuditState: React.Dispatch<React.SetStateAction<AuditLogState>>,
) {
  const logAction = useCallback(
    async (
      action: AuditAction,
      details: AuditDetails,
      context?: AuditContext,
    ): Promise<void> => {
      try {
        const entry: AuditEntry = {
          id: `audit_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
          timestamp: new Date(),
          userId,
          action,
          category: getCategoryForAction(action),
          context: context || { relationshipId },
          details,
          severity: AuditSeverity.LOW,
          outcome: AuditOutcome.SUCCESS,
          ipAddress: auditState.privacySettings.includeIPAddresses
            ? await getClientIP()
            : undefined,
          userAgent: auditState.privacySettings.includeUserAgent
            ? navigator.userAgent
            : undefined,
        };

        await saveAuditEntry(entry);
        setAuditState((prev) => ({
          ...prev,
          recentEntries: [entry, ...prev.recentEntries.slice(0, 99)],
        }));
      } catch {
        // Failed to log audit action
      }
    },
    [userId, relationshipId, auditState.privacySettings, setAuditState],
  );

  const logSecurityEvent = useCallback(
    async (event: SecurityEvent): Promise<void> => {
      const details: AuditDetails = {
        description: event.description,
        metadata: event.metadata,
      };

      const entry: AuditEntry = {
        id: `security_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        timestamp: new Date(),
        userId,
        action: AuditAction.PERMISSION_CHECK,
        category: AuditCategory.SECURITY,
        context: { relationshipId },
        details,
        severity: event.severity,
        outcome:
          event.type === "failed_login"
            ? AuditOutcome.FAILURE
            : AuditOutcome.SUCCESS,
        ipAddress: auditState.privacySettings.includeIPAddresses
          ? await getClientIP()
          : undefined,
        userAgent: auditState.privacySettings.includeUserAgent
          ? navigator.userAgent
          : undefined,
      };

      await saveAuditEntry(entry);
      setAuditState((prev) => ({
        ...prev,
        recentEntries: [entry, ...prev.recentEntries.slice(0, 99)],
      }));
    },
    [userId, relationshipId, auditState.privacySettings, setAuditState],
  );

  const logPermissionCheck = useCallback(
    async (
      permission: string,
      granted: boolean,
      context?: PermissionContext,
    ): Promise<void> => {
      const details: AuditDetails = {
        description: `Permission check: ${permission}`,
        metadata: { permission, granted, context },
      };

      await logAction(
        AuditAction.PERMISSION_CHECK,
        details,
        context as AuditContext,
      );
    },
    [logAction],
  );

  return { logAction, logSecurityEvent, logPermissionCheck };
}

function createQueryHandlers(auditState: AuditLogState) {
  const getEntriesByDateRange = useCallback(
    async (start: Date, end: Date): Promise<AuditEntry[]> => {
      return auditState.recentEntries.filter(
        (entry) => entry.timestamp >= start && entry.timestamp <= end,
      );
    },
    [auditState.recentEntries],
  );

  const getEntriesByCategory = useCallback(
    async (category: AuditCategory): Promise<AuditEntry[]> => {
      return auditState.recentEntries.filter(
        (entry) => entry.category === category,
      );
    },
    [auditState.recentEntries],
  );

  const getEntriesByUser = useCallback(
    async (targetUserId: string): Promise<AuditEntry[]> => {
      return auditState.recentEntries.filter(
        (entry) =>
          entry.userId === targetUserId ||
          entry.context.targetUserId === targetUserId,
      );
    },
    [auditState.recentEntries],
  );

  const searchEntries = useCallback(
    async (query: AuditSearchQuery): Promise<AuditEntry[]> => {
      let results = auditState.recentEntries;
      results = applyTextSearch(results, query.query);
      results = applySearchFilters(results, query.filters);
      results = applySorting(results, query.sortBy, query.sortOrder);
      results = applyPagination(results, query.limit, query.offset);
      return results;
    },
    [auditState.recentEntries],
  );

  return {
    getEntriesByDateRange,
    getEntriesByCategory,
    getEntriesByUser,
    searchEntries,
  };
}

function createManagementHandlers(
  userId: string,
  auditState: AuditLogState,
  setAuditState: React.Dispatch<React.SetStateAction<AuditLogState>>,
) {
  const applyFilters = useCallback(
    (filters: AuditFilter): void => {
      setAuditState((prev) => ({ ...prev, filters }));
    },
    [setAuditState],
  );

  const getSecuritySummary = useCallback((): SecurityAuditSummary => {
    const securityEntries = auditState.recentEntries.filter(
      (entry) => entry.category === AuditCategory.SECURITY,
    );

    const failedLogins = auditState.recentEntries.filter(
      (entry) =>
        entry.action === AuditAction.LOGIN &&
        entry.outcome === AuditOutcome.FAILURE,
    ).length;

    const permissionViolations = auditState.recentEntries.filter(
      (entry) =>
        entry.action === AuditAction.PERMISSION_CHECK &&
        entry.outcome === AuditOutcome.UNAUTHORIZED,
    ).length;

    const dataExports = auditState.recentEntries.filter(
      (entry) => entry.action === AuditAction.DATA_EXPORT,
    ).length;

    const lastSecurityEvent =
      securityEntries.length > 0 ? securityEntries[0].timestamp : undefined;

    return {
      totalEntries: auditState.recentEntries.length,
      securityEvents: securityEntries.length,
      failedLogins,
      permissionViolations,
      dataExports,
      lastSecurityEvent,
    };
  }, [auditState.recentEntries]);

  // Get compliance report (not yet exposed in return)
  const _getComplianceReport = useCallback((): ComplianceReport => {
    const now = new Date();
    const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

    const periodEntries = auditState.recentEntries.filter(
      (entry) => entry.timestamp >= thirtyDaysAgo,
    );

    const actionsByCategory = periodEntries.reduce(
      (acc, entry) => {
        acc[entry.category] = (acc[entry.category] || 0) + 1;
        return acc;
      },
      {} as Record<AuditCategory, number>,
    );

    const securityScore = calculateSecurityScore(periodEntries);

    return {
      period: {
        start: thirtyDaysAgo,
        end: now,
      },
      totalActions: periodEntries.length,
      actionsByCategory,
      securityScore,
      recommendations: generateSecurityRecommendations(periodEntries),
    };
  }, [auditState.recentEntries]);

  // Export audit log (not yet exposed in return)
  const _exportAuditLog = useCallback(
    async (
      format: ExportFormat,
      filters?: AuditFilter,
    ): Promise<AuditExport> => {
      let entries = auditState.recentEntries;

      // Apply filters if provided
      if (filters) {
        // Implementation would filter entries based on criteria
        entries = applyAuditFilters(entries, filters);
      }

      // Generate export data based on format
      let data: string | Uint8Array;
      let filename: string;

      switch (format) {
        case "json":
          data = JSON.stringify(entries, null, 2);
          filename = `audit-log-${Date.now()}.json`;
          break;
        case "csv":
          data = convertToCSV(entries);
          filename = `audit-log-${Date.now()}.csv`;
          break;
        case "pdf":
          data = await generatePDF(entries);
          filename = `audit-log-${Date.now()}.pdf`;
          break;
        default:
          throw new Error(`Unsupported export format: ${format}`);
      }

      return {
        format,
        data,
        filename,
        generatedAt: new Date(),
      };
    },
    [auditState.recentEntries],
  );

  // Share with keyholder (not yet exposed in return)
  const _shareWithKeyholder = useCallback(
    async (_entries: string[]): Promise<void> => {
      if (!relationshipId) {
        throw new Error("No relationship context for sharing");
      }

      // In real implementation, this would share selected entries with keyholder
      // In real implementation, this would share selected entries with keyholder
    },
    [],
  );

  // Update privacy settings
  const updatePrivacySettings = useCallback(
    async (settings: Partial<AuditPrivacySettings>): Promise<void> => {
      setAuditState((prev) => ({
        ...prev,
        privacySettings: { ...prev.privacySettings, ...settings },
      }));
      await savePrivacySettings(userId, settings);
    },
    [userId, setAuditState],
  );

  return { applyFilters, getSecuritySummary, updatePrivacySettings };
}

function calculateComputedValues(auditState: AuditLogState) {
  const totalEntries = auditState.recentEntries.length;
  const securityAlerts = auditState.recentEntries.filter(
    (entry) => entry.severity === AuditSeverity.HIGH,
  ).length;

  const lastLoginEntry = auditState.recentEntries.find(
    (entry) => entry.action === AuditAction.LOGIN,
  );
  const lastLoginTime = lastLoginEntry?.timestamp;

  const actionCounts = auditState.recentEntries.reduce(
    (acc, entry) => {
      acc[entry.action] = (acc[entry.action] || 0) + 1;
      return acc;
    },
    {} as Record<string, number>,
  );

  const mostCommonActions = Object.entries(actionCounts)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 5)
    .map(([action, count]) => ({ action, count }));

  const hasSecurityConcerns = auditState.recentEntries.some(
    (entry) =>
      entry.category === AuditCategory.SECURITY &&
      entry.severity === AuditSeverity.HIGH,
  );

  return {
    totalEntries,
    securityAlerts,
    lastLoginTime,
    mostCommonActions,
    hasSecurityConcerns,
  };
}

/**
 * usePermissions - Granular Permission System Hook
 *
 * Provides comprehensive permission checking system that validates user permissions
 * in real-time across all application contexts.
 */
import React, { useState, useEffect, useCallback, useMemo } from "react";
import { UserRole } from "../../types/core";
import {
  Permission,
  PermissionState,
  PermissionContext,
  PermissionDetails,
  PermissionHistoryEntry,
  PermissionRequest,
  ElevatedAccessRequest,
  PermissionCategory,
  PermissionLevel,
  PermissionScope,
  RolePermission,
  ContextPermission,
  PermissionCheckLog,
} from "../../types/security";

interface UsePermissionsOptions {
  userId: string;
  context?: PermissionContext;
  cacheEnabled?: boolean;
  cacheTTL?: number; // milliseconds
}

export const usePermissions = (options: UsePermissionsOptions) => {
  const {
    userId,
    context,
    cacheEnabled = true,
    cacheTTL = 5 * 60 * 1000,
  } = options;

  // Permission state
  const [permissionState, setPermissionState] = useState<PermissionState>({
    userPermissions: [],
    rolePermissions: [],
    contextPermissions: [],
    permissionCache: {},
    permissionChecks: [],
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Load user permissions on mount
  useEffect(() => {
    const loadPermissions = async () => {
      try {
        setLoading(true);
        setError(null);

        // In a real implementation, this would fetch from your backend/Firebase
        // For now, we'll simulate with default permissions based on user role
        const defaultPermissions = await getDefaultPermissions(userId);

        setPermissionState((prev) => ({
          ...prev,
          userPermissions: defaultPermissions.userPermissions,
          rolePermissions: defaultPermissions.rolePermissions,
          contextPermissions: defaultPermissions.contextPermissions,
        }));
      } catch (err) {
        setError(
          err instanceof Error ? err.message : "Failed to load permissions",
        );
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      loadPermissions();
    }
  }, [userId]);

  // Core permission checking function
  const hasPermission = useCallback(
    (permission: string, checkContext?: PermissionContext): boolean => {
      const contextToUse = checkContext || context;
      const cacheKey = `${permission}_${JSON.stringify(contextToUse)}`;

      // Check cache first if enabled
      if (cacheEnabled) {
        const cached = permissionState.permissionCache[cacheKey];
        if (cached && cached.expiresAt > new Date()) {
          return cached.result;
        }
      }

      const hasAccess = checkPermissionAccess(
        permission,
        contextToUse,
        permissionState,
      );

      // Cache and log the result
      cacheAndLogPermissionCheck({
        cacheKey,
        hasAccess,
        permission,
        context: contextToUse,
        userId,
        cacheEnabled,
        cacheTTL,
        setPermissionState,
      });

      return hasAccess;
    },
    [permissionState, context, userId, cacheEnabled, cacheTTL],
  );

  // Check multiple permissions (any)
  const hasAnyPermission = useCallback(
    (permissions: string[], checkContext?: PermissionContext): boolean => {
      return permissions.some((permission) =>
        hasPermission(permission, checkContext),
      );
    },
    [hasPermission],
  );

  // Check multiple permissions (all)
  const hasAllPermissions = useCallback(
    (permissions: string[], checkContext?: PermissionContext): boolean => {
      return permissions.every((permission) =>
        hasPermission(permission, checkContext),
      );
    },
    [hasPermission],
  );

  // Role-based checks
  const hasRole = useCallback(
    (role: UserRole): boolean => {
      return permissionState.rolePermissions.some((rp) => rp.role === role);
    },
    [permissionState.rolePermissions],
  );

  // Action-based permission check
  const canPerformAction = useCallback(
    (action: string, target?: string): boolean => {
      const actionPermission = `${action}${target ? `_${target}` : ""}`;
      return hasPermission(actionPermission);
    },
    [hasPermission],
  );

  // Resource access check
  const canAccessResource = useCallback(
    (resource: string, action: string): boolean => {
      return hasPermission(`${resource}_${action}`);
    },
    [hasPermission],
  );

  // Data modification check
  const canModifyData = useCallback(
    (dataType: string, ownerId: string): boolean => {
      // Can modify own data or have admin permissions
      return userId === ownerId || hasPermission(`${dataType}_modify_any`);
    },
    [hasPermission, userId],
  );

  // Permission request
  const requestPermission = useCallback(
    async (
      permission: string,
      justification: string,
    ): Promise<PermissionRequest> => {
      // In real implementation, this would submit to backend
      const request: PermissionRequest = {
        id: `req_${Date.now()}`,
        userId,
        permission,
        justification,
        status: "pending",
        requestedAt: new Date(),
      };

      // Simulate async request submission
      return new Promise((resolve) => {
        setTimeout(() => resolve(request), 100);
      });
    },
    [userId],
  );

  // Elevated access request
  const requestElevatedAccess = useCallback(
    async (
      duration: number,
      reason: string,
    ): Promise<ElevatedAccessRequest> => {
      const request: ElevatedAccessRequest = {
        id: `elev_${Date.now()}`,
        userId,
        duration,
        reason,
        status: "pending",
        requestedAt: new Date(),
      };

      return new Promise((resolve) => {
        setTimeout(() => resolve(request), 100);
      });
    },
    [userId],
  );

  // Get permission details
  const getPermissionDetails = useCallback(
    (permission: string): PermissionDetails | null => {
      const userPerm = permissionState.userPermissions.find(
        (p) => p.name === permission,
      );
      if (!userPerm) return null;

      return {
        permission: userPerm,
        currentStatus: hasPermission(permission),
        reasons: ["User permission"],
        restrictions: [],
        expiresAt: userPerm.expiresAt,
      };
    },
    [permissionState.userPermissions, hasPermission],
  );

  // Get permission history
  const getPermissionHistory = useCallback((): PermissionHistoryEntry[] => {
    // In real implementation, this would fetch from backend
    return [];
  }, []);

  // Computed values
  const computedValues = useMemo(() => {
    const isAdmin = hasRole(UserRole.KEYHOLDER);
    const isKeyholder = hasRole(UserRole.KEYHOLDER);
    const canElevate = hasPermission("request_elevation");

    const hasExpiredPermissions = permissionState.userPermissions.some(
      (p) => p.expiresAt && p.expiresAt < new Date(),
    );

    const permissionLevel = calculateOverallPermissionLevel(
      permissionState.userPermissions,
    );

    return {
      isAdmin,
      isKeyholder,
      canElevate,
      hasExpiredPermissions,
      permissionLevel,
    };
  }, [permissionState.userPermissions, hasRole, hasPermission]);

  return {
    // Permission state
    permissions: permissionState.userPermissions,
    rolePermissions: permissionState.rolePermissions,
    contextPermissions: permissionState.contextPermissions,
    loading,
    error,

    // Permission checking
    hasPermission,
    hasAnyPermission,
    hasAllPermissions,

    // Role-based checks
    hasRole,
    canPerformAction,

    // Context-specific checks
    canAccessResource,
    canModifyData,

    // Permission requests
    requestPermission,
    requestElevatedAccess,

    // Permission management
    getPermissionDetails,
    getPermissionHistory,

    // Computed values
    ...computedValues,
  };
};

// Helper functions
function matchesPermission(
  permission: Permission,
  requested: string,
  context?: PermissionContext,
): boolean {
  if (permission.name !== requested) return false;

  // Check if permission is expired
  if (permission.expiresAt && permission.expiresAt < new Date()) {
    return false;
  }

  // Check conditions if present
  if (permission.conditions && permission.conditions.length > 0) {
    return permission.conditions.every((condition) =>
      evaluateCondition(condition, context),
    );
  }

  return true;
}

function contextsMatch(
  permissionContext: PermissionContext,
  requestContext: PermissionContext,
): boolean {
  return (
    permissionContext.relationshipId === requestContext.relationshipId &&
    permissionContext.sessionId === requestContext.sessionId &&
    permissionContext.targetUserId === requestContext.targetUserId &&
    permissionContext.resourceType === requestContext.resourceType &&
    permissionContext.resourceId === requestContext.resourceId
  );
}

function evaluateCondition(
  _condition: {
    type: string;
    value: string | number | boolean | Date | string[];
    operator: string;
  },
  _context?: PermissionContext,
): boolean {
  // Simplified condition evaluation
  // In real implementation, this would be more sophisticated
  return true;
}

function calculateOverallPermissionLevel(
  permissions: Permission[],
): PermissionLevel {
  if (permissions.some((p) => p.level === PermissionLevel.ADMIN)) {
    return PermissionLevel.ADMIN;
  }
  if (permissions.some((p) => p.level === PermissionLevel.WRITE)) {
    return PermissionLevel.WRITE;
  }
  if (permissions.some((p) => p.level === PermissionLevel.READ)) {
    return PermissionLevel.READ;
  }
  return PermissionLevel.NONE;
}

async function getDefaultPermissions(_userId: string): Promise<{
  userPermissions: Permission[];
  rolePermissions: RolePermission[];
  contextPermissions: ContextPermission[];
}> {
