      id: "profile_edit_own",
      name: "profile_edit_own",
      category: PermissionCategory.PROFILE,
      level: PermissionLevel.WRITE,
      scope: PermissionScope.SELF,
    },
  ];

  const defaultRolePermissions: RolePermission[] = [
    {
