  securityState: SecuritySettingsState,
  setHasUnsavedChanges: React.Dispatch<React.SetStateAction<boolean>>,
  setError: React.Dispatch<React.SetStateAction<string | null>>,
): Promise<void> {
  try {
    await saveSecuritySettings(userId, securityState);
    setHasUnsavedChanges(false);
  } catch (err) {
    setError(err instanceof Error ? err.message : "Failed to save settings");
  }
}

/**
 * Pause Duration Tracking Hook
 * Tracks current pause duration with live updates
 */
import { useEffect } from "react";
import type { PauseStatus } from "../../types/pauseResume";

export function usePauseDurationTracking(
  pauseStatus: PauseStatus,
  updatePauseDuration: (duration: number) => void,
) {
  useEffect(() => {
    if (!pauseStatus.isPaused || !pauseStatus.pauseStartTime) {
      return;
    }

    const interval = setInterval(() => {
      const duration = Math.floor(
        (Date.now() - pauseStatus.pauseStartTime!.getTime()) / 1000,
      );
      updatePauseDuration(duration);
    }, 1000);

    return () => clearInterval(interval);
    // updatePauseDuration should be stable (useCallback) in parent component
  }, [pauseStatus.isPaused, pauseStatus.pauseStartTime]);
}

import { useState, useCallback, useEffect, useRef } from 'react';
import { doc, setDoc } from 'firebase/firestore';
import { db } from '../../firebase';

/**
 * @typedef {Object} PauseEvent
 * @property {string} id
 * @property {Date} startTime
 * @property {Date} [endTime]
 * @property {string} reason
 * @property {number} [duration]
 */

/**
 * @typedef {Object} PauseResumeState
 * @property {boolean} isPaused
 * @property {Date|null} pauseStartTime
 * @property {number} totalPauseTime
 * @property {PauseEvent[]} pauseEvents
 * @property {Date|null} cooldownEndTime
 */

/**
 * @typedef {Object} PauseResumeOptions
 * @property {string|null} userId
 * @property {boolean} isAuthReady
 * @property {boolean} sessionActive
 * @property {Function} [onStateChange]
 */

/**
 * Hook for managing session pause/resume functionality
 * @param {PauseResumeOptions} options
 * @returns {Object}
 */
export const usePauseResume = ({ 
  userId, 
  isAuthReady, 
  sessionActive, 
  onStateChange 
}) => {
  const [pauseState, setPauseState] = useState({
    isPaused: false,
    pauseStartTime: null,
    totalPauseTime: 0,
    pauseEvents: [],
    cooldownEndTime: null
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const pauseTimerRef = useRef(null);
  const cooldownTimerRef = useRef(null);

  const saveStateToFirestore = useCallback(async (newState) => {
    if (!userId || !isAuthReady) {
      return;
    }

    try {
      const userDocRef = doc(db, 'users', userId);
      await setDoc(userDocRef, { pauseResumeState: newState }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to save pause state');
    }
  }, [userId, isAuthReady]);

  const pauseSession = useCallback(async (reason = 'Manual pause') => {
    if (!sessionActive || pauseState.isPaused) {
      setError('Cannot pause: session not active or already paused');
      return;
    }

    // Check cooldown
    if (pauseState.cooldownEndTime && new Date() < pauseState.cooldownEndTime) {
      const remainingTime = Math.ceil((pauseState.cooldownEndTime.getTime() - new Date().getTime()) / 1000);
      setError(`Pause is on cooldown. Try again in ${remainingTime} seconds.`);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const pauseStartTime = new Date();
      const newPauseEvent = {
        id: crypto.randomUUID(),
        startTime: pauseStartTime,
        reason
      };

      const newState = {
        ...pauseState,
        isPaused: true,
        pauseStartTime,
        pauseEvents: [...pauseState.pauseEvents, newPauseEvent]
      };

      setPauseState(newState);
      await saveStateToFirestore(newState);
      if (onStateChange) onStateChange(newState);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to pause session');
    } finally {
      setIsLoading(false);
    }
  }, [sessionActive, pauseState, saveStateToFirestore, onStateChange]);

  const resumeSession = useCallback(async () => {
    if (!pauseState.isPaused || !pauseState.pauseStartTime) {
      setError('Cannot resume: session not paused');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const resumeTime = new Date();
      const pauseDuration = Math.floor((resumeTime.getTime() - pauseState.pauseStartTime.getTime()) / 1000);

      // Update the last pause event with end time and duration
      const updatedPauseEvents = [...pauseState.pauseEvents];
      const lastEventIndex = updatedPauseEvents.length - 1;
      if (lastEventIndex >= 0) {
        updatedPauseEvents[lastEventIndex] = {
          ...updatedPauseEvents[lastEventIndex],
          endTime: resumeTime,
          duration: pauseDuration
        };
      }

      // Set cooldown (12 hours from now)
      const cooldownEndTime = new Date(resumeTime.getTime() + (12 * 60 * 60 * 1000));

      const newState = {
        ...pauseState,
        isPaused: false,
        pauseStartTime: null,
        totalPauseTime: pauseState.totalPauseTime + pauseDuration,
        pauseEvents: updatedPauseEvents,
        cooldownEndTime
      };

      setPauseState(newState);
      await saveStateToFirestore(newState);
      if (onStateChange) onStateChange(newState);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to resume session');
    } finally {
      setIsLoading(false);
    }
  }, [pauseState, saveStateToFirestore, onStateChange]);

  const getCurrentPauseDuration = useCallback(() => {
    if (!pauseState.isPaused || !pauseState.pauseStartTime) {
      return 0;
    }
    return Math.floor((new Date().getTime() - pauseState.pauseStartTime.getTime()) / 1000);
  }, [pauseState.isPaused, pauseState.pauseStartTime]);

  // Timer to update current pause duration
  useEffect(() => {
    if (pauseState.isPaused && pauseState.pauseStartTime) {
      pauseTimerRef.current = setInterval(() => {
        // Force re-render to update current pause duration in UI
        setPauseState(prev => ({ ...prev }));
      }, 1000);
    } else {
      if (pauseTimerRef.current) {
        clearInterval(pauseTimerRef.current);
        pauseTimerRef.current = null;
      }
    }

    return () => {
      if (pauseTimerRef.current) {
        clearInterval(pauseTimerRef.current);
      }
    };
  }, [pauseState.isPaused, pauseState.pauseStartTime]);

  // Cooldown timer
  useEffect(() => {
    if (pauseState.cooldownEndTime && pauseState.cooldownEndTime > new Date()) {
      const timeUntilCooldownEnd = pauseState.cooldownEndTime.getTime() - new Date().getTime();

      cooldownTimerRef.current = setTimeout(() => {
        setPauseState(prev => ({ ...prev, cooldownEndTime: null }));
      }, timeUntilCooldownEnd);
    }

    return () => {
      if (cooldownTimerRef.current) {
        clearTimeout(cooldownTimerRef.current);
      }
    };
  }, [pauseState.cooldownEndTime]);

  return {
    pauseState,
    isLoading,
    error,
    pauseSession,
    resumeSession,
    getCurrentPauseDuration,
    canPause: sessionActive && !pauseState.isPaused && (!pauseState.cooldownEndTime || new Date() >= pauseState.cooldownEndTime),
    canResume: pauseState.isPaused
  };
};

/**
 * Enhanced Pause/Resume System Hook (Refactored)
 * Provides advanced pause/resume functionality with keyholder overrides,
 * intelligent cooldown management, and comprehensive analytics
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import { PauseCooldownService } from "../../services/PauseCooldownService";
import { serviceLogger } from "../../utils/logging";
import type {
  PauseStatus as _PauseStatus,
  CooldownState as _CooldownState,
  KeyholderOverrideCapabilities,
  PauseHistoryEntry as _PauseHistoryEntry,
  PauseAnalytics,
  PauseReason,
  PauseRequestStatus,
  OverrideRequestStatus,
  PausePattern,
  CooldownAnalytics,
} from "../../types/pauseResume";
import {
  calculatePauseFrequency,
  calculateCooldownDuration,
  calculatePauseAnalytics,
  analyzePausePatterns,
  calculateCooldownEffectiveness,
} from "../../utils/pauseAnalytics";
import {
  calculatePauseDuration,
  updatePauseHistoryOnResume,
  updatePauseStatusOnResume,
  createKeyholderOverrides,
} from "../../utils/pauseResumeHelpers";
import { usePauseState } from "./usePauseState";
import { useCooldownState } from "./useCooldownState";
import { usePauseDurationTracking } from "./usePauseDurationTracking";

const logger = serviceLogger("usePauseResume");

const createInitialKeyholderOverrides = (): KeyholderOverrideCapabilities => ({
  canOverrideCooldown: false,
  canForcePause: false,
  canForceResume: false,
  canModifyCooldownDuration: false,
  requiresReason: true,
});

const createInitialPauseAnalytics = (): PauseAnalytics => ({
  totalPauses: 0,
  averagePauseDuration: 0,
  pauseFrequency: 0,
  emergencyPauseCount: 0,
  keyholderInitiatedCount: 0,
  cooldownViolations: 0,
  patterns: [],
});

export const usePauseResume = (sessionId: string, relationshipId?: string) => {
  // Use sub-hooks for state management
  const {
    pauseStatus,
    pauseHistory,
    setPauseStatus,
    setPauseHistory,
    startPause,
    updatePauseDuration,
  } = usePauseState(sessionId);

  const { cooldownState, startCooldown, clearCooldown } = useCooldownState();

  const [keyholderOverrides, setKeyholderOverrides] = useState(
    createInitialKeyholderOverrides,
  );
  const [pauseAnalytics, setPauseAnalytics] = useState(
    createInitialPauseAnalytics,
  );
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Track pause duration
  usePauseDurationTracking(pauseStatus, updatePauseDuration);

  // Computed values
  const canPause = useMemo(
    () => !pauseStatus.isPaused && !cooldownState.isInCooldown,
    [pauseStatus.isPaused, cooldownState.isInCooldown],
  );

  const canResume = useMemo(() => pauseStatus.isPaused, [pauseStatus.isPaused]);

  const timeUntilNextPause = useMemo(() => {
    if (!cooldownState.nextPauseAvailable) return 0;
    return Math.max(
      0,
      Math.floor(
        (cooldownState.nextPauseAvailable.getTime() - Date.now()) / 1000,
      ),
    );
  }, [cooldownState.nextPauseAvailable]);

  const hasKeyholderOverride = useMemo(
    () => keyholderOverrides.canOverrideCooldown,
    [keyholderOverrides.canOverrideCooldown],
  );

  const pauseFrequency = useMemo(
    () => calculatePauseFrequency(pauseHistory),
    [pauseHistory],
  );

  // Initialize keyholder overrides
  const initializeKeyholderOverrides = useCallback(() => {
    if (relationshipId) {
      setKeyholderOverrides(createKeyholderOverrides());
    }
  }, [relationshipId]);

  // Load data functions
  const loadPauseState = useCallback(async () => {
    // Integration with pause state service
  }, []);

  const loadCooldownState = useCallback(async () => {
    try {
      const cooldownInfo = await PauseCooldownService.canUserPause(sessionId);
      if (cooldownInfo && !cooldownInfo.canPause) {
        startCooldown(
          cooldownInfo.cooldownRemaining || 0,
          keyholderOverrides.canOverrideCooldown,
        );
      }
    } catch (err) {
      logger.error("Failed to load cooldown state", { error: err });
    }
    // sessionId is a stable prop from the component, safe to omit from deps
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [keyholderOverrides.canOverrideCooldown, startCooldown]);

  const loadPauseHistory = useCallback(async () => {
    // Load pause history from service
  }, []);

  const loadPauseAnalytics = useCallback(async () => {
    try {
      const analytics = calculatePauseAnalytics(pauseHistory);
      setPauseAnalytics(analytics);
    } catch (err) {
      logger.error("Failed to load pause analytics", { error: err });
    }
  }, [pauseHistory]);

  // Initialization
  useEffect(() => {
    const initializePauseSystem = async () => {
      if (!sessionId) return;

      try {
        setIsLoading(true);
        setError(null);

        initializeKeyholderOverrides();
        await Promise.all([
          loadPauseState(),
          loadCooldownState(),
          loadPauseHistory(),
          loadPauseAnalytics(),
        ]);
      } catch (err) {
        logger.error("Failed to initialize pause system", { error: err });
        setError(
          err instanceof Error
            ? err.message
            : "Failed to initialize pause system",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializePauseSystem();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [sessionId, relationshipId]);

  // Pause session
  const pauseSession = useCallback(
    async (reason: PauseReason): Promise<void> => {
      if (!canPause) {
        throw new Error("Cannot pause: either already paused or in cooldown");
      }

      try {
        logger.debug("Pausing session", { sessionId, reason });
        startPause(reason);
        logger.info("Session paused successfully", { sessionId, reason });
      } catch (err) {
        logger.error("Failed to pause session", { error: err });
        throw err;
      }
    },
    [canPause, sessionId, startPause],
  );

  // Resume session
  const resumeSession = useCallback(async (): Promise<void> => {
    if (!canResume) {
      throw new Error("Cannot resume: session is not paused");
    }

    try {
      logger.debug("Resuming session", { sessionId });

      const resumeTime = new Date();
      const duration = calculatePauseDuration(
        pauseStatus.pauseStartTime,
        resumeTime,
      );

      setPauseStatus(updatePauseStatusOnResume(pauseStatus));
      setPauseHistory((prev) =>
        updatePauseHistoryOnResume(prev, resumeTime, duration),
      );

      const cooldownDur = calculateCooldownDuration(pauseAnalytics, duration);
      startCooldown(cooldownDur, keyholderOverrides.canOverrideCooldown);

      logger.info("Session resumed successfully", {
        sessionId,
        pauseDuration: duration,
      });
    } catch (err) {
      logger.error("Failed to resume session", { error: err });
      throw err;
    }
  }, [
    canResume,
    sessionId,
    pauseStatus,
    pauseAnalytics,
    keyholderOverrides.canOverrideCooldown,
    setPauseStatus,
    setPauseHistory,
    startCooldown,
  ]);

  // Emergency pause
  const requestEmergencyPause = useCallback(
    async (reason: string): Promise<PauseRequestStatus> => {
      try {
        logger.debug("Requesting emergency pause", { sessionId, reason });

        if (cooldownState.isInCooldown) {
          logger.warn("Emergency pause bypassing cooldown", { sessionId });
        }

        await pauseSession("emergency");

        return {
          approved: true,
          reason: "Emergency pause approved automatically",
          requestId: `emergency_${Date.now()}`,
          approvedBy: "emergency_protocol",
          approvedAt: new Date(),
        };
      } catch (err) {
        logger.error("Failed to request emergency pause", { error: err });
        return {
          approved: false,
          reason: err instanceof Error ? err.message : "Unknown error",
          requestId: `emergency_${Date.now()}`,
        };
      }
    },
    [sessionId, cooldownState.isInCooldown, pauseSession],
  );

  // Request cooldown override
  const requestCooldownOverride = useCallback(
    async (justification: string): Promise<OverrideRequestStatus> => {
      if (!relationshipId) {
        return {
          approved: false,
          reason: "Cooldown override requires keyholder relationship",
          requestId: `override_${Date.now()}`,
          overrideType: "cooldown",
        };
      }

      try {
        logger.debug("Requesting cooldown override", {
          sessionId,
          justification,
        });

        return {
          approved: false,
          reason: "Override request sent to keyholder",
          requestId: `override_${Date.now()}`,
          overrideType: "cooldown",
        };
      } catch (err) {
        logger.error("Failed to request cooldown override", { error: err });
        return {
          approved: false,
          reason: err instanceof Error ? err.message : "Unknown error",
          requestId: `override_${Date.now()}`,
          overrideType: "cooldown",
        };
      }
    },
    [sessionId, relationshipId],
  );

  // Keyholder force pause
  const keyholderForcePause = useCallback(
    async (reason: string): Promise<void> => {
      if (!keyholderOverrides.canForcePause) {
        throw new Error("Keyholder does not have force pause permissions");
      }

      try {
        logger.debug("Keyholder forcing pause", { sessionId, reason });
        startPause("keyholder_request", "keyholder");
        logger.info("Keyholder force pause successful", { sessionId });
      } catch (err) {
        logger.error("Failed to execute keyholder force pause", { error: err });
        throw err;
      }
    },
    [keyholderOverrides.canForcePause, sessionId, startPause],
  );

  // Keyholder force resume
  const keyholderForceResume = useCallback(
    async (reason: string): Promise<void> => {
      if (!keyholderOverrides.canForceResume) {
        throw new Error("Keyholder does not have force resume permissions");
      }

      try {
        logger.debug("Keyholder forcing resume", { sessionId, reason });
        await resumeSession();
        logger.info("Keyholder force resume successful", { sessionId });
      } catch (err) {
        logger.error("Failed to execute keyholder force resume", {
          error: err,
        });
        throw err;
      }
    },
    [keyholderOverrides.canForceResume, sessionId, resumeSession],
  );

  // Keyholder override cooldown
  const keyholderOverrideCooldown = useCallback(
    async (reason: string): Promise<void> => {
      if (!keyholderOverrides.canOverrideCooldown) {
        throw new Error(
          "Keyholder does not have cooldown override permissions",
        );
      }

      try {
        logger.debug("Keyholder overriding cooldown", { sessionId, reason });
        clearCooldown();
        logger.info("Keyholder cooldown override successful", { sessionId });
      } catch (err) {
        logger.error("Failed to execute keyholder cooldown override", {
          error: err,
        });
        throw err;
      }
    },
    [keyholderOverrides.canOverrideCooldown, sessionId, clearCooldown],
  );

  // Analytics
  const getPausePatterns = useCallback((): PausePattern[] => {
    return analyzePausePatterns(pauseHistory);
  }, [pauseHistory]);

  const getCooldownEffectiveness = useCallback((): CooldownAnalytics => {
    return {
      effectiveness: calculateCooldownEffectiveness(
        pauseHistory,
        cooldownState,
      ),
      averageCooldownDuration: cooldownState.adaptiveDuration,
      overrideFrequency: 0,
      adaptiveAdjustments: 0,
    };
  }, [pauseHistory, cooldownState]);

  return {
    // Enhanced state
    pauseStatus,
    cooldownState,
    keyholderOverrides,
    pauseAnalytics,

    // Basic actions
    pauseSession,
    resumeSession,

    // Enhanced actions
    requestEmergencyPause,
    requestCooldownOverride,

    // Keyholder actions
    keyholderForcePause,
    keyholderForceResume,
    keyholderOverrideCooldown,

    // Analytics
    getPausePatterns,
    getCooldownEffectiveness,

    // Computed values
    canPause,
    canResume,
    timeUntilNextPause,
    hasKeyholderOverride,
    pauseFrequency,

    // Loading states
    isLoading,
    error,
  };
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { doc, setDoc, onSnapshot, serverTimestamp } from 'firebase/firestore';
import { db } from '../../firebase';

/**
 * @typedef {Object} Session
 * @property {string} id
 * @property {Date} startTime
 * @property {Date} [endTime]
 * @property {boolean} isActive
 * @property {number} [duration]
 * @property {string} [reason]
 */

/**
 * @typedef {Object} SessionState
 * @property {Session|null} currentSession
 * @property {boolean} isSessionActive
 * @property {Date|null} sessionStartTime
 * @property {number} elapsedTime
 */

/**
 * @typedef {Object} SessionOptions
 * @property {string|null} userId
 * @property {boolean} isAuthReady
 * @property {Function} [onSessionChange]
 */

/**
 * Hook for managing user sessions
 * @param {SessionOptions} options
 * @returns {Object}
 */
export const useSession = ({ userId, isAuthReady, onSessionChange }) => {
  const [sessionState, setSessionState] = useState({
    currentSession: null,
    isSessionActive: false,
    sessionStartTime: null,
    elapsedTime: 0
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const timerRef = useRef(null);
  const unsubscribeRef = useRef(null);

  const saveSessionToFirestore = useCallback(async (sessionData) => {
    if (!userId || !isAuthReady) {
      return;
    }

    try {
      const userDocRef = doc(db, 'users', userId);
      await setDoc(userDocRef, { 
        currentSession: sessionData,
        sessionUpdatedAt: serverTimestamp()
      }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to save session');
    }
  }, [userId, isAuthReady]);

  const startSession = useCallback(async () => {
    if (sessionState.isSessionActive) {
      setError('Session already active');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const startTime = new Date();
      const newSession = {
        id: crypto.randomUUID(),
        startTime,
        isActive: true
      };

      const newState = {
        currentSession: newSession,
        isSessionActive: true,
        sessionStartTime: startTime,
        elapsedTime: 0
      };

      setSessionState(newState);
      await saveSessionToFirestore(newSession);
      if (onSessionChange) onSessionChange(newSession);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to start session');
    } finally {
      setIsLoading(false);
    }
  }, [sessionState.isSessionActive, saveSessionToFirestore, onSessionChange]);

  const endSession = useCallback(async (reason) => {
    if (!sessionState.isSessionActive || !sessionState.currentSession) {
      setError('No active session to end');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const endTime = new Date();
      const duration = sessionState.sessionStartTime 
        ? Math.floor((endTime.getTime() - sessionState.sessionStartTime.getTime()) / 1000)
        : 0;

      const endedSession = {
        ...sessionState.currentSession,
        endTime,
        isActive: false,
        duration,
        reason
      };

      const newState = {
        currentSession: null,
        isSessionActive: false,
        sessionStartTime: null,
        elapsedTime: 0
      };

      setSessionState(newState);
      await saveSessionToFirestore(endedSession);
      if (onSessionChange) onSessionChange(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to end session');
    } finally {
      setIsLoading(false);
    }
  }, [
    sessionState.isSessionActive, 
    sessionState.currentSession, 
    sessionState.sessionStartTime,
    saveSessionToFirestore, 
    onSessionChange
  ]);

  const updateSession = useCallback(async (updates) => {
    if (!sessionState.currentSession) {
      setError('No active session to update');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const updatedSession = {
        ...sessionState.currentSession,
        ...updates
      };

      setSessionState(prev => ({
        ...prev,
        currentSession: updatedSession
      }));

      await saveSessionToFirestore(updatedSession);
      if (onSessionChange) onSessionChange(updatedSession);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update session');
    } finally {
      setIsLoading(false);
    }
  }, [sessionState.currentSession, saveSessionToFirestore, onSessionChange]);

  // Real-time session listener
  useEffect(() => {
    if (!userId || !isAuthReady) {
      return;
    }

    const userDocRef = doc(db, 'users', userId);
    const unsubscribe = onSnapshot(userDocRef,
      (docSnap) => {
        if (docSnap.exists()) {
          const data = docSnap.data();
          if (data.currentSession) {
            const session = {
              ...data.currentSession,
              startTime: data.currentSession.startTime?.toDate(),
              endTime: data.currentSession.endTime?.toDate()
            };

            setSessionState(prev => ({
              ...prev,
              currentSession: session,
              isSessionActive: session.isActive,
              sessionStartTime: session.startTime
            }));
          } else {
            setSessionState({
              currentSession: null,
              isSessionActive: false,
              sessionStartTime: null,
              elapsedTime: 0
            });
          }
        }
        setError(null);
      },
      (err) => {
        setError(err.message);
      }
    );

    unsubscribeRef.current = unsubscribe;
    return () => {
      if (unsubscribeRef.current) {
        unsubscribeRef.current();
      }
    };
  }, [userId, isAuthReady]);

  // Timer for elapsed time
  useEffect(() => {
    if (sessionState.isSessionActive && sessionState.sessionStartTime) {
      timerRef.current = setInterval(() => {
        const elapsed = Math.floor((new Date().getTime() - sessionState.sessionStartTime.getTime()) / 1000);
        setSessionState(prev => ({
          ...prev,
          elapsedTime: elapsed
        }));
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [sessionState.isSessionActive, sessionState.sessionStartTime]);

  return {
    sessionState,
    isLoading,
    error,
    startSession,
    endSession,
    updateSession,
    clearError: useCallback(() => setError(null), [])
  };
};

/**
 * Enhanced Session Management Hook
 * Provides comprehensive session management with keyholder integration,
 * goal tracking, and advanced analytics
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import type { DBSession, DBGoal } from "../../types/database";
import type { KeyholderRelationship } from "../../types/core";
import { KeyholderRelationshipService } from "../../services/KeyholderRelationshipService";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useSession");

// ==================== INTERFACES ====================

export interface SessionGoals {
  personal: DBGoal[];
  keyholderAssigned: DBGoal[];
  active: DBGoal[];
}

export interface SessionContext {
  userId: string;
  relationshipId?: string;
  sessionType: "self_managed" | "keyholder_managed" | "collaborative";
  permissions: SessionPermission[];
}

export interface KeyholderInfo {
  id: string;
  name: string;
  lastSeen: Date;
}

export interface KeyholderSessionControls {
  canModify: boolean;
  canOverride: boolean;
  activeKeyholder: KeyholderInfo;
  controlHistory: ControlAction[];
}

export interface SessionAnalytics {
  averageSessionLength: number;
  completionRate: number;
  goalAchievementRate: number;
  totalSessions: number;
  consistencyScore: number;
}

export interface SessionInsights {
  predictedDuration: number;
  optimalGoalDuration: number;
  completionProbability: number;
  riskFactors: string[];
  recommendations: string[];
}

export interface PredictiveAnalytics {
  nextSessionPrediction: {
    suggestedDuration: number;
    successProbability: number;
    recommendedGoals: string[];
  };
  weeklyTrend: {
    direction: "improving" | "declining" | "stable";
    confidence: number;
  };
}

export interface SessionHistoryEntry {
  id: string;
  startTime: Date;
  endTime?: Date;
  duration: number;
  goals: DBGoal[];
  completedGoals: string[];
  notes?: string;
  rating?: number;
}

export interface EnhancedSessionState {
  currentSession: DBSession | null;
  sessionContext: SessionContext;
  keyholderControls: KeyholderSessionControls | null;
  goals: SessionGoals;
  history: SessionHistoryEntry[];
  analytics: SessionAnalytics;
}

export type SessionPermission =
  | "start_session"
  | "end_session"
  | "modify_goals"
  | "pause_session"
  | "self_modify"
  | "override_restrictions";

export interface ControlAction {
  id: string;
  action: string;
  timestamp: Date;
  reason?: string;
  performedBy: "keyholder" | "submissive" | "system";
}

export interface SessionModifications {
  goalDuration?: number;
  goals?: Partial<DBGoal>[];
  notes?: string;
  endReason?: string;
}

export interface ModificationRequest {
  id: string;
  type: "goal_change" | "early_end" | "time_extension";
  requestedBy: string;
  reason: string;
  details: Record<string, unknown>;
  status: "pending" | "approved" | "rejected";
  createdAt: Date;
}

export interface ApprovalStatus {
  approved: boolean;
  reason?: string;
  approvedBy?: string;
  approvedAt?: Date;
}

// ==================== HOOK IMPLEMENTATION ====================

export const useSession = (userId: string, relationshipId?: string) => {
  // ==================== STATE ====================

  const [currentSession, setCurrentSession] = useState<DBSession | null>(null);
  const [goals, setGoals] = useState<SessionGoals>({
    personal: [],
    keyholderAssigned: [],
    active: [],
  });
  const [_history, setHistory] = useState<SessionHistoryEntry[]>([]);
  const [keyholderControls, setKeyholderControls] =
    useState<KeyholderSessionControls | null>(null);
  const [analytics, setAnalytics] = useState<SessionAnalytics>({
    averageSessionLength: 0,
    completionRate: 0,
    goalAchievementRate: 0,
    totalSessions: 0,
    consistencyScore: 0,
  });
  const [relationship, setRelationship] =
    useState<KeyholderRelationship | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // ==================== COMPUTED VALUES ====================

  const sessionContext = useMemo((): SessionContext => {
    if (!userId) {
      return {
        userId: "",
        sessionType: "self_managed",
        permissions: [],
      };
    }

    return {
      userId,
      relationshipId,
      sessionType: relationshipId
        ? keyholderControls?.canModify
          ? "keyholder_managed"
          : "collaborative"
        : "self_managed",
      permissions: derivePermissions(relationship, keyholderControls),
    };
  }, [userId, relationshipId, keyholderControls, relationship]);

  const isActive = useMemo(
    () => currentSession?.endTime == null && currentSession != null,
    [currentSession],
  );

  const duration = useMemo(
    () => (currentSession ? calculateDuration(currentSession) : 0),
    [currentSession],
  );

  const goalProgress = useMemo(
    () => calculateGoalProgress(currentSession, goals),
    [currentSession, goals],
  );

  const isUnderKeyholderControl = useMemo(
    () => !!relationshipId && !!keyholderControls,
    [relationshipId, keyholderControls],
  );

  const canSelfModify = useMemo(
    () =>
      !keyholderControls?.canModify ||
      sessionContext.permissions.includes("self_modify"),
    [keyholderControls, sessionContext.permissions],
  );

  // ==================== INITIALIZATION ====================

  useEffect(() => {
    const initializeSession = async () => {
      if (!userId) return;

      try {
        setIsLoading(true);
        setError(null);

        // Load relationship if provided
        if (relationshipId) {
          const relationships =
            await KeyholderRelationshipService.getUserRelationships(userId);
          const activeRelationship =
            relationships.asSubmissive.find(
              (rel) => rel.id === relationshipId && rel.status === "active",
            ) ||
            relationships.asKeyholder.find(
              (rel) => rel.id === relationshipId && rel.status === "active",
            );

          setRelationship(activeRelationship || null);

          if (activeRelationship) {
            setKeyholderControls({
              canModify:
                activeRelationship.permissions.canLockSessions || false,
              canOverride:
                activeRelationship.permissions.canUnlockSessions || false,
              activeKeyholder: {
                id: activeRelationship.keyholderUserId,
                name: "Keyholder", // Would get from user profile
                lastSeen: new Date(),
              },
              controlHistory: [],
            });
          }
        }

        // Load current session, goals, history, and analytics
        await Promise.all([
          loadCurrentSession(),
          loadGoals(),
          loadHistory(),
          loadAnalytics(),
        ]);
      } catch (err) {
        logger.error("Failed to initialize session", { error: err });
        setError(
          err instanceof Error ? err.message : "Failed to initialize session",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializeSession();
    // Callback functions are stable (wrapped in useCallback below)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, relationshipId]);

  // ==================== DATA LOADING FUNCTIONS ====================

  const loadCurrentSession = useCallback(async () => {
    // This would integrate with your existing session service
    // For now, return mock data structure
    setCurrentSession(null);
  }, []); // userId is passed but not used in mock implementation

  const loadGoals = useCallback(async () => {
    // This would integrate with your existing goals service
    setGoals({
      personal: [],
      keyholderAssigned: [],
      active: [],
    });
  }, []); // userId and relationshipId are passed but not used in mock

  const loadHistory = useCallback(async () => {
    // This would integrate with your existing history service
    setHistory([]);
  }, []); // userId is passed but not used in mock

  const loadAnalytics = useCallback(async () => {
    // This would integrate with your existing analytics service
    setAnalytics({
      averageSessionLength: 0,
      completionRate: 0,
      goalAchievementRate: 0,
      totalSessions: 0,
      consistencyScore: 0,
    });
  }, []); // userId is passed but not used in mock

  // ==================== SESSION LIFECYCLE ====================

  const startSession = useCallback(
    async (sessionGoals?: SessionGoals): Promise<DBSession> => {
      if (!userId) throw new Error("User ID required to start session");

      try {
        logger.debug("Starting new session", {
          userId,
          hasGoals: !!sessionGoals,
        });

        // Check permissions
        if (
          sessionContext.sessionType === "keyholder_managed" &&
          !sessionContext.permissions.includes("start_session")
        ) {
          throw new Error("Keyholder approval required to start session");
        }

        // Create new session
        const newSession: DBSession = {
          id: `session_${Date.now()}`,
          userId,
          startTime: new Date(),
          isPaused: false,
          accumulatedPauseTime: 0,
          isHardcoreMode: false,
          keyholderApprovalRequired:
            sessionContext.sessionType === "keyholder_managed",
          syncStatus: "pending",
          lastModified: new Date(),
        };

        // Add goals if provided
        if (sessionGoals) {
          await setGoals(sessionGoals);
        }

        setCurrentSession(newSession);
        logger.info("Session started successfully", {
          sessionId: newSession.id,
        });

        return newSession;
      } catch (error) {
        logger.error("Failed to start session", { error });
        throw error;
      }
    },
    [userId, sessionContext],
  );

  const stopSession = useCallback(
    async (reason?: string): Promise<void> => {
      if (!currentSession) throw new Error("No active session to stop");

      try {
        logger.debug("Stopping session", {
          sessionId: currentSession.id,
          reason,
        });

        // Check permissions
        if (
          sessionContext.sessionType === "keyholder_managed" &&
          !sessionContext.permissions.includes("end_session")
        ) {
          throw new Error("Keyholder approval required to end session");
        }

        const updatedSession: DBSession = {
          ...currentSession,
          endTime: new Date(),
          endReason: reason,
          lastModified: new Date(),
        };

        setCurrentSession(updatedSession);
        await loadHistory(); // Refresh history
        await loadAnalytics(); // Refresh analytics

        logger.info("Session stopped successfully", {
          sessionId: currentSession.id,
        });
      } catch (error) {
        logger.error("Failed to stop session", { error });
        throw error;
      }
    },
    [currentSession, sessionContext, loadHistory, loadAnalytics],
  );

  // ==================== ENHANCED CONTROLS ====================

  const modifySession = useCallback(
    async (modifications: SessionModifications): Promise<void> => {
      if (!currentSession) throw new Error("No active session to modify");

      if (!canSelfModify) {
        throw new Error("Session modification requires keyholder approval");
      }

      try {
        logger.debug("Modifying session", {
          sessionId: currentSession.id,
          modifications,
        });

        const updatedSession: DBSession = {
          ...currentSession,
          ...modifications,
          lastModified: new Date(),
        };

        setCurrentSession(updatedSession);
        logger.info("Session modified successfully", {
          sessionId: currentSession.id,
        });
      } catch (error) {
        logger.error("Failed to modify session", { error });
        throw error;
      }
    },
    [currentSession, canSelfModify],
  );

  const setSessionGoals = useCallback(
    async (sessionGoals: SessionGoals): Promise<void> => {
      if (!sessionContext.permissions.includes("modify_goals")) {
        throw new Error("Goal modification requires appropriate permissions");
      }

      try {
        logger.debug("Setting session goals", {
          userId,
          goalCount: sessionGoals.active.length,
        });
        setGoals(sessionGoals);
        logger.info("Session goals updated successfully");
      } catch (error) {
        logger.error("Failed to set session goals", { error });
        throw error;
      }
    },
    [sessionContext.permissions, userId],
  );

  const requestModification = useCallback(
    async (request: ModificationRequest): Promise<void> => {
      if (!relationshipId)
        throw new Error("Modification requests require active relationship");

      try {
        logger.debug("Creating modification request", {
          requestId: request.id,
          type: request.type,
        });
        // This would integrate with your notification/request system
        logger.info("Modification request created successfully", {
          requestId: request.id,
        });
      } catch (error) {
        logger.error("Failed to create modification request", { error });
        throw error;
      }
    },
    [relationshipId],
  );

  // ==================== KEYHOLDER INTEGRATION ====================

  const requestKeyholderApproval = useCallback(
    async (action: string): Promise<ApprovalStatus> => {
      if (!keyholderControls) {
        return { approved: true }; // Auto-approve if no keyholder control
      }

      try {
        logger.debug("Requesting keyholder approval", {
          action,
          keyholderId: keyholderControls.activeKeyholder.id,
        });

        // This would integrate with your keyholder notification system
        // For now, return pending status
        return {
          approved: false,
          reason: "Approval request sent to keyholder",
        };
      } catch (error) {
        logger.error("Failed to request keyholder approval", { error });
        throw error;
      }
    },
    [keyholderControls],
  );

  // ==================== ANALYTICS AND INSIGHTS ====================

  const getSessionInsights = useCallback((): SessionInsights => {
    return {
      predictedDuration: analytics.averageSessionLength,
      optimalGoalDuration: analytics.averageSessionLength * 1.2,
      completionProbability: analytics.completionRate,
      riskFactors: [],
      recommendations: [],
    };
  }, [analytics]);

  const getPredictiveAnalytics = useCallback((): PredictiveAnalytics => {
    return {
      nextSessionPrediction: {
        suggestedDuration: analytics.averageSessionLength,
        successProbability: analytics.completionRate,
        recommendedGoals: ["Duration Goal", "Consistency Goal"],
      },
      weeklyTrend: {
        direction: "stable",
        confidence: 0.8,
      },
    };
  }, [analytics]);

  // ==================== RETURN HOOK INTERFACE ====================

  return {
    // Enhanced state
    session: currentSession,
    context: sessionContext,
    keyholderControls,
    goals,
    analytics,

    // Session lifecycle
    startSession,
    stopSession,

    // Enhanced controls
    modifySession,
    setGoals: setSessionGoals,
    requestModification,

    // Keyholder integration
    requestKeyholderApproval,

    // Analytics and insights
    getSessionInsights,
    getPredictiveAnalytics,

    // Computed values
    isActive,
    duration,
    goalProgress,
    isUnderKeyholderControl,
    canSelfModify,

    // Loading states
    isLoading,
    error,
  };
};

// ==================== HELPER FUNCTIONS ====================

function derivePermissions(
  relationship: KeyholderRelationship | null,
  controls: KeyholderSessionControls | null,
): SessionPermission[] {
  const permissions: SessionPermission[] = [];

  if (!relationship || !controls) {
    // Self-managed session - all permissions
    return [
      "start_session",
      "end_session",
      "modify_goals",
      "pause_session",
      "self_modify",
    ];
  }

  // Keyholder-managed permissions
  if (relationship.permissions.canLockSessions)
    permissions.push("start_session");
  if (relationship.permissions.canUnlockSessions)
    permissions.push("end_session");
  if (relationship.permissions.canEditGoals) permissions.push("modify_goals");
  if (relationship.permissions.canLockSessions)
    permissions.push("pause_session");
  // Add basic self-modify permission for submissives
  permissions.push("self_modify");
  if (controls.canOverride) permissions.push("override_restrictions");

  return permissions;
}

function calculateDuration(session: DBSession): number {
  if (!session.startTime) return 0;

  const endTime = session.endTime || new Date();
  const totalTime = endTime.getTime() - session.startTime.getTime();

  return Math.max(0, Math.floor(totalTime / 1000));
}

function calculateGoalProgress(
  session: DBSession | null,
  goals: SessionGoals,
): number {
  if (!session || goals.active.length === 0) return 0;

  const completedGoals = goals.active.filter((goal) => goal.isCompleted).length;
  return Math.floor((completedGoals / goals.active.length) * 100);
}

/**
 * Session Actions Hook
 * Provides session control operations (start, end, pause, resume, lock, unlock)
 * Extracts session action logic from ActionButtons component
 */
import { useState, useCallback, useMemo } from "react";
import { useSession } from "./useSession";
import { usePauseResume } from "./usePauseResume";
import { serviceLogger } from "../../utils/logging";
import type { DBGoal } from "../../types/database";

const logger = serviceLogger("useSessionActions");

export interface SessionConfig {
  goalDuration?: number;
  isHardcoreMode?: boolean;
  keyholderApprovalRequired?: boolean;
  goals?: DBGoal[];
  notes?: string;
}

export interface UseSessionActionsReturn {
  // Actions
  startSession: (config?: SessionConfig) => Promise<void>;
  endSession: (reason?: string) => Promise<void>;
  pauseSession: (reason?: string) => Promise<void>;
  resumeSession: () => Promise<void>;

  // State
  isStarting: boolean;
  isEnding: boolean;
  isPausing: boolean;
  isResuming: boolean;

  // Permissions
  canStart: boolean;
  canEnd: boolean;
  canPause: boolean;
  canResume: boolean;

  // Error handling
  error: Error | null;
  clearError: () => void;

  // Current session info
  isActive: boolean;
  isPaused: boolean;
  sessionId: string | null;
}

export interface UseSessionActionsOptions {
  userId: string;
  onSessionStarted?: () => void;
  onSessionEnded?: () => void;
  onSessionPaused?: () => void;
  onSessionResumed?: () => void;
  onError?: (error: Error) => void;
}

export function useSessionActions({
  userId,
  onSessionStarted,
  onSessionEnded,
  onSessionPaused,
  onSessionResumed,
  onError,
}: UseSessionActionsOptions): UseSessionActionsReturn {
  const [isStarting, setIsStarting] = useState(false);
  const [isEnding, setIsEnding] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  // Use existing session management hooks
  const {
    session,
    isActive,
    startSession: startSessionCore,
    stopSession: stopSessionCore,
    canSelfModify,
  } = useSession(userId);

  const {
    pauseStatus,
    cooldownState,
    pauseSession: pauseSessionCore,
    resumeSession: resumeSessionCore,
  } = usePauseResume(userId, session?.id);

  const isPaused = pauseStatus.isPaused;
  const isPausing = pauseStatus.pauseCount > 0 && pauseStatus.isPaused;
  const isResuming = false; // Track this internally if needed

  // Permissions
  const canStart = useMemo(() => {
    return !isActive && canSelfModify && !isStarting;
  }, [isActive, canSelfModify, isStarting]);

  const canEnd = useMemo(() => {
    return isActive && canSelfModify && !isEnding;
  }, [isActive, canSelfModify, isEnding]);

  const canPause = useMemo(() => {
    return (
      isActive &&
      !isPaused &&
      pauseStatus.canResume &&
      !cooldownState.isInCooldown
    );
  }, [isActive, isPaused, pauseStatus.canResume, cooldownState.isInCooldown]);

  const canResume = useMemo(() => {
    return isActive && isPaused && pauseStatus.canResume;
  }, [isActive, isPaused, pauseStatus.canResume]);

  // Clear error
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Handle errors internally
  const handleError = useCallback(
    (err: unknown, context: string) => {
      const error = err instanceof Error ? err : new Error(String(err));
      setError(error);
      logger.error(`${context} failed`, { error, userId });
      onError?.(error);
      return error;
    },
    [userId, onError],
  );

  // Start session
  const startSession = useCallback(
    async (config?: SessionConfig): Promise<void> => {
      if (!canStart) {
        const err = new Error("Cannot start session");
        handleError(err, "Start session");
        return;
      }

      setIsStarting(true);
      setError(null);

      try {
        logger.debug("Starting session", { userId, config });

        await startSessionCore({
          goalDuration: config?.goalDuration,
          isHardcoreMode: config?.isHardcoreMode || false,
          keyholderApprovalRequired: config?.keyholderApprovalRequired || false,
          notes: config?.notes,
        });

        logger.info("Session started successfully", { userId });
        onSessionStarted?.();
      } catch (err) {
        handleError(err, "Start session");
        throw err;
      } finally {
        setIsStarting(false);
      }
    },
    [canStart, userId, startSessionCore, handleError, onSessionStarted],
  );

  // End session
  const endSession = useCallback(
    async (reason?: string): Promise<void> => {
      if (!canEnd) {
        const err = new Error("Cannot end session");
        handleError(err, "End session");
        return;
      }

      setIsEnding(true);
      setError(null);

      try {
        logger.debug("Ending session", { userId, reason });

        await stopSessionCore(reason || "User ended session");

        logger.info("Session ended successfully", { userId });
        onSessionEnded?.();
      } catch (err) {
        handleError(err, "End session");
        throw err;
      } finally {
        setIsEnding(false);
      }
    },
    [canEnd, userId, stopSessionCore, handleError, onSessionEnded],
  );

  // Pause session
  const pauseSession = useCallback(
    async (reason?: string): Promise<void> => {
      if (!canPause) {
        const err = new Error("Cannot pause session");
        handleError(err, "Pause session");
        return;
      }

      setError(null);

      try {
        logger.debug("Pausing session", { userId, reason });

        await pauseSessionCore(reason || "bathroom");

        logger.info("Session paused successfully", { userId });
        onSessionPaused?.();
      } catch (err) {
        handleError(err, "Pause session");
        throw err;
      }
    },
    [canPause, userId, pauseSessionCore, handleError, onSessionPaused],
  );

  // Resume session
  const resumeSession = useCallback(async (): Promise<void> => {
    if (!canResume) {
      const err = new Error("Cannot resume session");
      handleError(err, "Resume session");
      return;
    }

    setError(null);

    try {
      logger.debug("Resuming session", { userId });

      await resumeSessionCore();

      logger.info("Session resumed successfully", { userId });
      onSessionResumed?.();
    } catch (err) {
      handleError(err, "Resume session");
      throw err;
    }
  }, [canResume, userId, resumeSessionCore, handleError, onSessionResumed]);

  return {
    // Actions
    startSession,
    endSession,
    pauseSession,
    resumeSession,

    // State
    isStarting,
    isEnding,
    isPausing,
    isResuming,

    // Permissions
    canStart,
    canEnd,
    canPause,
    canResume,

    // Error handling
    error,
    clearError,

    // Current session info
    isActive,
    isPaused,
    sessionId: session?.id || null,
  };
}

import { useState, useEffect, useCallback } from 'react';
import { doc, setDoc, onSnapshot, collection, addDoc, query, orderBy, getDocs } from 'firebase/firestore';
import { db } from '../../firebase';

/**
 * @typedef {Object} Goal
 * @property {string} id
 * @property {string} title
 * @property {string} description
 * @property {number} targetDuration - in seconds
 * @property {boolean} isCompleted
 * @property {Date} createdAt
 * @property {Date} [completedAt]
 * @property {string} [reward]
 */

/**
 * @typedef {Object} GoalsState
 * @property {Goal[]} goals
 * @property {Goal|null} activeGoal
 * @property {number} progress - percentage
 */

/**
 * @typedef {Object} SessionGoalsOptions
 * @property {string|null} userId
 * @property {boolean} isAuthReady
 * @property {number} currentSessionDuration
 * @property {Function} [onGoalCompleted]
 */

/**
 * Hook for managing session goals
 * @param {SessionGoalsOptions} options
 * @returns {Object}
 */
export const useSessionGoals = ({ 
  userId, 
  isAuthReady, 
  currentSessionDuration,
  onGoalCompleted 
}) => {
  const [goalsState, setGoalsState] = useState({
    goals: [],
    activeGoal: null,
    progress: 0
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const saveGoalsToFirestore = useCallback(async (goals) => {
    if (!userId || !isAuthReady) {
      return;
    }

    try {
      const userDocRef = doc(db, 'users', userId);
      await setDoc(userDocRef, { goals }, { merge: true });
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to save goals');
    }
  }, [userId, isAuthReady]);

  const addGoal = useCallback(async (goalData) => {
    if (!userId || !isAuthReady) {
      setError('User not authenticated');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const newGoal = {
        ...goalData,
        id: crypto.randomUUID(),
        isCompleted: false,
        createdAt: new Date()
      };

      const goalsCollection = collection(db, 'users', userId, 'goals');
      await addDoc(goalsCollection, newGoal);

      setGoalsState(prev => ({
        ...prev,
        goals: [...prev.goals, newGoal]
      }));
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to add goal');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady]);

  const completeGoal = useCallback(async (goalId) => {
    if (!userId || !isAuthReady) {
      setError('User not authenticated');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const updatedGoals = goalsState.goals.map(goal => 
        goal.id === goalId 
          ? { ...goal, isCompleted: true, completedAt: new Date() }
          : goal
      );

      const completedGoal = updatedGoals.find(goal => goal.id === goalId);

      setGoalsState(prev => ({
        ...prev,
        goals: updatedGoals,
        activeGoal: prev.activeGoal?.id === goalId ? null : prev.activeGoal
      }));

      await saveGoalsToFirestore(updatedGoals);

      if (completedGoal && onGoalCompleted) {
        onGoalCompleted(completedGoal);
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to complete goal');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady, goalsState.goals, saveGoalsToFirestore, onGoalCompleted]);

  const setActiveGoal = useCallback(async (goalId) => {
    const activeGoal = goalId ? goalsState.goals.find(goal => goal.id === goalId) || null : null;

    setGoalsState(prev => ({
      ...prev,
      activeGoal
    }));

    if (userId && isAuthReady) {
      try {
        const userDocRef = doc(db, 'users', userId);
        await setDoc(userDocRef, { activeGoalId: goalId }, { merge: true });
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to set active goal');
      }
    }
  }, [goalsState.goals, userId, isAuthReady]);

  const fetchGoals = useCallback(async () => {
    if (!userId || !isAuthReady) {
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const goalsCollection = collection(db, 'users', userId, 'goals');
      const q = query(goalsCollection, orderBy('createdAt', 'desc'));
      const querySnapshot = await getDocs(q);

      const goals = querySnapshot.docs.map(doc => ({
        ...doc.data(),
        id: doc.id,
        createdAt: doc.data().createdAt?.toDate() || new Date(),
        completedAt: doc.data().completedAt?.toDate()
      }));

      setGoalsState(prev => ({ ...prev, goals }));
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch goals');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady]);

  // Load goals and active goal on mount
  useEffect(() => {
    fetchGoals();
  }, [fetchGoals]);

  // Listen for active goal updates
  useEffect(() => {
    if (!userId || !isAuthReady) {
      return;
    }

    const userDocRef = doc(db, 'users', userId);
    const unsubscribe = onSnapshot(userDocRef,
      (docSnap) => {
        if (docSnap.exists()) {
          const data = docSnap.data();
          if (data.activeGoalId) {
            const activeGoal = goalsState.goals.find(goal => goal.id === data.activeGoalId);
            setGoalsState(prev => ({ ...prev, activeGoal: activeGoal || null }));
          }
        }
      },
      (err) => {
        setError(err.message);
      }
    );

    return () => unsubscribe();
  }, [userId, isAuthReady, goalsState.goals]);

  // Calculate progress for active goal
  useEffect(() => {
    if (goalsState.activeGoal && currentSessionDuration > 0) {
      const progress = Math.min(
        (currentSessionDuration / goalsState.activeGoal.targetDuration) * 100,
        100
      );

      setGoalsState(prev => ({ ...prev, progress }));

      // Auto-complete goal if target reached
      if (progress >= 100 && !goalsState.activeGoal.isCompleted) {
        completeGoal(goalsState.activeGoal.id);
      }
    } else {
      setGoalsState(prev => ({ ...prev, progress: 0 }));
    }
  }, [goalsState.activeGoal, currentSessionDuration, completeGoal]);

  const getNextGoal = useCallback(() => {
    return goalsState.goals
      .filter(goal => !goal.isCompleted)
      .sort((a, b) => a.targetDuration - b.targetDuration)[0] || null;
  }, [goalsState.goals]);

  return {
    goalsState,
    isLoading,
    error,
    addGoal,
    completeGoal,
    setActiveGoal,
    getNextGoal,
    refreshGoals: fetchGoals
  };
};

/**
 * Session Goals Management Hook
 * Provides comprehensive goal management with keyholder controls,
 * progress tracking, and achievement integration
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import type { KeyholderRelationship } from "../../types/core";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useSessionGoals");

// ==================== INTERFACES ====================

import type * as _Types from "./types/SessionGoals";
export type * from "./types/SessionGoals";

export const useSessionGoals = (userId: string, relationshipId?: string) => {
  // ==================== STATE ====================

  const [activeGoals, setActiveGoals] = useState<SessionGoal[]>([]);
  const [goalTemplates, setGoalTemplates] = useState<GoalTemplate[]>([]);
  const [progress, setProgress] = useState<GoalProgress[]>([]);
  const [keyholderGoals, setKeyholderGoals] = useState<KeyholderAssignedGoal[]>(
    [],
  );
  const [goalHistory, setGoalHistory] = useState<GoalHistoryEntry[]>([]);
  const [achievements, setAchievements] = useState<GoalAchievement[]>([]);
  const [_relationship, _setRelationship] =
    useState<KeyholderRelationship | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // ==================== COMPUTED VALUES ====================

  const totalActiveGoals = useMemo(() => activeGoals.length, [activeGoals]);

  const completionRate = useMemo(
    () => calculateCompletionRate(progress),
    [progress],
  );

  const hasRequiredGoals = useMemo(
    () => activeGoals.some((goal) => goal.isRequired),
    [activeGoals],
  );

  const goalDifficulty = useMemo(
    () => calculateOverallDifficulty(activeGoals),
    [activeGoals],
  );

  const estimatedCompletionTime = useMemo(
    () => predictCompletionTime(activeGoals, progress),
    [activeGoals, progress],
  );

  // ==================== INITIALIZATION ====================

  useEffect(() => {
    const initializeGoals = async () => {
      if (!userId) return;

      try {
        setIsLoading(true);
        setError(null);

        // Load relationship data if available
        if (relationshipId) {
          // This would load keyholder goals and permissions
        }

        // Load all goal-related data
        await Promise.all([
          loadActiveGoals(),
          loadGoalTemplates(),
          loadProgress(),
          loadKeyholderGoals(),
          loadGoalHistory(),
          loadAchievements(),
        ]);
      } catch (err) {
        logger.error("Failed to initialize goals", { error: err });
        setError(
          err instanceof Error ? err.message : "Failed to initialize goals",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializeGoals();
    // Callback functions are stable (wrapped in useCallback below)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, relationshipId]);

  // ==================== PROGRESS TRACKING ====================

  useEffect(() => {
    const updateProgressInterval = setInterval(() => {
      updateActiveGoalProgress();
    }, 30000); // Update every 30 seconds

    return () => clearInterval(updateProgressInterval);
    // updateActiveGoalProgress is stable (useCallback)
  }, [activeGoals]);

  // ==================== DATA LOADING FUNCTIONS ====================

  const loadActiveGoals = useCallback(async () => {
    try {
      // This would integrate with your goal database service
      setActiveGoals([]);
    } catch (error) {
      logger.error("Failed to load active goals", { error });
    }
  }, []); // userId is passed but not used in mock

  const loadGoalTemplates = useCallback(async () => {
    try {
      // Load pre-defined goal templates
      const templates: GoalTemplate[] = [
        {
          id: "duration_24h",
          name: "24 Hour Challenge",
          description: "Complete a full 24-hour session without breaks",
          category: "session_length",
          defaultTarget: {
            value: 24,
            unit: "hours",
            comparison: "minimum",
          },
          difficulty: "intermediate",
          estimatedDuration: 1440,
          tags: ["endurance", "milestone"],
          isPopular: true,
        },
        {
          id: "consistency_7day",
          name: "7-Day Consistency",
          description: "Complete daily sessions for 7 consecutive days",
          category: "daily_goals",
          defaultTarget: {
            value: 7,
            unit: "days",
            comparison: "exact",
          },
          difficulty: "beginner",
          estimatedDuration: 10080,
          tags: ["consistency", "habit"],
          isPopular: true,
        },
      ];

      setGoalTemplates(templates);
    } catch (error) {
      logger.error("Failed to load goal templates", { error });
    }
  }, []);

  const loadProgress = useCallback(async () => {
    try {
      // Calculate progress for all active goals
      const progressData = activeGoals.map((goal) =>
        calculateGoalProgress(goal),
      );
      setProgress(progressData);
    } catch (error) {
      logger.error("Failed to load goal progress", { error });
    }
  }, [activeGoals]);

  const loadKeyholderGoals = useCallback(async () => {
    try {
      if (!relationshipId) {
        setKeyholderGoals([]);
        return;
      }
      // Load goals assigned by keyholder
      setKeyholderGoals([]);
    } catch (error) {
      logger.error("Failed to load keyholder goals", { error });
    }
  }, [relationshipId]);

  const loadGoalHistory = useCallback(async () => {
    try {
      // Load historical goal data
      setGoalHistory([]);
    } catch (error) {
      logger.error("Failed to load goal history", { error });
    }
  }, []);

  const loadAchievements = useCallback(async () => {
    try {
      // Load goal-related achievements
      setAchievements([]);
    } catch (error) {
      logger.error("Failed to load achievements", { error });
    }
  }, []);

  // ==================== GOAL MANAGEMENT ====================

  const setGoal = useCallback(
    async (goalRequest: CreateGoalRequest): Promise<SessionGoal> => {
      try {
        logger.debug("Creating new goal", { userId, goalRequest });

        const newGoal: SessionGoal = {
          id: `goal_${Date.now()}`,
          type: goalRequest.type,
          category: goalRequest.category,
          target: goalRequest.target,
          current: 0,
          progress: 0,
          assignedBy: "self",
          isRequired: goalRequest.isRequired || false,
          deadline: goalRequest.deadline,
          priority: goalRequest.priority,
          status: "active",
          createdAt: new Date(),
          description: goalRequest.description,
          tags: goalRequest.tags,
        };

        setActiveGoals((prev) => [...prev, newGoal]);

        // Add to history
        const historyEntry: GoalHistoryEntry = {
          id: `history_${Date.now()}`,
          goalId: newGoal.id,
          action: "created",
          timestamp: new Date(),
          details: { goalRequest },
          performedBy: "submissive",
        };

        setGoalHistory((prev) => [...prev, historyEntry]);

        logger.info("Goal created successfully", { goalId: newGoal.id });
        return newGoal;
      } catch (error) {
        logger.error("Failed to create goal", { error });
        throw error;
      }
    },
    [userId],
  );

  const updateGoal = useCallback(
    async (goalId: string, updates: Partial<SessionGoal>): Promise<void> => {
      try {
        logger.debug("Updating goal", { goalId, updates });

        setActiveGoals((prev) =>
          prev.map((goal) =>
            goal.id === goalId
              ? { ...goal, ...updates, updatedAt: new Date() }
              : goal,
          ),
        );

        // Add to history
        const historyEntry: GoalHistoryEntry = {
          id: `history_${Date.now()}`,
          goalId,
          action: "updated",
          timestamp: new Date(),
          details: { updates },
          performedBy: "submissive",
        };

        setGoalHistory((prev) => [...prev, historyEntry]);

        logger.info("Goal updated successfully", { goalId });
      } catch (error) {
        logger.error("Failed to update goal", { error });
        throw error;
      }
    },
    [],
  );

  const removeGoal = useCallback(async (goalId: string): Promise<void> => {
    try {
      logger.debug("Removing goal", { goalId });

      setActiveGoals((prev) => prev.filter((goal) => goal.id !== goalId));
      setProgress((prev) => prev.filter((p) => p.goalId !== goalId));

      // Add to history
      const historyEntry: GoalHistoryEntry = {
        id: `history_${Date.now()}`,
        goalId,
        action: "abandoned",
        timestamp: new Date(),
        details: {},
        performedBy: "submissive",
      };

      setGoalHistory((prev) => [...prev, historyEntry]);

      logger.info("Goal removed successfully", { goalId });
    } catch (error) {
      logger.error("Failed to remove goal", { error });
      throw error;
    }
  }, []);

  // ==================== PROGRESS TRACKING ====================

  const updateProgress = useCallback(
    async (goalId: string, progressValue: number): Promise<void> => {
      try {
        logger.debug("Updating goal progress", { goalId, progressValue });

        setProgress((prev) =>
          prev.map((p) =>
            p.goalId === goalId
              ? {
                  ...p,
                  currentValue: progressValue,
                  progressPercentage: Math.min(
                    100,
                    (progressValue / p.targetValue) * 100,
                  ),
                  lastUpdated: new Date(),
                }
              : p,
          ),
        );

        // Update the goal's current value
        setActiveGoals((prev) =>
          prev.map((goal) =>
            goal.id === goalId
              ? {
                  ...goal,
                  current: progressValue,
                  progress: Math.min(
                    100,
                    (progressValue / goal.target.value) * 100,
                  ),
                }
              : goal,
          ),
        );

        logger.debug("Goal progress updated", { goalId, progressValue });
      } catch (error) {
        logger.error("Failed to update goal progress", { error });
        throw error;
      }
    },
    [],
  );

  const checkGoalCompletion = useCallback(async (): Promise<
    GoalCompletionStatus[]
  > => {
    try {
      const completionStatuses: GoalCompletionStatus[] = [];

      for (const goal of activeGoals) {
        const goalProgress = progress.find((p) => p.goalId === goal.id);
        if (!goalProgress) continue;

        const isCompleted = checkIfGoalCompleted(goal, goalProgress);
        const completionPercentage = goalProgress.progressPercentage;

        completionStatuses.push({
          goalId: goal.id,
          isCompleted,
          completionPercentage,
          timeRemaining: goalProgress.estimatedCompletion
            ? Math.max(
                0,
                goalProgress.estimatedCompletion.getTime() - Date.now(),
              ) / 1000
            : undefined,
          canComplete: goal.status === "active",
          blockers: [],
        });

        // If goal is completed, update its status
        if (isCompleted && goal.status !== "completed") {
          await updateGoal(goal.id, {
            status: "completed",
            completedAt: new Date(),
          });

          // Check for achievements
          await checkForAchievements(goal);
        }
      }

      return completionStatuses;
    } catch (error) {
      logger.error("Failed to check goal completion", { error });
      return [];
    }
  }, [activeGoals, progress, updateGoal, checkForAchievements]);

  const updateActiveGoalProgress = useCallback(async () => {
    try {
      // This would calculate progress based on current session data
      // For now, just trigger completion check
      await checkGoalCompletion();
    } catch (error) {
      logger.error("Failed to update active goal progress", { error });
    }
  }, [checkGoalCompletion]);

  // ==================== KEYHOLDER INTEGRATION ====================

  const acceptKeyholderGoal = useCallback(
    async (goalId: string): Promise<void> => {
      try {
        logger.debug("Accepting keyholder goal", { goalId });

        const keyholderGoal = keyholderGoals.find((g) => g.id === goalId);
        if (!keyholderGoal) {
          throw new Error("Keyholder goal not found");
        }

        // Move from keyholder goals to active goals
        setActiveGoals((prev) => [
          ...prev,
          { ...keyholderGoal, assignedBy: "keyholder" },
        ]);
        setKeyholderGoals((prev) => prev.filter((g) => g.id !== goalId));

        logger.info("Keyholder goal accepted", { goalId });
      } catch (error) {
        logger.error("Failed to accept keyholder goal", { error });
        throw error;
      }
    },
    [keyholderGoals],
  );

  const requestGoalModification = useCallback(
    async (goalId: string, reason: string): Promise<ModificationRequest> => {
      if (!relationshipId) {
        throw new Error(
          "Goal modification requests require keyholder relationship",
        );
      }

      try {
        logger.debug("Requesting goal modification", { goalId, reason });

        const request: ModificationRequest = {
          id: `mod_req_${Date.now()}`,
          goalId,
          requestedChanges: {}, // Would be populated with specific changes
          reason,
          status: "pending",
          createdAt: new Date(),
        };

        // This would send the request to the keyholder
        logger.info("Goal modification request created", {
          requestId: request.id,
        });
        return request;
      } catch (error) {
        logger.error("Failed to request goal modification", { error });
        throw error;
      }
    },
    [relationshipId],
  );

  // ==================== TEMPLATES AND SUGGESTIONS ====================

  const getSuggestedGoals = useCallback((): GoalSuggestion[] => {
    try {
      // Analyze user history and preferences to suggest goals
      const suggestions: GoalSuggestion[] = goalTemplates
        .filter((template) => template.isPopular)
        .map((template) => ({
          templateId: template.id,
          name: template.name,
          description: template.description,
          reasonForSuggestion: "Popular template for your experience level",
          confidence: 80,
          basedOn: "trending" as const,
        }));

      return suggestions;
    } catch (error) {
      logger.error("Failed to get suggested goals", { error });
      return [];
    }
  }, [goalTemplates]);

  const createGoalFromTemplate = useCallback(
    async (
      templateId: string,
      customizations?: GoalCustomization,
    ): Promise<SessionGoal> => {
      try {
        const template = goalTemplates.find((t) => t.id === templateId);
        if (!template) {
          throw new Error("Goal template not found");
        }

        const goalRequest: CreateGoalRequest = {
          type: "duration", // Map from template
          category: template.category,
          target: customizations?.target
            ? { ...template.defaultTarget, ...customizations.target }
            : template.defaultTarget,
          priority: customizations?.priority || "medium",
          deadline: customizations?.deadline,
          description: customizations?.description || template.description,
          tags: customizations?.tags || template.tags,
        };

        return await setGoal(goalRequest);
      } catch (error) {
        logger.error("Failed to create goal from template", { error });
        throw error;
      }
    },
    [goalTemplates, setGoal],
  );

  // ==================== ANALYTICS ====================

  const getGoalAnalytics = useCallback((): GoalAnalytics => {
    const completedGoals = goalHistory.filter((h) => h.action === "completed");
    const totalGoals = goalHistory.filter((h) => h.action === "created");

    return {
      completionRate:
        totalGoals.length > 0
          ? (completedGoals.length / totalGoals.length) * 100
          : 0,
      averageCompletionTime: 0, // Calculate from history
      mostSuccessfulCategories: [],
      challengingCategories: [],
      streakData: {
        current: 0,
        best: 0,
        type: "daily_completion",
      },
      improvementTrends: [],
    };
  }, [goalHistory]);

  const getPredictiveGoals = useCallback((): PredictiveGoalSuggestion[] => {
    // AI-powered goal suggestions based on user data
    return [];
  }, []);

  const checkForAchievements = useCallback(
    async (completedGoal: SessionGoal): Promise<void> => {
      try {
        // Check if goal completion unlocks any achievements
        const newAchievements: GoalAchievement[] = [];

        // Example achievement checks
        if (
          completedGoal.type === "duration" &&
          completedGoal.target.value >= 24
        ) {
          newAchievements.push({
            id: `achievement_${Date.now()}`,
            goalId: completedGoal.id,
            name: "Endurance Master",
            description: "Completed a 24+ hour goal",
            earnedAt: new Date(),
            category: "endurance",
            points: 100,
            rarity: "uncommon",
          });
        }

        if (newAchievements.length > 0) {
          setAchievements((prev) => [...prev, ...newAchievements]);
          logger.info("New achievements unlocked", {
            count: newAchievements.length,
          });
        }
      } catch (error) {
        logger.error("Failed to check for achievements", { error });
      }
    },
    [],
  );

  // ==================== RETURN HOOK INTERFACE ====================

  return {
    // Goals state
    activeGoals,
    goalTemplates,
    progress,
    keyholderGoals,
    achievements,

    // Goal management
    setGoal,
    updateGoal,
    removeGoal,

    // Progress tracking
    updateProgress,
    checkGoalCompletion,

    // Keyholder integration
    acceptKeyholderGoal,
    requestGoalModification,

    // Templates and suggestions
    getSuggestedGoals,
    createGoalFromTemplate,

    // Analytics
    getGoalAnalytics,
    getPredictiveGoals,

    // Computed values
    totalActiveGoals,
    completionRate,
    hasRequiredGoals,
    goalDifficulty,
    estimatedCompletionTime,

    // Loading states
    isLoading,
    error,
  };
};

// ==================== HELPER FUNCTIONS ====================

function calculateCompletionRate(progress: GoalProgress[]): number {
  if (progress.length === 0) return 0;

  const completedGoals = progress.filter(
    (p) => p.progressPercentage >= 100,
  ).length;
  return Math.floor((completedGoals / progress.length) * 100);
}

function calculateOverallDifficulty(goals: SessionGoal[]): GoalDifficulty {
  if (goals.length === 0) return "beginner";

  // Simple difficulty calculation based on goal types and targets
  const highPriorityGoals = goals.filter(
    (g) => g.priority === "high" || g.priority === "critical",
  ).length;
  const complexGoals = goals.filter(
    (g) => g.type === "behavioral" || g.type === "performance",
  ).length;

  if (highPriorityGoals > 2 || complexGoals > 1) return "expert";
  if (highPriorityGoals > 1 || complexGoals > 0) return "advanced";
  if (goals.length > 3) return "intermediate";

  return "beginner";
}

function predictCompletionTime(
  goals: SessionGoal[],
  progress: GoalProgress[],
): number {
  if (goals.length === 0 || progress.length === 0) return 0;

  // Calculate estimated completion time based on current progress velocity
  let totalEstimatedTime = 0;

  for (const goalProgress of progress) {
    if (goalProgress.progressPercentage >= 100) continue;

    const remainingProgress = 100 - goalProgress.progressPercentage;
    const velocity = goalProgress.velocity || 1; // Progress per hour
    const estimatedHours = remainingProgress / velocity;

    totalEstimatedTime = Math.max(totalEstimatedTime, estimatedHours);
  }

  return Math.floor(totalEstimatedTime * 60); // Return in minutes
}

function calculateGoalProgress(goal: SessionGoal): GoalProgress {
  return {
    goalId: goal.id,
    currentValue: goal.current,
    targetValue: goal.target.value,
    progressPercentage: goal.progress,
    milestones: [], // Would be populated with actual milestones
    lastUpdated: new Date(),
    velocity: 1, // Would be calculated from historical data
    estimatedCompletion: goal.deadline,
  };
}

function checkIfGoalCompleted(
  goal: SessionGoal,
  progress: GoalProgress,
): boolean {
  switch (goal.target.comparison) {
    case "minimum":
      return progress.currentValue >= goal.target.value;
    case "exact":
      return progress.currentValue === goal.target.value;
    case "maximum":
      return progress.currentValue <= goal.target.value;
    case "range":
      return (
        progress.currentValue >= goal.target.value &&
        progress.currentValue <= (goal.target.rangeMax || goal.target.value)
      );
    default:
      return false;
  }
}

import { useState, useEffect, useCallback } from 'react';
import { collection, query, orderBy, limit, startAfter, getDocs, doc, deleteDoc } from 'firebase/firestore';
import { db } from '../../firebase';

/**
 * @typedef {Object} HistorySession
 * @property {string} id
 * @property {Date} startTime
 * @property {Date} endTime
 * @property {number} duration
 * @property {string} [reason]
 * @property {Array} [pauseEvents]
 * @property {number} [totalPauseTime]
 * @property {number} [netDuration] - duration minus pause time
 */

/**
 * @typedef {Object} HistoryFilters
 * @property {Object} [dateRange]
 * @property {Date} dateRange.start
 * @property {Date} dateRange.end
 * @property {number} [minDuration]
 * @property {number} [maxDuration]
 */

/**
 * @typedef {Object} SessionHistoryOptions
 * @property {string|null} userId
 * @property {boolean} isAuthReady
 * @property {number} [pageSize=20]
 */

/**
 * Hook for managing session history
 * @param {SessionHistoryOptions} options
 * @returns {Object}
 */
export const useSessionHistory = ({ 
  userId, 
  isAuthReady, 
  pageSize = 20 
}) => {
  const [sessions, setSessions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [hasMore, setHasMore] = useState(true);
  const [filters, setFilters] = useState({});

  const fetchSessions = useCallback(async (
    append = false, 
    lastDoc
  ) => {
    if (!userId || !isAuthReady) {
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const historyCollection = collection(db, 'users', userId, 'sessionHistory');
      let q = query(
        historyCollection, 
        orderBy('startTime', 'desc'),
        limit(pageSize)
      );

      if (lastDoc) {
        q = query(
          historyCollection,
          orderBy('startTime', 'desc'),
          startAfter(lastDoc),
          limit(pageSize)
        );
      }

      const querySnapshot = await getDocs(q);
      const newSessions = querySnapshot.docs.map(doc => {
        const data = doc.data();
        return {
          id: doc.id,
          ...data,
          startTime: data.startTime?.toDate() || new Date(),
          endTime: data.endTime?.toDate() || new Date(),
          pauseEvents: data.pauseEvents?.map((event) => ({
            ...event,
            startTime: event.startTime?.toDate(),
            endTime: event.endTime?.toDate()
          })) || []
        };
      });

      // Apply filters
      const filteredSessions = newSessions.filter(session => {
        if (filters.dateRange) {
          const sessionDate = session.startTime;
          if (sessionDate < filters.dateRange.start || sessionDate > filters.dateRange.end) {
            return false;
          }
        }

        if (filters.minDuration && session.duration < filters.minDuration) {
          return false;
        }

        if (filters.maxDuration && session.duration > filters.maxDuration) {
          return false;
        }

        return true;
      });

      setSessions(prev => append ? [...prev, ...filteredSessions] : filteredSessions);
      setHasMore(querySnapshot.docs.length === pageSize);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch session history');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady, pageSize, filters]);

  const loadMore = useCallback(() => {
    if (sessions.length > 0 && hasMore && !isLoading) {
      const lastSession = sessions[sessions.length - 1];
      fetchSessions(true, lastSession);
    }
  }, [sessions, hasMore, isLoading, fetchSessions]);

  const deleteSession = useCallback(async (sessionId) => {
    if (!userId || !isAuthReady) {
      setError('User not authenticated');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const sessionDocRef = doc(db, 'users', userId, 'sessionHistory', sessionId);
      await deleteDoc(sessionDocRef);

      setSessions(prev => prev.filter(session => session.id !== sessionId));
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to delete session');
    } finally {
      setIsLoading(false);
    }
  }, [userId, isAuthReady]);

  const updateFilters = useCallback((newFilters) => {
    setFilters(newFilters);
    setSessions([]);
    setHasMore(true);
  }, []);

  const getStatistics = useCallback(() => {
    const totalSessions = sessions.length;
    const totalDuration = sessions.reduce((acc, session) => acc + session.duration, 0);
    const averageDuration = totalSessions > 0 ? totalDuration / totalSessions : 0;
    const longestSession = sessions.reduce((max, session) => 
      Math.max(max, session.duration), 0);
    const shortestSession = sessions.reduce((min, session) => 
      Math.min(min, session.duration), Infinity);

    // Calculate net duration (excluding pause time)
    const totalNetDuration = sessions.reduce((acc, session) => {
      const pauseTime = session.totalPauseTime || 0;
      return acc + (session.duration - pauseTime);
    }, 0);
    const averageNetDuration = totalSessions > 0 ? totalNetDuration / totalSessions : 0;

    return {
      totalSessions,
      totalDuration,
      averageDuration,
      longestSession: longestSession === 0 ? 0 : longestSession,
      shortestSession: shortestSession === Infinity ? 0 : shortestSession,
      totalNetDuration,
      averageNetDuration
    };
  }, [sessions]);

  const exportHistory = useCallback(() => {
    if (sessions.length === 0) {
      setError('No sessions to export');
      return;
    }

    try {
      const dataToExport = {
        exportedAt: new Date().toISOString(),
        userId,
        sessions: sessions.map(session => ({
          ...session,
          startTime: session.startTime.toISOString(),
          endTime: session.endTime.toISOString(),
          pauseEvents: session.pauseEvents?.map(event => ({
            ...event,
            startTime: event.startTime?.toISOString(),
            endTime: event.endTime?.toISOString()
          }))
        })),
        statistics: getStatistics()
      };

      const dataStr = JSON.stringify(dataToExport, null, 2);
      const dataBlob = new Blob([dataStr], { type: 'application/json' });
      const url = URL.createObjectURL(dataBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `session-history-${new Date().toISOString().slice(0, 10)}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to export history');
    }
  }, [sessions, userId, getStatistics]);

  // Initial load
  useEffect(() => {
    fetchSessions();
  }, [fetchSessions]);

  return {
    sessions,
    isLoading,
    error,
    hasMore,
    filters,
    loadMore,
    deleteSession,
    updateFilters,
    getStatistics,
    exportHistory,
    refresh: useCallback(() => {
      setSessions([]);
      setHasMore(true);
      fetchSessions();
    }, [fetchSessions])
  };
};

/**
 * Session History Management Hook
 * Provides comprehensive session history with privacy controls,
 * data visualization support, and keyholder access management
 */
import { useState, useEffect, useCallback, useMemo } from "react";
import type { KeyholderRelationship } from "../../types/core";
import { serviceLogger } from "../../utils/logging";
import {
  createEmptyTrendData,
  calculateOverallCompletionRate,
  calculateLongestStreak,
  calculatePauseFrequency,
  calculateImprovementTrend,
  calculateConsistencyScore,
  calculateSessionLengthTrend,
  calculateGoalCompletionTrend,
  calculateConsistencyTrend,
  calculatePauseFrequencyTrend,
  calculateOverallProgressTrend,
} from "../../utils/sessionHistoryHelpers";

const logger = serviceLogger("useSessionHistory");

// ==================== INTERFACES ====================

export interface HistoricalSession {
  id: string;
  startTime: Date;
  endTime: Date;
  duration: number; // Total duration in seconds
  effectiveDuration: number; // Duration minus pauses
  goals: SessionGoal[];
  goalCompletion: GoalCompletionRecord[];
  pauseEvents: PauseEvent[];
  keyholderInteractions: KeyholderInteraction[];
  tags: string[];
  notes: string;
  rating?: SessionRating;
  isHardcoreMode: boolean;
  wasKeyholderControlled: boolean;
  endReason?: string;
  emergencyEnd?: boolean;
}

export interface SessionGoal {
  id: string;
  type: string;
  target: number;
  unit: string;
  completed: boolean;
  progress: number;
}

export interface GoalCompletionRecord {
  goalId: string;
  goalName: string;
  targetValue: number;
  achievedValue: number;
  completionPercentage: number;
  completed: boolean;
  completedAt?: Date;
}

export interface PauseEvent {
  id: string;
  startTime: Date;
  endTime?: Date;
  duration: number;
  reason: string;
  initiatedBy: "submissive" | "keyholder" | "emergency";
}

export interface KeyholderInteraction {
  id: string;
  type: "message" | "control_action" | "approval" | "modification";
  timestamp: Date;
  description: string;
  keyholderName: string;
}

export interface SessionRating {
  overall: number; // 1-5 stars
  difficulty: number; // 1-5
  satisfaction: number; // 1-5
  wouldRepeat: boolean;
  notes?: string;
}

export interface HistoryPrivacySettings {
  shareWithKeyholder: boolean;
  shareDuration: boolean;
  shareGoals: boolean;
  sharePauses: boolean;
  shareNotes: boolean;
  shareRatings: boolean;
  retentionPeriod: number; // Days to keep history
  allowExport: boolean;
  anonymizeOldData: boolean;
}

export interface KeyholderHistoryAccess {
  hasAccess: boolean;
  accessLevel: "summary" | "detailed" | "full";
  canViewRatings: boolean;
  canViewNotes: boolean;
  canViewPauses: boolean;
  lastAccessedAt?: Date;
}

export interface HistoryInsights {
  totalSessions: number;
  totalEffectiveTime: number;
  averageSessionLength: number;
  longestSession: HistoricalSession;
  shortestSession: HistoricalSession;
  mostRecentSession: HistoricalSession;
  goalCompletionRate: number;
  pauseFrequency: number;
  improvementTrend: "improving" | "stable" | "declining";
  consistencyScore: number; // 0-100
  keyholderSatisfactionScore?: number;
}

export interface HistoryTrends {
  sessionLength: TrendData;
  goalCompletion: TrendData;
  consistency: TrendData;
  pauseFrequency: TrendData;
  overallProgress: TrendData;
}

export interface TrendData {
  direction: "improving" | "stable" | "declining";
  changePercentage: number;
  confidence: number; // 0-100
  timeframe: "week" | "month" | "quarter" | "year";
  dataPoints: TrendPoint[];
}

export interface TrendPoint {
  date: Date;
  value: number;
  label?: string;
}

export interface SessionHistoryState {
  sessions: HistoricalSession[];
  privacySettings: HistoryPrivacySettings;
  keyholderAccess: KeyholderHistoryAccess;
  insights: HistoryInsights;
  trends: HistoryTrends;
}

export interface HistorySearchQuery {
  dateRange?: {
    start: Date;
    end: Date;
  };
  minDuration?: number;
  maxDuration?: number;
  goalTypes?: string[];
  hasKeyholderControl?: boolean;
  completedGoals?: boolean;
  tags?: string[];
  rating?: {
    min: number;
    max: number;
  };
  textSearch?: string;
}

export interface PersonalDataExport {
  exportId: string;
  generatedAt: Date;
  format: "json" | "csv" | "pdf";
  data: {
    sessions: HistoricalSession[];
    goals: SessionGoal[];
    settings: HistoryPrivacySettings;
    analytics: HistoryInsights;
  };
  fileSize: number;
  downloadUrl: string;
  expiresAt: Date;
}

export interface KeyholderHistoryView {
  allowedSessions: Partial<HistoricalSession>[];
  summaryStats: {
    totalSessions: number;
    averageDuration: number;
    goalCompletionRate: number;
    lastSessionDate: Date;
  };
  accessLevel: "summary" | "detailed" | "full";
  restrictions: string[];
}

export interface PerformanceTrends {
  sessionDuration: {
    average: number;
    trend: "improving" | "stable" | "declining";
    weeklyChange: number;
  };
  goalAchievement: {
    rate: number;
    trend: "improving" | "stable" | "declining";
    weeklyChange: number;
  };
  consistency: {
    score: number;
    streak: number;
    trend: "improving" | "stable" | "declining";
  };
}

export interface GoalProgressHistory {
  goalId: string;
  goalName: string;
  progressOverTime: {
    date: Date;
    progress: number;
  }[];
  milestones: {
    date: Date;
    description: string;
    achieved: boolean;
  }[];
}

export interface ComparisonMetrics {
  thisWeek: {
    sessions: number;
    totalTime: number;
    goalCompletion: number;
  };
  lastWeek: {
    sessions: number;
    totalTime: number;
    goalCompletion: number;
  };
  thisMonth: {
    sessions: number;
    totalTime: number;
    goalCompletion: number;
  };
  lastMonth: {
    sessions: number;
    totalTime: number;
    goalCompletion: number;
  };
}

// ==================== HOOK IMPLEMENTATION ====================

export const useSessionHistory = (userId: string, relationshipId?: string) => {
  // ==================== STATE ====================

  const [sessions, setSessions] = useState<HistoricalSession[]>([]);
  const [privacySettings, setPrivacySettings] =
    useState<HistoryPrivacySettings>({
      shareWithKeyholder: false,
      shareDuration: true,
      shareGoals: true,
      sharePauses: false,
      shareNotes: false,
      shareRatings: false,
      retentionPeriod: 365, // 1 year
      allowExport: true,
      anonymizeOldData: false,
    });
  const [keyholderAccess, setKeyholderAccess] =
    useState<KeyholderHistoryAccess>({
      hasAccess: false,
      accessLevel: "summary",
      canViewRatings: false,
      canViewNotes: false,
      canViewPauses: false,
    });
  const [insights, setInsights] = useState<HistoryInsights>({
    totalSessions: 0,
    totalEffectiveTime: 0,
    averageSessionLength: 0,
    longestSession: {} as HistoricalSession,
    shortestSession: {} as HistoricalSession,
    mostRecentSession: {} as HistoricalSession,
    goalCompletionRate: 0,
    pauseFrequency: 0,
    improvementTrend: "stable",
    consistencyScore: 0,
  });
  const [trends, setTrends] = useState<HistoryTrends>({
    sessionLength: createEmptyTrendData(),
    goalCompletion: createEmptyTrendData(),
    consistency: createEmptyTrendData(),
    pauseFrequency: createEmptyTrendData(),
    overallProgress: createEmptyTrendData(),
  });
  const [_relationship, _setRelationship] =
    useState<KeyholderRelationship | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // ==================== COMPUTED VALUES ====================

  const totalSessions = useMemo(() => sessions.length, [sessions]);

  const averageSessionLength = useMemo(
    () =>
      sessions.length > 0
        ? sessions.reduce(
            (sum, session) => sum + session.effectiveDuration,
            0,
          ) / sessions.length
        : 0,
    [sessions],
  );

  const goalCompletionRate = useMemo(
    () => calculateOverallCompletionRate(sessions),
    [sessions],
  );

  const longestStreak = useMemo(
    () => calculateLongestStreak(sessions),
    [sessions],
  );

  const hasPrivacyRestrictions = useMemo(
    () => !privacySettings.shareWithKeyholder,
    [privacySettings.shareWithKeyholder],
  );

  // ==================== INITIALIZATION ====================

  useEffect(() => {
    const initializeHistory = async () => {
      if (!userId) return;

      try {
        setIsLoading(true);
        setError(null);

        // Load relationship data if available
        if (relationshipId) {
          // Set keyholder access based on relationship permissions
          setKeyholderAccess({
            hasAccess: privacySettings.shareWithKeyholder,
            accessLevel: privacySettings.shareWithKeyholder
              ? "detailed"
              : "summary",
            canViewRatings: privacySettings.shareRatings,
            canViewNotes: privacySettings.shareNotes,
            canViewPauses: privacySettings.sharePauses,
          });
        }

        // Load historical data
        await Promise.all([
          loadSessions(),
          loadPrivacySettings(),
          calculateInsights(),
          calculateTrends(),
        ]);
      } catch (err) {
        logger.error("Failed to initialize session history", { error: err });
        setError(
          err instanceof Error
            ? err.message
            : "Failed to initialize session history",
        );
      } finally {
        setIsLoading(false);
      }
    };

    initializeHistory();
  }, [
    userId,
    relationshipId,
    privacySettings.shareWithKeyholder,
    privacySettings.shareRatings,
    privacySettings.shareNotes,
    privacySettings.sharePauses,
    loadSessions,
    loadPrivacySettings,
    calculateTrends,
    calculateInsights,
  ]);

  // ==================== DATA LOADING FUNCTIONS ====================

  const loadSessions = useCallback(async () => {
    try {
      // This would integrate with your session database service
      // Load sessions with privacy filtering applied
      const allSessions: HistoricalSession[] = [];

      // Apply retention policy
      const retentionDate = new Date();
      retentionDate.setDate(
        retentionDate.getDate() - privacySettings.retentionPeriod,
      );

      const filteredSessions = allSessions.filter(
        (session) => session.startTime >= retentionDate,
      );

      setSessions(filteredSessions);
    } catch (error) {
      logger.error("Failed to load sessions", { error });
    }
  }, [privacySettings.retentionPeriod]);

  const loadPrivacySettings = useCallback(async () => {
    try {
      // Load user's privacy preferences from database
      // For now, use defaults
    } catch (error) {
      logger.error("Failed to load privacy settings", { error });
    }
  }, []);

  const calculateInsights = useCallback(async () => {
    try {
      if (sessions.length === 0) return;

      const totalEffectiveTime = sessions.reduce(
        (sum, s) => sum + s.effectiveDuration,
        0,
      );
      const sortedByDuration = [...sessions].sort(
        (a, b) => b.effectiveDuration - a.effectiveDuration,
      );
      const sortedByDate = [...sessions].sort(
        (a, b) => b.startTime.getTime() - a.startTime.getTime(),
      );

      const newInsights: HistoryInsights = {
        totalSessions: sessions.length,
        totalEffectiveTime,
        averageSessionLength: totalEffectiveTime / sessions.length,
        longestSession: sortedByDuration[0],
        shortestSession: sortedByDuration[sortedByDuration.length - 1],
        mostRecentSession: sortedByDate[0],
        goalCompletionRate: calculateOverallCompletionRate(sessions),
        pauseFrequency: calculatePauseFrequency(sessions),
        improvementTrend: calculateImprovementTrend(sessions),
        consistencyScore: calculateConsistencyScore(sessions),
      };

      setInsights(newInsights);
    } catch (error) {
      logger.error("Failed to calculate insights", { error });
    }
  }, [sessions]);

  const calculateTrends = useCallback(async () => {
    try {
      const newTrends: HistoryTrends = {
        sessionLength: calculateSessionLengthTrend(sessions),
        goalCompletion: calculateGoalCompletionTrend(sessions),
        consistency: calculateConsistencyTrend(sessions),
        pauseFrequency: calculatePauseFrequencyTrend(sessions),
        overallProgress: calculateOverallProgressTrend(sessions),
      };

      setTrends(newTrends);
    } catch (error) {
      logger.error("Failed to calculate trends", { error });
    }
  }, [sessions]);

  // ==================== DATA RETRIEVAL ====================

  const getSessionsByDateRange = useCallback(
    (start: Date, end: Date): HistoricalSession[] => {
      return sessions.filter(
        (session) => session.startTime >= start && session.startTime <= end,
      );
    },
    [sessions],
  );

  const getSessionsByGoal = useCallback(
    (goalType: string): HistoricalSession[] => {
      return sessions.filter((session) =>
        session.goals.some((goal) => goal.type === goalType),
      );
    },
    [sessions],
  );

  const searchSessions = useCallback(
    (query: HistorySearchQuery): HistoricalSession[] => {
      let filteredSessions = [...sessions];

      // Date range filter
      if (query.dateRange) {
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.startTime >= query.dateRange!.start &&
            session.startTime <= query.dateRange!.end,
        );
      }

      // Duration filters
      if (query.minDuration) {
        filteredSessions = filteredSessions.filter(
          (session) => session.effectiveDuration >= query.minDuration!,
        );
      }

      if (query.maxDuration) {
        filteredSessions = filteredSessions.filter(
          (session) => session.effectiveDuration <= query.maxDuration!,
        );
      }

      // Goal type filter
      if (query.goalTypes && query.goalTypes.length > 0) {
        filteredSessions = filteredSessions.filter((session) =>
          session.goals.some((goal) => query.goalTypes!.includes(goal.type)),
        );
      }

      // Keyholder control filter
      if (query.hasKeyholderControl !== undefined) {
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.wasKeyholderControlled === query.hasKeyholderControl,
        );
      }

      // Completed goals filter
      if (query.completedGoals !== undefined) {
        filteredSessions = filteredSessions.filter((session) => {
          const hasCompletedGoals = session.goals.some(
            (goal) => goal.completed,
          );
          return hasCompletedGoals === query.completedGoals;
        });
      }

      // Tags filter
      if (query.tags && query.tags.length > 0) {
        filteredSessions = filteredSessions.filter((session) =>
          query.tags!.some((tag) => session.tags.includes(tag)),
        );
      }

      // Rating filter
      if (query.rating && query.rating.min && query.rating.max) {
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.rating &&
            session.rating.overall >= query.rating!.min &&
            session.rating.overall <= query.rating!.max,
        );
      }

      // Text search
      if (query.textSearch) {
        const searchTerm = query.textSearch.toLowerCase();
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.notes.toLowerCase().includes(searchTerm) ||
            session.tags.some((tag) =>
              tag.toLowerCase().includes(searchTerm),
            ) ||
            session.endReason?.toLowerCase().includes(searchTerm),
        );
      }

      return filteredSessions;
    },
    [sessions],
  );

  // ==================== PRIVACY MANAGEMENT ====================

  const updatePrivacySettings = useCallback(
    async (settings: Partial<HistoryPrivacySettings>): Promise<void> => {
      try {
        logger.debug("Updating privacy settings", { settings });

        const updatedSettings = { ...privacySettings, ...settings };
        setPrivacySettings(updatedSettings);

        // Update keyholder access based on new settings
        if (relationshipId) {
          setKeyholderAccess((prev) => ({
            ...prev,
            hasAccess: updatedSettings.shareWithKeyholder,
            accessLevel: updatedSettings.shareWithKeyholder
              ? "detailed"
              : "summary",
            canViewRatings: updatedSettings.shareRatings,
            canViewNotes: updatedSettings.shareNotes,
            canViewPauses: updatedSettings.sharePauses,
          }));
        }

        // Reload sessions if retention period changed
        if (
          settings.retentionPeriod &&
          settings.retentionPeriod !== privacySettings.retentionPeriod
        ) {
          await loadSessions();
        }

        logger.info("Privacy settings updated successfully");
      } catch (error) {
        logger.error("Failed to update privacy settings", { error });
        throw error;
      }
    },
    [privacySettings, relationshipId, loadSessions],
  );

  const exportPersonalData =
    useCallback(async (): Promise<PersonalDataExport> => {
      try {
        logger.debug("Exporting personal data", { userId });

        const exportData: PersonalDataExport = {
          exportId: `export_${Date.now()}`,
          generatedAt: new Date(),
          format: "json",
          data: {
            sessions,
            goals: sessions.flatMap((s) => s.goals),
            settings: privacySettings,
            analytics: insights,
          },
          fileSize: 0, // Would be calculated
          downloadUrl: "", // Would be generated
          expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        };

        logger.info("Personal data export created", {
          exportId: exportData.exportId,
        });
        return exportData;
      } catch (error) {
        logger.error("Failed to export personal data", { error });
        throw error;
      }
    }, [sessions, privacySettings, insights, userId]);

  const deleteHistoricalData = useCallback(
    async (before: Date): Promise<void> => {
      try {
        logger.debug("Deleting historical data", { before, userId });

        const sessionsToKeep = sessions.filter(
          (session) => session.startTime >= before,
        );
        const deletedCount = sessions.length - sessionsToKeep.length;

        setSessions(sessionsToKeep);

        // Recalculate insights and trends
        await Promise.all([calculateInsights(), calculateTrends()]);

        logger.info("Historical data deleted", { deletedCount });
      } catch (error) {
        logger.error("Failed to delete historical data", { error });
        throw error;
      }
    },
    [sessions, calculateInsights, calculateTrends, userId],
  );

  // ==================== KEYHOLDER ACCESS ====================

  const getKeyholderView = useCallback((): KeyholderHistoryView => {
    if (!keyholderAccess.hasAccess) {
      return {
        allowedSessions: [],
        summaryStats: {
          totalSessions: 0,
          averageDuration: 0,
          goalCompletionRate: 0,
          lastSessionDate: new Date(),
        },
        accessLevel: "summary",
        restrictions: ["No access granted by submissive"],
      };
    }

    const allowedSessions = sessions.map((session) => {
      const filteredSession: Partial<HistoricalSession> = {
        id: session.id,
        startTime: session.startTime,
        endTime: session.endTime,
        duration: privacySettings.shareDuration ? session.duration : undefined,
        effectiveDuration: privacySettings.shareDuration
          ? session.effectiveDuration
          : undefined,
        goals: privacySettings.shareGoals ? session.goals : [],
        pauseEvents: privacySettings.sharePauses ? session.pauseEvents : [],
        notes: privacySettings.shareNotes ? session.notes : "",
        rating: privacySettings.shareRatings ? session.rating : undefined,
        keyholderInteractions: session.keyholderInteractions,
      };

      return filteredSession;
    });

    return {
      allowedSessions,
      summaryStats: {
        totalSessions: sessions.length,
        averageDuration: averageSessionLength,
        goalCompletionRate,
        lastSessionDate:
          sessions.length > 0 ? sessions[0].startTime : new Date(),
      },
      accessLevel: keyholderAccess.accessLevel,
      restrictions: [],
    };
  }, [
    keyholderAccess,
    sessions,
    privacySettings,
    averageSessionLength,
    goalCompletionRate,
  ]);

  const shareHistoryWithKeyholder = useCallback(
    async (sessionIds: string[]): Promise<void> => {
      try {
        logger.debug("Sharing specific sessions with keyholder", {
          sessionIds,
        });

        // This would create a special sharing link or send specific data
        // For now, just log the action
        logger.info("History shared with keyholder", {
          sessionCount: sessionIds.length,
        });
      } catch (error) {
        logger.error("Failed to share history with keyholder", { error });
        throw error;
      }
    },
    [],
  );

  // ==================== ANALYTICS ====================

  const getPerformanceTrends = useCallback((): PerformanceTrends => {
    return {
      sessionDuration: {
        average: averageSessionLength,
        trend: trends.sessionLength.direction,
        weeklyChange: trends.sessionLength.changePercentage,
      },
      goalAchievement: {
        rate: goalCompletionRate,
        trend: trends.goalCompletion.direction,
        weeklyChange: trends.goalCompletion.changePercentage,
      },
      consistency: {
        score: insights.consistencyScore,
        streak: longestStreak,
        trend: trends.consistency.direction,
      },
    };
  }, [
    averageSessionLength,
    trends,
    goalCompletionRate,
    insights.consistencyScore,
    longestStreak,
  ]);

  const getGoalProgressHistory = useCallback((): GoalProgressHistory[] => {
    const goalProgressMap = new Map<string, GoalProgressHistory>();

    sessions.forEach((session) => {
      session.goals.forEach((goal) => {
        if (!goalProgressMap.has(goal.id)) {
          goalProgressMap.set(goal.id, {
            goalId: goal.id,
            goalName: goal.type,
            progressOverTime: [],
            milestones: [],
          });
        }

        const progressHistory = goalProgressMap.get(goal.id)!;
        progressHistory.progressOverTime.push({
          date: session.startTime,
          progress: goal.progress,
        });
      });
    });

    return Array.from(goalProgressMap.values());
  }, [sessions]);

  const getComparisonMetrics = useCallback((): ComparisonMetrics => {
    const now = new Date();
    const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
    const twoWeeksAgo = new Date(now.getTime() - 14 * 24 * 60 * 60 * 1000);
    const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
    const twoMonthsAgo = new Date(now.getTime() - 60 * 24 * 60 * 60 * 1000);

    const thisWeekSessions = getSessionsByDateRange(weekAgo, now);
    const lastWeekSessions = getSessionsByDateRange(twoWeeksAgo, weekAgo);
    const thisMonthSessions = getSessionsByDateRange(monthAgo, now);
    const lastMonthSessions = getSessionsByDateRange(twoMonthsAgo, monthAgo);

    return {
      thisWeek: {
        sessions: thisWeekSessions.length,
        totalTime: thisWeekSessions.reduce(
          (sum, s) => sum + s.effectiveDuration,
          0,
        ),
        goalCompletion: calculateOverallCompletionRate(thisWeekSessions),
      },
      lastWeek: {
        sessions: lastWeekSessions.length,
        totalTime: lastWeekSessions.reduce(
          (sum, s) => sum + s.effectiveDuration,
          0,
        ),
        goalCompletion: calculateOverallCompletionRate(lastWeekSessions),
      },
      thisMonth: {
        sessions: thisMonthSessions.length,
        totalTime: thisMonthSessions.reduce(
          (sum, s) => sum + s.effectiveDuration,
          0,
        ),
        goalCompletion: calculateOverallCompletionRate(thisMonthSessions),
      },
      lastMonth: {
        sessions: lastMonthSessions.length,
        totalTime: lastMonthSessions.reduce(
          (sum, s) => sum + s.effectiveDuration,
          0,
        ),
        goalCompletion: calculateOverallCompletionRate(lastMonthSessions),
      },
    };
  }, [getSessionsByDateRange]);

  // ==================== RETURN HOOK INTERFACE ====================

  return {
    // History data
    sessions,
    insights,
    trends,
    privacySettings,

    // Data retrieval
    getSessionsByDateRange,
    getSessionsByGoal,
    searchSessions,

    // Privacy management
    updatePrivacySettings,
    exportPersonalData,
    deleteHistoricalData,

    // Keyholder access
    getKeyholderView,
    shareHistoryWithKeyholder,

    // Analytics
    getPerformanceTrends,
    getGoalProgressHistory,
    getComparisonMetrics,

    // Computed values
    totalSessions,
    averageSessionLength,
    goalCompletionRate,
    longestStreak,
    hasPrivacyRestrictions,

    // Loading states
    isLoading,
    error,
  };
};

/**
 * Session History Query Functions
 * Composable hook for querying and managing session history data
 */
import { useCallback } from "react";
import type {
  HistoricalSession,
  HistorySearchQuery,
  HistoryPrivacySettings,
  HistoryInsights,
  PersonalDataExport,
} from "./types/sessionHistory";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useSessionHistoryQueries");

export const useSessionHistoryQueries = (
  sessions: HistoricalSession[],
  privacySettings: HistoryPrivacySettings,
  insights: HistoryInsights,
  userId: string,
  setSessions: (sessions: HistoricalSession[]) => void,
  setPrivacySettings: (settings: HistoryPrivacySettings) => void,
  calculateInsights: () => void,
  calculateTrends: () => void,
) => {
  // ==================== DATA RETRIEVAL ====================

  const getSessionsByDateRange = useCallback(
    (start: Date, end: Date): HistoricalSession[] => {
      return sessions.filter(
        (session) => session.startTime >= start && session.startTime <= end,
      );
    },
    [sessions],
  );

  const getSessionsByGoal = useCallback(
    (goalType: string): HistoricalSession[] => {
      return sessions.filter((session) =>
        session.goals.some((goal) => goal.type === goalType),
      );
    },
    [sessions],
  );

  const searchSessions = useCallback(
    (query: HistorySearchQuery): HistoricalSession[] => {
      let filteredSessions = [...sessions];

      // Date range filter
      if (query.dateRange) {
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.startTime >= query.dateRange!.start &&
            session.startTime <= query.dateRange!.end,
        );
      }

      // Duration filters
      if (query.minDuration) {
        filteredSessions = filteredSessions.filter(
          (session) => session.effectiveDuration >= query.minDuration!,
        );
      }

      if (query.maxDuration) {
        filteredSessions = filteredSessions.filter(
          (session) => session.effectiveDuration <= query.maxDuration!,
        );
      }

      // Goal type filter
      if (query.goalTypes && query.goalTypes.length > 0) {
        filteredSessions = filteredSessions.filter((session) =>
          session.goals.some((goal) => query.goalTypes!.includes(goal.type)),
        );
      }

      // Keyholder control filter
      if (query.hasKeyholderControl !== undefined) {
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.wasKeyholderControlled === query.hasKeyholderControl,
        );
      }

      // Completed goals filter
      if (query.completedGoals !== undefined) {
        filteredSessions = filteredSessions.filter((session) => {
          const hasCompletedGoals = session.goals.some(
            (goal) => goal.completed,
          );
          return query.completedGoals ? hasCompletedGoals : !hasCompletedGoals;
        });
      }

      // Tags filter
      if (query.tags && query.tags.length > 0) {
        filteredSessions = filteredSessions.filter((session) =>
          query.tags!.some((tag) => session.tags.includes(tag)),
        );
      }

      // Rating filter
      if (query.rating && query.rating.min && query.rating.max) {
        filteredSessions = filteredSessions.filter((session) => {
          if (!session.rating) return false;
          return (
            session.rating.overall >= query.rating!.min &&
            session.rating.overall <= query.rating!.max
          );
        });
      }

      // Text search (searches in notes and tags)
      if (query.textSearch) {
        const searchLower = query.textSearch.toLowerCase();
        filteredSessions = filteredSessions.filter(
          (session) =>
            session.notes.toLowerCase().includes(searchLower) ||
            session.tags.some((tag) => tag.toLowerCase().includes(searchLower)),
        );
      }

      return filteredSessions;
    },
    [sessions],
  );

  // ==================== PRIVACY MANAGEMENT ====================

  const updatePrivacySettings = useCallback(
    async (newSettings: Partial<HistoryPrivacySettings>): Promise<void> => {
      try {
        logger.debug("Updating privacy settings", { newSettings, userId });

        const updatedSettings = {
          ...privacySettings,
          ...newSettings,
        };

        setPrivacySettings(updatedSettings);

        // If reducing sharing permissions, we may need to notify keyholder
        if (
          privacySettings.shareWithKeyholder &&
          !updatedSettings.shareWithKeyholder
        ) {
          logger.info("Keyholder access revoked", { userId });
        }
      } catch (error) {
        logger.error("Failed to update privacy settings", { error });
        throw error;
      }
    },
    [privacySettings, userId, setPrivacySettings],
  );

  const exportPersonalData =
    useCallback(async (): Promise<PersonalDataExport> => {
      try {
        logger.debug("Exporting personal data", { userId });

        const exportData: PersonalDataExport = {
          exportId: `export-${Date.now()}`,
          generatedAt: new Date(),
          format: "json",
          data: {
            sessions,
            goals: sessions.flatMap((s) => s.goals),
            settings: privacySettings,
            analytics: insights,
          },
          fileSize: 0, // Would be calculated based on actual data size
          downloadUrl: "", // Would be generated by backend
          expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        };

        return exportData;
      } catch (error) {
        logger.error("Failed to export personal data", { error });
        throw error;
      }
    }, [sessions, privacySettings, insights, userId]);

  const deleteHistoricalData = useCallback(
    async (before: Date): Promise<void> => {
      try {
        logger.debug("Deleting historical data", { before, userId });

        const sessionsToKeep = sessions.filter(
          (session) => session.startTime >= before,
        );
        const deletedCount = sessions.length - sessionsToKeep.length;

        setSessions(sessionsToKeep);

        // Recalculate insights and trends
        calculateInsights();
        calculateTrends();

        logger.info("Historical data deleted", { deletedCount, userId });
      } catch (error) {
        logger.error("Failed to delete historical data", { error });
        throw error;
      }
    },
    [sessions, userId, setSessions, calculateInsights, calculateTrends],
  );

  return {
    // Data retrieval
    getSessionsByDateRange,
    getSessionsByGoal,
    searchSessions,

    // Privacy management
    updatePrivacySettings,
    exportPersonalData,
    deleteHistoricalData,
  };
};

/**
 * Session Loader Hook
 * Provides session loading and restoration functionality for SessionLoader component
 * Extracts business logic from component to maintain clean architecture
 */
import { useState, useCallback, useEffect } from "react";
import { useSessionPersistence } from "../useSessionPersistence";
import type { DBSession } from "../../types/database";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useSessionLoader");

export interface UseSessionLoaderReturn {
  // State
  session: DBSession | null;
  isLoading: boolean;
  error: Error | null;
  hasBackup: boolean;

  // Actions
  loadSession: (userId: string) => Promise<void>;
  restoreFromBackup: () => Promise<DBSession | null>;
  clearBackup: () => Promise<void>;

  // Status
  isRestoring: boolean;
  canRestore: boolean;

  // Progress tracking
  progress: number;
}

export function useSessionLoader(): UseSessionLoaderReturn {
  const [session, setSession] = useState<DBSession | null>(null);
  const [error, setError] = useState<Error | null>(null);
  const [isRestoring, setIsRestoring] = useState(false);
  const [progress, setProgress] = useState(0);

  const {
    initializeSession,
    isInitializing,
    restorationResult,
    error: persistenceError,
    getBackupState,
  } = useSessionPersistence({ autoInitialize: false });

  // Check if there's a backup available
  const hasBackup = useCallback(() => {
    const backupState = getBackupState();
    return Boolean(backupState?.activeSessionId);
  }, [getBackupState]);

  // Determine if restore is possible
  const canRestore = hasBackup() && !isRestoring && !isInitializing;

  // Load session for a user
  const loadSession = useCallback(
    async (userId: string): Promise<void> => {
      if (!userId) {
        const err = new Error("User ID is required");
        setError(err);
        logger.error("Load session failed: no user ID", { error: err });
        return;
      }

      try {
        setError(null);
        setProgress(25);

        logger.debug("Loading session", { userId });

        // Initialize session state using the persistence hook
        const result = await initializeSession(userId);
        setProgress(75);

        if (result.session) {
          setSession(result.session);
          logger.info("Session loaded successfully", {
            sessionId: result.session.id,
            wasRestored: result.wasRestored,
          });
        }

        setProgress(100);
      } catch (err) {
        const error = err instanceof Error ? err : new Error(String(err));
        setError(error);
        logger.error("Failed to load session", { error, userId });
      }
    },
    [initializeSession],
  );

  // Restore session from backup
  const restoreFromBackup = useCallback(async (): Promise<DBSession | null> => {
    try {
      setIsRestoring(true);
      setError(null);

      const backupState = getBackupState();
      if (!backupState?.activeSessionId) {
        throw new Error("No backup available to restore");
      }

      logger.debug("Restoring session from backup", {
        sessionId: backupState.activeSessionId,
      });

      // The restoration is handled by the persistence service
      // We just need to signal that we're restoring and get the result
      const restoredSession = restorationResult?.session || null;

      if (restoredSession) {
        setSession(restoredSession);
        logger.info("Session restored from backup", {
          sessionId: restoredSession.id,
        });
      }

      return restoredSession;
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));
      setError(error);
      logger.error("Failed to restore from backup", { error });
      throw error;
    } finally {
      setIsRestoring(false);
    }
  }, [getBackupState, restorationResult]);

  // Clear backup data
  const clearBackup = useCallback(async (): Promise<void> => {
    try {
      logger.debug("Clearing session backup");

      // Clear localStorage backup
      localStorage.removeItem("chastity_session_backup");

      logger.info("Session backup cleared");
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));
      setError(error);
      logger.error("Failed to clear backup", { error });
      throw error;
    }
  }, []);

  // Sync persistence error to local error state
  useEffect(() => {
    if (persistenceError) {
      setError(new Error(persistenceError));
    }
  }, [persistenceError]);

  // Update session when restoration result changes
  useEffect(() => {
    if (restorationResult?.session) {
      setSession(restorationResult.session);
    }
  }, [restorationResult]);

  const isLoading = isInitializing || isRestoring;

  return {
    // State
    session,
    isLoading,
    error,
    hasBackup: hasBackup(),

    // Actions
    loadSession,
    restoreFromBackup,
    clearBackup,

    // Status
    isRestoring,
    canRestore,

    // Progress
    progress,
  };
}

/**
 * useHealthCheck Hook - System Health Monitoring
 *
 * Monitor overall system health, service availability, and provide early warning
 * for potential issues.
 */

import { useState, useCallback } from "react";
import { useQuery } from "@tanstack/react-query";
import { logger } from "../../utils/logging";

// Health status enum
export enum HealthStatus {
  HEALTHY = "healthy",
  WARNING = "warning",
  CRITICAL = "critical",
  UNKNOWN = "unknown",
}

// Service status
export interface ServiceStatus {
  name: string;
  status: HealthStatus;
  responseTime: number;
  lastChecked: Date;
  error?: string;
  uptime: number;
}

// System metrics
export interface SystemMetrics {
  memoryUsage: number;
  cpuUsage: number;
  storageUsage: number;
  networkLatency: number;
  errorRate: number;
  activeConnections: number;
}

// Health check result
export interface HealthCheckResult {
  overall: HealthStatus;
  services: ServiceStatus[];
  metrics: SystemMetrics;
  alerts: HealthAlert[];
  lastUpdated: Date;
  checkDuration: number;
}

// Health alert
export interface HealthAlert {
  id: string;
  type: "warning" | "error" | "info";
  message: string;
  service?: string;
  timestamp: Date;
  acknowledged: boolean;
}

// Health check configuration
interface HealthCheckConfig {
  checkInterval: number;
  timeout: number;
  retryAttempts: number;
  services: string[];
}

const DEFAULT_CONFIG: HealthCheckConfig = {
  checkInterval: 30000, // 30 seconds
  timeout: 5000, // 5 seconds
  retryAttempts: 3,
  services: ["firebase", "storage", "network"],
};

/**
 * System Health Check Hook
 */
export const useHealthCheck = (config: Partial<HealthCheckConfig> = {}) => {
  const fullConfig = { ...DEFAULT_CONFIG, ...config };
  const [alerts, setAlerts] = useState<HealthAlert[]>([]);
  const [isMonitoring, setIsMonitoring] = useState(true);

  // Check service health
  const checkServiceHealth = useCallback(
    async (serviceName: string): Promise<ServiceStatus> => {
      const startTime = Date.now();
      let status: HealthStatus = HealthStatus.UNKNOWN;
      let error: string | undefined;
      let responseTime = 0;

      try {
        switch (serviceName) {
          case "firebase":
            // Check Firebase connectivity
            status = await checkFirebaseHealth();
            break;
          case "storage":
            status = await checkStorageHealth();
            break;
          case "network":
            status = await checkNetworkHealth();
            break;
          default:
            status = HealthStatus.UNKNOWN;
        }

        responseTime = Date.now() - startTime;
      } catch (err) {
        status = HealthStatus.CRITICAL;
        error = err instanceof Error ? err.message : "Unknown error";
        responseTime = Date.now() - startTime;
        logger.error(`Health check failed for ${serviceName}`, { error: err });
      }

      return {
        name: serviceName,
        status,
        responseTime,
        lastChecked: new Date(),
        error,
        uptime: calculateUptime(serviceName),
      };
    },
    [],
  );

  // Check Firebase health
  const checkFirebaseHealth = async (): Promise<HealthStatus> => {
    try {
      // Simple connectivity test - try to access Firebase
      if (
        typeof window !== "undefined" &&
        (window as Window & { firebase?: unknown }).firebase
      ) {
        return HealthStatus.HEALTHY;
      }
      return HealthStatus.WARNING;
    } catch {
      return HealthStatus.CRITICAL;
    }
  };

  // Check storage health
  const checkStorageHealth = async (): Promise<HealthStatus> => {
    try {
      // Test localStorage availability
      const testKey = "health-check-test";
      const testValue = "test";

      localStorage.setItem(testKey, testValue);
      const retrieved = localStorage.getItem(testKey);
      localStorage.removeItem(testKey);

      if (retrieved === testValue) {
        return HealthStatus.HEALTHY;
      }
      return HealthStatus.WARNING;
    } catch {
      return HealthStatus.CRITICAL;
    }
  };

  // Check network health
  const checkNetworkHealth = async (): Promise<HealthStatus> => {
    if (!navigator.onLine) {
      return HealthStatus.CRITICAL;
    }

    try {
      const startTime = Date.now();
      const response = await fetch("/favicon.ico", {
        method: "HEAD",
        cache: "no-cache",
      });
      const responseTime = Date.now() - startTime;

      if (response.ok && responseTime < 2000) {
        return HealthStatus.HEALTHY;
      } else if (response.ok && responseTime < 5000) {
        return HealthStatus.WARNING;
      }
      return HealthStatus.CRITICAL;
    } catch {
      return HealthStatus.CRITICAL;
    }
  };

  // Calculate service uptime (simplified)
  const calculateUptime = (_serviceName: string): number => {
    // In a real implementation, this would track actual uptime
    // For now, return a mock value
    return Math.random() * 100;
  };

  // Get system metrics
  const getSystemMetrics = useCallback(async (): Promise<SystemMetrics> => {
    const metrics: SystemMetrics = {
      memoryUsage: 0,
      cpuUsage: 0,
      storageUsage: 0,
      networkLatency: 0,
      errorRate: 0,
      activeConnections: 1,
    };

    try {
      // Memory usage
      if ("memory" in performance) {
        const memory = (
          performance as Performance & {
            memory?: { usedJSHeapSize: number; totalJSHeapSize: number };
          }
        ).memory;
        if (memory) {
          metrics.memoryUsage =
            (memory.usedJSHeapSize / memory.totalJSHeapSize) * 100;
        }
      }

      // Storage usage
      if ("storage" in navigator && "estimate" in navigator.storage) {
        const estimate = await navigator.storage.estimate();
        if (estimate.usage && estimate.quota) {
          metrics.storageUsage = (estimate.usage / estimate.quota) * 100;
        }
      }

      // Network latency (simplified)
      const startTime = Date.now();
      await fetch("/favicon.ico", { method: "HEAD", cache: "no-cache" });
      metrics.networkLatency = Date.now() - startTime;
    } catch (error) {
      logger.warn("Failed to get some system metrics", error);
    }

    return metrics;
  }, []);

  // Determine overall health status
  const calculateOverallHealth = useCallback(
    (services: ServiceStatus[]): HealthStatus => {
      const statuses = services.map((s) => s.status);

      if (statuses.includes(HealthStatus.CRITICAL)) {
        return HealthStatus.CRITICAL;
      }

      if (statuses.includes(HealthStatus.WARNING)) {
        return HealthStatus.WARNING;
      }

      if (statuses.every((s) => s === HealthStatus.HEALTHY)) {
        return HealthStatus.HEALTHY;
      }

      return HealthStatus.UNKNOWN;
    },
    [],
  );

  // Generate alerts based on health status
  const generateAlerts = useCallback(
    (services: ServiceStatus[], metrics: SystemMetrics): HealthAlert[] => {
      const newAlerts: HealthAlert[] = [];

      // Service alerts
      services.forEach((service) => {
        if (service.status === HealthStatus.CRITICAL) {
          newAlerts.push({
            id: `service-${service.name}-${Date.now()}`,
            type: "error",
            message: `Service ${service.name} is critical: ${service.error || "Unknown error"}`,
            service: service.name,
            timestamp: new Date(),
            acknowledged: false,
          });
        } else if (service.status === HealthStatus.WARNING) {
          newAlerts.push({
            id: `service-${service.name}-${Date.now()}`,
            type: "warning",
            message: `Service ${service.name} is experiencing issues`,
            service: service.name,
            timestamp: new Date(),
            acknowledged: false,
          });
        }
      });

      // Metrics alerts
      if (metrics.memoryUsage > 90) {
        newAlerts.push({
          id: `memory-${Date.now()}`,
          type: "warning",
          message: `High memory usage: ${metrics.memoryUsage.toFixed(1)}%`,
          timestamp: new Date(),
          acknowledged: false,
        });
      }

      if (metrics.storageUsage > 90) {
        newAlerts.push({
          id: `storage-${Date.now()}`,
          type: "warning",
          message: `High storage usage: ${metrics.storageUsage.toFixed(1)}%`,
          timestamp: new Date(),
          acknowledged: false,
        });
      }

      if (metrics.networkLatency > 5000) {
        newAlerts.push({
          id: `network-${Date.now()}`,
          type: "warning",
          message: `High network latency: ${metrics.networkLatency}ms`,
          timestamp: new Date(),
          acknowledged: false,
        });
      }

      return newAlerts;
    },
    [],
  );

  // Main health check query
  const {
    data: healthStatus,
    isLoading,
    error,
    refetch,
  } = useQuery<HealthCheckResult>({
    queryKey: ["system", "health"],
    queryFn: async () => {
      const startTime = Date.now();

      // Check all services
      const serviceChecks = await Promise.all(
        fullConfig.services.map((service) => checkServiceHealth(service)),
      );

      // Get system metrics
      const metrics = await getSystemMetrics();

      // Calculate overall health
      const overall = calculateOverallHealth(serviceChecks);

      // Generate alerts
      const newAlerts = generateAlerts(serviceChecks, metrics);

      const result: HealthCheckResult = {
        overall,
        services: serviceChecks,
        metrics,
        alerts: newAlerts,
        lastUpdated: new Date(),
        checkDuration: Date.now() - startTime,
      };

      // Update alerts state
      if (newAlerts.length > 0) {
        setAlerts((prev) => [...prev.slice(-19), ...newAlerts]); // Keep last 20 alerts
      }

      return result;
    },
    refetchInterval: isMonitoring ? fullConfig.checkInterval : false,
    retry: fullConfig.retryAttempts,
    staleTime: fullConfig.checkInterval / 2,
  });

  // Acknowledge alert
  const acknowledgeAlert = useCallback((alertId: string) => {
    setAlerts((prev) =>
      prev.map((alert) =>
        alert.id === alertId ? { ...alert, acknowledged: true } : alert,
      ),
    );
    logger.info("Health alert acknowledged", { alertId });
  }, []);

  // Clear acknowledged alerts
  const clearAcknowledgedAlerts = useCallback(() => {
    setAlerts((prev) => prev.filter((alert) => !alert.acknowledged));
    logger.info("Acknowledged alerts cleared");
  }, []);

  // Start/stop monitoring
  const toggleMonitoring = useCallback((enabled: boolean) => {
    setIsMonitoring(enabled);
    logger.info(`Health monitoring ${enabled ? "started" : "stopped"}`);
  }, []);

  // Force health check
  const forceCheck = useCallback(() => {
    refetch();
    logger.info("Manual health check triggered");
  }, [refetch]);

  return {
    // Health status
    healthStatus,
    isLoading,
    error,

    // Alerts
    alerts: alerts.filter((alert) => !alert.acknowledged),
    acknowledgedAlerts: alerts.filter((alert) => alert.acknowledged),
    allAlerts: alerts,

    // Controls
    acknowledgeAlert,
    clearAcknowledgedAlerts,
    toggleMonitoring,
    forceCheck,

    // State
    isMonitoring,

    // Computed properties
    isHealthy: healthStatus?.overall === HealthStatus.HEALTHY,
    hasWarnings: healthStatus?.overall === HealthStatus.WARNING,
    isCritical: healthStatus?.overall === HealthStatus.CRITICAL,
    hasUnacknowledgedAlerts: alerts.some((alert) => !alert.acknowledged),
    uptime:
      healthStatus?.services.reduce((acc, service) => acc + service.uptime, 0) /
      (healthStatus?.services.length || 1),
    averageResponseTime:
      healthStatus?.services.reduce(
        (acc, service) => acc + service.responseTime,
        0,
      ) / (healthStatus?.services.length || 1),
  };
};

/**
 * useMigration Hook - Data Migration Management
 *
 * Handle data migrations, schema updates, and legacy data conversion with
 * progress tracking and rollback capabilities.
 */

import { useState, useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { logger } from "../../utils/logging";

// Migration status
export enum MigrationStatus {
  PENDING = "pending",
  RUNNING = "running",
  COMPLETED = "completed",
  FAILED = "failed",
  ROLLED_BACK = "rolled_back",
}

// Migration definition
export interface Migration {
  id: string;
  version: string;
  name: string;
  description: string;
  status: MigrationStatus;
  createdAt: Date;
  startedAt?: Date;
  completedAt?: Date;
  progress: number;
  error?: string;
  rollbackAvailable: boolean;
}

// Migration batch
export interface MigrationBatch {
  id: string;
  migrations: Migration[];
  status: MigrationStatus;
  totalProgress: number;
  startedAt?: Date;
  completedAt?: Date;
}

// Migration result
export interface MigrationResult {
  success: boolean;
  migrationsRun: number;
  errors: string[];
  warnings: string[];
  duration: number;
}

// Storage keys
const STORAGE_KEYS = {
  MIGRATION_STATE: "chastity-migration-state",
  MIGRATION_BACKUPS: "chastity-migration-backups",
};

// Sample migrations (in a real app, these would be defined elsewhere)
const AVAILABLE_MIGRATIONS: Omit<
  Migration,
  "status" | "progress" | "createdAt"
>[] = [
  {
    id: "v4.0.0-theme-system",
    version: "4.0.0",
    name: "Theme System Migration",
    description: "Migrate existing theme preferences to new theme system",
    rollbackAvailable: true,
  },
  {
    id: "v4.0.0-enhanced-goals",
    version: "4.0.0",
    name: "Enhanced Goals Migration",
    description: "Convert legacy goals to enhanced goal format",
    rollbackAvailable: true,
  },
  {
    id: "v4.0.0-gamification",
    version: "4.0.0",
    name: "Gamification System Migration",
    description: "Initialize gamification data from existing achievements",
    rollbackAvailable: false,
  },
];

/**
 * Data Migration Hook
 */
export const useMigration = () => {
  const queryClient = useQueryClient();
  const [isRunning, setIsRunning] = useState(false);

  // Get migration state
  const { data: migrationState } = useQuery({
    queryKey: ["migration", "state"],
    queryFn: () => {
      const stored = localStorage.getItem(STORAGE_KEYS.MIGRATION_STATE);
      if (stored) {
        return JSON.parse(stored);
      }

      // Initialize migration state
      const initialMigrations: Migration[] = AVAILABLE_MIGRATIONS.map(
        (migration) => ({
          ...migration,
          status: MigrationStatus.PENDING,
          progress: 0,
          createdAt: new Date(),
        }),
      );

      return {
        migrations: initialMigrations,
        lastRun: null,
        currentVersion: "3.0.0",
      };
    },
    staleTime: Infinity,
  });

  // Get pending migrations
  const pendingMigrations =
    migrationState?.migrations?.filter(
      (m: Migration) => m.status === MigrationStatus.PENDING,
    ) || [];

  // Get completed migrations
  const completedMigrations =
    migrationState?.migrations?.filter(
      (m: Migration) => m.status === MigrationStatus.COMPLETED,
    ) || [];

  // Create backup before migration
  const createBackup = useCallback(
    async (migrationId: string) => {
      try {
        const backup = {
          id: `backup-${migrationId}-${Date.now()}`,
          migrationId,
          timestamp: new Date(),
          data: {
            // In a real implementation, this would backup relevant data
            localStorage: { ...localStorage },
            version: migrationState?.currentVersion,
          },
        };

        const existingBackups = JSON.parse(
          localStorage.getItem(STORAGE_KEYS.MIGRATION_BACKUPS) || "[]",
        );

        const updatedBackups = [...existingBackups, backup];
        localStorage.setItem(
          STORAGE_KEYS.MIGRATION_BACKUPS,
          JSON.stringify(updatedBackups),
        );

        logger.info("Migration backup created", {
          migrationId,
          backupId: backup.id,
        });
        return backup.id;
      } catch (error) {
        logger.error("Failed to create migration backup", {
          migrationId,
          error,
        });
        throw error;
      }
    },
    [migrationState],
  );

  // Migration implementations
  const migrateThemeSystem = useCallback(
    async (onProgress: (progress: number) => void) => {
      onProgress(25);
      await new Promise((resolve) => setTimeout(resolve, 500));

      // Convert old theme settings
      const oldTheme = localStorage.getItem("theme");
      if (oldTheme) {
        localStorage.setItem(
          "chastity-theme-current",
          JSON.stringify(
            oldTheme === "dark" ? "default-dark" : "default-light",
          ),
        );
      }

      onProgress(75);
      await new Promise((resolve) => setTimeout(resolve, 500));
      onProgress(100);
    },
    [],
  );

  const migrateEnhancedGoals = useCallback(
    async (onProgress: (progress: number) => void) => {
      onProgress(30);
      await new Promise((resolve) => setTimeout(resolve, 300));

      // Convert legacy goals (simplified)
      const legacyGoals = localStorage.getItem("personal-goals");
      if (legacyGoals) {
        // Transform format here
        onProgress(70);
        await new Promise((resolve) => setTimeout(resolve, 300));
      }

      onProgress(100);
    },
    [],
  );

  const migrateGamificationSystem = useCallback(
    async (onProgress: (progress: number) => void) => {
      onProgress(20);
      await new Promise((resolve) => setTimeout(resolve, 400));

      // Initialize gamification data
      onProgress(60);
      await new Promise((resolve) => setTimeout(resolve, 400));
      onProgress(100);
    },
    [],
  );

  // Execute migration logic
  const executeMigrationLogic = useCallback(
    async (migrationId: string, onProgress: (progress: number) => void) => {
      switch (migrationId) {
        case "v4.0.0-theme-system":
          await migrateThemeSystem(onProgress);
          break;
        case "v4.0.0-enhanced-goals":
          await migrateEnhancedGoals(onProgress);
          break;
        case "v4.0.0-gamification":
          await migrateGamificationSystem(onProgress);
          break;
        default:
          throw new Error(`Unknown migration: ${migrationId}`);
      }
    },
    [migrateThemeSystem, migrateEnhancedGoals, migrateGamificationSystem],
  );

  // Execute single migration
  const executeMigration = useCallback(
    async (migration: Migration): Promise<void> => {
      logger.info("Starting migration", { migrationId: migration.id });

      // Update migration status
      const updatedMigrations = migrationState.migrations.map((m: Migration) =>
        m.id === migration.id
          ? {
              ...m,
              status: MigrationStatus.RUNNING,
              startedAt: new Date(),
              progress: 0,
            }
          : m,
      );

      const newState = { ...migrationState, migrations: updatedMigrations };
      localStorage.setItem(
        STORAGE_KEYS.MIGRATION_STATE,
        JSON.stringify(newState),
      );
      queryClient.setQueryData(["migration", "state"], newState);

      try {
        // Create backup if rollback is available
        if (migration.rollbackAvailable) {
          await createBackup(migration.id);
        }

        // Execute migration logic based on ID
        await executeMigrationLogic(migration.id, (progress: number) => {
          // Update progress
          const progressUpdatedMigrations = migrationState.migrations.map(
            (m: Migration) => (m.id === migration.id ? { ...m, progress } : m),
          );

          const progressState = {
            ...migrationState,
            migrations: progressUpdatedMigrations,
          };
          localStorage.setItem(
            STORAGE_KEYS.MIGRATION_STATE,
            JSON.stringify(progressState),
          );
          queryClient.setQueryData(["migration", "state"], progressState);
        });

        // Mark as completed
        const completedMigrations = migrationState.migrations.map(
          (m: Migration) =>
            m.id === migration.id
              ? {
                  ...m,
                  status: MigrationStatus.COMPLETED,
                  completedAt: new Date(),
                  progress: 100,
                }
              : m,
        );

        const completedState = {
          ...migrationState,
          migrations: completedMigrations,
        };
        localStorage.setItem(
          STORAGE_KEYS.MIGRATION_STATE,
          JSON.stringify(completedState),
        );
        queryClient.setQueryData(["migration", "state"], completedState);

        logger.info("Migration completed", { migrationId: migration.id });
      } catch (error) {
        // Mark as failed
        const failedMigrations = migrationState.migrations.map(
          (m: Migration) =>
            m.id === migration.id
              ? {
                  ...m,
                  status: MigrationStatus.FAILED,
                  error:
                    error instanceof Error ? error.message : "Unknown error",
                }
              : m,
        );

        const failedState = { ...migrationState, migrations: failedMigrations };
        localStorage.setItem(
          STORAGE_KEYS.MIGRATION_STATE,
          JSON.stringify(failedState),
        );
        queryClient.setQueryData(["migration", "state"], failedState);

        logger.error("Migration failed", { migrationId: migration.id, error });
        throw error;
      }
    },
    [migrationState, queryClient, createBackup, executeMigrationLogic],
  );

  // Run migrations mutation
  const runMigrationsMutation = useMutation({
    mutationFn: async (migrationIds?: string[]) => {
      setIsRunning(true);
      const startTime = Date.now();
      const result: MigrationResult = {
        success: true,
        migrationsRun: 0,
        errors: [],
        warnings: [],
        duration: 0,
      };

      try {
        const migrationsToRun = migrationIds
          ? migrationState.migrations.filter((m: Migration) =>
              migrationIds.includes(m.id),
            )
          : pendingMigrations;

        for (const migration of migrationsToRun) {
          try {
            await executeMigration(migration);
            result.migrationsRun++;
          } catch (error) {
            result.success = false;
            result.errors.push(
              `${migration.name}: ${error instanceof Error ? error.message : "Unknown error"}`,
            );
          }
        }

        result.duration = Date.now() - startTime;
        return result;
      } finally {
        setIsRunning(false);
      }
    },
  });

  // Rollback migration mutation
  const rollbackMigrationMutation = useMutation({
    mutationFn: async (migrationId: string) => {
      const migration = migrationState.migrations.find(
        (m: Migration) => m.id === migrationId,
      );
      if (!migration) throw new Error("Migration not found");
      if (!migration.rollbackAvailable)
        throw new Error("Rollback not available for this migration");

      logger.info("Rolling back migration", { migrationId });

      // Find and restore backup
      const backups = JSON.parse(
        localStorage.getItem(STORAGE_KEYS.MIGRATION_BACKUPS) || "[]",
      );
      const backup = backups.find(
        (b: { migrationId: string }) => b.migrationId === migrationId,
      );

      if (!backup) throw new Error("Backup not found");

      // Restore data from backup
      Object.entries(backup.data.localStorage).forEach(([key, value]) => {
        localStorage.setItem(key, value as string);
      });

      // Update migration status
      const rolledBackMigrations = migrationState.migrations.map(
        (m: Migration) =>
          m.id === migrationId
            ? { ...m, status: MigrationStatus.ROLLED_BACK }
            : m,
      );

      const rolledBackState = {
        ...migrationState,
        migrations: rolledBackMigrations,
      };
      localStorage.setItem(
        STORAGE_KEYS.MIGRATION_STATE,
        JSON.stringify(rolledBackState),
      );
      queryClient.setQueryData(["migration", "state"], rolledBackState);

      logger.info("Migration rolled back", { migrationId });
    },
  });

  return {
    // Migration state
    migrations: migrationState?.migrations || [],
    pendingMigrations,
    completedMigrations,
    isRunning,

    // Actions
    runMigrations: runMigrationsMutation.mutate,
    rollbackMigration: rollbackMigrationMutation.mutate,

    // Status
    hasPendingMigrations: pendingMigrations.length > 0,
    hasFailedMigrations:
      migrationState?.migrations?.some(
        (m: Migration) => m.status === MigrationStatus.FAILED,
      ) || false,

    // Loading states
    isRunningMigrations: runMigrationsMutation.isPending,
    isRollingBack: rollbackMigrationMutation.isPending,

    // Results
    lastResult: runMigrationsMutation.data,
    error: runMigrationsMutation.error || rollbackMigrationMutation.error,
  };
};

/**
 * useOfflineStatus Hook - Network Status Monitoring
 *
 * Monitor network connectivity and provide offline capabilities with intelligent
 * sync when connection is restored.
 */

import { useState, useEffect, useCallback } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { logger } from "../../utils/logging";

// Network quality enum
export enum NetworkQuality {
  EXCELLENT = "excellent",
  GOOD = "good",
  FAIR = "fair",
  POOR = "poor",
  OFFLINE = "offline",
}

// Connection type
export enum ConnectionType {
  WIFI = "wifi",
  CELLULAR = "cellular",
  ETHERNET = "ethernet",
  BLUETOOTH = "bluetooth",
  UNKNOWN = "unknown",
}

// Offline status interface
export interface OfflineStatus {
  isOnline: boolean;
  isOffline: boolean;
  networkQuality: NetworkQuality;
  connectionType: ConnectionType;
  downlink: number;
  rtt: number;
  effectiveType: string;
  lastOnline: Date | null;
  offlineDuration: number;
  hasPendingSync: boolean;
  syncQueueSize: number;
}

// Offline capabilities
export interface OfflineCapabilities {
  canReadCache: boolean;
  canWriteCache: boolean;
  canQueueOperations: boolean;
  estimatedStorageUsage: number;
  maxStorageLimit: number;
}

// Network event
export interface NetworkEvent {
  type: "online" | "offline" | "quality-change";
  timestamp: Date;
  details?: Record<string, string | number | boolean>;
}

/**
 * Network Status Hook
 */
export const useOfflineStatus = () => {
  const queryClient = useQueryClient();
  const [networkEvents, setNetworkEvents] = useState<NetworkEvent[]>([]);
  const [syncQueue, setSyncQueue] = useState<Record<string, unknown>[]>([]);
  const [lastOnline, setLastOnline] = useState<Date | null>(null);

  // Get network information if available
  const getNetworkInfo = useCallback(() => {
    const nav = navigator as Navigator & {
      connection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
      mozConnection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
      webkitConnection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
    };
    const connection =
      nav.connection || nav.mozConnection || nav.webkitConnection;

    return {
      downlink: connection?.downlink || 0,
      rtt: connection?.rtt || 0,
      effectiveType: connection?.effectiveType || "unknown",
      type: connection?.type || ConnectionType.UNKNOWN,
    };
  }, []);

  // Determine network quality
  const getNetworkQuality = useCallback(
    (downlink: number, rtt: number): NetworkQuality => {
      if (!navigator.onLine) return NetworkQuality.OFFLINE;

      if (downlink >= 10 && rtt < 100) return NetworkQuality.EXCELLENT;
      if (downlink >= 5 && rtt < 200) return NetworkQuality.GOOD;
      if (downlink >= 1.5 && rtt < 500) return NetworkQuality.FAIR;
      return NetworkQuality.POOR;
    },
    [],
  );

  // Current offline status query
  const { data: offlineStatus } = useQuery<OfflineStatus>({
    queryKey: ["network", "status"],
    queryFn: () => {
      const networkInfo = getNetworkInfo();
      const quality = getNetworkQuality(networkInfo.downlink, networkInfo.rtt);
      const now = new Date();

      const offlineDuration =
        lastOnline && !navigator.onLine
          ? now.getTime() - lastOnline.getTime()
          : 0;

      return {
        isOnline: navigator.onLine,
        isOffline: !navigator.onLine,
        networkQuality: quality,
        connectionType: networkInfo.type,
        downlink: networkInfo.downlink,
        rtt: networkInfo.rtt,
        effectiveType: networkInfo.effectiveType,
        lastOnline,
        offlineDuration,
        hasPendingSync: syncQueue.length > 0,
        syncQueueSize: syncQueue.length,
      };
    },
    refetchInterval: 5000, // Check every 5 seconds
    staleTime: 1000,
  });

  // Offline capabilities query
  const { data: capabilities } = useQuery<OfflineCapabilities>({
    queryKey: ["network", "capabilities"],
    queryFn: async () => {
      let storageUsage = 0;
      let storageLimit = 0;

      if ("storage" in navigator && "estimate" in navigator.storage) {
        try {
          const estimate = await navigator.storage.estimate();
          storageUsage = estimate.usage || 0;
          storageLimit = estimate.quota || 0;
        } catch (error) {
          logger.warn("Could not estimate storage", error);
        }
      }

      return {
        canReadCache: "caches" in window,
        canWriteCache: "caches" in window && navigator.onLine,
        canQueueOperations: true,
        estimatedStorageUsage: storageUsage,
        maxStorageLimit: storageLimit,
      };
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Add network event
  const addNetworkEvent = useCallback(
    (
      type: NetworkEvent["type"],
      details?: Record<string, string | number | boolean>,
    ) => {
      const event: NetworkEvent = {
        type,
        timestamp: new Date(),
        details,
      };

      setNetworkEvents((prev) => [...prev.slice(-49), event]); // Keep last 50 events
      logger.info(`Network event: ${type}`, details);
    },
    [],
  );

  // Handle online event
  const handleOnline = useCallback(() => {
    setLastOnline(new Date());
    addNetworkEvent("online", {
      wasOffline: !navigator.onLine,
      syncQueueSize: syncQueue.length,
    });

    queryClient.invalidateQueries({ queryKey: ["network"] });

    // Process sync queue when back online
    if (syncQueue.length > 0) {
      processSyncQueue();
    }
  }, [syncQueue, addNetworkEvent, queryClient, processSyncQueue]);

  // Handle offline event
  const handleOffline = useCallback(() => {
    addNetworkEvent("offline", {
      lastOnline: lastOnline?.toISOString(),
      networkInfo: getNetworkInfo(),
    });

    queryClient.invalidateQueries({ queryKey: ["network"] });
  }, [lastOnline, addNetworkEvent, getNetworkInfo, queryClient]);

  // Process sync queue
  const processSyncQueue = useCallback(async () => {
    if (!navigator.onLine || syncQueue.length === 0) return;

    logger.info("Processing sync queue", { queueSize: syncQueue.length });

    const processedItems: Record<string, unknown>[] = [];

    for (const item of syncQueue) {
      try {
        // Here you would implement actual sync logic
        // For now, we'll just simulate processing
        await new Promise((resolve) => setTimeout(resolve, 100));
        processedItems.push(item);
        logger.debug("Sync item processed", { item });
      } catch (error) {
        logger.error("Failed to sync item", { item, error });
        break; // Stop processing on error
      }
    }

    // Remove processed items from queue
    setSyncQueue((prev) =>
      prev.filter((item) => !processedItems.includes(item)),
    );

    if (processedItems.length > 0) {
      queryClient.invalidateQueries({ queryKey: ["network"] });
      logger.info("Sync queue processed", {
        processedCount: processedItems.length,
        remainingCount: syncQueue.length - processedItems.length,
      });
    }
  }, [syncQueue, queryClient]);

  // Add item to sync queue
  const queueForSync = useCallback((item: Record<string, unknown>) => {
    setSyncQueue((prev) => [...prev, { ...item, queuedAt: new Date() }]);
    logger.debug("Item queued for sync", { item });
  }, []);

  // Clear sync queue
  const clearSyncQueue = useCallback(() => {
    setSyncQueue([]);
    queryClient.invalidateQueries({ queryKey: ["network"] });
    logger.info("Sync queue cleared");
  }, [queryClient]);

  // Retry sync
  const retrySync = useCallback(() => {
    if (navigator.onLine) {
      processSyncQueue();
    } else {
      logger.warn("Cannot retry sync while offline");
    }
  }, [processSyncQueue]);

  // Monitor connection changes
  useEffect(() => {
    const handleConnectionChange = () => {
      const networkInfo = getNetworkInfo();
      const newQuality = getNetworkQuality(
        networkInfo.downlink,
        networkInfo.rtt,
      );

      if (offlineStatus && newQuality !== offlineStatus.networkQuality) {
        addNetworkEvent("quality-change", {
          oldQuality: offlineStatus.networkQuality,
          newQuality,
          networkInfo,
        });
      }

      queryClient.invalidateQueries({ queryKey: ["network", "status"] });
    };

    // Listen for online/offline events
    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    // Listen for connection changes if supported
    const nav = navigator as Navigator & {
      connection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
      mozConnection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
      webkitConnection?: {
        downlink?: number;
        rtt?: number;
        effectiveType?: string;
        type?: string;
        addEventListener?: (type: string, listener: EventListener) => void;
      };
    };
    const connection =
      nav.connection || nav.mozConnection || nav.webkitConnection;
    if (connection) {
      connection.addEventListener("change", handleConnectionChange);
    }

    // Set initial online status
    if (navigator.onLine && !lastOnline) {
      setLastOnline(new Date());
    }

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
      if (connection) {
        connection.removeEventListener("change", handleConnectionChange);
      }
    };
    // addNetworkEvent, getNetworkInfo, getNetworkQuality are stable (no/stable deps)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [handleOnline, handleOffline, offlineStatus, queryClient, lastOnline]);

  return {
    // Status
    ...offlineStatus,
    capabilities,

    // Events
    networkEvents,

    // Sync management
    queueForSync,
    clearSyncQueue,
    retrySync,
    processSyncQueue,

    // Computed properties
    connectionStrength: offlineStatus?.networkQuality || NetworkQuality.OFFLINE,
    canPerformOperations: offlineStatus?.isOnline || false,
    needsSync: (offlineStatus?.syncQueueSize || 0) > 0,

    // Helper methods
    isGoodConnection:
      offlineStatus?.networkQuality === NetworkQuality.EXCELLENT ||
      offlineStatus?.networkQuality === NetworkQuality.GOOD,
    isPoorConnection: offlineStatus?.networkQuality === NetworkQuality.POOR,
    hasRecentEvents:
      networkEvents.filter((e) => Date.now() - e.timestamp.getTime() < 60000)
        .length > 0,
  };
};

/**
 * usePerformance Hook - Performance Monitoring
 *
 * Monitor application performance metrics, identify bottlenecks, and provide
 * optimization recommendations.
 */

import { useState, useEffect, useCallback } from "react";
import { useQuery } from "@tanstack/react-query";
import { logger } from "../../utils/logging";

// Performance metric types
export interface PerformanceMetrics {
  // Core Web Vitals
  lcp: number; // Largest Contentful Paint
  fid: number; // First Input Delay
  cls: number; // Cumulative Layout Shift

  // Loading performance
  domContentLoaded: number;
  loadComplete: number;
  firstPaint: number;
  firstContentfulPaint: number;

  // Memory usage
  memoryUsed: number;
  memoryTotal: number;
  memoryUsagePercent: number;

  // Network
  networkType: string;
  effectiveType: string;
  downlink: number;
  rtt: number;

  // Custom metrics
  componentRenderTime: number;
  apiResponseTime: number;

  timestamp: Date;
}

// Performance alert
export interface PerformanceAlert {
  id: string;
  type: "warning" | "critical";
  metric: keyof PerformanceMetrics;
  value: number;
  threshold: number;
  message: string;
  timestamp: Date;
}

// Performance recommendation
export interface PerformanceRecommendation {
  id: string;
  category: "loading" | "runtime" | "memory" | "network";
  priority: "low" | "medium" | "high";
  title: string;
  description: string;
  impact: string;
  effort: string;
}

// Performance thresholds
const PERFORMANCE_THRESHOLDS = {
  lcp: { good: 2500, poor: 4000 },
  fid: { good: 100, poor: 300 },
  cls: { good: 0.1, poor: 0.25 },
  memoryUsagePercent: { good: 70, poor: 90 },
  loadComplete: { good: 3000, poor: 6000 },
  apiResponseTime: { good: 500, poor: 2000 },
};

/**
 * Performance Monitoring Hook
 */
export const usePerformance = () => {
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);
  const [isMonitoring, setIsMonitoring] = useState(true);

  // Get current performance metrics
  const { data: metrics, isLoading } = useQuery<PerformanceMetrics>({
    queryKey: ["performance", "metrics"],
    queryFn: async () => {
      const perfMetrics: PerformanceMetrics = {
        lcp: 0,
        fid: 0,
        cls: 0,
        domContentLoaded: 0,
        loadComplete: 0,
        firstPaint: 0,
        firstContentfulPaint: 0,
        memoryUsed: 0,
        memoryTotal: 0,
        memoryUsagePercent: 0,
        networkType: "unknown",
        effectiveType: "unknown",
        downlink: 0,
        rtt: 0,
        componentRenderTime: 0,
        apiResponseTime: 0,
        timestamp: new Date(),
      };

      // Get performance timing
      if (performance.timing) {
        const timing = performance.timing;
        perfMetrics.domContentLoaded =
          timing.domContentLoadedEventEnd - timing.navigationStart;
        perfMetrics.loadComplete = timing.loadEventEnd - timing.navigationStart;
      }

      // Get paint timing
      const paintEntries = performance.getEntriesByType("paint");
      paintEntries.forEach((entry) => {
        if (entry.name === "first-paint") {
          perfMetrics.firstPaint = entry.startTime;
        } else if (entry.name === "first-contentful-paint") {
          perfMetrics.firstContentfulPaint = entry.startTime;
        }
      });

      // Get memory usage
      if ("memory" in performance) {
        const memory = (
          performance as Performance & {
            memory?: {
              usedJSHeapSize: number;
              totalJSHeapSize: number;
            };
          }
        ).memory;
        if (memory) {
          perfMetrics.memoryUsed = memory.usedJSHeapSize;
          perfMetrics.memoryTotal = memory.totalJSHeapSize;
          perfMetrics.memoryUsagePercent =
            (memory.usedJSHeapSize / memory.totalJSHeapSize) * 100;
        }
      }

      // Get network information
      const nav = navigator as Navigator & {
        connection?: {
          type?: string;
          effectiveType?: string;
          downlink?: number;
          rtt?: number;
        };
        mozConnection?: {
          type?: string;
          effectiveType?: string;
          downlink?: number;
          rtt?: number;
        };
        webkitConnection?: {
          type?: string;
          effectiveType?: string;
          downlink?: number;
          rtt?: number;
        };
      };
      const connection =
        nav.connection || nav.mozConnection || nav.webkitConnection;
      if (connection) {
        perfMetrics.networkType = connection.type || "unknown";
        perfMetrics.effectiveType = connection.effectiveType || "unknown";
        perfMetrics.downlink = connection.downlink || 0;
        perfMetrics.rtt = connection.rtt || 0;
      }

      return perfMetrics;
    },
    refetchInterval: isMonitoring ? 10000 : false, // Every 10 seconds
    staleTime: 5000,
  });

  // Generate performance recommendations
  const { data: recommendations = [] } = useQuery<PerformanceRecommendation[]>({
    queryKey: ["performance", "recommendations", metrics],
    queryFn: () => {
      if (!metrics) return [];

      const recs: PerformanceRecommendation[] = [];

      // Loading performance recommendations
      if (metrics.loadComplete > PERFORMANCE_THRESHOLDS.loadComplete.poor) {
        recs.push({
          id: "slow-loading",
          category: "loading",
          priority: "high",
          title: "Slow Page Loading",
          description: "Page load time exceeds recommended thresholds",
          impact: "High - affects user experience and engagement",
          effort: "Medium - requires code splitting and optimization",
        });
      }

      // Memory usage recommendations
      if (
        metrics.memoryUsagePercent >
        PERFORMANCE_THRESHOLDS.memoryUsagePercent.poor
      ) {
        recs.push({
          id: "high-memory",
          category: "memory",
          priority: "high",
          title: "High Memory Usage",
          description: "Application is using excessive memory",
          impact: "High - can cause crashes and slowdowns",
          effort: "High - requires memory leak investigation",
        });
      }

      // Network recommendations
      if (metrics.rtt > 500) {
        recs.push({
          id: "high-latency",
          category: "network",
          priority: "medium",
          title: "High Network Latency",
          description: "Network requests are slow",
          impact: "Medium - affects data loading",
          effort: "Low - implement caching strategies",
        });
      }

      return recs;
    },
    enabled: Boolean(metrics),
    staleTime: 60000, // 1 minute
  });

  // Check for performance issues and generate alerts
  const checkPerformanceAlerts = useCallback(
    (currentMetrics: PerformanceMetrics) => {
      const newAlerts: PerformanceAlert[] = [];

      // Check each threshold
      Object.entries(PERFORMANCE_THRESHOLDS).forEach(([key, thresholds]) => {
        const value = currentMetrics[key as keyof PerformanceMetrics] as number;

        if (value > thresholds.poor) {
          newAlerts.push({
            id: `${key}-critical-${Date.now()}`,
            type: "critical",
            metric: key as keyof PerformanceMetrics,
            value,
            threshold: thresholds.poor,
            message: `${key.toUpperCase()} is critically high: ${value.toFixed(2)}ms`,
            timestamp: new Date(),
          });
        } else if (value > thresholds.good) {
          newAlerts.push({
            id: `${key}-warning-${Date.now()}`,
            type: "warning",
            metric: key as keyof PerformanceMetrics,
            value,
            threshold: thresholds.good,
            message: `${key.toUpperCase()} exceeds good threshold: ${value.toFixed(2)}ms`,
            timestamp: new Date(),
          });
        }
      });

      if (newAlerts.length > 0) {
        setAlerts((prev) => [...prev.slice(-19), ...newAlerts]); // Keep last 20 alerts
      }
    },
    [],
  );

  // Monitor performance changes
  useEffect(() => {
    if (metrics && isMonitoring) {
      checkPerformanceAlerts(metrics);
    }
  }, [metrics, isMonitoring, checkPerformanceAlerts]);

  // Measure component render time
  const measureRenderTime = useCallback(
    (componentName: string, renderFn: () => void) => {
      const startTime = performance.now();
      renderFn();
      const endTime = performance.now();
      const renderTime = endTime - startTime;

      logger.debug(
        `Component ${componentName} render time: ${renderTime.toFixed(2)}ms`,
      );
      return renderTime;
    },
    [],
  );

  // Measure API response time
  const measureApiTime = useCallback(
    async <T>(apiCall: () => Promise<T>): Promise<T> => {
      const startTime = performance.now();
      try {
        const result = await apiCall();
        const endTime = performance.now();
        const responseTime = endTime - startTime;

        logger.debug(`API response time: ${responseTime.toFixed(2)}ms`);
        return { result, responseTime };
      } catch (error) {
        const endTime = performance.now();
        const responseTime = endTime - startTime;

        logger.warn(`API error after ${responseTime.toFixed(2)}ms`, error);
        throw error;
      }
    },
    [],
  );

  // Clear old alerts
  const clearOldAlerts = useCallback(() => {
    const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000);
    setAlerts((prev) => prev.filter((alert) => alert.timestamp > oneHourAgo));
  }, []);

  // Performance score calculation
  const performanceScore = useCallback(
    (currentMetrics: PerformanceMetrics): number => {
      if (!currentMetrics) return 0;

      let score = 100;

      // Deduct points for poor metrics
      Object.entries(PERFORMANCE_THRESHOLDS).forEach(([key, thresholds]) => {
        const value = currentMetrics[key as keyof PerformanceMetrics] as number;

        if (value > thresholds.poor) {
          score -= 30;
        } else if (value > thresholds.good) {
          score -= 15;
        }
      });

      return Math.max(0, score);
    },
    [],
  );

  return {
    // Current metrics
    metrics,
    isLoading,

    // Performance insights
    performanceScore: metrics ? performanceScore(metrics) : 0,
    recommendations,
    alerts,

    // Controls
    isMonitoring,
    setIsMonitoring,
    clearOldAlerts,

    // Measurement tools
    measureRenderTime,
    measureApiTime,

    // Computed properties
    isPerformant: metrics ? performanceScore(metrics) > 80 : false,
    hasIssues: alerts.length > 0,
    criticalIssues: alerts.filter((a) => a.type === "critical").length,
    warningIssues: alerts.filter((a) => a.type === "warning").length,

    // Quick checks
    isSlowLoading: metrics?.loadComplete
      ? metrics.loadComplete > PERFORMANCE_THRESHOLDS.loadComplete.good
      : false,
    isHighMemory: metrics?.memoryUsagePercent
      ? metrics.memoryUsagePercent >
        PERFORMANCE_THRESHOLDS.memoryUsagePercent.good
      : false,
    isSlowNetwork: metrics?.rtt ? metrics.rtt > 500 : false,
  };
};

import React, { useState } from "react";
import type { DBTask, TaskStatus } from "../../types/database";
import {
  FaCheckCircle,
  FaTimesCircle,
  FaTrophy,
  FaClock,
} from "../../utils/iconImport";

// Task status configuration type
interface TaskStatusConfig {
  icon: React.ReactNode;
  text: string;
  borderColor: string;
}

// Task priority styles type
interface TaskPriorityStyles {
  bgColor: string;
  textColor: string;
}

// Custom hook for task item logic
export const useTaskItem = (
  task: DBTask,
  onSubmit: (taskId: string, note: string) => void,
) => {
  const [note, setNote] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Task status configuration logic
  const getStatusConfig = (status: TaskStatus): TaskStatusConfig => {
    switch (status) {
      case "pending":
        return {
          icon: <FaClock className="text-nightly-aquamarine" />,
          text: "Pending",
          borderColor: "border-nightly-aquamarine",
        };
      case "submitted":
        return {
          icon: <FaClock className="text-yellow-400" />,
          text: "Submitted",
          borderColor: "border-yellow-400",
        };
      case "approved":
        return {
          icon: <FaCheckCircle className="text-green-400" />,
          text: "Approved",
          borderColor: "border-green-400",
        };
      case "rejected":
        return {
          icon: <FaTimesCircle className="text-red-400" />,
          text: "Rejected",
          borderColor: "border-red-400",
        };
      case "completed":
        return {
          icon: <FaTrophy className="text-nightly-lavender-floral" />,
          text: "Completed",
          borderColor: "border-nightly-lavender-floral",
        };
      default:
        return {
          icon: <FaClock className="text-gray-400" />,
          text: "Unknown",
          borderColor: "border-gray-400",
        };
    }
  };

  // Priority styling logic
  const getPriorityStyles = (priority: string): TaskPriorityStyles => {
    switch (priority) {
      case "critical":
        return {
          bgColor: "bg-red-500/20",
          textColor: "text-red-300",
        };
      case "high":
        return {
          bgColor: "bg-orange-500/20",
          textColor: "text-orange-300",
        };
      case "medium":
        return {
          bgColor: "bg-yellow-500/20",
          textColor: "text-yellow-300",
        };
      default:
        return {
          bgColor: "bg-gray-500/20",
          textColor: "text-gray-300",
        };
    }
  };

  // Submit handler logic
  const handleSubmit = async () => {
    setIsSubmitting(true);
    try {
      await onSubmit(task.id, note);
      setNote("");
    } finally {
      setIsSubmitting(false);
    }
  };

  // Derived values
  const statusConfig = getStatusConfig(task.status);
  const priorityStyles = task.priority
    ? getPriorityStyles(task.priority)
    : null;
  const isOverdue = task.dueDate && new Date() > task.dueDate;

  return {
    // State
    note,
    isSubmitting,

    // Actions
    setNote,
    handleSubmit,

    // Computed values
    statusConfig,
    priorityStyles,
    isOverdue: Boolean(isOverdue),

    // Helper functions (exposed for flexibility)
    getStatusConfig,
    getPriorityStyles,
  };
};

/**
 * Task Management Hook
 *
 * Extracts task CRUD operations and management logic from TaskManagement component.
 * Provides comprehensive task filtering, sorting, and bulk operations.
 */

import { useState, useCallback, useMemo, useRef, useEffect } from "react";
import { Task, TaskStatus, TaskPriority } from "@/types/core";
import { Timestamp } from "firebase/firestore";

// Filter and sort types
export interface TaskFilter {
  status?: TaskStatus[];
  priority?: TaskPriority[];
  assignedBy?: ("submissive" | "keyholder")[];
  hasDeadline?: boolean;
  searchText?: string;
}

export type TaskSortBy =
  | "createdAt"
  | "dueDate"
  | "priority"
  | "status"
  | "title";

export type SortDirection = "asc" | "desc";

// Input types for creating and updating tasks
export interface CreateTaskInput {
  title: string;
  description?: string;
  category?: string;
  priority?: TaskPriority;
  dueDate?: Date;
  assignedBy?: "submissive" | "keyholder";
}

export interface UpdateTaskInput {
  title?: string;
  description?: string;
  category?: string;
  priority?: TaskPriority;
  status?: TaskStatus;
  dueDate?: Date;
  submissiveNote?: string;
  keyholderFeedback?: string;
}

// Hook return interface
export interface UseTaskManagementReturn {
  // Data
  tasks: Task[];
  filteredTasks: Task[];
  isLoading: boolean;
  error: Error | null;

  // Actions
  createTask: (task: CreateTaskInput) => Promise<Task>;
  updateTask: (id: string, updates: UpdateTaskInput) => Promise<Task>;
  deleteTask: (id: string) => Promise<void>;
  assignTask: (taskId: string, wearerId: string) => Promise<void>;
  bulkAssign: (taskIds: string[], wearerId: string) => Promise<void>;
  completeTask: (id: string) => Promise<void>;

  // Filtering
  setFilter: (filter: TaskFilter) => void;
  currentFilter: TaskFilter;
  clearFilters: () => void;

  // Sorting
  setSortBy: (sort: TaskSortBy, direction?: SortDirection) => void;
  currentSort: TaskSortBy;
  currentDirection: SortDirection;

  // State
  isCreating: boolean;
  isUpdating: boolean;
  isDeleting: boolean;
}

/**
 * Task Management Hook
 *
 * @param keyholderMode - Whether to operate in keyholder mode (shows all wearer tasks)
 * @returns Task management interface with CRUD operations and filtering
 */
export function useTaskManagement(
  _keyholderMode: boolean = false,
): UseTaskManagementReturn {
  // State
  const [tasks, setTasks] = useState<Task[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);
  const [currentFilter, setCurrentFilter] = useState<TaskFilter>({});
  const [currentSort, setCurrentSort] = useState<TaskSortBy>("createdAt");
  const [currentDirection, setCurrentDirection] =
    useState<SortDirection>("desc");

  // Action states
  const [isCreating, setIsCreating] = useState<boolean>(false);
  const [isUpdating, setIsUpdating] = useState<boolean>(false);
  const [isDeleting, setIsDeleting] = useState<boolean>(false);

  // Use a ref to always have access to the latest tasks
  const tasksRef = useRef(tasks);
  useEffect(() => {
    tasksRef.current = tasks;
  }, [tasks]);

  // Filter tasks based on current filter
  const filteredTasks = useMemo(() => {
    let filtered = [...tasks];

    // Status filter
    if (currentFilter.status && currentFilter.status.length > 0) {
      filtered = filtered.filter((task) =>
        currentFilter.status!.includes(task.status),
      );
    }

    // Priority filter
    if (currentFilter.priority && currentFilter.priority.length > 0) {
      filtered = filtered.filter((task) =>
        currentFilter.priority!.includes(task.priority),
      );
    }

    // Assigned by filter
    if (currentFilter.assignedBy && currentFilter.assignedBy.length > 0) {
      filtered = filtered.filter((task) =>
        currentFilter.assignedBy!.includes(task.assignedBy),
      );
    }

    // Has deadline filter
    if (currentFilter.hasDeadline !== undefined) {
      filtered = filtered.filter((task) =>
        currentFilter.hasDeadline ? !!task.dueDate : !task.dueDate,
      );
    }

    // Search text filter
    if (currentFilter.searchText) {
      const searchLower = currentFilter.searchText.toLowerCase();
      filtered = filtered.filter(
        (task) =>
          task.title.toLowerCase().includes(searchLower) ||
          (task.description?.toLowerCase().includes(searchLower) ?? false) ||
          (task.category?.toLowerCase().includes(searchLower) ?? false),
      );
    }

    // Sort tasks
    filtered.sort((a, b) => {
      let comparison = 0;

      switch (currentSort) {
        case "createdAt":
          comparison =
            (a.createdAt?.getTime() ?? 0) - (b.createdAt?.getTime() ?? 0);
          break;
        case "dueDate":
          comparison =
            (a.dueDate?.getTime() ?? Infinity) -
            (b.dueDate?.getTime() ?? Infinity);
          break;
        case "priority": {
          const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
          comparison = priorityOrder[a.priority] - priorityOrder[b.priority];
          break;
        }
        case "status": {
          const statusOrder = {
            pending: 1,
            in_progress: 2,
            submitted: 3,
            approved: 4,
            rejected: 5,
            completed: 6,
            overdue: 7,
          };
          comparison = statusOrder[a.status] - statusOrder[b.status];
          break;
        }
        case "title":
          comparison = a.title.localeCompare(b.title);
          break;
      }

      return currentDirection === "asc" ? comparison : -comparison;
    });

    return filtered;
  }, [tasks, currentFilter, currentSort, currentDirection]);

  // Create a new task
  const createTask = useCallback(
    async (task: CreateTaskInput): Promise<Task> => {
      setIsCreating(true);
      setError(null);

      try {
        // Mock implementation - in production, this would call Firebase/API
        const newTask: Task = {
          id: `task-${Date.now()}`,
          userId: "current-user-id", // Would come from auth context
          title: task.title,
          description: task.description,
          category: task.category,
          status: TaskStatus.PENDING,
          priority: task.priority ?? TaskPriority.MEDIUM,
          assignedBy: task.assignedBy ?? "submissive",
          createdAt: Timestamp.fromDate(new Date()),
          dueDate: task.dueDate ? Timestamp.fromDate(task.dueDate) : undefined,
          isRecurring: false,
        };

        setTasks((prev) => {
          const newTasks = [...prev, newTask];
          tasksRef.current = newTasks; // Update ref immediately
          return newTasks;
        });
        return newTask;
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to create task");
        setError(error);
        throw error;
      } finally {
        setIsCreating(false);
      }
    },
    [],
  );

  // Update an existing task
  const updateTask = useCallback(
    async (id: string, updates: UpdateTaskInput): Promise<Task> => {
      setIsUpdating(true);
      setError(null);

      try {
        const currentTasks = tasksRef.current;
        const taskIndex = currentTasks.findIndex((t) => t.id === id);

        if (taskIndex === -1) {
          throw new Error(`Task with id ${id} not found`);
        }

        const updatedTask = { ...currentTasks[taskIndex], ...updates };
        const newTasks = [...currentTasks];
        newTasks[taskIndex] = updatedTask;

        setTasks(newTasks);
        tasksRef.current = newTasks; // Update ref immediately

        return updatedTask;
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to update task");
        setError(error);
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [],
  );

  // Delete a task
  const deleteTask = useCallback(async (id: string): Promise<void> => {
    setIsDeleting(true);
    setError(null);

    try {
      setTasks((prev) => {
        const newTasks = prev.filter((t) => t.id !== id);
        tasksRef.current = newTasks; // Update ref immediately
        return newTasks;
      });
    } catch (err) {
      const error =
        err instanceof Error ? err : new Error("Failed to delete task");
      setError(error);
      throw error;
    } finally {
      setIsDeleting(false);
    }
  }, []);

  // Assign a task to a wearer
  const assignTask = useCallback(
    async (taskId: string, _wearerId: string): Promise<void> => {
      setError(null);

      try {
        // In production, this would update the task's wearer assignment
        await updateTask(taskId, {
          // Custom field for wearer assignment (not in base Task type)
          // Would need to extend Task type or use metadata
        } as Partial<Task>);
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to assign task");
        setError(error);
        throw error;
      }
    },
    [updateTask],
  );

  // Bulk assign tasks to a wearer
  const bulkAssign = useCallback(
    async (taskIds: string[], wearerId: string): Promise<void> => {
      setError(null);

      try {
        await Promise.all(taskIds.map((id) => assignTask(id, wearerId)));
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to bulk assign tasks");
        setError(error);
        throw error;
      }
    },
    [assignTask],
  );

  // Complete a task
  const completeTask = useCallback(
    async (id: string): Promise<void> => {
      setError(null);

      try {
        await updateTask(id, {
          status: TaskStatus.COMPLETED,
        });
      } catch (err) {
        const error =
          err instanceof Error ? err : new Error("Failed to complete task");
        setError(error);
        throw error;
      }
    },
    [updateTask],
  );

  // Set filter
  const setFilter = useCallback((filter: TaskFilter) => {
    setCurrentFilter(filter);
  }, []);

  // Clear all filters
  const clearFilters = useCallback(() => {
    setCurrentFilter({});
  }, []);

  // Set sort
  const setSortBy = useCallback(
    (sort: TaskSortBy, direction?: SortDirection) => {
      setCurrentSort(sort);
      if (direction) {
        setCurrentDirection(direction);
      }
    },
    [],
  );

  // Simulate initial load
  useState(() => {
    setTimeout(() => setIsLoading(false), 100);
  });

  return {
    // Data
    tasks,
    filteredTasks,
    isLoading,
    error,

    // Actions
    createTask,
    updateTask,
    deleteTask,
    assignTask,
    bulkAssign,
    completeTask,

    // Filtering
    setFilter,
    currentFilter,
    clearFilters,

    // Sorting
    setSortBy,
    currentSort,
    currentDirection,

    // State
    isCreating,
    isUpdating,
    isDeleting,
  };
}

/**
 * useTheme Hook - Enhanced Theme Management
 *
 * Advanced theme management beyond basic store functionality, with dynamic themes,
 * user customization, and accessibility features.
 */

import { useCallback, useEffect, useMemo, useState } from "react";
import {
  useQuery,
  useMutation,
  useQueryClient,
  UseMutationResult,
} from "@tanstack/react-query";
import {
  Theme,
  CustomTheme,
  CustomThemeDefinition,
  ThemePreferences,
  AccessibilitySettings,
  EnhancedThemeState,
  ThemeCategory,
  FontScale,
  ColorPalette,
} from "../../types/theme";
import { logger } from "../../utils/logging";

// Default themes
const DEFAULT_LIGHT_THEME: Theme = {
  id: "default-light",
  name: "Light",
  category: ThemeCategory.LIGHT,
  colors: {
    primary: "#6366f1",
    secondary: "#8b5cf6",
    accent: "#06b6d4",
    background: "#ffffff",
