    lastPauseTime: undefined,
    nextPauseAvailable: undefined,
    cooldownRemaining: undefined,
  };

  return {
    pauseState,
    pauseStateLoading,
    pauseStateError,
    refreshPauseState,
    ...mockSessionData,
    mockPauseState,
  };
};

