/**
 * Keyholder Store
 * UI state management for keyholder mode functionality
 * Zustand store - handles keyholder UI state only
 */
import { create } from "zustand";
import { devtools } from "zustand/middleware";
import { sha256 } from "@/utils/helpers/hash";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("KeyholderStore");

// Helper functions for password operations
const handlePasswordCheck = async (
  passwordAttempt: string,
  storedHash: string,
  set: (state: Partial<KeyholderState>) => void,
  get: () => KeyholderState,
) => {
  const state = get();

  if (state.isCheckingPassword) {
    logger.debug("Password check already in progress");
    return;
  }

  set({ isCheckingPassword: true, keyholderMessage: "" });

  try {
    logger.debug("Checking keyholder password");

    if (!storedHash) {
      const message = "Error: No keyholder password is set in the database.";
      set({
        keyholderMessage: message,
        isCheckingPassword: false,
      });
      logger.warn("No keyholder password hash found");
      return;
    }

    const attemptHash = await sha256(passwordAttempt);

    if (attemptHash === storedHash) {
      set({
        isKeyholderModeUnlocked: true,
        keyholderMessage: "Controls are now unlocked.",
        isPasswordDialogOpen: false,
        passwordAttempt: "",
        isCheckingPassword: false,
      });
      logger.info("Keyholder password correct, mode unlocked");
    } else {
      set({
        keyholderMessage: "Incorrect password. Please try again.",
        passwordAttempt: "",
        isCheckingPassword: false,
      });
      logger.warn("Incorrect keyholder password attempt");
    }
  } catch (error) {
    const message = "Failed to check password. Please try again.";
    set({
      keyholderMessage: message,
      isCheckingPassword: false,
    });
    logger.error("Error checking keyholder password", {
      error: error as Error,
    });
  }
};

const handlePasswordSet = async (
  newPassword: string,
  onSave: (hash: string) => Promise<void>,
  set: (state: Partial<KeyholderState>) => void,
  get: () => KeyholderState,
) => {
  const state = get();

  if (state.isSavingPassword) {
    logger.debug("Password save already in progress");
    return;
  }

  if (!newPassword || newPassword.length < 6) {
    set({
      keyholderMessage: "Password must be at least 6 characters long.",
    });
    return;
  }

  set({ isSavingPassword: true, keyholderMessage: "" });

  try {
    logger.debug("Setting permanent keyholder password");

    const newHash = await sha256(newPassword);
    await onSave(newHash);

    set({
      keyholderMessage: "Permanent password has been updated successfully!",
      newPassword: "",
      confirmPassword: "",
      isPasswordSettingMode: false,
      isPasswordDialogOpen: false,
      isSavingPassword: false,
    });

    logger.info("Permanent keyholder password updated successfully");
  } catch (error) {
    const message = "Failed to update password. Please try again.";
    set({
      keyholderMessage: message,
      isSavingPassword: false,
    });
    logger.error("Error setting permanent password", {
      error: error as Error,
    });
  }
};

export interface KeyholderState {
  // UI State
  isKeyholderModeUnlocked: boolean;
  keyholderMessage: string;
  isPasswordDialogOpen: boolean;
  isPasswordSettingMode: boolean;

  // Form State
  passwordAttempt: string;
  newPassword: string;
  confirmPassword: string;

  // Loading States
  isCheckingPassword: boolean;
  isSavingPassword: boolean;
}

export interface KeyholderActions {
  // Password Management
  checkPassword: (passwordAttempt: string, storedHash: string) => Promise<void>;
  setTempPassword: (keyholderName: string) => Promise<string>;
  setPermanentPassword: (
    newPassword: string,
    onSave: (hash: string) => Promise<void>,
  ) => Promise<void>;

  // UI Actions
  unlockKeyholderMode: () => void;
  lockKeyholderControls: () => void;
  setMessage: (message: string) => void;
  clearMessage: () => void;

  // Dialog Management
  openPasswordDialog: () => void;
  closePasswordDialog: () => void;
  setPasswordSettingMode: (isSettingMode: boolean) => void;

  // Form Actions
  setPasswordAttempt: (password: string) => void;
  setNewPassword: (password: string) => void;
  setConfirmPassword: (password: string) => void;
  clearForm: () => void;

  // Reset
  resetStore: () => void;
}

export interface KeyholderStore extends KeyholderState, KeyholderActions {}

const initialState: KeyholderState = {
  isKeyholderModeUnlocked: false,
  keyholderMessage: "",
  isPasswordDialogOpen: false,
  isPasswordSettingMode: false,
  passwordAttempt: "",
  newPassword: "",
  confirmPassword: "",
  isCheckingPassword: false,
  isSavingPassword: false,
};

export const useKeyholderStore = create<KeyholderStore>()(
  devtools(
    (set, get) => ({
      ...initialState,

      // Password Management
      checkPassword: async (passwordAttempt: string, storedHash: string) => {
        await handlePasswordCheck(passwordAttempt, storedHash, set, get);
      },

      setTempPassword: async (keyholderName: string): Promise<string> => {
        try {
          logger.debug("Generating temporary keyholder password", {
            keyholderName,
          });

          const tempPassword = Math.random()
            .toString(36)
            .substring(2, 8)
            .toUpperCase();
          const message = `Your keyholder password is: ${tempPassword}. This is now the permanent password unless you set a custom one.`;

          set({ keyholderMessage: message });

          logger.info("Temporary keyholder password generated");
          return tempPassword;
        } catch (error) {
          const message = "Failed to generate temporary password.";
          set({ keyholderMessage: message });
          logger.error("Error generating temporary password", {
            error: error as Error,
          });
          throw error;
        }
      },

      setPermanentPassword: async (
        newPassword: string,
        onSave: (hash: string) => Promise<void>,
      ) => {
        await handlePasswordSet(newPassword, onSave, set, get);
      },

      // UI Actions
      unlockKeyholderMode: () => {
        set({
          isKeyholderModeUnlocked: true,
          keyholderMessage: "Controls are now unlocked.",
          isPasswordDialogOpen: false,
        });
        logger.info("Keyholder mode unlocked manually");
      },

      lockKeyholderControls: () => {
        set({
          isKeyholderModeUnlocked: false,
          keyholderMessage: "",
          isPasswordDialogOpen: false,
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
        });
        logger.info("Keyholder controls locked");
      },

      setMessage: (message: string) => {
        set({ keyholderMessage: message });
      },

      clearMessage: () => {
        set({ keyholderMessage: "" });
      },

      // Dialog Management
      openPasswordDialog: () => {
        set({
          isPasswordDialogOpen: true,
          keyholderMessage: "",
          passwordAttempt: "",
        });
      },

      closePasswordDialog: () => {
        set({
          isPasswordDialogOpen: false,
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
          isPasswordSettingMode: false,
        });
      },

      setPasswordSettingMode: (isSettingMode: boolean) => {
        set({
          isPasswordSettingMode: isSettingMode,
          keyholderMessage: "",
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
        });
      },

      // Form Actions
      setPasswordAttempt: (password: string) => {
        set({ passwordAttempt: password });
      },

      setNewPassword: (password: string) => {
        set({ newPassword: password });
      },

      setConfirmPassword: (password: string) => {
        set({ confirmPassword: password });
      },

      clearForm: () => {
        set({
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
          keyholderMessage: "",
        });
      },

      // Reset
      resetStore: () => {
        set(initialState);
        logger.debug("Keyholder store reset to initial state");
      },
    }),
    {
      name: "keyholder-store",
      // Only enable devtools in development
      enabled:
        import.meta.env.MODE === "development" ||
        import.meta.env.MODE === "nightly",
    },
  ),
);

/**
 * Notification Store - Temporary UI Feedback
 * Manages toast messages, alerts, and temporary UI feedback
 */
import { create } from "zustand";
import { devtools } from "zustand/middleware";

export interface Notification {
  id: string;
  type: "success" | "error" | "warning" | "info";
  priority: "low" | "medium" | "high" | "urgent";
  title?: string;
  message: string;
  duration?: number; // in milliseconds, 0 means persistent
  dismissible?: boolean;
  requireInteraction?: boolean; // for urgent priority
  position?:
    | "top-left"
    | "top-right"
    | "bottom-left"
    | "bottom-right"
    | "top-center"
    | "bottom-center";
  action?: {
    label: string;
    onClick: () => void;
  };
  icon?: string; // optional custom icon
  metadata?: Record<string, unknown>; // extensible metadata
  timestamp: Date;
}

export interface NotificationState {
  // Notifications list
  notifications: Notification[];

  // Configuration
  pauseOnHover?: boolean;

  // Actions
  addNotification: (
    notification: Omit<Notification, "id" | "timestamp">,
  ) => string;
  removeNotification: (id: string) => void;
  clearAllNotifications: () => void;
  updateNotification: (id: string, updates: Partial<Notification>) => void;

  // Convenience methods
  showSuccess: (message: string, title?: string, duration?: number) => string;
  showError: (message: string, title?: string, duration?: number) => string;
  showWarning: (message: string, title?: string, duration?: number) => string;
  showInfo: (message: string, title?: string, duration?: number) => string;

  // Reset function for testing
  resetStore: () => void;
}

// Additional type exports for compatibility with index.ts
export interface NotificationActions {
  addNotification: (
    notification: Omit<Notification, "id" | "timestamp">,
  ) => string;
  removeNotification: (id: string) => void;
  clearAllNotifications: () => void;
  showSuccess: (message: string, title?: string, duration?: number) => string;
  showError: (message: string, title?: string, duration?: number) => string;
  showWarning: (message: string, title?: string, duration?: number) => string;
  showInfo: (message: string, title?: string, duration?: number) => string;
}

export type NotificationStore = NotificationState;
export type NotificationType = "success" | "error" | "warning" | "info";
export type NotificationPriority = "low" | "medium" | "high" | "urgent";
export type NotificationAction = {
  label: string;
  onClick: () => void;
};

// Default durations for different notification priorities
const DEFAULT_DURATIONS = {
  low: 3000,
  medium: 5000,
  high: 7000,
  urgent: 0, // Persistent - requires interaction
};

// Fallback durations by type (legacy support)
const TYPE_DURATIONS = {
  success: 4000,
  error: 0, // Persistent for errors
  warning: 6000,
  info: 4000,
};

const initialState = {
  notifications: [],
};

export const useNotificationStore = create<NotificationState>()(
  devtools(
    (set, get) => ({
      // Initial state
      ...initialState,
      pauseOnHover: true,

      // Actions
      addNotification: (notification) => {
        const id = `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

        // Determine priority from type if not specified
        const priority = notification.priority || (
          notification.type === 'error' ? 'high' : 
          notification.type === 'warning' ? 'medium' : 'low'
        );

        // Set duration based on priority, with fallback to type
        const duration = notification.duration !== undefined 
          ? notification.duration 
          : DEFAULT_DURATIONS[priority] || TYPE_DURATIONS[notification.type];

        // Urgent priority requires interaction
        const requireInteraction = priority === 'urgent' || notification.requireInteraction;

        const newNotification: Notification = {
          id,
          timestamp: new Date(),
          priority,
          duration,
          dismissible: true,
          requireInteraction,
          ...notification,
        };

        set(
          (state) => ({
            notifications: [...state.notifications, newNotification],
          }),
          false,
          `addNotification:${notification.type}:${priority}`,
        );

        // Auto-remove notification after duration if specified and not requiring interaction
        if (newNotification.duration && newNotification.duration > 0 && !newNotification.requireInteraction) {
          setTimeout(() => {
            useNotificationStore.getState().removeNotification(id);
          }, newNotification.duration);
        }

        return id;
      },

      removeNotification: (id: string) =>
        set(
          (state) => ({
            notifications: state.notifications.filter((n) => n.id !== id),
          }),
          false,
          `removeNotification:${id}`,
        ),

      clearAllNotifications: () =>
        set({ notifications: [] }, false, "clearAllNotifications"),

      updateNotification: (id: string, updates: Partial<Notification>) =>
        set(
          (state) => ({
            notifications: state.notifications.map((n) =>
              n.id === id ? { ...n, ...updates } : n,
            ),
          }),
          false,
          `updateNotification:${id}`,
        ),

      // Convenience methods
      showSuccess: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "success",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showError: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "error",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showWarning: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "warning",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showInfo: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "info",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      // Reset function for testing
      resetStore: () => set(initialState, false, "resetStore"),
    }),
    {
      name: "notification-store",
    },
  ),
);

// Selector hooks for better performance
export const useNotifications = () =>
  useNotificationStore((state) => state.notifications);

export const useNotificationActions = () =>
  useNotificationStore((state) => ({
    addNotification: state.addNotification,
    removeNotification: state.removeNotification,
    clearAllNotifications: state.clearAllNotifications,
    showSuccess: state.showSuccess,
    showError: state.showError,
    showWarning: state.showWarning,
    showInfo: state.showInfo,
  }));

// Utility hooks for common notification patterns
export const useErrorHandler = () => {
  const { showError } = useNotificationActions();

  const handleError = (error: Error | string, title = "Error") => {
    const message = typeof error === "string" ? error : error.message;
    return showError(message, title);
  };

  return { handleError };
};

export const useSuccessHandler = () => {
  const { showSuccess } = useNotificationActions();

  const handleSuccess = (message: string, title = "Success") => {
    return showSuccess(message, title);
  };

  return { handleSuccess };
};

