/**
 * Achievement Dashboard Component
 * Overview of user's achievement progress and recent unlocks
 */

import React from "react";
import { Link } from "react-router-dom";
import {
  FaTrophy,
  FaFire,
  FaBullseye,
  FaClock,
  FaStar,
  FaArrowRight,
} from "../../utils/iconImport";
import { useAchievements } from "../../hooks/useAchievements";
import { useAuthState } from "../../contexts";
import { AchievementCategory, UserAchievement, Achievement } from "../../types";

export const AchievementDashboard: React.FC = () => {
  const { user } = useAuthState();
  const {
    achievementStats,
    userAchievements,
    unreadNotifications,
    allAchievements,
    getAchievementsByCategory,
    isLoading,
  } = useAchievements(user?.uid);

  if (isLoading || !achievementStats) {
    return (
      <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-gray-300 rounded w-1/3 mb-4"></div>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-20 bg-gray-300 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const recentAchievements = userAchievements
    .sort(
      (a: UserAchievement, b: UserAchievement) =>
        b.earnedAt.toDate().getTime() - a.earnedAt.toDate().getTime(),
    )
    .slice(0, 3);

  const categories: AchievementCategory[] = [
    AchievementCategory.SESSION_MILESTONES,
    AchievementCategory.CONSISTENCY_BADGES,
    AchievementCategory.STREAK_ACHIEVEMENTS,
    AchievementCategory.GOAL_BASED,
    AchievementCategory.TASK_COMPLETION,
    AchievementCategory.SPECIAL_ACHIEVEMENTS,
  ];

  const categoryProgress = categories.map((category) => {
    const categoryAchievements = getAchievementsByCategory(category);
    const earned = achievementStats.categoryCounts[category] || 0;
    const total = categoryAchievements.length;
    const percentage = total > 0 ? (earned / total) * 100 : 0;

    return {
      category,
      earned,
      total,
      percentage,
      name: getCategoryDisplayName(category),
      icon: getCategoryIcon(category),
    };
  });

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <FaTrophy className="text-2xl text-nightly-lavender-floral" />
          <h2 className="text-2xl font-bold text-nightly-honeydew">
            Achievements
          </h2>
        </div>
        <Link
          to="/achievements"
          className="flex items-center space-x-2 text-nightly-aquamarine hover:text-nightly-lavender-floral transition-colors"
        >
          <span>View All</span>
          <FaArrowRight />
        </Link>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-gradient-to-br from-nightly-aquamarine/20 to-nightly-aquamarine/10 rounded-lg p-4 border border-nightly-aquamarine/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-nightly-celadon">Total Earned</p>
              <p className="text-2xl font-bold text-nightly-honeydew">
                {achievementStats.totalEarned}
              </p>
            </div>
            <FaTrophy className="text-2xl text-nightly-aquamarine" />
          </div>
        </div>

        <div className="bg-gradient-to-br from-nightly-lavender-floral/20 to-nightly-lavender-floral/10 rounded-lg p-4 border border-nightly-lavender-floral/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-nightly-celadon">Total Points</p>
              <p className="text-2xl font-bold text-nightly-honeydew">
                {achievementStats.totalPoints}
              </p>
            </div>
            <FaStar className="text-2xl text-nightly-lavender-floral" />
          </div>
        </div>

        <div className="bg-gradient-to-br from-nightly-spring-green/20 to-nightly-spring-green/10 rounded-lg p-4 border border-nightly-spring-green/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-nightly-celadon">Completion</p>
              <p className="text-2xl font-bold text-nightly-honeydew">
                {achievementStats.completionPercentage.toFixed(0)}%
              </p>
            </div>
            <FaBullseye className="text-2xl text-nightly-spring-green" />
          </div>
        </div>

        <div className="bg-gradient-to-br from-red-500/20 to-red-500/10 rounded-lg p-4 border border-red-500/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-nightly-celadon">New Unlocks</p>
              <p className="text-2xl font-bold text-nightly-honeydew">
                {unreadNotifications.length}
              </p>
            </div>
            <FaFire className="text-2xl text-red-400" />
          </div>
        </div>
      </div>

      {/* Recent Achievements */}
      {recentAchievements.length > 0 && (
        <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
          <h3 className="text-lg font-semibold text-nightly-honeydew mb-4">
            Recent Achievements
          </h3>
          <div className="space-y-3">
            {recentAchievements.map((userAchievement: UserAchievement) => {
              const achievement = allAchievements.find(
                (a: Achievement) => a.id === userAchievement.achievementId,
              );
              if (!achievement) return null;

              return (
                <div
                  key={userAchievement.id}
                  className="flex items-center space-x-3 p-3 bg-white/5 rounded-lg"
                >
                  <div className="text-2xl">{achievement.icon}</div>
                  <div className="flex-1">
                    <h4 className="font-semibold text-nightly-honeydew">
                      {achievement.name}
                    </h4>
                    <p className="text-sm text-nightly-celadon">
                      {achievement.description}
                    </p>
                    <div className="flex items-center space-x-2 mt-1">
                      <span className="text-xs bg-yellow-500/20 text-yellow-300 px-2 py-1 rounded">
                        +{achievement.points} points
                      </span>
                      <span className="text-xs text-nightly-celadon">
                        {formatDate(userAchievement.earnedAt.toDate())}
                      </span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}

      {/* Category Progress */}
      <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
        <h3 className="text-lg font-semibold text-nightly-honeydew mb-4">
          Progress by Category
        </h3>
        <div className="space-y-4">
          {categoryProgress.map((category) => (
            <div key={category.category} className="space-y-2">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  {category.icon}
                  <span className="text-nightly-celadon">{category.name}</span>
                </div>
                <span className="text-sm text-nightly-celadon">
                  {category.earned} / {category.total}
                </span>
              </div>
              <div className="w-full bg-gray-700 rounded-full h-2">
                <div
                  className="bg-gradient-to-r from-nightly-aquamarine to-nightly-lavender-floral h-2 rounded-full transition-all duration-300"
                  style={{ width: `${category.percentage}%` }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

function getCategoryDisplayName(category: AchievementCategory): string {
  switch (category) {
    case "session_milestones":
      return "Session Milestones";
    case "consistency_badges":
      return "Consistency";
    case "streak_achievements":
      return "Streaks";
    case "goal_based":
      return "Goals";
    case "task_completion":
      return "Tasks";
    case "special_achievements":
      return "Special";
    default:
      return "Unknown";
  }
}

function getCategoryIcon(category: AchievementCategory) {
  switch (category) {
    case "session_milestones":
      return <FaClock className="text-nightly-aquamarine" />;
    case "consistency_badges":
      return <FaBullseye className="text-nightly-lavender-floral" />;
    case "streak_achievements":
      return <FaFire className="text-red-400" />;
    case "goal_based":
      return <FaTrophy className="text-yellow-400" />;
    case "task_completion":
      return <FaStar className="text-green-400" />;
    case "special_achievements":
      return <FaStar className="text-purple-400" />;
    default:
      return <FaTrophy className="text-gray-400" />;
  }
}

function formatDate(date: Date): string {
  const now = new Date();
  const diffInMs = now.getTime() - date.getTime();
  const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24));

  if (diffInDays === 0) {
    return "Today";
  } else if (diffInDays === 1) {
    return "Yesterday";
  } else if (diffInDays < 7) {
    return `${diffInDays} days ago`;
  } else {
    return date.toLocaleDateString();
  }
}

export default AchievementDashboard;

/**
 * Conflict Resolution Modal
 * User interface for resolving data sync conflicts
 */
import React, { useState } from "react";
import type { ConflictInfo } from "@/types/database";

interface ConflictResolutionProps {
  conflicts: ConflictInfo[];
  onResolve: (resolutions: Record<string, "local" | "remote">) => void;
  onCancel: () => void;
  isOpen: boolean;
}

const formatTimestamp = (date: Date): string => {
  return new Intl.DateTimeFormat("en-US", {
    dateStyle: "short",
    timeStyle: "short",
  }).format(date);
};

const formatValue = (value: unknown): string => {
  if (value === null || value === undefined) return "Not set";
  if (typeof value === "boolean") return value ? "Yes" : "No";
  if (typeof value === "object") return JSON.stringify(value, null, 2);
  return String(value);
};

export const ConflictResolutionModal: React.FC<ConflictResolutionProps> = ({
  conflicts,
  onResolve,
  onCancel,
  isOpen,
}) => {
  const [resolutions, setResolutions] = useState<
    Record<string, "local" | "remote">
  >({});

  if (!isOpen || conflicts.length === 0) {
    return null;
  }

  const handleResolve = () => {
    if (Object.keys(resolutions).length === conflicts.length) {
      onResolve(resolutions);
    }
  };

  const handleResolutionChange = (
    conflictId: string,
    resolution: "local" | "remote",
  ) => {
    setResolutions((prev) => ({
      ...prev,
      [conflictId]: resolution,
    }));
  };

  const getDeviceLabel = (isLocal: boolean): string => {
    return isLocal ? "This Device" : "Other Device";
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-800 border border-purple-700 rounded-lg shadow-xl max-w-4xl w-full mx-4 max-h-[90vh] overflow-hidden">
        <div className="p-6 border-b border-purple-700">
          <h2 className="text-xl font-semibold text-purple-300">
            Data Sync Conflicts Detected
          </h2>
          <p className="text-gray-400 mt-2">
            Your data was modified on multiple devices. Please choose which
            version to keep for each conflict:
          </p>
        </div>

        <div className="p-6 overflow-y-auto max-h-[60vh]">
          <div className="space-y-6">
            {conflicts.map((conflict, index) => {
              const conflictId = `${conflict.collection}-${conflict.documentId}-${index}`;
              const localTimestamp = new Date(
                conflict.localData.lastModified as string,
              );
              const remoteTimestamp = new Date(
                conflict.remoteData.lastModified as string,
              );

              return (
                <div
                  key={conflictId}
                  className="border border-gray-700 rounded-lg p-4"
                >
                  <h3 className="text-lg font-medium text-purple-200 mb-3">
                    {conflict.collection.charAt(0).toUpperCase() +
                      conflict.collection.slice(1)}{" "}
                    Conflict
                  </h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* Local Version */}
                    <label className="cursor-pointer">
                      <input
                        type="radio"
                        name={conflictId}
                        value="local"
                        checked={resolutions[conflictId] === "local"}
                        onChange={() =>
                          handleResolutionChange(conflictId, "local")
                        }
                        className="sr-only"
                      />
                      <div
                        className={`border-2 rounded-lg p-4 transition-colors ${
                          resolutions[conflictId] === "local"
                            ? "border-purple-500 bg-purple-500/10"
                            : "border-gray-600 hover:border-purple-400"
                        }`}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <span className="font-medium text-purple-300">
                            {getDeviceLabel(true)}
                          </span>
                          <span className="text-sm text-gray-400">
                            {formatTimestamp(localTimestamp)}
                          </span>
                        </div>

                        <div className="bg-gray-900 rounded p-3 text-sm">
                          <pre className="whitespace-pre-wrap text-gray-300 overflow-x-auto">
                            {formatValue(conflict.localData)}
                          </pre>
                        </div>
                      </div>
                    </label>

                    {/* Remote Version */}
                    <label className="cursor-pointer">
                      <input
                        type="radio"
                        name={conflictId}
                        value="remote"
                        checked={resolutions[conflictId] === "remote"}
                        onChange={() =>
                          handleResolutionChange(conflictId, "remote")
                        }
                        className="sr-only"
                      />
                      <div
                        className={`border-2 rounded-lg p-4 transition-colors ${
                          resolutions[conflictId] === "remote"
                            ? "border-purple-500 bg-purple-500/10"
                            : "border-gray-600 hover:border-purple-400"
                        }`}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <span className="font-medium text-purple-300">
                            {getDeviceLabel(false)}
                          </span>
                          <span className="text-sm text-gray-400">
                            {formatTimestamp(remoteTimestamp)}
                          </span>
                        </div>

                        <div className="bg-gray-900 rounded p-3 text-sm">
                          <pre className="whitespace-pre-wrap text-gray-300 overflow-x-auto">
                            {formatValue(conflict.remoteData)}
                          </pre>
                        </div>
                      </div>
                    </label>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        <div className="p-6 border-t border-purple-700 flex justify-between items-center">
          <div className="text-sm text-gray-400">
            {Object.keys(resolutions).length} of {conflicts.length} conflicts
            resolved
          </div>

          <div className="flex space-x-3">
            <button
              onClick={onCancel}
              className="px-4 py-2 text-gray-400 hover:text-white transition-colors"
            >
              Cancel
            </button>
            <button
              onClick={handleResolve}
              disabled={Object.keys(resolutions).length !== conflicts.length}
              className="px-6 py-2 bg-purple-600 hover:bg-purple-700 disabled:bg-gray-600 disabled:cursor-not-allowed text-white rounded-lg transition-colors"
            >
              Resolve Conflicts
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// src/components/feedback/FeedbackFAB.tsx

import React, { useState } from "react";
import { FaPlus, FaBug, FaLightbulb, FaComment } from "../../utils/iconImport";
import FeedbackModal from "./FeedbackModal";
// TODO: Replace with proper hook - components shouldn't import services directly
// import { FeedbackService } from "../../services/feedbackService";
import type { FeedbackType, FeedbackData } from "../../types/feedback";
import { logger } from "../../utils/logging";

const FeedbackFAB: React.FC = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [feedbackType, setFeedbackType] = useState<FeedbackType | null>(null);

  const openFeedback = (type: FeedbackType) => {
    setFeedbackType(type);
    setShowModal(true);
    setIsExpanded(false);
  };

  const handleFeedbackSubmit = async (feedback: FeedbackData) => {
    try {
      // TODO: Replace with proper hook - components shouldn't call services directly
      // await FeedbackService.submitFeedback(feedback);
      logger.info(
        "Feedback submission temporarily disabled - needs hook implementation",
        feedback,
      );
    } catch (error) {
      logger.error("Failed to submit feedback", error);
      // Error handling - could show a toast notification
      throw error;
    }
  };

  return (
    <>
      {/* Main FAB */}
      <div className="fixed bottom-6 right-6 z-40">
        <div
          className={`transition-all duration-300 ${isExpanded ? "space-y-3" : "space-y-0"}`}
        >
          {/* Expanded Options */}
          {isExpanded && (
            <div className="space-y-3">
              <button
                onClick={() => openFeedback("bug")}
                className="flex items-center gap-2 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full shadow-lg transition-all transform translate-y-0 opacity-100"
              >
                <FaBug />
                <span className="text-sm font-medium">Report Bug</span>
              </button>

              <button
                onClick={() => openFeedback("feature")}
                className="flex items-center gap-2 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full shadow-lg transition-all transform translate-y-0 opacity-100"
              >
                <FaLightbulb />
                <span className="text-sm font-medium">Suggest Feature</span>
              </button>

              <button
                onClick={() => openFeedback("general")}
                className="flex items-center gap-2 bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full shadow-lg transition-all transform translate-y-0 opacity-100"
              >
                <FaComment />
                <span className="text-sm font-medium">General Feedback</span>
              </button>
            </div>
          )}

          {/* Main Button */}
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className={`w-14 h-14 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-lg flex items-center justify-center transition-all ${
              isExpanded ? "rotate-45" : "rotate-0"
            }`}
            aria-label="Open feedback options"
          >
            <FaPlus className="text-xl" />
          </button>
        </div>
      </div>

      {/* Backdrop for closing when expanded */}
      {isExpanded && (
        <div
          className="fixed inset-0 z-30"
          onClick={() => setIsExpanded(false)}
        />
      )}

      {/* Feedback Modal */}
      {showModal && (
        <FeedbackModal
          type={feedbackType}
          onClose={() => {
            setShowModal(false);
            setFeedbackType(null);
          }}
          onSubmit={handleFeedbackSubmit}
        />
      )}
    </>
  );
};

export default FeedbackFAB;

// src/components/feedback/FeedbackModal.tsx

import React, { useState } from "react";
import {
  FaBug,
  FaLightbulb,
  FaComment,
  FaTimes,
  FaPaperPlane,
} from "../../utils/iconImport";
import type {
  FeedbackModalProps,
  FeedbackData,
  FeedbackType,
} from "../../types/feedback";
import { collectSystemInfo } from "../../utils/systemInfo";
import { logger } from "../../utils/logging";

// Form field components
interface FormFieldProps {
  label: string;
  required?: boolean;
  children: React.ReactNode;
}

const FormField: React.FC<FormFieldProps> = ({ label, required, children }) => (
  <div>
    <label className="block text-sm font-medium text-gray-300 mb-2">
      {label} {required && <span className="text-red-400">*</span>}
    </label>
    {children}
  </div>
);

interface TextInputProps {
  value: string;
  onChange: (value: string) => void;
  placeholder: string;
  disabled?: boolean;
  required?: boolean;
}

const TextInput: React.FC<TextInputProps> = ({
  value,
  onChange,
  placeholder,
  disabled,
  required,
}) => (
  <input
    type="text"
    value={value}
    onChange={(e) => onChange(e.target.value)}
    className="w-full bg-white/5 border border-white/10 rounded p-3 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
    placeholder={placeholder}
    required={required}
    disabled={disabled}
  />
);

interface TextAreaProps {
  value: string;
  onChange: (value: string) => void;
  placeholder: string;
  rows?: number;
  disabled?: boolean;
  required?: boolean;
}

const TextArea: React.FC<TextAreaProps> = ({
  value,
  onChange,
  placeholder,
  rows = 4,
  disabled,
  required,
}) => (
  <textarea
    value={value}
    onChange={(e) => onChange(e.target.value)}
    className="w-full bg-white/5 border border-white/10 rounded p-3 text-white placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
    rows={rows}
    placeholder={placeholder}
    required={required}
    disabled={disabled}
  />
);

// Priority selector component
interface PrioritySelectorProps {
  value: string;
  onChange: (value: string) => void;
  disabled?: boolean;
}

const PrioritySelector: React.FC<PrioritySelectorProps> = ({
  value,
  onChange,
  disabled,
}) => (
  <select
    value={value}
    onChange={(e) => onChange(e.target.value)}
    className="w-full bg-white/5 border border-white/10 rounded p-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
    disabled={disabled}
  >
    <option value="low">Low - Minor inconvenience</option>
    <option value="medium">Medium - Affects functionality</option>
    <option value="high">High - Prevents core features</option>
  </select>
);

// Bug-specific fields component
interface BugFieldsProps {
  formData: {
    steps: string;
    expected: string;
    actual: string;
    priority: string;
  };
  onUpdateField: (field: string, value: string) => void;
  disabled: boolean;
}

const BugFields: React.FC<BugFieldsProps> = ({
  formData,
  onUpdateField,
  disabled,
}) => (
  <>
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <FormField label="Steps to Reproduce">
        <TextArea
          value={formData.steps}
          onChange={(value) => onUpdateField("steps", value)}
          placeholder="1. Go to...&#10;2. Click on...&#10;3. See error"
          rows={3}
          disabled={disabled}
        />
      </FormField>

      <FormField label="Priority">
        <PrioritySelector
          value={formData.priority}
          onChange={(value) => onUpdateField("priority", value)}
          disabled={disabled}
        />
      </FormField>
    </div>

    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <FormField label="Expected Behavior">
        <TextArea
          value={formData.expected}
          onChange={(value) => onUpdateField("expected", value)}
          placeholder="What should have happened?"
          rows={2}
          disabled={disabled}
        />
      </FormField>

      <FormField label="Actual Behavior">
        <TextArea
          value={formData.actual}
          onChange={(value) => onUpdateField("actual", value)}
          placeholder="What actually happened?"
          rows={2}
          disabled={disabled}
        />
      </FormField>
    </div>
  </>
);

// Helper functions extracted from main component
const getModalTitle = (type: FeedbackType): string => {
  switch (type) {
    case "bug":
      return "Report a Bug";
    case "feature":
      return "Suggest a Feature";
    case "general":
      return "General Feedback";
    default:
      return "Feedback";
  }
};

const getModalIcon = (type: FeedbackType): React.ReactElement => {
  switch (type) {
    case "bug":
      return <FaBug className="text-red-400" />;
    case "feature":
      return <FaLightbulb className="text-blue-400" />;
    case "general":
      return <FaComment className="text-green-400" />;
    default:
      return <FaComment className="text-green-400" />;
  }
};

const getTitlePlaceholder = (type: FeedbackType): string => {
  switch (type) {
    case "bug":
      return "Brief description of the bug";
    case "feature":
      return "Short title for your feature request";
    case "general":
      return "Summary of your feedback";
    default:
      return "Brief title";
  }
};

const getDescriptionPlaceholder = (type: FeedbackType): string => {
  switch (type) {
    case "bug":
      return "Describe what happened and what you expected to happen";
    case "feature":
      return "Describe your feature idea and how it would help";
    case "general":
      return "Share your thoughts and suggestions";
    default:
      return "Describe your feedback";
  }
};

const getTitleLabel = (type: FeedbackType): string => {
  switch (type) {
    case "bug":
      return "Bug Summary";
    case "feature":
      return "Feature Title";
    default:
      return "Feedback Title";
  }
};

// Form content component
interface FeedbackFormProps {
  type: FeedbackType;
  formData: {
    title: string;
    description: string;
    steps: string;
    expected: string;
    actual: string;
    priority: string;
