const getSubmitButtonText = (
  type: FeedbackType,
  isSubmitting: boolean,
): string => {
  if (isSubmitting) return "Submitting...";

  switch (type) {
    case "bug":
      return "Submit Bug Report";
    case "feature":
      return "Submit Feature Request";
    default:
      return "Submit Feedback";
  }
};

const FeedbackModal: React.FC<FeedbackModalProps> = ({
  type,
  onClose,
  onSubmit,
}) => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    steps: "", // For bug reports
    expected: "", // For bug reports
    actual: "", // For bug reports
    priority: "medium" as "low" | "medium" | "high",
    category: "",
    contactEmail: "",
    includeSystemInfo: true,
    screenshot: null as File | null,
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  if (!type) return null;

  const updateField = (
    field: string,
    value: string | boolean | File | null,
  ) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const feedbackData: FeedbackData = {
        type,
        ...formData,
        systemInfo: formData.includeSystemInfo
          ? await collectSystemInfo()
          : null,
        timestamp: new Date(),
        userAgent: navigator.userAgent,
        url: window.location.pathname,
      };

      await onSubmit(feedbackData);
      onClose();
    } catch (error) {
      logger.error("Failed to submit feedback", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-gradient-to-br from-gray-900 to-gray-800 max-w-2xl w-full max-h-[90vh] rounded-lg border border-gray-600 overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-600">
          <div className="flex items-center gap-3">
            {getModalIcon(type)}
            <h2 className="text-xl font-bold text-white">
              {getModalTitle(type)}
            </h2>
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-white/10 rounded-lg transition-colors"
            disabled={isSubmitting}
          >
            <FaTimes className="text-gray-400" />
          </button>
        </div>

        <FeedbackForm
          type={type}
          formData={formData}
          updateField={updateField}
          isSubmitting={isSubmitting}
          onSubmit={handleSubmit}
          onClose={onClose}
        />
      </div>
    </div>
  );
};

export default FeedbackModal;

import React from "react";
import {
  FaCalendarAlt,
  FaTrophy,
  FaLock,
  FaBan,
  FaCheckCircle,
  FaSpinner,
} from "@/utils/iconImport";
import { useSpecialChallenges } from "@/hooks/useSpecialChallenges";
import { logger } from "@/utils/logging";

interface SpecialChallengeSectionProps {
  userId: string | null;
}

export const SpecialChallengeSection: React.FC<
  SpecialChallengeSectionProps
> = ({ userId }) => {
  const {
    challengeStatus,
    isLoading,
    error,
    joinChallenge,
    getChallengeProgress,
  } = useSpecialChallenges(userId);

  if (isLoading) {
    return (
      <div className="bg-white/10 backdrop-blur-xs border-white/20 p-4 rounded-lg">
        <div className="flex items-center justify-center py-8">
          <FaSpinner className="animate-spin text-2xl text-blue-400" />
          <span className="ml-2 text-blue-200">Loading challenges...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-900/20 border-red-500/50 p-4 rounded-lg">
        <p className="text-red-300">Error loading challenges: {error}</p>
      </div>
    );
  }

  const handleJoinChallenge = async (
    challengeType: "locktober" | "no_nut_november",
  ) => {
    try {
      await joinChallenge(challengeType);
    } catch (err) {
      // Error is handled by the hook
      logger.error(
        "Failed to join challenge",
        { error: err },
        "SpecialChallengeSection",
      );
    }
  };

  const ChallengeCard: React.FC<{
    type: "locktober" | "no_nut_november";
    title: string;
    description: string;
    icon: React.ReactNode;
    month: string;
    colorClasses: string;
  }> = ({ type, title, description, icon, month, colorClasses }) => {
    const challengeMap = {
      locktober: challengeStatus.locktober,
      no_nut_november: challengeStatus.noNutNovember,
    };
    const challenge = challengeMap[type as keyof typeof challengeMap];
    const progress = getChallengeProgress(type);

    return (
      <div className={`p-4 rounded-lg border ${colorClasses}`}>
        <div className="flex items-center mb-3">
          {icon}
          <h3 className="text-xl font-bold ml-2">{title}</h3>
        </div>

        <p className="text-sm opacity-80 mb-4">{description}</p>

        <div className="flex items-center text-sm mb-3">
          <FaCalendarAlt className="mr-2" />
          <span>Active during {month}</span>
        </div>

        {!challenge.available && (
          <div className="text-center py-3">
            <p className="text-sm opacity-60">Available during {month} only</p>
          </div>
        )}

        {challenge.available && !challenge.active && !challenge.completed && (
          <button
            onClick={() => handleJoinChallenge(type)}
            className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-200 flex items-center justify-center"
          >
            <FaTrophy className="mr-2" />
            Join Challenge
          </button>
        )}

        {challenge.active && (
          <div>
            <div className="mb-3">
              <div className="flex justify-between text-sm mb-1">
                <span>Progress</span>
                <span>{progress.toFixed(1)}%</span>
              </div>
              <div className="w-full bg-gray-700 rounded-full h-2">
                <div
                  className="bg-gradient-to-r from-green-400 to-blue-500 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${Math.min(100, progress)}%` }}
                />
              </div>
            </div>
            <div className="text-center py-2">
              <div className="flex items-center justify-center text-green-400">
                <FaCheckCircle className="mr-2" />
                <span className="font-semibold">Challenge Active!</span>
              </div>
              <p className="text-xs opacity-70 mt-1">
                Keep going to complete the challenge
              </p>
            </div>
          </div>
        )}

        {challenge.completed && (
          <div className="text-center py-3">
            <div className="flex items-center justify-center text-gold-400 mb-2">
              <FaTrophy className="text-2xl mr-2" />
              <span className="text-lg font-bold">Completed!</span>
            </div>
            <p className="text-sm opacity-80">
              Congratulations on completing the challenge!
            </p>
          </div>
        )}
      </div>
    );
  };

  // Only show section if at least one challenge is available or active
  const shouldShow =
    challengeStatus.locktober.available ||
    challengeStatus.noNutNovember.available ||
    challengeStatus.locktober.active ||
    challengeStatus.noNutNovember.active ||
    challengeStatus.locktober.completed ||
    challengeStatus.noNutNovember.completed;

  if (!shouldShow) {
    return null;
  }

  return (
    <div className="bg-white/10 backdrop-blur-xs border-white/20 p-4 rounded-lg">
      <h2 className="text-2xl font-bold mb-4 flex items-center">
        <FaCalendarAlt className="mr-2 text-purple-400" />
        Special Challenges
      </h2>

      <p className="text-sm opacity-80 mb-6">
        Take on month-long challenges for special achievements and recognition!
      </p>

      <div className="grid gap-4 md:grid-cols-2">
        <ChallengeCard
          type="locktober"
          title="Locktober"
          description="Stay locked in chastity for the entire month of October"
          icon={<FaLock className="text-2xl text-orange-400" />}
          month="October"
          colorClasses="bg-orange-900/20 border-orange-500/50 text-orange-100"
        />

        <ChallengeCard
          type="no_nut_november"
          title="No Nut November"
          description="Abstain from orgasms for the entire month of November"
          icon={<FaBan className="text-2xl text-blue-400" />}
          month="November"
          colorClasses="bg-blue-900/20 border-blue-500/50 text-blue-100"
        />
      </div>
    </div>
  );
};

/**
 * Account Linking Component
 * Manages keyholder-submissive relationship creation and management
 */
import React from "react";
import { useKeyholderRelationships } from "../../hooks/useKeyholderRelationships";
import { getAccountLinkingState } from "./AccountLinkingHelpers";
import { AccountLinkingLoading } from "./AccountLinkingLoading";
import { AccountLinkingHeader } from "./AccountLinkingHeader";
import { LinkingMessageDisplay } from "./LinkingMessageDisplay";
import { RelationshipSummary } from "./RelationshipSummary";
import { ActiveKeyholderDisplay } from "./ActiveKeyholderDisplay";
import { InviteCodeCreationSection } from "./InviteCodeCreationSection";
import { ActiveInviteCodesDisplay } from "./ActiveInviteCodesDisplay";
import { AcceptInviteCodeSection } from "./AcceptInviteCodeSection";
import { SubmissiveRelationshipsDisplay } from "./SubmissiveRelationshipsDisplay";
import { AccountLinkingHelp } from "./AccountLinkingHelp";

interface AccountLinkingProps {
  className?: string;
}

export const AccountLinking: React.FC<AccountLinkingProps> = ({
  className = "",
}) => {
  const {
    relationships,
    activeKeyholder,
    activeInviteCodes,
    relationshipSummary,
    isLoading,
    isCreatingInvite,
    isAcceptingInvite,
    inviteCodeInput,
    keyholderNameInput,
    message,
    messageType,
    createInviteCode,
    acceptInviteCode,
    revokeInviteCode,
    endRelationship,
    setInviteCodeInput,
    setKeyholderNameInput,
    clearMessage,
    validateInviteCode,
  } = useKeyholderRelationships();

  // Helper functions to reduce complexity
  const handleCreateInvite = async () => {
    await createInviteCode(24); // 24 hour expiration
  };

  const handleAcceptInvite = async () => {
    if (!validateInviteCode(inviteCodeInput)) {
      return;
    }
    await acceptInviteCode(inviteCodeInput, keyholderNameInput);
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
    } catch {
      // Fallback for older browsers
      const textArea = document.createElement("textarea");
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      document.execCommand("copy");
      document.body.removeChild(textArea);
    }
  };

  const linkingState = getAccountLinkingState(
    relationships,
    activeKeyholder,
    activeInviteCodes,
    message,
    messageType,
  );

  if (isLoading) {
    return <AccountLinkingLoading className={className} />;
  }

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Header */}
      <AccountLinkingHeader />

      {/* Messages */}
      <LinkingMessageDisplay
        message={message}
        messageType={messageType}
        onClearMessage={clearMessage}
      />

      {/* Relationship Summary */}
      <RelationshipSummary relationshipSummary={relationshipSummary} />

      {/* Active Keyholder Relationship */}
      {activeKeyholder && (
        <ActiveKeyholderDisplay
          activeKeyholder={{
            ...activeKeyholder,
            permissions: activeKeyholder.permissions as unknown as Record<
              string,
              boolean
            >,
          }}
          onEndRelationship={endRelationship}
        />
      )}

      {/* Create Invite Code Section */}
      <InviteCodeCreationSection
        shouldShow={!linkingState.hasActiveKeyholder}
        isCreatingInvite={isCreatingInvite}
        onCreateInvite={handleCreateInvite}
      />

      {/* Active Invite Codes */}
      <ActiveInviteCodesDisplay
        activeInviteCodes={activeInviteCodes}
        onCopyCode={copyToClipboard}
        onRevokeCode={revokeInviteCode}
      />

      {/* Accept Invite Code Section */}
      <AcceptInviteCodeSection
        inviteCodeInput={inviteCodeInput}
        keyholderNameInput={keyholderNameInput}
        isAcceptingInvite={isAcceptingInvite}
        onSetInviteCodeInput={setInviteCodeInput}
        onSetKeyholderNameInput={setKeyholderNameInput}
        onAcceptInvite={handleAcceptInvite}
        validateInviteCode={validateInviteCode}
      />

      {/* Submissive Relationships */}
      <SubmissiveRelationshipsDisplay
        relationships={relationships.asKeyholder.map((rel) => ({
          ...rel,
          permissions: rel.permissions as unknown as Record<string, boolean>,
        }))}
        onEndRelationship={endRelationship}
      />

      {/* Help Section */}
      <AccountLinkingHelp />
    </div>
  );
};

export default AccountLinking;

