/**
 * Bottom Sheet Component
 * Mobile-optimized modal that slides up from the bottom
 */
import React, { useEffect } from "react";
import { createPortal } from "react-dom";
import { useViewport } from "../../hooks/mobile/useViewport";
import { useHapticFeedback } from "../../hooks/mobile/useHapticFeedback";
import { useTouchGestures } from "../../hooks/mobile/useTouchGestures";

interface BottomSheetProps {
  isOpen: boolean;
  onClose: () => void;
  children: React.ReactNode;
  title?: string;
  maxHeight?: string;
  preventClose?: boolean;
  className?: string;
}

export const BottomSheet: React.FC<BottomSheetProps> = ({
  isOpen,
  onClose,
  children,
  title,
  maxHeight = "80vh",
  preventClose = false,
  className = "",
}) => {
  const { isMobile, safeAreaInsets } = useViewport();
  const { light } = useHapticFeedback();

  // Handle swipe down to close
  const { onTouchStart, onTouchMove, onTouchEnd } = useTouchGestures(
    {
      onSwipeDown: () => {
        if (!preventClose) {
          light();
          onClose();
        }
      },
    },
    {
      threshold: 50,
      minDistance: 100,
    },
  );

  // Handle escape key
  useEffect(() => {
    if (!isOpen) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape" && !preventClose) {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [isOpen, onClose, preventClose]);

  // Prevent body scroll when open
  useEffect(() => {
    if (isOpen && isMobile) {
      document.body.style.overflow = "hidden";
      return () => {
        document.body.style.overflow = "";
      };
    }
    return undefined;
  }, [isOpen, isMobile]);

  if (!isOpen) return null;

  const bottomSheetContent = (
    <div className="fixed inset-0 z-50 flex items-end justify-center">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black/50 backdrop-blur-sm transition-opacity duration-300"
        onClick={!preventClose ? onClose : undefined}
        style={{
          paddingBottom: safeAreaInsets.bottom,
        }}
      />

      {/* Bottom Sheet */}
      <div
        className={`
          relative w-full bg-white dark:bg-gray-900 
          rounded-t-3xl shadow-xl transform transition-transform duration-300 ease-out
          ${isOpen ? "translate-y-0" : "translate-y-full"}
          ${className}
        `}
        style={{
          maxHeight,
          paddingBottom: safeAreaInsets.bottom,
        }}
        onTouchStart={onTouchStart}
        onTouchMove={onTouchMove}
        onTouchEnd={onTouchEnd}
      >
        {/* Handle */}
        <div className="flex justify-center pt-4 pb-2">
          <div className="w-12 h-1 bg-gray-300 dark:bg-gray-600 rounded-full" />
        </div>

        {/* Header */}
        {title && (
          <div className="flex items-center justify-between px-6 pb-4">
            <h2 className="text-lg font-semibold text-gray-900 dark:text-white">
              {title}
            </h2>
            {!preventClose && (
              <button
                onClick={onClose}
                className="touch-target p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
                aria-label="Close"
              >
                <svg
                  className="w-5 h-5 text-gray-500"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>
            )}
          </div>
        )}

        {/* Content */}
        <div
          className="overflow-auto"
          style={{ maxHeight: "calc(80vh - 120px)" }}
        >
          <div className="px-6 pb-6">{children}</div>
        </div>
      </div>
    </div>
  );

  return createPortal(bottomSheetContent, document.body);
};

export default BottomSheet;

/**
 * Mobile Button Component
 * Touch-optimized button with haptic feedback
 */
import React, { forwardRef } from "react";
import { useHapticFeedback } from "../../hooks/mobile/useHapticFeedback";

interface MobileButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "outline" | "ghost" | "danger";
  size?: "sm" | "md" | "lg" | "xl";
  fullWidth?: boolean;
  loading?: boolean;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  hapticFeedback?: "light" | "medium" | "heavy" | "none";
}

export const MobileButton = forwardRef<HTMLButtonElement, MobileButtonProps>(
  (
    {
      variant = "primary",
      size = "md",
      fullWidth = false,
      loading = false,
      leftIcon,
      rightIcon,
      hapticFeedback = "light",
      children,
      className = "",
      onClick,
      disabled,
      ...props
    },
    ref,
  ) => {
    const { light, medium, heavy } = useHapticFeedback();

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      if (disabled || loading) return;

      // Trigger haptic feedback
      if (hapticFeedback !== "none") {
        switch (hapticFeedback) {
          case "light":
            light();
            break;
          case "medium":
            medium();
            break;
          case "heavy":
            heavy();
            break;
        }
      }

      onClick?.(e);
    };

    const sizeClasses = {
      sm: "h-10 px-4 text-sm font-medium",
      md: "h-12 px-6 text-base font-semibold",
      lg: "h-14 px-8 text-lg font-semibold",
      xl: "h-16 px-10 text-xl font-bold",
    };

    const variantClasses = {
      primary: `
      bg-tekhelet hover:bg-tekhelet-600 active:bg-tekhelet-700
      text-white border-2 border-tekhelet
      shadow-lg hover:shadow-xl active:shadow-md
    `,
      secondary: `
      bg-lavender_web hover:bg-lavender_web-600 active:bg-lavender_web-700
      text-dark_purple border-2 border-lavender_web
      shadow-lg hover:shadow-xl active:shadow-md
    `,
      outline: `
      bg-transparent hover:bg-tekhelet/10 active:bg-tekhelet/20
      text-tekhelet border-2 border-tekhelet
      hover:text-white hover:bg-tekhelet
    `,
      ghost: `
      bg-transparent hover:bg-gray-100 dark:hover:bg-gray-800 active:bg-gray-200 dark:active:bg-gray-700
      text-gray-700 dark:text-gray-300 border-2 border-transparent
    `,
      danger: `
      bg-red-500 hover:bg-red-600 active:bg-red-700
      text-white border-2 border-red-500
      shadow-lg hover:shadow-xl active:shadow-md
    `,
    };

    const baseClasses = `
    relative
    inline-flex
    items-center
    justify-center
    min-h-[44px]
    rounded-xl
    font-medium
    transition-all
    duration-200
    ease-in-out
    focus:outline-none
    focus-visible:ring-2
    focus-visible:ring-purple-500
    focus-visible:ring-offset-2
    focus-visible:ring-offset-white
    dark:focus-visible:ring-offset-gray-900
    transform
    active:scale-98
    disabled:opacity-50
    disabled:cursor-not-allowed
    disabled:transform-none
    ${fullWidth ? "w-full" : ""}
    ${sizeClasses[size]}
    ${variantClasses[variant]}
    ${className}
  `;

    return (
      <button
        ref={ref}
        className={baseClasses}
        onClick={handleClick}
        disabled={disabled || loading}
        {...props}
      >
        {/* Loading Spinner */}
        {loading && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-5 h-5 border-2 border-current border-t-transparent rounded-full animate-spin" />
          </div>
        )}

        {/* Content */}
        <div
          className={`flex items-center space-x-2 ${loading ? "opacity-0" : "opacity-100"}`}
        >
          {leftIcon && <span className="flex-shrink-0">{leftIcon}</span>}

          {children && <span className="truncate">{children}</span>}

          {rightIcon && <span className="flex-shrink-0">{rightIcon}</span>}
        </div>

        {/* Ripple effect overlay */}
        <div className="absolute inset-0 rounded-xl overflow-hidden">
          <div className="absolute inset-0 bg-white/20 transform scale-0 group-active:scale-100 transition-transform duration-150 ease-out" />
        </div>
      </button>
    );
  },
);

MobileButton.displayName = "MobileButton";

export default MobileButton;

/**
 * Mobile Input Component
 * Touch-optimized form input with mobile-specific enhancements
 */
import React, { forwardRef } from "react";

interface MobileInputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "size"> {
  label?: string;
  error?: string;
  helpText?: string;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  size?: "sm" | "md" | "lg";
  variant?: "default" | "filled" | "borderless";
}

// Helper function to get input mode based on type
const getInputMode = (
  type: string,
): React.HTMLAttributes<HTMLInputElement>["inputMode"] => {
  switch (type) {
    case "email":
      return "email";
    case "tel":
      return "tel";
    case "number":
      return "numeric";
    case "url":
      return "url";
    default:
      return "text";
  }
};

// Helper function to get autocomplete value
const getAutoComplete = (
  type: string,
  existingAutoComplete?: string,
): string => {
  if (existingAutoComplete) return existingAutoComplete;

  switch (type) {
    case "email":
      return "email";
    case "tel":
      return "tel";
    case "password":
      return "current-password";
    default:
      return "off";
  }
};

// Helper function to get border and focus styles
const getBorderStyles = (error?: string): string => {
  if (error) return "border-red-500 focus:border-red-500";
  return "focus:border-purple-500";
};

// Helper function to get border radius
const getBorderRadius = (variant: string): string => {
  return variant !== "borderless" ? "rounded-lg" : "";
};

export const MobileInput = forwardRef<HTMLInputElement, MobileInputProps>(
  (
    {
      label,
      error,
      helpText,
      leftIcon,
      rightIcon,
      size = "md",
      variant = "default",
      className = "",
      type = "text",
      ...props
    },
    ref,
  ) => {
    const sizeClasses = {
      sm: "h-10 px-3 text-sm",
