/**
 * Pull to Refresh Component
 * Wraps content with pull-to-refresh functionality
 */
import React from "react";
import { usePullToRefresh } from "../../hooks/mobile/usePullToRefresh";
import { useViewport } from "../../hooks/mobile/useViewport";

interface PullToRefreshProps {
  children: React.ReactNode;
  onRefresh: () => Promise<void> | void;
  disabled?: boolean;
  threshold?: number;
  maxPullDistance?: number;
  className?: string;
}

// Helper function to calculate indicator transform
const getIndicatorTransform = (pullDistance: number): string => {
  const translateY = Math.min(pullDistance - 40, 20);
  return `translateX(-50%) translateY(${translateY}px)`;
};

// Helper function to get content transform
const getContentTransform = (
  isPulling: boolean,
  isRefreshing: boolean,
  pullDistance: number,
): string => {
  if (isPulling && !isRefreshing) {
    return `translateY(${pullDistance * 0.5}px)`;
  }
  return "translateY(0)";
};

// Helper function to determine if indicator should be shown
const shouldShowIndicator = (
  isMobile: boolean,
  isPulling: boolean,
  isRefreshing: boolean,
): boolean => {
  return isMobile && (isPulling || isRefreshing);
};

export const PullToRefresh: React.FC<PullToRefreshProps> = ({
  children,
  onRefresh,
  disabled = false,
  threshold = 80,
  maxPullDistance = 120,
  className = "",
}) => {
  const { isMobile } = useViewport();

  const {
    isRefreshing,
    isPulling,
    pullDistance,
    pullPercentage,
    onTouchStart,
    onTouchMove,
    onTouchEnd,
    onScroll,
  } = usePullToRefresh({
    onRefresh,
    disabled: disabled || !isMobile,
    threshold,
    maxPullDistance,
  });

  const refreshIndicatorOpacity = Math.min(pullPercentage / 100, 1);
  const refreshIndicatorScale = Math.min(0.5 + (pullPercentage / 100) * 0.5, 1);

  return (
    <div
      className={`relative overflow-hidden ${className}`}
      onTouchStart={onTouchStart}
      onTouchMove={onTouchMove}
      onTouchEnd={onTouchEnd}
      onScroll={onScroll}
    >
      {/* Pull to refresh indicator */}
      {shouldShowIndicator(isMobile, isPulling, isRefreshing) && (
        <div
          className="absolute top-0 left-1/2 transform -translate-x-1/2 z-10 transition-all duration-200 ease-out"
          style={{
            transform: getIndicatorTransform(pullDistance),
            opacity: isRefreshing ? 1 : refreshIndicatorOpacity,
          }}
        >
          <div
            className="flex items-center justify-center w-10 h-10 bg-white dark:bg-gray-800 rounded-full shadow-lg border border-gray-200 dark:border-gray-700"
            style={{
              transform: `scale(${isRefreshing ? 1 : refreshIndicatorScale})`,
            }}
          >
            {isRefreshing ? (
              <div className="w-5 h-5 border-2 border-purple-500 border-t-transparent rounded-full animate-spin" />
            ) : (
              <svg
                className={`w-5 h-5 text-purple-500 transition-transform duration-200 ${
                  pullPercentage >= 100 ? "rotate-180" : ""
                }`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 14l-7 7m0 0l-7-7m7 7V3"
                />
              </svg>
            )}
          </div>
        </div>
      )}

      {/* Content with transform during pull */}
      <div
        className="transition-transform duration-200 ease-out"
        style={{
          transform: getContentTransform(isPulling, isRefreshing, pullDistance),
        }}
      >
        {children}
      </div>

      {/* Loading overlay during refresh */}
      {isRefreshing && (
        <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-purple-500 to-pink-500 animate-pulse" />
      )}
    </div>
  );
};

export default PullToRefresh;

/**
 * Swipeable Card Component
 * Card that responds to swipe gestures for actions
 */
import React, { useState, useRef } from "react";
import { useTouchGestures } from "../../hooks/mobile/useTouchGestures";
import { useHapticFeedback } from "../../hooks/mobile/useHapticFeedback";

interface SwipeAction {
  id: string;
  label: string;
  icon?: React.ReactNode;
  color: "red" | "green" | "blue" | "yellow" | "purple";
  action: () => void;
}

interface SwipeableCardProps {
  children: React.ReactNode;
  className?: string;
  leftActions?: SwipeAction[];
  rightActions?: SwipeAction[];
  onSwipeThreshold?: number;
  disabled?: boolean;
}

export const SwipeableCard: React.FC<SwipeableCardProps> = ({
  children,
  className = "",
  leftActions = [],
  rightActions = [],
  onSwipeThreshold: _onSwipeThreshold = 0.3,
  disabled = false,
}) => {
  const [translateX, setTranslateX] = useState(0);
  const [isRevealed, setIsRevealed] = useState(false);
  const [revealedSide, setRevealedSide] = useState<"left" | "right" | null>(
    null,
  );
  const cardRef = useRef<HTMLDivElement>(null);
  const _startX = useRef(0);
  const { medium, success } = useHapticFeedback();

  const maxSwipeDistance = 120; // Maximum pixels to swipe

  const { onTouchStart, onTouchMove, onTouchEnd } = useTouchGestures(
    {
      onSwipeLeft: () => {
        if (disabled || rightActions.length === 0) return;
        handleSwipeReveal("right");
      },
      onSwipeRight: () => {
        if (disabled || leftActions.length === 0) return;
        handleSwipeReveal("left");
      },
    },
    {
      threshold: 20,
      minDistance: 50,
    },
  );

  const handleSwipeReveal = (side: "left" | "right") => {
    if (isRevealed && revealedSide === side) {
      // Close if already revealed on the same side
      closeActions();
    } else {
      // Reveal actions
      medium();
      setIsRevealed(true);
      setRevealedSide(side);
      setTranslateX(side === "left" ? maxSwipeDistance : -maxSwipeDistance);
    }
  };

  const closeActions = () => {
    setIsRevealed(false);
    setRevealedSide(null);
    setTranslateX(0);
  };

  const handleActionClick = (action: SwipeAction) => {
    success();
    action.action();
    closeActions();
  };

  const actionColorClasses = {
    red: "bg-red-500 hover:bg-red-600 text-white",
    green: "bg-green-500 hover:bg-green-600 text-white",
    blue: "bg-blue-500 hover:bg-blue-600 text-white",
    yellow: "bg-yellow-500 hover:bg-yellow-600 text-black",
    purple: "bg-purple-500 hover:bg-purple-600 text-white",
  };

  const renderActions = (actions: SwipeAction[], side: "left" | "right") => (
    <div
      className={`
      absolute inset-y-0 flex items-center
      ${side === "left" ? "left-0" : "right-0"}
    `}
    >
      {actions.map((action, index) => (
        <button
          key={action.id}
          onClick={() => handleActionClick(action)}
          className={`
            touch-target
            flex flex-col items-center justify-center
            w-16 h-full
            text-xs font-medium
            transition-all duration-200
            ${actionColorClasses[action.color]}
            ${index === 0 && side === "left" ? "rounded-l-lg" : ""}
            ${index === actions.length - 1 && side === "right" ? "rounded-r-lg" : ""}
          `}
          style={{
            transform: `translateX(${side === "left" ? -100 + index * 16 : 100 - index * 16}px)`,
          }}
        >
          {action.icon && <div className="mb-1">{action.icon}</div>}
          <span className="leading-tight">{action.label}</span>
        </button>
      ))}
    </div>
  );

  return (
    <div className={`relative overflow-hidden ${className}`}>
      {/* Background Actions */}
      {leftActions.length > 0 && renderActions(leftActions, "left")}
      {rightActions.length > 0 && renderActions(rightActions, "right")}

      {/* Main Card Content */}
      <div
        ref={cardRef}
        className={`
          relative z-10
          bg-white dark:bg-gray-800
          transition-transform duration-300 ease-out
          ${!disabled ? "touch-target" : ""}
        `}
        style={{
          transform: `translateX(${translateX}px)`,
        }}
        onTouchStart={onTouchStart}
        onTouchMove={onTouchMove}
        onTouchEnd={onTouchEnd}
        onClick={isRevealed ? closeActions : undefined}
      >
        {children}
      </div>

      {/* Overlay when actions are revealed */}
      {isRevealed && (
        <div
          className="absolute inset-0 z-20 bg-transparent"
          onClick={closeActions}
        />
      )}
    </div>
  );
};

export default SwipeableCard;

/**
 * Touch Target Component
 * Ensures minimum touch target size for mobile accessibility
 */
/// <reference types="react" />
import React from "react";
import { useHapticFeedback } from "../../hooks/mobile/useHapticFeedback";

interface TouchTargetProps {
  children: React.ReactNode;
  onTap?: () => void;
  onLongPress?: () => void;
  className?: string;
  disabled?: boolean;
  hapticFeedback?: "light" | "medium" | "heavy" | "none";
  as?: keyof React.JSX.IntrinsicElements;
  [key: string]: unknown; // For additional props
}

export const TouchTarget: React.FC<TouchTargetProps> = ({
  children,
  onTap,
  onLongPress,
  className = "",
  disabled = false,
  hapticFeedback = "light",
  as: Component = "button" as keyof React.JSX.IntrinsicElements,
  ...props
}) => {
  const { light, medium, heavy } = useHapticFeedback();

  const handleClick = () => {
    if (disabled) return;

    // Trigger haptic feedback
    if (hapticFeedback !== "none") {
      switch (hapticFeedback) {
        case "light":
          light();
          break;
        case "medium":
          medium();
          break;
        case "heavy":
          heavy();
          break;
      }
    }

    onTap?.();
  };

  const handleContextMenu = (e: React.MouseEvent) => {
    if (disabled) return;
    if (onLongPress) {
      e.preventDefault();
      onLongPress();
    }
  };

  const baseClasses = `
    touch-target
    relative
    inline-flex
    items-center
    justify-center
    min-h-[44px]
    min-w-[44px]
    transition-all
    duration-150
    ease-in-out
    focus:outline-none
    focus-visible:ring-2
    focus-visible:ring-purple-500
    focus-visible:ring-offset-2
    ${disabled ? "opacity-50 cursor-not-allowed" : "cursor-pointer active:scale-98"}
    ${className}
  `;

  return (
    <Component
      className={baseClasses}
      onClick={handleClick}
      onContextMenu={handleContextMenu}
      disabled={disabled}
      {...props}
    >
      {children}
    </Component>
  );
};

export default TouchTarget;

/**
 * Virtual List Component
 * Optimized for mobile performance with large lists
 */
import React, { useState, useEffect, useRef, useCallback } from "react";
import { useViewport } from "../../hooks/mobile/useViewport";

interface VirtualListProps<T> {
  items: T[];
  renderItem: (item: T, index: number) => React.ReactNode;
  itemHeight?: number;
  overscan?: number;
  className?: string;
  onEndReached?: () => void;
  endReachedThreshold?: number;
  loading?: boolean;
  loadingComponent?: React.ReactNode;
  emptyComponent?: React.ReactNode;
}

export function VirtualList<T>({
  items,
  renderItem,
  itemHeight = 80,
  overscan = 5,
  className = "",
  onEndReached,
  endReachedThreshold = 0.8,
  loading = false,
  loadingComponent,
  emptyComponent,
}: VirtualListProps<T>) {
  const { isMobile } = useViewport();
  const containerRef = useRef<HTMLDivElement>(null);
  const [scrollTop, setScrollTop] = useState(0);
  const [containerHeight, setContainerHeight] = useState(0);

  // Calculate visible range
  const startIndex = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
  const endIndex = Math.min(
    items.length - 1,
    Math.ceil((scrollTop + containerHeight) / itemHeight) + overscan,
  );

  const visibleItems = items.slice(startIndex, endIndex + 1);

  // Handle scroll with optimized performance for mobile
  const handleScroll = useCallback(
    (e: React.UIEvent<HTMLDivElement>) => {
      const target = e.currentTarget;
      const newScrollTop = target.scrollTop;

      setScrollTop(newScrollTop);

      // Check if we need to load more items
      if (onEndReached && !loading) {
        const scrollRatio =
          (newScrollTop + containerHeight) / target.scrollHeight;
        if (scrollRatio >= endReachedThreshold) {
          onEndReached();
        }
      }
    },
    [containerHeight, onEndReached, loading, endReachedThreshold],
  );

  // Update container height on resize
  useEffect(() => {
    const updateHeight = () => {
      if (containerRef.current) {
        setContainerHeight(containerRef.current.clientHeight);
      }
    };

    updateHeight();
    window.addEventListener("resize", updateHeight);
    return () => window.removeEventListener("resize", updateHeight);
  }, []);

  // Mobile-specific optimizations
  const scrollerProps = isMobile
    ? {
        style: {
          WebkitOverflowScrolling: "touch" as const,
          transform: "translateZ(0)", // Enable hardware acceleration
        },
      }
    : {};

  if (items.length === 0 && !loading) {
    return (
      <div className={`flex items-center justify-center h-32 ${className}`}>
        {emptyComponent || (
          <p className="text-gray-500 dark:text-gray-400 text-center">
            No items to display
          </p>
        )}
      </div>
    );
  }

  return (
    <div
      ref={containerRef}
      className={`overflow-auto ${className}`}
      onScroll={handleScroll}
      {...scrollerProps}
    >
      {/* Total height container */}
      <div
        style={{
          height: items.length * itemHeight,
          position: "relative",
        }}
      >
        {/* Visible items container */}
        <div
          style={{
            transform: `translateY(${startIndex * itemHeight}px)`,
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
          }}
        >
          {visibleItems.map((item, index) => (
            <div
              key={startIndex + index}
              style={{
                height: itemHeight,
                overflow: "hidden",
              }}
              className="flex-shrink-0"
            >
              {renderItem(item, startIndex + index)}
            </div>
          ))}
        </div>
      </div>

      {/* Loading indicator */}
      {loading && (
        <div className="flex items-center justify-center py-4">
          {loadingComponent || (
            <div className="flex items-center space-x-2 text-gray-500">
              <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
              <span>Loading...</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VirtualList;

