const NotificationContainer: React.FC = () => {
  // Selective subscriptions for specific notification store values
  const notifications = useNotificationStore((state) => state.notifications);
  const removeNotification = useNotificationStore(
    (state) => state.removeNotification,
  );
  const pauseOnHover = useNotificationStore((state) => state.pauseOnHover);

  // Group notifications by position
  const notificationsByPosition = notifications.reduce(
    (acc: Record<string, Notification[]>, notification: Notification) => {
      const position = notification.position || "top-right";
      if (!acc[position]) {
        acc[position] = [];
      }
      acc[position].push(notification);
      return acc;
    },
    {} as Record<string, Notification[]>,
  );

  const getPositionClasses = (position: string) => {
    switch (position) {
      case "top-left":
        return "top-4 left-4";
      case "top-center":
        return "top-4 left-1/2 transform -translate-x-1/2";
      case "top-right":
        return "top-4 right-4";
      case "bottom-left":
        return "bottom-4 left-4";
      case "bottom-center":
        return "bottom-4 left-1/2 transform -translate-x-1/2";
      case "bottom-right":
        return "bottom-4 right-4";
      default:
        return "top-4 right-4";
    }
  };

  const getTypeClasses = (type: string) => {
    switch (type) {
      case "success":
        return "bg-green-50 border-green-200 text-green-800";
      case "error":
        return "bg-red-50 border-red-200 text-red-800";
      case "warning":
        return "bg-yellow-50 border-yellow-200 text-yellow-800";
      case "loading":
        return "bg-blue-50 border-blue-200 text-blue-800";
      default:
        return "bg-blue-50 border-blue-200 text-blue-800";
    }
  };

  return (
    <>
      {Object.entries(notificationsByPosition).map(
        ([position, positionNotifications]) => (
          <div
            key={position}
            className={`fixed z-50 ${getPositionClasses(position)} space-y-2 max-w-sm w-full`}
          >
            {positionNotifications.map((notification: Notification) => (
              <div
                key={notification.id}
                className={`
                flex items-start p-4 border rounded-lg shadow-lg backdrop-blur-sm
                ${getTypeClasses(notification.type)}
                ${pauseOnHover ? "hover:shadow-xl transition-shadow" : ""}
              `}
                role="alert"
              >
                <div className="flex-shrink-0">
                  <NotificationIcon type={notification.type} />
                </div>

                <div className="ml-3 flex-1">
                  {notification.title && (
                    <h4 className="text-sm font-medium mb-1">
                      {notification.title}
                    </h4>
                  )}
                  <p className="text-sm">{notification.message}</p>

                  {notification.action && (
                    <div className="mt-3">
                      <button
                        onClick={() => {
                          notification.action!.onClick();
                          if (notification.dismissible !== false) {
                            removeNotification(notification.id);
                          }
                        }}
                        className="text-xs px-2 py-1 rounded border border-purple-300 text-purple-700 hover:bg-purple-100 transition-colors"
                      >
                        {notification.action.label}
                      </button>
                    </div>
                  )}
                </div>

                {notification.dismissible !== false && (
                  <button
                    onClick={() => removeNotification(notification.id)}
                    className="ml-2 flex-shrink-0 text-gray-400 hover:text-gray-600 transition-colors"
                    aria-label="Dismiss notification"
                  >
                    <svg
                      className="w-4 h-4"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path
                        fillRule="evenodd"
                        d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                        clipRule="evenodd"
                      />
                    </svg>
                  </button>
                )}
              </div>
            ))}
          </div>
        ),
      )}
    </>
  );
};

export default NotificationContainer;

/**
 * Application Context
 * Manages global app-level state (non-auth, non-UI)
 * Context layer - App state like connection status, sync status, etc.
 */
import React, {
  createContext,
  useContext,
  useEffect,
  useState,
  ReactNode,
} from "react";
import { firebaseSync } from "../services/sync";
import { preloadCriticalServices } from "../services/firebase";
import { achievementIntegration } from "../services/AchievementIntegration";
import { serviceLogger } from "../utils/logging";
import { db } from "../services/database";
import type { SyncStatus } from "../types/database";

// Extend Navigator interface for connection API
interface NavigatorWithConnection extends Navigator {
  connection?: {
    effectiveType: string;
    addEventListener: (event: string, callback: () => void) => void;
    removeEventListener: (event: string, callback: () => void) => void;
  };
}

const logger = serviceLogger("AppContext");

export interface AppState {
  isInitialized: boolean;
  isOnline: boolean;
  syncStatus: SyncStatus | null;
  lastSyncTime: Date | null;
  hasUnreadNotifications: boolean;
  connectionType: string | null;
  sessionPersistenceReady: boolean;
}

export interface AppActions {
  initializeApp: () => Promise<void>;
  triggerSync: (userId: string) => Promise<void>;
  markNotificationsRead: () => void;
  updateConnectionStatus: (isOnline: boolean) => void;
}

export interface AppContextType {
  state: AppState;
  actions: AppActions;
}

const AppContext = createContext<AppContextType | null>(null);

interface AppProviderProps {
  children: ReactNode;
}

export const AppProvider: React.FC<AppProviderProps> = ({ children }) => {
  const [state, setState] = useState<AppState>({
    isInitialized: false,
    isOnline: navigator.onLine,
    syncStatus: null,
    lastSyncTime: null,
    hasUnreadNotifications: false,
    connectionType: null,
    sessionPersistenceReady: false,
  });

  // Initialize app on mount
  useEffect(() => {
    const initializeApp = async () => {
      try {
        logger.info("Initializing application");

        // Initialize Dexie database first
        await db.initialize();
        logger.info("Dexie database initialized");

        // Preload critical Firebase services
        await preloadCriticalServices();

        // Initialize achievement system
        await achievementIntegration.initialize();

        // Initialize session persistence service
        // The service initializes automatically as a singleton
        logger.info("Session persistence service ready");
        setState((prev) => ({ ...prev, sessionPersistenceReady: true }));

        // Initialize sync service
        // FirebaseSync initializes automatically

        // Set initial sync status
        const syncStatus = "synced" as SyncStatus;

        // Detect connection type
        const connection = (navigator as NavigatorWithConnection).connection;
        const connectionType = connection?.effectiveType || "unknown";

        setState((prev) => ({
          ...prev,
          isInitialized: true,
          syncStatus,
          connectionType,
        }));

        logger.info("Application initialized successfully", { connectionType });
      } catch (error) {
        logger.error("Failed to initialize application", {
          error: error as Error,
        });
        setState((prev) => ({
          ...prev,
          isInitialized: false,
        }));
      }
    };

    initializeApp();

    // Listen for online/offline events
    const handleOnline = () => {
      setState((prev) => ({ ...prev, isOnline: true }));
      logger.debug("App went online");
    };

    const handleOffline = () => {
      setState((prev) => ({ ...prev, isOnline: false }));
      logger.debug("App went offline");
    };

    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    // Listen for connection changes
    const handleConnectionChange = () => {
      const connection = (navigator as NavigatorWithConnection).connection;
      if (connection) {
        setState((prev) => ({
          ...prev,
          connectionType: connection.effectiveType || "unknown",
        }));
        logger.debug("Connection type changed", {
          type: connection.effectiveType,
        });
      }
    };

    if ("connection" in navigator) {
      (navigator as NavigatorWithConnection).connection?.addEventListener(
        "change",
        handleConnectionChange,
      );
    }

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);

      if ("connection" in navigator) {
        (navigator as NavigatorWithConnection).connection?.removeEventListener(
          "change",
          handleConnectionChange,
        );
      }
    };
  }, []);

  const actions: AppActions = {
    initializeApp: async () => {
      if (state.isInitialized) {
        logger.debug("App already initialized, skipping");
        return;
      }

      setState((prev) => ({ ...prev, isInitialized: false }));

      try {
        // Initialize Dexie database first
        await db.initialize();
        logger.info("Dexie database re-initialized");

        await preloadCriticalServices();
        // FirebaseSync initializes automatically

        const syncStatus = "synced" as SyncStatus;

        setState((prev) => ({
          ...prev,
          isInitialized: true,
          syncStatus,
        }));

        logger.info("App re-initialized successfully");
      } catch (error) {
        logger.error("Failed to re-initialize app", { error: error as Error });
      }
    },

    triggerSync: async (userId: string) => {
      logger.debug("Triggering manual sync", { userId });

      try {
        await firebaseSync.sync();
        const result = {
          success: true,
          data: {
            syncStatus: "synced" as SyncStatus,
            lastSyncTime: new Date(),
          },
        };

        if (result.success && result.data) {
          setState((prev) => ({
            ...prev,
            syncStatus: result.data.syncStatus,
            lastSyncTime: result.data.lastSyncTime,
          }));
          logger.info("Manual sync completed", { userId });
        }
      } catch (error) {
        const result = {
          success: false,
          error: error instanceof Error ? error.message : "Sync failed",
        };
        logger.warn("Manual sync failed", { userId, error: result.error });
      }
    },

    markNotificationsRead: () => {
      setState((prev) => ({
        ...prev,
        hasUnreadNotifications: false,
      }));
      logger.debug("Notifications marked as read");
    },

    updateConnectionStatus: (isOnline: boolean) => {
      setState((prev) => ({
        ...prev,
        isOnline,
      }));
      logger.debug("Connection status updated", { isOnline });
    },
  };

  const contextValue: AppContextType = {
    state,
    actions,
  };

  return (
    <AppContext.Provider value={contextValue}>{children}</AppContext.Provider>
  );
};

export const useApp = (): AppContextType => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error("useApp must be used within an AppProvider");
  }
  return context;
};

// Convenience hooks for common use cases
export const useAppState = () => {
  const { state } = useApp();
  return state;
};

export const useAppActions = () => {
  const { actions } = useApp();
  return actions;
};

export const useConnectionStatus = () => {
  const { state } = useApp();
  return {
    isOnline: state.isOnline,
    connectionType: state.connectionType,
  };
};

export const useSyncStatus = () => {
  const { state, actions } = useApp();
  return {
    syncStatus: state.syncStatus,
    lastSyncTime: state.lastSyncTime,
    triggerSync: actions.triggerSync,
  };
};

/**
 * Authentication Context
 * Manages auth state using the AuthService
 * Context layer - React state management for authentication
 */
import React, {
  createContext,
  useContext,
  useEffect,
  useState,
  ReactNode,
} from "react";
import { AuthService } from "@/services/auth/auth-service";
import { getFirebaseAuth } from "@/services/firebase";
import { User, LoginForm, RegisterForm, ApiResponse } from "@/types";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("AuthContext");

export interface AuthState {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  error: string | null;
}

export interface AuthActions {
  signIn: (credentials: LoginForm) => Promise<ApiResponse<User>>;
  register: (userData: RegisterForm) => Promise<ApiResponse<User>>;
  signOut: () => Promise<ApiResponse<void>>;
  resetPassword: (email: string) => Promise<ApiResponse<void>>;
  updatePassword: (newPassword: string) => Promise<ApiResponse<void>>;
  updateProfile: (updates: Partial<User>) => Promise<ApiResponse<User>>;
  clearError: () => void;
}

export interface AuthContextType {
  state: AuthState;
  actions: AuthActions;
  // Direct access properties for backwards compatibility
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
}

const AuthContext = createContext<AuthContextType | null>(null);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [state, setState] = useState<AuthState>({
    user: null,
    isAuthenticated: false,
    isLoading: true,
    error: null,
  });

  // Initialize auth state on mount
  useEffect(() => {
    const initializeAuth = async () => {
      try {
        logger.debug("Initializing auth state");

        const auth = await getFirebaseAuth();

        // Check if user is already authenticated
        const currentUser = await AuthService.getCurrentUser();

        if (currentUser) {
          setState({
            user: currentUser,
            isAuthenticated: true,
            isLoading: false,
            error: null,
          });
          logger.info("User already authenticated", { uid: currentUser.uid });
        } else {
          setState((prev) => ({
            ...prev,
            isLoading: false,
          }));
          logger.debug("No authenticated user found");
        }

        // Listen for auth state changes
        const unsubscribe = auth.onAuthStateChanged(async (firebaseUser) => {
          if (firebaseUser) {
            logger.debug("Firebase auth state changed: user signed in", {
              uid: firebaseUser.uid,
            });

            // Get full user profile from our service
            const user = await AuthService.getCurrentUser();
            if (user) {
              setState({
                user,
                isAuthenticated: true,
                isLoading: false,
                error: null,
              });
            }
          } else {
            logger.debug("Firebase auth state changed: user signed out");
            setState({
              user: null,
              isAuthenticated: false,
              isLoading: false,
              error: null,
            });
          }
        });

        return unsubscribe;
      } catch (error) {
        logger.error("Failed to initialize auth", { error: error as Error });
        setState({
          user: null,
          isAuthenticated: false,
          isLoading: false,
          error: "Failed to initialize authentication",
        });
        return undefined;
      }
    };

    const unsubscribePromise = initializeAuth();

    return () => {
      unsubscribePromise.then((unsubscribe) => {
        if (unsubscribe) {
          unsubscribe();
        }
      });
    };
  }, []);

  const actions: AuthActions = {
    signIn: async (credentials: LoginForm) => {
      setState((prev) => ({ ...prev, isLoading: true, error: null }));

      const result = await AuthService.signIn(credentials);

      if (result.success && result.data) {
        setState({
          user: result.data,
          isAuthenticated: true,
          isLoading: false,
          error: null,
        });
        logger.info("User signed in via context", { uid: result.data.uid });
      } else {
        setState((prev) => ({
          ...prev,
          isLoading: false,
          error: result.error || "Sign in failed",
        }));
        logger.warn("Sign in failed via context", { error: result.error });
      }

      return result;
    },

    register: async (userData: RegisterForm) => {
      setState((prev) => ({ ...prev, isLoading: true, error: null }));

      const result = await AuthService.register(userData);

      if (result.success && result.data) {
        setState({
          user: result.data,
          isAuthenticated: true,
          isLoading: false,
          error: null,
        });
        logger.info("User registered via context", { uid: result.data.uid });
      } else {
        setState((prev) => ({
          ...prev,
          isLoading: false,
          error: result.error || "Registration failed",
        }));
        logger.warn("Registration failed via context", { error: result.error });
      }

      return result;
    },

    signOut: async () => {
      setState((prev) => ({ ...prev, isLoading: true, error: null }));

      const result = await AuthService.signOut();

      if (result.success) {
        setState({
          user: null,
          isAuthenticated: false,
          isLoading: false,
          error: null,
        });
        logger.info("User signed out via context");
      } else {
        setState((prev) => ({
          ...prev,
          isLoading: false,
          error: result.error || "Sign out failed",
        }));
        logger.warn("Sign out failed via context", { error: result.error });
      }

      return result;
    },

    resetPassword: async (email: string) => {
      setState((prev) => ({ ...prev, error: null }));

      const result = await AuthService.resetPassword(email);

      if (!result.success) {
        setState((prev) => ({
          ...prev,
          error: result.error || "Password reset failed",
        }));
        logger.warn("Password reset failed via context", {
          error: result.error,
          email,
        });
      } else {
        logger.info("Password reset sent via context", { email });
      }

      return result;
    },

    updatePassword: async (newPassword: string) => {
      setState((prev) => ({ ...prev, error: null }));

      const result = await AuthService.updatePassword(newPassword);

      if (!result.success) {
        setState((prev) => ({
          ...prev,
          error: result.error || "Password update failed",
        }));
        logger.warn("Password update failed via context", {
          error: result.error,
        });
      } else {
        logger.info("Password updated via context");
      }

      return result;
    },

    updateProfile: async (updates: Partial<User>) => {
      if (!state.user) {
        const error = "No authenticated user found";
        setState((prev) => ({ ...prev, error }));
        return { success: false, error };
      }

      setState((prev) => ({ ...prev, error: null }));

      const result = await AuthService.updateUserProfile(
        state.user.uid,
        updates,
      );

      if (result.success && result.data) {
        setState((prev) => ({
          ...prev,
          user: result.data!,
        }));
        logger.info("Profile updated via context", { uid: state.user.uid });
      } else {
        setState((prev) => ({
          ...prev,
          error: result.error || "Profile update failed",
        }));
        logger.warn("Profile update failed via context", {
          error: result.error,
          uid: state.user.uid,
        });
      }

      return result;
    },

    clearError: () => {
      setState((prev) => ({ ...prev, error: null }));
    },
  };

  const contextValue: AuthContextType = {
    state,
    actions,
    // Direct access properties for backwards compatibility
    user: state.user,
    isAuthenticated: state.isAuthenticated,
    isLoading: state.isLoading,
  };

  return (
    <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
  );
};

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

// Convenience hooks for common use cases
export const useAuthState = () => {
  const { state } = useAuth();
  return state;
};

export const useAuthActions = () => {
  const { actions } = useAuth();
  return actions;
};

/**
 * Sync Context
 * Provides sync state and conflict resolution across the app
 */
import React, { createContext, useContext, useState, useEffect } from "react";
import { useSync } from "@/hooks/useSync";
import { useAuth } from "@/hooks/api/useAuth";
import { ConflictResolutionModal } from "@/components/common/ConflictResolutionModal";
import type { ConflictInfo } from "@/types/database";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("SyncContext");

interface SyncContextType {
  isSyncing: boolean;
  lastSyncTime: Date | null;
  syncStatus: "synced" | "pending" | "conflict" | "error";
  pendingConflicts: ConflictInfo[];
  triggerSync: () => Promise<void>;
  hasConflicts: boolean;
}

const SyncContext = createContext<SyncContextType | undefined>(undefined);

export const useSyncContext = (): SyncContextType => {
  const context = useContext(SyncContext);
  if (!context) {
    throw new Error("useSyncContext must be used within SyncProvider");
  }
  return context;
};

interface SyncProviderProps {
  children: React.ReactNode;
}

export const SyncProvider: React.FC<SyncProviderProps> = ({ children }) => {
  const { data: user } = useAuth();
  const userId = user?.uid;
  const {
    isSyncing,
    lastSyncResult: _lastSyncResult,
    pendingConflicts,
    sync,
    resolveConflicts,
    error,
  } = useSync();

  const [showConflictModal, setShowConflictModal] = useState(false);
  const [lastSyncTime, setLastSyncTime] = useState<Date | null>(null);

  // Determine sync status
  const getSyncStatus = (): SyncContextType["syncStatus"] => {
    if (error) return "error";
    if (pendingConflicts.length > 0) return "conflict";
    if (isSyncing) return "pending";
    return "synced";
  };

  // Auto-sync on user change and periodically
  useEffect(() => {
    if (!userId) return;

    const performSync = async () => {
      try {
        await sync(userId, { conflictResolution: "auto" });
        setLastSyncTime(new Date());
      } catch (error) {
        logger.error("Auto-sync failed", { error: error as Error });
      }
    };

    // Initial sync
    performSync();

    // Periodic sync every 5 minutes
    const interval = setInterval(performSync, 5 * 60 * 1000);

    return () => clearInterval(interval);
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [userId, sync]);

  // Show conflict modal when conflicts are detected
  useEffect(() => {
    if (pendingConflicts.length > 0 && !showConflictModal) {
      setShowConflictModal(true);
    }
  }, [pendingConflicts.length, showConflictModal]);

  const triggerSync = async (): Promise<void> => {
    if (!userId) {
      throw new Error("No user authenticated");
    }

    try {
      await sync(userId, { force: true });
      setLastSyncTime(new Date());
    } catch (error) {
      logger.error("Manual sync failed", { error: error as Error });
      throw error;
    }
  };

  const handleResolveConflicts = async (
    resolutions: Record<string, "local" | "remote">,
  ) => {
    try {
      await resolveConflicts(resolutions);
      setShowConflictModal(false);

      // Trigger a sync after resolving conflicts
      if (userId) {
        await sync(userId);
        setLastSyncTime(new Date());
      }
    } catch (error) {
      logger.error("Failed to resolve conflicts", { error: error as Error });
    }
  };

  const handleCancelConflictResolution = () => {
    setShowConflictModal(false);
    // Conflicts remain pending - user can resolve them later
  };

  const contextValue: SyncContextType = {
    isSyncing,
    lastSyncTime,
    syncStatus: getSyncStatus(),
    pendingConflicts,
    triggerSync,
    hasConflicts: pendingConflicts.length > 0,
  };

  return (
    <SyncContext.Provider value={contextValue}>
      {children}

      <ConflictResolutionModal
        conflicts={pendingConflicts}
        onResolve={handleResolveConflicts}
        onCancel={handleCancelConflictResolution}
        isOpen={showConflictModal}
      />
    </SyncContext.Provider>
  );
};

/**
 * Account Linking Hook
 * React hook for managing keyholder-wearer account linking
 */
import { useState, useCallback, useEffect, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { AccountLinkingService } from "../../services/auth/account-linking";
import { useAuthState } from "../../contexts";
import {
  LinkCodeResponse,
  AdminRelationship,
  AdminSession,
  GenerateLinkCodeRequest,
  UseLinkCodeRequest,
  UpdateRelationshipRequest,
  AccountLinkingState,
} from "../../types/account-linking";
import { ApiResponse } from "../../types";
import { serviceLogger } from "../../utils/logging";

const logger = serviceLogger("useAccountLinking");

// Query keys
const QUERY_KEYS = {
  relationships: (userId: string) => ["adminRelationships", userId],
  validation: (code: string) => ["linkCodeValidation", code],
  adminSession: (relationshipId: string) => ["adminSession", relationshipId],
} as const;

export const useAccountLinking = () => {
  const { user } = useAuthState();
  const queryClient = useQueryClient();

  // Local state
  const [state, setState] = useState<AccountLinkingState>({
    isGeneratingCode: false,
    currentLinkCode: null,
    linkCodeError: null,
    isUsingCode: false,
    codeUsageError: null,
    adminRelationships: [],
    selectedWearerId: null,
    currentAdminSession: null,
    isAdminSessionActive: false,
    showQRCode: false,
    showDisconnectionDialog: false,
    showPermissionEditor: false,
  });

  // ==================== QUERIES ====================

  // Get admin relationships for current user
  const {
    data: relationships = [],
    isLoading: isLoadingRelationships,
    error: relationshipsError,
  } = useQuery({
    queryKey: QUERY_KEYS.relationships(user?.uid || ""),
    queryFn: () => AccountLinkingService.getAdminRelationships(user!.uid),
    enabled: !!user,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Update local state when relationships change
  useEffect(() => {
    setState((prev) => ({ ...prev, adminRelationships: relationships }));
  }, [relationships]);

  // ==================== MUTATIONS ====================

  // Generate link code
  const generateLinkCodeMutation = useMutation({
    mutationFn: (request: GenerateLinkCodeRequest) =>
      AccountLinkingService.generateLinkCode(request),
    onMutate: () => {
      setState((prev) => ({
        ...prev,
        isGeneratingCode: true,
        linkCodeError: null,
      }));
    },
    onSuccess: (response: ApiResponse<LinkCodeResponse>) => {
      if (response.success && response.data) {
        setState((prev) => ({
          ...prev,
          currentLinkCode: response.data!,
          isGeneratingCode: false,
        }));
        logger.info("Link code generated successfully");
      } else {
        setState((prev) => ({
          ...prev,
          linkCodeError: response.error || "Failed to generate link code",
          isGeneratingCode: false,
        }));
      }
    },
    onError: (error: Error) => {
      setState((prev) => ({
        ...prev,
        linkCodeError: error.message,
        isGeneratingCode: false,
      }));
      logger.error("Failed to generate link code", { error });
    },
  });

  // Use link code
  const useLinkCodeMutation = useMutation({
    mutationFn: (request: UseLinkCodeRequest) =>
      AccountLinkingService.redeemLinkCode(request),
    onMutate: () => {
      setState((prev) => ({
        ...prev,
        isUsingCode: true,
        codeUsageError: null,
      }));
    },
    onSuccess: (response: ApiResponse<AdminRelationship>) => {
      if (response.success && response.data) {
        setState((prev) => ({
          ...prev,
          isUsingCode: false,
        }));
        // Refresh relationships
        queryClient.invalidateQueries({
          queryKey: QUERY_KEYS.relationships(user?.uid || ""),
        });
        logger.info("Link code used successfully");
      } else {
        setState((prev) => ({
          ...prev,
          codeUsageError: response.error || "Failed to use link code",
          isUsingCode: false,
        }));
      }
    },
    onError: (error: Error) => {
      setState((prev) => ({
        ...prev,
        codeUsageError: error.message,
        isUsingCode: false,
      }));
      logger.error("Failed to use link code", { error });
    },
  });

  // Update relationship
  const updateRelationshipMutation = useMutation({
    mutationFn: (request: UpdateRelationshipRequest) =>
      AccountLinkingService.updateRelationship(request),
    onSuccess: (response: ApiResponse<AdminRelationship>) => {
      if (response.success) {
        // Refresh relationships
        queryClient.invalidateQueries({
          queryKey: QUERY_KEYS.relationships(user?.uid || ""),
        });
        logger.info("Relationship updated successfully");
      }
    },
    onError: (error: Error) => {
      logger.error("Failed to update relationship", { error });
    },
  });

  // Start admin session
  const startAdminSessionMutation = useMutation({
    mutationFn: (relationshipId: string) =>
      AccountLinkingService.startAdminSession(relationshipId),
    onSuccess: (response: ApiResponse<AdminSession>) => {
      if (response.success && response.data) {
        setState((prev) => ({
          ...prev,
          currentAdminSession: response.data!,
          isAdminSessionActive: true,
        }));
        logger.info("Admin session started successfully");
      }
    },
    onError: (error: Error) => {
      logger.error("Failed to start admin session", { error });
    },
  });

  // ==================== CALLBACK FUNCTIONS ====================

  const generateLinkCode = useCallback(
    (request: GenerateLinkCodeRequest = {}) => {
      if (!user) {
        setState((prev) => ({
          ...prev,
          linkCodeError: "Authentication required",
        }));
        return;
      }
      generateLinkCodeMutation.mutate(request);
    },
    [user, generateLinkCodeMutation],
  );

  const redeemLinkCode = useCallback(
    (request: UseLinkCodeRequest) => {
      if (!user) {
        setState((prev) => ({
          ...prev,
          codeUsageError: "Authentication required",
        }));
        return;
      }
      useLinkCodeMutation.mutate(request);
    },
    [user, useLinkCodeMutation],
  );

  const updateRelationship = useCallback(
    (request: UpdateRelationshipRequest) => {
      updateRelationshipMutation.mutate(request);
    },
    [updateRelationshipMutation],
  );

  const startAdminSession = useCallback(
    (relationshipId: string) => {
      startAdminSessionMutation.mutate(relationshipId);
    },
    [startAdminSessionMutation],
  );

  const disconnectKeyholder = useCallback(
    (relationshipId: string, reason?: string) => {
      updateRelationship({
        relationshipId,
        status: "terminated",
        terminationReason: reason,
      });
    },
    [updateRelationship],
  );

  const clearLinkCode = useCallback(() => {
    setState((prev) => ({
      ...prev,
      currentLinkCode: null,
      linkCodeError: null,
    }));
  }, []);

  const clearAllErrors = useCallback(() => {
    setState((prev) => ({
      ...prev,
      currentLinkCode: null,
      linkCodeError: null,
      codeUsageError: null,
    }));
  }, []);

  const setSelectedWearer = useCallback((wearerId: string | null) => {
    setState((prev) => ({ ...prev, selectedWearerId: wearerId }));
  }, []);

  // Combined toggle function for boolean state properties
  const toggleStateProperty = useCallback(
    (
      property: keyof Pick<
        AccountLinkingState,
        "showQRCode" | "showDisconnectionDialog" | "showPermissionEditor"
      >,
    ) => {
      setState((prev) => ({ ...prev, [property]: !prev[property] }));
    },
    [],
  );

  const toggleQRCode = useCallback(
    () => toggleStateProperty("showQRCode"),
    [toggleStateProperty],
  );
  const toggleDisconnectionDialog = useCallback(
    () => toggleStateProperty("showDisconnectionDialog"),
    [toggleStateProperty],
  );
  const togglePermissionEditor = useCallback(
    () => toggleStateProperty("showPermissionEditor"),
    [toggleStateProperty],
  );

  // ==================== DERIVED STATE ====================

  // User role calculations
  const userRoles = useMemo(
    () => ({
      isKeyholder: relationships.some((r) => r.keyholderId === user?.uid),
      isWearer: relationships.some((r) => r.wearerId === user?.uid),
      hasActiveRelationships: relationships.some((r) => r.status === "active"),
    }),
    [relationships, user?.uid],
  );

  // Relationship filtering
  const relationshipsByRole = useMemo(
    () => ({
      keyholderRelationships: relationships.filter(
        (r) => r.keyholderId === user?.uid,
      ),
      wearerRelationships: relationships.filter(
        (r) => r.wearerId === user?.uid,
      ),
    }),
    [relationships, user?.uid],
  );

  const selectedRelationship = state.selectedWearerId
    ? relationships.find((r) => r.wearerId === state.selectedWearerId)
    : null;

  // ==================== RETURN OBJECT ====================

  return {
    // State
    ...state,
    relationships,
    isLoadingRelationships,
    relationshipsError,

    // Derived state
    ...userRoles,
    selectedRelationship,
    ...relationshipsByRole,

    // Actions
    generateLinkCode,
    redeemLinkCode,
    updateRelationship,
    startAdminSession,
    disconnectKeyholder,
    clearLinkCode,
    clearAllErrors,
    setSelectedWearer,
    toggleQRCode,
    toggleDisconnectionDialog,
    togglePermissionEditor,

    // Loading states
    isGeneratingCode: generateLinkCodeMutation.isPending,
    isUsingCode: useLinkCodeMutation.isPending,
    isUpdatingRelationship: updateRelationshipMutation.isPending,
    isStartingSession: startAdminSessionMutation.isPending,
  };
};

// ==================== ADDITIONAL HOOKS ====================

/**
 * Hook for validating link codes
 */
export const useLinkCodeValidation = (code: string) => {
  return useQuery({
    queryKey: QUERY_KEYS.validation(code),
    queryFn: () => AccountLinkingService.validateLinkCode(code),
    enabled: code.length > 0,
    staleTime: 30 * 1000, // 30 seconds
  });
};

/**
 * Hook for admin access validation
 */
export const useAdminAccess = (wearerId: string) => {
  const { user } = useAuthState();
  const { relationships } = useAccountLinking();

  const adminRelationship = relationships.find(
    (r) =>
      r.keyholderId === user?.uid &&
      r.wearerId === wearerId &&
      r.status === "active",
  );

  const hasAccess = !!adminRelationship;
  const permissions = adminRelationship?.permissions || null;
  const security = adminRelationship?.security || null;

  const canPerformAction = useCallback(
    (action: keyof typeof permissions) => {
      return hasAccess && permissions?.[action] === true;
    },
    [hasAccess, permissions],
  );

  return {
    hasAccess,
    adminRelationship,
    permissions,
    security,
    canPerformAction,
  };
};

import {
  useMutation,
  useQuery,
  useQueryClient,
  useInfiniteQuery,
} from "@tanstack/react-query";
import { eventDBService } from "../../services/database/EventDBService";
import { Event, EventType } from "../../types/events";
import { DBEvent, EventFilters } from "../../types/database";
import { logger } from "../../utils/logging";

/**
 * Event Management Hooks - TanStack Query Integration
 *
 * Integrates with:
 * - eventDBService  Dexie  Firebase sync
 * - LogEventPage.tsx, LogEventForm.tsx (critical fixes needed)
 *
 * Fixes:
 * - LogEventForm.tsx:92-93 (commented eventDBService.create)
 * - LogEventPage.tsx:20 (eventDBService.findByUserId)
 *
 * Strategy: Dexie-first write, Firebase background sync
 */

// Utility function to convert Event to DBEvent format
const eventToDBEvent = (
  event: Event,
): Omit<DBEvent, "lastModified" | "syncStatus"> => {
  return {
    id: event.id,
    userId: event.userId,
    type: event.type,
    timestamp: event.timestamp,
    details: event.details,
    isPrivate: false, // Default value, can be overridden
    sessionId: undefined, // Can be set if available
  };
};

// Query Keys
export const eventKeys = {
  all: ["events"] as const,
  lists: () => [...eventKeys.all, "list"] as const,
  list: (userId: string, filters?: EventFilters) =>
    [...eventKeys.lists(), userId, filters] as const,
  infinite: (userId: string, filters?: EventFilters) =>
    [...eventKeys.all, "infinite", userId, filters] as const,
  detail: (eventId: string) => [...eventKeys.all, "detail", eventId] as const,
  recent: (userId: string, limit?: number) =>
    [...eventKeys.all, "recent", userId, limit] as const,
} as const;

// Types
interface EventFilters {
  type?: EventType;
  startDate?: Date;
  endDate?: Date;
  limit?: number;
}

interface CreateEventData {
  type: EventType;
  details: Record<string, unknown>;
  timestamp?: Date;
}

interface UpdateEventData {
  type?: EventType;
  details?: Record<string, unknown>;
  timestamp?: Date;
}

/**
 * Get event history for a user with filters
 * Fixes: LogEventPage.tsx:20 (eventDBService.findByUserId)
 */
export function useEventHistory(userId: string, filters?: EventFilters) {
  return useQuery({
    queryKey: eventKeys.list(userId, filters),
    queryFn: async (): Promise<DBEvent[]> => {
      logger.info("Fetching event history", { userId, filters });

      try {
        const events = await eventDBService.findByUserId(userId);

        // Apply filters
        let filteredEvents = events;

        if (filters?.type) {
          filteredEvents = filteredEvents.filter(
            (event) => event.type === filters.type,
          );
        }

        if (filters?.startDate) {
          filteredEvents = filteredEvents.filter(
            (event) => new Date(event.timestamp) >= filters.startDate!,
          );
        }

        if (filters?.endDate) {
          filteredEvents = filteredEvents.filter(
            (event) => new Date(event.timestamp) <= filters.endDate!,
          );
        }

        // Sort by timestamp (newest first)
        filteredEvents.sort(
          (a, b) =>
            new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime(),
        );

        if (filters?.limit) {
          filteredEvents = filteredEvents.slice(0, filters.limit);
        }

        logger.info("Event history retrieved", {
          userId,
          totalEvents: events.length,
          filteredEvents: filteredEvents.length,
        });

        return filteredEvents;
      } catch (error) {
        logger.error("Failed to fetch event history", {
          error: error instanceof Error ? error.message : String(error),
          userId,
        });
        throw error;
      }
    },
    staleTime: 5 * 60 * 1000, // 5 minutes - warm data
    gcTime: 30 * 60 * 1000, // 30 minutes garbage collection
    enabled: !!userId,
  });
}

/**
 * Infinite query for event history with pagination
 * Useful for long event lists with infinite scroll
 */
export function useInfiniteEventHistory(
  userId: string,
  filters?: EventFilters,
  pageSize = 20,
) {
  return useInfiniteQuery({
    queryKey: eventKeys.infinite(userId, filters),
    queryFn: async ({
      pageParam = 0,
    }): Promise<{ events: DBEvent[]; nextPage?: number }> => {
      logger.info("Fetching infinite event page", {
        userId,
        pageParam,
        pageSize,
      });

      const events = await eventDBService.findByUserId(userId);

      // Apply filters and sorting (same as useEventHistory)
      let filteredEvents = events;

      if (filters?.type) {
        filteredEvents = filteredEvents.filter(
          (event) => event.type === filters.type,
        );
      }

      if (filters?.startDate) {
        filteredEvents = filteredEvents.filter(
          (event) => new Date(event.timestamp) >= filters.startDate!,
        );
      }

      if (filters?.endDate) {
        filteredEvents = filteredEvents.filter(
          (event) => new Date(event.timestamp) <= filters.endDate!,
        );
      }

      filteredEvents.sort(
        (a, b) =>
          new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime(),
      );

      // Pagination
      const start = pageParam * pageSize;
      const end = start + pageSize;
      const pageEvents = filteredEvents.slice(start, end);

      const hasMore = end < filteredEvents.length;

      return {
        events: pageEvents,
        nextPage: hasMore ? pageParam + 1 : undefined,
      };
    },
    initialPageParam: 0,
    getNextPageParam: (lastPage) => lastPage.nextPage,
    staleTime: 5 * 60 * 1000,
    enabled: !!userId,
  });
}

/**
 * Get recent events for quick overview
 * Useful for dashboard summaries
 */
export function useRecentEvents(userId: string, limit = 10) {
  return useQuery({
    queryKey: eventKeys.recent(userId, limit),
    queryFn: async (): Promise<DBEvent[]> => {
      logger.info("Fetching recent events", { userId, limit });

      const events = await eventDBService.findByUserId(userId);

      // Sort by timestamp and take most recent
      const recentEvents = events
        .sort(
          (a, b) =>
            new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime(),
        )
        .slice(0, limit);

      return recentEvents;
    },
    staleTime: 2 * 60 * 1000, // 2 minutes - recent data changes frequently
    enabled: !!userId,
  });
}

/**
 * Get single event by ID
 */
export function useEvent(eventId: string) {
  return useQuery({
    queryKey: eventKeys.detail(eventId),
    queryFn: async (): Promise<DBEvent | null> => {
      logger.info("Fetching event detail", { eventId });
      const event = await eventDBService.findById(eventId);
      return event ?? null;
    },
    staleTime: 10 * 60 * 1000, // 10 minutes - historical data is stable
    enabled: !!eventId,
  });
}

/**
 * Create new event
 * Fixes: LogEventForm.tsx:92-93 (commented eventDBService.create)
 * Strategy: Dexie-first write, Firebase background sync
 */
export function useCreateEvent() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      userId,
      eventData,
    }: {
      userId: string;
      eventData: CreateEventData;
    }): Promise<Event> => {
      logger.info("Creating new event", { userId, eventType: eventData.type });

      // Generate event ID
      const eventId = `event_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      const newEvent: DBEvent = {
        id: eventId,
        userId,
        syncStatus: "pending",
        lastModified: new Date(),
        type: eventData.type,
        timestamp: eventData.timestamp || new Date(),
        details: eventData.details || {},
        isPrivate: eventData.isPrivate || false,
      };

      // Dexie-first write for immediate UI response
      const dbEvent = eventToDBEvent(newEvent);
      await eventDBService.create(dbEvent);

      logger.info("Event created successfully", {
        eventId,
        userId,
        type: eventData.type,
      });

      return newEvent;
    },
    onSuccess: (newEvent, { userId }) => {
      logger.info("Event creation successful", {
        eventId: newEvent.id,
        userId,
      });

      // Invalidate relevant queries to trigger refetch
      queryClient.invalidateQueries({ queryKey: eventKeys.lists() });
      queryClient.invalidateQueries({ queryKey: eventKeys.recent(userId) });
      queryClient.invalidateQueries({ queryKey: eventKeys.infinite(userId) });

      // Optimistically add to cache if we have existing data
      queryClient.setQueriesData(
        { queryKey: eventKeys.list(userId) },
        (oldData: Event[] | undefined) => {
          if (!oldData) return undefined;
          return [newEvent, ...oldData];
        },
      );
    },
    onError: (error, { userId, eventData }) => {
      logger.error("Event creation failed", {
        error: error instanceof Error ? error.message : String(error),
        userId,
        eventType: eventData.type,
      });
    },
  });
}

/**
 * Update existing event
 */
export function useUpdateEvent() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      eventId,
      userId,
      updates,
    }: {
      eventId: string;
      userId: string;
      updates: UpdateEventData;
    }): Promise<DBEvent> => {
      logger.info("Updating event", { eventId, userId });

      const existingEvent = await eventDBService.findById(eventId);
      if (!existingEvent) {
        throw new Error(`Event not found: ${eventId}`);
      }

      const updatedEvent: DBEvent = {
        ...existingEvent,
        ...updates,
        createdAt: existingEvent.createdAt || new Date(),
        lastModified: new Date(),
        updatedAt: new Date(),
      };

      const dbUpdatedEvent = eventToDBEvent(updatedEvent);
      await eventDBService.update(eventId, dbUpdatedEvent);

      logger.info("Event updated successfully", { eventId, userId });

      return updatedEvent;
    },
    onSuccess: (updatedEvent, { userId, eventId }) => {
      logger.info("Event update successful", { eventId, userId });

      // Update detail cache
      queryClient.setQueryData(eventKeys.detail(eventId), updatedEvent);

      // Invalidate list queries to reflect changes
      queryClient.invalidateQueries({ queryKey: eventKeys.lists() });
      queryClient.invalidateQueries({ queryKey: eventKeys.recent(userId) });
    },
    onError: (error, { eventId, userId }) => {
      logger.error("Event update failed", {
        error: error instanceof Error ? error.message : String(error),
        eventId,
        userId,
      });
    },
  });
}

/**
 * Delete event
 */
export function useDeleteEvent() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      eventId,
      userId,
    }: {
      eventId: string;
      userId: string;
    }): Promise<void> => {
      logger.info("Deleting event", { eventId, userId });

      await eventDBService.delete(eventId);

      logger.info("Event deleted successfully", { eventId, userId });
    },
    onSuccess: (_, { eventId, userId }) => {
      logger.info("Event deletion successful", { eventId, userId });

      // Remove from detail cache
      queryClient.removeQueries({ queryKey: eventKeys.detail(eventId) });

      // Invalidate list queries
      queryClient.invalidateQueries({ queryKey: eventKeys.lists() });
      queryClient.invalidateQueries({ queryKey: eventKeys.recent(userId) });
      queryClient.invalidateQueries({ queryKey: eventKeys.infinite(userId) });

      // Optimistically remove from cached lists
      queryClient.setQueriesData(
        { queryKey: eventKeys.list(userId) },
        (oldData: Event[] | undefined) => {
          if (!oldData) return undefined;
          return oldData.filter((event) => event.id !== eventId);
        },
      );
    },
    onError: (error, { eventId, userId }) => {
      logger.error("Event deletion failed", {
        error: error instanceof Error ? error.message : String(error),
        eventId,
        userId,
      });
    },
  });
}

/**
 * Get event statistics for analytics
 */
export function useEventStats(
  userId: string,
  timeRange?: { start: Date; end: Date },
) {
  return useQuery({
    queryKey: [...eventKeys.all, "stats", userId, timeRange],
    queryFn: async () => {
      logger.info("Calculating event statistics", { userId, timeRange });

      const events = await eventDBService.findByUserId(userId);

      let filteredEvents = events;
      if (timeRange) {
        filteredEvents = events.filter((event) => {
          const eventDate = new Date(event.timestamp);
          return eventDate >= timeRange.start && eventDate <= timeRange.end;
        });
      }

      // Calculate statistics
      const stats = {
        totalEvents: filteredEvents.length,
        eventsByType: {} as Record<string, number>,
        eventsPerDay: {} as Record<string, number>,
        mostRecentEvent:
          filteredEvents.length > 0
            ? filteredEvents.reduce((latest, event) =>
                new Date(event.timestamp) > new Date(latest.timestamp)
                  ? event
                  : latest,
              )
            : null,
      };

      // Count events by type
      filteredEvents.forEach((event) => {
        stats.eventsByType[event.type] =
          (stats.eventsByType[event.type] || 0) + 1;

        const dateKey = new Date(event.timestamp).toDateString();
        stats.eventsPerDay[dateKey] = (stats.eventsPerDay[dateKey] || 0) + 1;
      });

      return stats;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    enabled: !!userId,
  });
}

/**
 * Events TanStack Query Hooks
 * Manages event log data with Dexie as backend
 */
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { eventDBService } from "@/services/database";
import { cacheConfig } from "@/services/cache-config";
import { firebaseSync } from "@/services/sync";
import type { DBEvent, EventType } from "@/types/database";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useEventsQuery");

/**
 * Query for getting all events for a user
 */
export function useEventsQuery(userId: string | undefined) {
  return useQuery({
    queryKey: ["events", "user", userId],
    queryFn: async () => {
      if (!userId) return [];

      // Always read from local Dexie first for instant response
      const events = await eventDBService.findByUserId(userId);

      // Trigger background sync if online to ensure data freshness
      if (navigator.onLine) {
        firebaseSync.syncUserEvents(userId).catch((error) => {
          logger.warn("Background events sync failed", { error });
        });
      }

      return events;
    },
    ...cacheConfig.events, // Apply specific cache settings
    enabled: !!userId, // Only run when userId is available
  });
}

/**
 * Query for getting recent events (last 30 days)
 */
export function useRecentEventsQuery(
  userId: string | undefined,
  enabled = true,
) {
  return useQuery({
    queryKey: ["events", "recent", userId],
    queryFn: async () => {
      if (!userId) return [];

      const thirtyDaysAgo = new Date();
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

      const allEvents = await eventDBService.findByUserId(userId);
      return allEvents
        .filter((event) => event.timestamp >= thirtyDaysAgo)
        .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
    },
    ...cacheConfig.events,
    enabled: !!userId && enabled,
  });
}

/**
 * Query for getting events by type
 */
export function useEventsByTypeQuery(
  userId: string | undefined,
  eventType: EventType,
  enabled = true,
) {
  return useQuery({
    queryKey: ["events", "type", userId, eventType],
    queryFn: async () => {
      if (!userId) return [];

      const allEvents = await eventDBService.findByUserId(userId);
      return allEvents.filter((event) => event.type === eventType);
    },
    ...cacheConfig.events,
    enabled: !!userId && enabled,
  });
}

/**
 * Mutations for event operations
 */
export function useEventMutations() {
  const queryClient = useQueryClient();

  const createEvent = useMutation({
    mutationFn: async (params: {
      userId: string;
      type: EventType;
      timestamp: Date;
      notes?: string;
      duration?: number;
      isPrivate?: boolean;
      sessionId?: string;
      metadata?: Record<string, unknown>;
    }) => {
      // 1. Write to local Dexie immediately for optimistic update
      const { notes, duration, ...restParams } = params;
      const eventData = {
        ...restParams,
        isPrivate: restParams.isPrivate ?? false, // Ensure isPrivate is always defined
        details: {
          notes,
          duration,
        },
      };
      const event = await eventDBService.createEvent(eventData);

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserEvents(params.userId).catch((error) => {
          logger.warn("Event creation sync failed", { error });
        });
      }

      return event;
    },
    onSuccess: (data, variables) => {
      // Add event to the cache
      queryClient.setQueryData(
        ["events", "user", variables.userId],
        (oldEvents: DBEvent[] | undefined) => {
          if (!oldEvents) return [data];
          return [data, ...oldEvents].sort((a, b) => {
            // Type guard to ensure we have DBEvent objects with timestamp
            if (typeof a === "string" || typeof b === "string") return 0;
            return b.timestamp.getTime() - a.timestamp.getTime();
          });
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["events", "recent", variables.userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["events", "type", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to create event", { error });
    },
  });

  const updateEvent = useMutation({
    mutationFn: async (params: {
      eventId: string;
      userId: string;
      updates: Partial<DBEvent>;
    }) => {
      // 1. Update local Dexie immediately
      const updatedEvent = await eventDBService.updateEvent(
        params.eventId,
        params.updates,
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserEvents(params.userId).catch((error) => {
          logger.warn("Event update sync failed", { error });
        });
      }

      return updatedEvent;
    },
    onSuccess: (data, variables) => {
      // Update event in cache
      queryClient.setQueryData(
        ["events", "user", variables.userId],
        (oldEvents: DBEvent[] | undefined) => {
          if (!oldEvents) return oldEvents;
          return oldEvents.map((event) =>
            event.id === variables.eventId
              ? { ...event, ...(data as Partial<DBEvent>) }
              : event,
          );
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["events", "recent", variables.userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["events", "type", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to update event", { error });
    },
  });

  const deleteEvent = useMutation({
    mutationFn: async (params: { eventId: string; userId: string }) => {
      // 1. Delete from local Dexie immediately
      await eventDBService.deleteEvent(params.eventId);

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserEvents(params.userId).catch((error) => {
          logger.warn("Event deletion sync failed", { error });
        });
      }

      return params.eventId;
    },
    onSuccess: (eventId, variables) => {
      // Remove event from cache
      queryClient.setQueryData(
        ["events", "user", variables.userId],
        (oldEvents: DBEvent[] | undefined) => {
          if (!oldEvents) return oldEvents;
          return oldEvents.filter((event) => event.id !== eventId);
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["events", "recent", variables.userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["events", "type", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to delete event", { error });
    },
  });

  const bulkCreateEvents = useMutation({
    mutationFn: async (params: {
      userId: string;
      events: Array<{
        type: EventType;
        timestamp: Date;
        notes?: string;
        duration?: number;
        isPrivate?: boolean;
        sessionId?: string;
        metadata?: Record<string, unknown>;
      }>;
    }) => {
      // 1. Create all events in local Dexie
      const createdEvents = await Promise.all(
        params.events.map((eventData) => {
          const { notes, duration, ...restEventData } = eventData;
          return eventDBService.createEvent({
            ...restEventData,
            userId: params.userId,
            isPrivate: restEventData.isPrivate ?? false, // Ensure isPrivate is always defined
            details: {
              notes,
              duration,
            },
          });
        }),
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserEvents(params.userId).catch((error) => {
          logger.warn("Bulk event creation sync failed", { error });
        });
      }

      return createdEvents;
    },
    onSuccess: (data, variables) => {
      // Add all events to cache
      queryClient.setQueryData(
        ["events", "user", variables.userId],
        (oldEvents: DBEvent[] | undefined) => {
          if (!oldEvents) return data;
          return [...data, ...oldEvents].sort((a, b) => {
            // Type guard to ensure we have DBEvent objects with timestamp
            if (typeof a === "string" || typeof b === "string") return 0;
            return b.timestamp.getTime() - a.timestamp.getTime();
          });
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["events", "recent", variables.userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["events", "type", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to bulk create events", { error });
    },
  });

  return {
    createEvent,
    updateEvent,
    deleteEvent,
    bulkCreateEvents,
  };
}

/**
 * Session TanStack Query Hooks
 * Manages chastity session data with Dexie as backend
 */
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { sessionDBService } from "@/services/database";
import { cacheConfig } from "@/services/cache-config";
import { firebaseSync } from "@/services/sync";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useSessionQuery");

/**
 * Query for getting current active session
 */
export function useCurrentSession(userId: string | undefined) {
  return useQuery({
    queryKey: ["session", "current", userId],
    queryFn: async () => {
      if (!userId) return null;

      // Always read from local Dexie first for instant response
      const session = await sessionDBService.getCurrentSession(userId);

      // Trigger background sync if online to ensure data freshness
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(userId).catch((error) => {
          logger.warn("Background session sync failed:", { error });
        });
      }

      return session;
    },
    ...cacheConfig.currentSession, // Apply specific cache settings
    enabled: !!userId, // The query will not run until the userId is available
  });
}

/**
 * Query for getting session history
 */
export function useSessionHistory(userId: string | undefined, enabled = true) {
  return useQuery({
    queryKey: ["sessions", "history", userId],
    queryFn: async () => {
      if (!userId) return [];

      const sessions = await sessionDBService.findByUserId(userId);

      // Trigger background sync if online
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(userId).catch((error) => {
          logger.warn("Background session history sync failed:", { error });
        });
      }

      return sessions.sort(
        (a, b) => b.startTime.getTime() - a.startTime.getTime(),
      );
    },
    ...cacheConfig.sessionHistory,
    enabled: !!userId && enabled,
  });
}

/**
 * Mutations for session operations
 */
export function useSessionMutations() {
  const queryClient = useQueryClient();

  const startSession = useMutation({
    mutationFn: async (params: {
      userId: string;
      startTime?: Date;
      requiredDuration?: number;
    }) => {
      // 1. Write to local Dexie immediately for optimistic update
      const sessionId = await sessionDBService.startSession(params.userId, {
        goalDuration: params.requiredDuration,
        notes: `Session started at ${params.startTime || new Date()}`,
      });

      // Get the created session
      const session = await sessionDBService.findById(sessionId);
      if (!session) {
        throw new Error("Failed to create session");
      }

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(params.userId).catch((error) => {
          logger.warn("Session start sync failed:", { error });
        });
      }

      return session;
    },
    onSuccess: (data, variables) => {
      // Update the current session cache
      queryClient.setQueryData(["session", "current", variables.userId], data);

      // Invalidate session history to include new session
      queryClient.invalidateQueries({
        queryKey: ["sessions", "history", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to start session:", { error });
    },
  });

  const endSession = useMutation({
    mutationFn: async (params: {
      userId: string;
      endTime?: Date;
      reason?: string;
    }) => {
      // 1. Update local Dexie immediately
      const updatedSession = await sessionDBService.endSession(
        params.userId,
        params.endTime || new Date(),
        params.reason,
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(params.userId).catch((error) => {
          logger.warn("Session end sync failed:", { error });
        });
      }

      return updatedSession;
    },
    onSuccess: (data, variables) => {
      // Clear current session since it's ended
      queryClient.setQueryData(["session", "current", variables.userId], null);

      // Invalidate session history to reflect ended session
      queryClient.invalidateQueries({
        queryKey: ["sessions", "history", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to end session:", { error });
    },
  });

  const pauseSession = useMutation({
    mutationFn: async (params: { userId: string; reason?: string }) => {
      // 1. Get current session first
      const currentSession = await sessionDBService.getCurrentSession(
        params.userId,
      );
      if (!currentSession) {
        throw new Error("No active session to pause");
      }

      // 2. Update local Dexie immediately
      await sessionDBService.pauseSession(currentSession.id, new Date());

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(params.userId).catch((error) => {
          logger.warn("Session pause sync failed:", { error });
        });
      }

      // Return the updated session
      const updatedSession = await sessionDBService.getCurrentSession(
        params.userId,
      );
      return updatedSession;
    },
    onSuccess: (data, variables) => {
      // Update current session cache
      queryClient.setQueryData(["session", "current", variables.userId], data);
    },
    onError: (error) => {
      logger.error("Failed to pause session:", { error });
    },
  });

  const resumeSession = useMutation({
    mutationFn: async (params: { userId: string }) => {
      // 1. Update local Dexie immediately
      const updatedSession = await sessionDBService.resumeSession(
        params.userId,
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSessions(params.userId).catch((error) => {
          logger.warn("Session resume sync failed:", { error });
        });
      }

      return updatedSession;
    },
    onSuccess: (data, variables) => {
      // Update current session cache
      queryClient.setQueryData(["session", "current", variables.userId], data);
    },
    onError: (error) => {
      logger.error("Failed to resume session:", { error });
    },
  });

  return {
    startSession,
    endSession,
    pauseSession,
    resumeSession,
  };
}

/**
 * Settings TanStack Query Hooks
 * Manages user settings with Dexie as backend
 */
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { settingsDBService } from "@/services/database";
import { cacheConfig } from "@/services/cache-config";
import { firebaseSync } from "@/services/sync";
import type { DBSettings } from "@/types/database";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useSettingsQuery");

/**
 * Query for getting user settings
 */
export function useSettingsQuery(userId: string | undefined) {
  return useQuery({
    queryKey: ["settings", "user", userId],
    queryFn: async () => {
      if (!userId) return null;

      // Always read from local Dexie first for instant response
      let settings = await settingsDBService.getUserSettings(userId);

      // If no settings exist, create default settings
      if (!settings) {
        settings = await settingsDBService.createDefaultSettings(userId);
      }

      // Trigger background sync if online to ensure data freshness
      if (navigator.onLine) {
        firebaseSync.syncUserSettings(userId).catch((error) => {
          logger.warn("Background settings sync failed:", { error });
        });
      }

      return settings;
    },
    ...cacheConfig.userSettings, // Apply specific cache settings
    enabled: !!userId, // Only run when userId is available
  });
}

/**
 * Mutations for settings operations
 */
export function useSettingsMutations() {
  const queryClient = useQueryClient();

  const updateSettings = useMutation({
    mutationFn: async (params: {
      userId: string;
      settings: Partial<DBSettings>;
    }) => {
      // 1. Update local Dexie immediately for optimistic update
      const updatedSettings = await settingsDBService.updateSettings(
        params.userId,
        params.settings,
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSettings(params.userId).catch((error) => {
          logger.warn("Settings update sync failed:", { error });
        });
      }

      return updatedSettings;
    },
    onSuccess: (data, variables) => {
      // Update the settings cache immediately
      queryClient.setQueryData(["settings", "user", variables.userId], data);
    },
    onError: (error) => {
      logger.error("Failed to update settings:", { error });

      // Invalidate cache to refetch from server in case of error
      queryClient.invalidateQueries({
        queryKey: ["settings", "user"],
      });
    },
  });

  const resetSettings = useMutation({
    mutationFn: async (params: { userId: string }) => {
      // 1. Reset to defaults in local Dexie
      const defaultSettings = await settingsDBService.createDefaultSettings(
        params.userId,
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserSettings(params.userId).catch((error) => {
          logger.warn("Settings reset sync failed:", { error });
        });
      }

      return defaultSettings;
    },
    onSuccess: (data, variables) => {
      // Update the settings cache with default values
      queryClient.setQueryData(["settings", "user", variables.userId], data);
    },
    onError: (error) => {
      logger.error("Failed to reset settings:", { error });
    },
  });

  const updateTheme = useMutation({
    mutationFn: async (params: { userId: string; theme: "light" | "dark" }) => {
      // Quick theme update
      const updatedSettings = await settingsDBService.updateSettings(
        params.userId,
        { theme: params.theme },
      );

      // Background sync
      if (navigator.onLine) {
        firebaseSync.syncUserSettings(params.userId).catch((error) => {
          logger.warn("Theme update sync failed:", { error });
        });
      }

      return updatedSettings;
    },
    onSuccess: (data, variables) => {
      // Update settings cache
      queryClient.setQueryData(["settings", "user", variables.userId], data);
    },
  });

  const updateEventDisplayMode = useMutation({
    mutationFn: async (params: {
      userId: string;
      eventDisplayMode: "kinky" | "vanilla";
    }) => {
      const updatedSettings = await settingsDBService.updateSettings(
        params.userId,
        { eventDisplayMode: params.eventDisplayMode },
      );

      // Background sync
      if (navigator.onLine) {
        firebaseSync.syncUserSettings(params.userId).catch((error) => {
          logger.warn("Event display mode sync failed:", { error });
        });
      }

      return updatedSettings;
    },
    onSuccess: (data, variables) => {
      queryClient.setQueryData(["settings", "user", variables.userId], data);
    },
  });

  return {
    updateSettings,
    resetSettings,
    updateTheme,
    updateEventDisplayMode,
  };
}

/**
 * Task TanStack Query Hooks
 * Manages server state for tasks with Dexie as backend
 */
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { taskDBService } from "@/services/database";
import { cacheConfig } from "@/services/cache-config";
import { firebaseSync } from "@/services/sync";
import type { DBTask, TaskStatus } from "@/types/database";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useTaskQuery");

/**
 * Query for getting all tasks for a user
 */
export function useTasksQuery(userId: string | undefined) {
  return useQuery({
    queryKey: ["tasks", "user", userId],
    queryFn: async () => {
      if (!userId) return [];

      // Always read from local Dexie first for instant response
      const tasks = await taskDBService.findByUserId(userId);

      // Trigger background sync if online to ensure data freshness
      if (navigator.onLine) {
        firebaseSync.syncUserTasks(userId).catch((error) => {
          logger.warn("Background task sync failed", { error });
        });
      }

      return tasks;
    },
    ...cacheConfig.tasks, // Apply specific cache settings
    enabled: !!userId, // Only run when userId is available
  });
}

/**
 * Query for getting pending tasks that need keyholder attention
 */
export function usePendingTasksQuery(
  userId: string | undefined,
  enabled = true,
) {
  return useQuery({
    queryKey: ["tasks", "pending", userId],
    queryFn: async () => {
      if (!userId) return [];

      const allTasks = await taskDBService.findByUserId(userId);
      return allTasks.filter((task) =>
        ["pending", "submitted"].includes(task.status),
      );
    },
    ...cacheConfig.tasks,
    enabled: !!userId && enabled,
  });
}

/**
 * Mutations for task operations
 */
export function useTaskMutations() {
  const queryClient = useQueryClient();

  const createTask = useMutation({
    mutationFn: async (params: {
      userId: string;
      title: string;
      description?: string;
      deadline?: Date;
    }) => {
      // 1. Write to local Dexie immediately for optimistic update
      const task = await taskDBService.createTask({
        ...params,
        status: "pending" as TaskStatus,
        text: params.description || params.title, // Use description or title as text
        priority: "medium" as const, // Default priority
        assignedBy: "submissive" as const, // Default assignment source
      });

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserTasks(params.userId).catch((error) => {
          logger.warn("Task creation sync failed", { error });
        });
      }

      return task;
    },
    onSuccess: (data, variables) => {
      // Invalidate and refetch tasks queries
      queryClient.invalidateQueries({
        queryKey: ["tasks", "user", variables.userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["tasks", "pending", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to create task", { error });
    },
  });

  const updateTaskStatus = useMutation({
    mutationFn: async (params: {
      taskId: string;
      userId: string;
      status: TaskStatus;
      feedback?: string;
    }) => {
      // 1. Update local Dexie immediately
      const updatedTask = await taskDBService.updateTaskStatus(
        params.taskId,
        params.status,
        {
          keyholderFeedback: params.feedback,
        },
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserTasks(params.userId).catch((error) => {
          logger.warn("Task status update sync failed", { error });
        });
      }

      return updatedTask;
    },
    onSuccess: (data, variables) => {
      // Update specific task in cache
      queryClient.setQueryData(
        ["tasks", "user", variables.userId],
        (oldTasks: DBTask[] | undefined) => {
          if (!oldTasks) return oldTasks;
          return oldTasks.map((task) =>
            task.id === variables.taskId
              ? { ...task, ...(data as Partial<DBTask>) }
              : task,
          );
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["tasks", "pending", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to update task status", { error });
    },
  });

  const deleteTask = useMutation({
    mutationFn: async (params: { taskId: string; userId: string }) => {
      // 1. Delete from local Dexie immediately
      await taskDBService.deleteTask(params.taskId);

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserTasks(params.userId).catch((error) => {
          logger.warn("Task deletion sync failed", { error });
        });
      }

      return params.taskId;
    },
    onSuccess: (taskId, variables) => {
      // Remove task from cache
      queryClient.setQueryData(
        ["tasks", "user", variables.userId],
        (oldTasks: DBTask[] | undefined) => {
          if (!oldTasks) return oldTasks;
          return oldTasks.filter((task) => task.id !== taskId);
        },
      );

      // Invalidate related queries
      queryClient.invalidateQueries({
        queryKey: ["tasks", "pending", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to delete task", { error });
    },
  });

  const submitTaskForReview = useMutation({
    mutationFn: async (params: {
      taskId: string;
      userId: string;
      note?: string;
    }) => {
      // 1. Update local Dexie immediately
      const updatedTask = await taskDBService.updateTaskStatus(
        params.taskId,
        "submitted",
        {
          submissiveNote: params.note,
        },
      );

      // 2. Trigger Firebase sync in background
      if (navigator.onLine) {
        firebaseSync.syncUserTasks(params.userId).catch((error) => {
          logger.warn("Task submission sync failed", { error });
        });
      }

      return updatedTask;
    },
    onSuccess: (data, variables) => {
      // Update task in cache
      queryClient.setQueryData(
        ["tasks", "user", variables.userId],
        (oldTasks: DBTask[] | undefined) => {
          if (!oldTasks) return oldTasks;
          return oldTasks.map((task) =>
            task.id === variables.taskId
              ? { ...task, ...(data as Partial<DBTask>) }
              : task,
          );
        },
      );

      // Invalidate pending tasks since this affects that query
      queryClient.invalidateQueries({
        queryKey: ["tasks", "pending", variables.userId],
      });
    },
    onError: (error) => {
      logger.error("Failed to submit task for review", { error });
    },
  });

  return {
    createTask,
    updateTaskStatus,
    deleteTask,
    submitTaskForReview,
  };
}

import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { taskDBService } from "../../services/database/TaskDBService";
import { Task, TaskStatus } from "../../types/database";
import { logger } from "../../utils/logging";

/**
 * Task Management Hooks - TanStack Query Integration
 *
 * Integrates with:
 * - taskDBService  Dexie  Firebase sync
 * - TasksPage.tsx, TaskManagement.tsx (critical fixes needed)
 *
 * Fixes:
 * - TasksPage.tsx:20 (taskDBService.findByUserId)
 * - TasksPage.tsx:34 (taskDBService.updateTaskStatus)
 *
 * Strategy: Optimistic update + background sync
 */

// Query Keys
export const taskKeys = {
  all: ["tasks"] as const,
  lists: () => [...taskKeys.all, "list"] as const,
  list: (userId: string, filters?: TaskFilters) =>
    [...taskKeys.lists(), userId, filters] as const,
  detail: (taskId: string) => [...taskKeys.all, "detail", taskId] as const,
  byStatus: (userId: string, status: TaskStatus) =>
    [...taskKeys.all, "status", userId, status] as const,
  assigned: (userId: string) => [...taskKeys.all, "assigned", userId] as const,
  assignedBy: (keyholderUid: string) =>
    [...taskKeys.all, "assignedBy", keyholderUid] as const,
} as const;

// Types
interface TaskFilters {
  status?: TaskStatus;
  priority?: "low" | "medium" | "high";
  category?: string;
  assignedBy?: string;
  dueDate?: {
    start?: Date;
    end?: Date;
  };
}

interface CreateTaskData {
  title: string;
  description?: string;
  priority?: "low" | "medium" | "high";
  category?: string;
  dueDate?: Date;
  assignedBy?: string; // keyholder UID
  metadata?: Record<string, unknown>;
}

interface UpdateTaskData {
  title?: string;
  description?: string;
  priority?: "low" | "medium" | "high";
  category?: string;
  dueDate?: Date;
  metadata?: Record<string, unknown>;
}

/**
 * Get tasks for a user with optional filtering
 * Fixes: TasksPage.tsx:20 (taskDBService.findByUserId)
 */
export function useTasks(userId: string, filters?: TaskFilters) {
  return useQuery({
    queryKey: taskKeys.list(userId, filters),
    queryFn: async (): Promise<Task[]> => {
      logger.info("Fetching tasks", { userId, filters });

      try {
        const tasks = await taskDBService.findByUserId(userId);

        // Apply filters
        let filteredTasks = tasks;

        if (filters?.status) {
          filteredTasks = filteredTasks.filter(
            (task) => task.status === filters.status,
          );
        }

        if (filters?.priority) {
          filteredTasks = filteredTasks.filter(
            (task) => task.priority === filters.priority,
          );
        }

        if (filters?.category) {
          filteredTasks = filteredTasks.filter(
            (task) => task.category === filters.category,
          );
        }

        if (filters?.assignedBy) {
          filteredTasks = filteredTasks.filter(
            (task) => task.assignedBy === filters.assignedBy,
          );
        }

        if (filters?.dueDate) {
          filteredTasks = filteredTasks.filter((task) => {
            if (!task.dueDate) return false;
            const taskDue = new Date(task.dueDate);

            if (filters.dueDate?.start && taskDue < filters.dueDate.start)
              return false;
            if (filters.dueDate?.end && taskDue > filters.dueDate.end)
              return false;

            return true;
          });
        }

        // Sort by priority and due date
        filteredTasks.sort((a, b) => {
          // Priority sorting (high > medium > low)
          const priorityOrder = { high: 3, medium: 2, low: 1 };
          const aPriority = priorityOrder[a.priority || "medium"];
          const bPriority = priorityOrder[b.priority || "medium"];

          if (aPriority !== bPriority) {
            return bPriority - aPriority;
          }

          // Due date sorting (sooner first)
          if (a.dueDate && b.dueDate) {
            return (
              new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime()
            );
          }

          if (a.dueDate && !b.dueDate) return -1;
          if (!a.dueDate && b.dueDate) return 1;

          // Created date sorting (newest first)
          return (
            new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
          );
        });

        logger.info("Tasks retrieved successfully", {
          userId,
          totalTasks: tasks.length,
          filteredTasks: filteredTasks.length,
        });

        return filteredTasks;
      } catch (error) {
        logger.error("Failed to fetch tasks", {
          error: error instanceof Error ? error.message : String(error),
          userId,
          filters,
        });
        throw error;
      }
    },
    staleTime: 2 * 60 * 1000, // 2 minutes - active data
    gcTime: 30 * 60 * 1000, // 30 minutes garbage collection
    enabled: !!userId,
  });
}

/**
 * Get tasks by status (useful for kanban boards)
 */
export function useTasksByStatus(userId: string, status: TaskStatus) {
  return useQuery({
    queryKey: taskKeys.byStatus(userId, status),
    queryFn: async (): Promise<Task[]> => {
      const tasks = await taskDBService.findByUserId(userId);
      return tasks.filter((task) => task.status === status);
    },
    staleTime: 2 * 60 * 1000,
    enabled: !!userId,
  });
}

/**
 * Get single task by ID
 */
export function useTask(taskId: string) {
  return useQuery({
    queryKey: taskKeys.detail(taskId),
    queryFn: async (): Promise<Task | null> => {
      logger.info("Fetching task detail", { taskId });
      const task = await taskDBService.findById(taskId);
      return task ?? null;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes - task details are stable
    enabled: !!taskId,
  });
}

/**
 * Get tasks assigned by a specific keyholder
 */
export function useTasksAssignedBy(keyholderUid: string) {
  return useQuery({
    queryKey: taskKeys.assignedBy(keyholderUid),
    queryFn: async (): Promise<Task[]> => {
      logger.info("Fetching tasks assigned by keyholder", { keyholderUid });

      // Note: This would require a different query method in the future
      // For now, we'll need to scan all tasks (inefficient but works)
      const allTasks = await taskDBService.getAll();
      return allTasks.filter((task: Task) => task.assignedBy === keyholderUid);
    },
    staleTime: 5 * 60 * 1000,
    enabled: !!keyholderUid,
  });
}

/**
 * Create new task
 */
export function useCreateTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      userId,
      taskData,
    }: {
      userId: string;
      taskData: CreateTaskData;
    }): Promise<Task> => {
      logger.info("Creating new task", { userId, title: taskData.title });

      // Generate task ID
      const taskId = `task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      const newTask: Task = {
        id: taskId,
        userId,
        title: taskData.title,
        description: taskData.description || "",
        status: "pending",
        priority: taskData.priority || "medium",
        category: taskData.category,
        dueDate: taskData.dueDate,
        assignedBy:
          (taskData.assignedBy as "submissive" | "keyholder") || "submissive",
        createdAt: new Date(),
        updatedAt: new Date(),
      };

      // Dexie-first write for immediate UI response
      await taskDBService.create(newTask);

      logger.info("Task created successfully", {
        taskId,
        userId,
        title: taskData.title,
      });

      return newTask;
    },
    onSuccess: (
      newTask: Task,
      { userId }: { userId: string; taskData: CreateTaskData },
    ) => {
      logger.info("Task creation successful", { taskId: newTask.id, userId });

      // Invalidate task lists to trigger refetch
      queryClient.invalidateQueries({ queryKey: taskKeys.lists() });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, "pending"),
      });

      // Optimistically add to cache if we have existing data
      queryClient.setQueriesData(
        { queryKey: taskKeys.list(userId) },
        (oldData: Task[] | undefined) => {
          if (!oldData) return undefined;
          return [newTask, ...oldData];
        },
      );
    },
    onError: (error, { userId, taskData }) => {
      logger.error("Task creation failed", {
        error: error instanceof Error ? error.message : String(error),
        userId,
        title: taskData.title,
      });
    },
  });
}

/**
 * Update task status (most common operation)
 * Fixes: TasksPage.tsx:34 (taskDBService.updateTaskStatus)
 * Strategy: Optimistic update + background sync
 */
export function useUpdateTaskStatus() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      taskId,
      userId,
      status,
      completedAt,
    }: {
      taskId: string;
      userId: string;
      status: TaskStatus;
      completedAt?: Date;
    }): Promise<Task> => {
      logger.info("Updating task status", { taskId, userId, status });

      const existingTask = await taskDBService.findById(taskId);
      if (!existingTask) {
        throw new Error(`Task not found: ${taskId}`);
      }

      const updatedTask: Task = {
        ...existingTask,
        status,
        completedAt:
          status === "completed" ? completedAt || new Date() : undefined,
        updatedAt: new Date(),
      };

      await taskDBService.updateTaskStatus(taskId, status);

      logger.info("Task status updated successfully", {
        taskId,
        userId,
        status,
      });

      return updatedTask;
    },
    onMutate: async ({ taskId, userId, status }) => {
      // Cancel outgoing refetches
      await queryClient.cancelQueries({ queryKey: taskKeys.detail(taskId) });
      await queryClient.cancelQueries({ queryKey: taskKeys.list(userId) });

      // Snapshot previous values
      const previousTask = queryClient.getQueryData(taskKeys.detail(taskId));
      const previousTasks = queryClient.getQueryData(taskKeys.list(userId));

      // Optimistically update task detail
      queryClient.setQueryData(
        taskKeys.detail(taskId),
        (old: Task | undefined) => {
          if (!old) return old;
          return { ...old, status, updatedAt: new Date() };
        },
      );

      // Optimistically update task lists
      queryClient.setQueriesData(
        { queryKey: taskKeys.list(userId) },
        (oldData: Task[] | undefined) => {
          if (!oldData) return oldData;
          return oldData.map((task) =>
            task.id === taskId
              ? { ...task, status, updatedAt: new Date() }
              : task,
          );
        },
      );

      return { previousTask, previousTasks };
    },
    onSuccess: (
      updatedTask: Task,
      {
        userId,
        taskId,
      }: { userId: string; taskId: string; status: TaskStatus },
    ) => {
      logger.info("Task status update successful", {
        taskId,
        userId,
        status: updatedTask.status,
      });

      // Invalidate relevant queries
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, updatedTask.status),
      });

      // If task was completed, invalidate pending tasks
      if (updatedTask.status === "completed") {
        queryClient.invalidateQueries({
          queryKey: taskKeys.byStatus(userId, "pending"),
        });
      }
    },
    onError: (error, { taskId, userId }, context) => {
      logger.error("Task status update failed", {
        error: error instanceof Error ? error.message : String(error),
        taskId,
        userId,
      });

      // Rollback optimistic updates
      if (context?.previousTask) {
        queryClient.setQueryData(taskKeys.detail(taskId), context.previousTask);
      }
      if (context?.previousTasks) {
        queryClient.setQueryData(taskKeys.list(userId), context.previousTasks);
      }
    },
  });
}

/**
 * Update full task details
 */
export function useUpdateTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      taskId,
      userId,
      updates,
    }: {
      taskId: string;
      userId: string;
      updates: UpdateTaskData;
    }): Promise<Task> => {
      logger.info("Updating task", { taskId, userId });

      const existingTask = await taskDBService.findById(taskId);
      if (!existingTask) {
        throw new Error(`Task not found: ${taskId}`);
      }

      const updatedTask: Task = {
        ...existingTask,
        ...updates,
        updatedAt: new Date(),
      };

      await taskDBService.update(taskId, updatedTask);

      logger.info("Task updated successfully", { taskId, userId });

      return updatedTask;
    },
    onSuccess: (
      updatedTask: Task,
      {
        userId,
        taskId,
      }: { userId: string; taskId: string; updates: UpdateTaskData },
    ) => {
      logger.info("Task update successful", { taskId, userId });

      // Update detail cache
      queryClient.setQueryData(taskKeys.detail(taskId), updatedTask);

      // Invalidate list queries to reflect changes
      queryClient.invalidateQueries({ queryKey: taskKeys.lists() });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, updatedTask.status),
      });
    },
    onError: (error, { taskId, userId }) => {
      logger.error("Task update failed", {
        error: error instanceof Error ? error.message : String(error),
        taskId,
        userId,
      });
    },
  });
}

/**
 * Delete task
 */
export function useDeleteTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({
      taskId,
      userId,
    }: {
      taskId: string;
      userId: string;
    }): Promise<void> => {
      logger.info("Deleting task", { taskId, userId });

      await taskDBService.delete(taskId);

      logger.info("Task deleted successfully", { taskId, userId });
    },
    onSuccess: (
      _: void,
      { taskId, userId }: { taskId: string; userId: string },
    ) => {
      logger.info("Task deletion successful", { taskId, userId });

      // Remove from detail cache
      queryClient.removeQueries({ queryKey: taskKeys.detail(taskId) });

      // Invalidate list queries
      queryClient.invalidateQueries({ queryKey: taskKeys.lists() });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, "pending"),
      });
      queryClient.invalidateQueries({
        queryKey: taskKeys.byStatus(userId, "completed"),
      });

      // Optimistically remove from cached lists
      queryClient.setQueriesData(
        { queryKey: taskKeys.list(userId) },
        (oldData: Task[] | undefined) => {
          if (!oldData) return undefined;
          return oldData.filter((task) => task.id !== taskId);
        },
      );
    },
    onError: (error, { taskId, userId }) => {
      logger.error("Task deletion failed", {
        error: error instanceof Error ? error.message : String(error),
        taskId,
        userId,
      });
    },
  });
}

/**
 * Get task statistics for dashboard
 */
export function useTaskStats(userId: string) {
  return useQuery({
    queryKey: [...taskKeys.all, "stats", userId],
    queryFn: async () => {
      logger.info("Calculating task statistics", { userId });

      const tasks = await taskDBService.findByUserId(userId);

      const stats = {
        total: tasks.length,
        pending: tasks.filter((t) => t.status === "pending").length,
        inProgress: tasks.filter((t) => t.status === "in_progress").length,
        completed: tasks.filter((t) => t.status === "completed").length,
        overdue: tasks.filter((t) => {
          if (!t.dueDate || t.status === "completed") return false;
          return new Date(t.dueDate) < new Date();
        }).length,
        byPriority: {
          high: tasks.filter((t) => t.priority === "high").length,
          medium: tasks.filter((t) => t.priority === "medium").length,
          low: tasks.filter((t) => t.priority === "low").length,
        },
        completionRate:
          tasks.length > 0
            ? Math.round(
                (tasks.filter((t) => t.status === "completed").length /
                  tasks.length) *
                  100,
              )
            : 0,
      };

      return stats;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    enabled: !!userId,
  });
}

/**
 * Pull to Refresh Hook
 * Provides pull-to-refresh functionality for mobile devices
 */
import { useCallback, useRef, useState, useEffect } from "react";
import type { React } from "react";
import { useHapticFeedback } from "./useHapticFeedback";
import { logger } from "../../utils/logging";

interface PullToRefreshOptions {
  threshold?: number;
  maxPullDistance?: number;
  onRefresh: () => Promise<void> | void;
  disabled?: boolean;
}

interface PullToRefreshReturn {
  isRefreshing: boolean;
  isPulling: boolean;
  pullDistance: number;
  pullPercentage: number;
  onTouchStart: (e: React.TouchEvent) => void;
  onTouchMove: (e: React.TouchEvent) => void;
  onTouchEnd: (e: React.TouchEvent) => void;
  onScroll: (e: React.UIEvent) => void;
}

export const usePullToRefresh = ({
  threshold = 80,
  maxPullDistance = 120,
  onRefresh,
  disabled = false,
}: PullToRefreshOptions): PullToRefreshReturn => {
  const { light: hapticLight, success: hapticSuccess } = useHapticFeedback();

  const [isRefreshing, setIsRefreshing] = useState(false);
  const [isPulling, setIsPulling] = useState(false);
  const [pullDistance, setPullDistance] = useState(0);
  const [_scrollTop, setScrollTop] = useState(0);

  const startY = useRef<number>(0);
  const currentY = useRef<number>(0);
  const isAtTop = useRef<boolean>(true);
  const hasTriggeredHaptic = useRef<boolean>(false);

  const pullPercentage = Math.min((pullDistance / threshold) * 100, 100);

  const handleTouchStart = useCallback(
    (e: React.TouchEvent) => {
      if (disabled || isRefreshing) return;

      const touch = e.touches[0];
      if (!touch) return;

      startY.current = touch.clientY;
      currentY.current = touch.clientY;
      hasTriggeredHaptic.current = false;
    },
    [disabled, isRefreshing],
  );

  const handleTouchMove = useCallback(
    (e: React.TouchEvent) => {
      if (disabled || isRefreshing || !isAtTop.current) return;

      const touch = e.touches[0];
      if (!touch) return;

      currentY.current = touch.clientY;
      const deltaY = currentY.current - startY.current;

      if (deltaY > 0) {
        // Pulling down
        const distance = Math.min(deltaY * 0.5, maxPullDistance); // Apply resistance
        setPullDistance(distance);
        setIsPulling(distance > 10);

        // Haptic feedback when threshold is reached
        if (distance >= threshold && !hasTriggeredHaptic.current) {
          hapticLight();
          hasTriggeredHaptic.current = true;
        }

        // Prevent default scrolling when pulling
        if (deltaY > 10) {
          e.preventDefault();
        }
      } else {
        setPullDistance(0);
        setIsPulling(false);
      }
    },
    [disabled, isRefreshing, threshold, maxPullDistance, hapticLight],
  );

  const handleTouchEnd = useCallback(async () => {
    if (disabled || isRefreshing) return;

    if (pullDistance >= threshold) {
      setIsRefreshing(true);
      hapticSuccess();

      try {
        await onRefresh();
      } catch (error) {
        logger.error("Pull to refresh failed", { error }, "usePullToRefresh");
      } finally {
        setIsRefreshing(false);
      }
    }

    setPullDistance(0);
    setIsPulling(false);
    hasTriggeredHaptic.current = false;
  }, [
    disabled,
    isRefreshing,
    pullDistance,
    threshold,
    onRefresh,
    hapticSuccess,
  ]);

  const handleScroll = useCallback(
    (e: React.UIEvent) => {
      const target = e.currentTarget as HTMLElement;
      const scrollTop = target.scrollTop;
      setScrollTop(scrollTop);
      isAtTop.current = scrollTop <= 0;

      // Reset pull state when scrolling away from top
      if (scrollTop > 0 && isPulling) {
        setPullDistance(0);
        setIsPulling(false);
      }
    },
    [isPulling],
  );

  // Reset states when disabled changes
  useEffect(() => {
    if (disabled) {
      setPullDistance(0);
      setIsPulling(false);
      setIsRefreshing(false);
    }
  }, [disabled]);

  return {
    isRefreshing,
    isPulling,
    pullDistance,
    pullPercentage,
    onTouchStart: handleTouchStart,
    onTouchMove: handleTouchMove,
    onTouchEnd: handleTouchEnd,
    onScroll: handleScroll,
  };
};

/**
 * Touch Gestures Hook
 * Provides swipe, tap, and other touch interactions for mobile devices
 */
import { useCallback, useRef, useState } from "react";
import type { React } from "react";

interface TouchPoint {
  x: number;
  y: number;
  timestamp: number;
}

interface SwipeOptions {
  minDistance?: number;
  maxTime?: number;
  threshold?: number;
}

interface UseTouchGesturesReturn {
  onTouchStart: (e: React.TouchEvent) => void;
  onTouchMove: (e: React.TouchEvent) => void;
  onTouchEnd: (e: React.TouchEvent) => void;
  isSwipeActive: boolean;
}

interface TouchGestureHandlers {
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
  onTap?: () => void;
  onLongPress?: () => void;
}

// Helper function to determine if a gesture is a tap
const isTapGesture = (
  deltaX: number,
  deltaY: number,
  threshold: number,
): boolean => {
  return Math.abs(deltaX) < threshold && Math.abs(deltaY) < threshold;
};

// Helper function to determine swipe direction and trigger handler
const handleSwipeGesture = (
  deltaX: number,
  deltaY: number,
  minDistance: number,
  handlers: TouchGestureHandlers,
): void => {
  const absDeltaX = Math.abs(deltaX);
  const absDeltaY = Math.abs(deltaY);

  if (absDeltaX > absDeltaY && absDeltaX >= minDistance) {
    // Horizontal swipe
    if (deltaX > 0) {
      handlers.onSwipeRight?.();
    } else {
      handlers.onSwipeLeft?.();
    }
  } else if (absDeltaY >= minDistance) {
    // Vertical swipe
    if (deltaY > 0) {
      handlers.onSwipeDown?.();
    } else {
      handlers.onSwipeUp?.();
    }
  }
};

// Helper function to reset touch state
const resetTouchState = (
  touchStart: React.MutableRefObject<TouchPoint | null>,
  touchCurrent: React.MutableRefObject<TouchPoint | null>,
  setIsSwipeActive: (active: boolean) => void,
): void => {
  touchStart.current = null;
  touchCurrent.current = null;
  setIsSwipeActive(false);
};

export const useTouchGestures = (
  handlers: TouchGestureHandlers,
  options: SwipeOptions = {},
): UseTouchGesturesReturn => {
  const { minDistance = 50, maxTime = 1000, threshold = 30 } = options;

  const touchStart = useRef<TouchPoint | null>(null);
  const touchCurrent = useRef<TouchPoint | null>(null);
  const longPressTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const [isSwipeActive, setIsSwipeActive] = useState(false);

  const clearLongPressTimer = useCallback(() => {
    if (longPressTimer.current) {
      clearTimeout(longPressTimer.current);
      longPressTimer.current = null;
    }
  }, []);

  const handleTouchStart = useCallback(
    (e: React.TouchEvent) => {
      const touch = e.touches[0];
      if (!touch) return;

      const touchPoint: TouchPoint = {
        x: touch.clientX,
        y: touch.clientY,
        timestamp: Date.now(),
      };

      touchStart.current = touchPoint;
      touchCurrent.current = touchPoint;
      setIsSwipeActive(false);

      // Start long press timer
      if (handlers.onLongPress) {
        longPressTimer.current = setTimeout(() => {
          handlers.onLongPress?.();
        }, 500);
      }

      // Prevent default to avoid iOS bounce
      if (e.cancelable) {
        e.preventDefault();
      }
    },
    [handlers],
  );

  const handleTouchMove = useCallback(
    (e: React.TouchEvent) => {
      const touch = e.touches[0];
      if (!touch || !touchStart.current) return;

      touchCurrent.current = {
        x: touch.clientX,
        y: touch.clientY,
        timestamp: Date.now(),
      };

      const deltaX = Math.abs(touch.clientX - touchStart.current.x);
      const deltaY = Math.abs(touch.clientY - touchStart.current.y);

      // Clear long press if we've moved significantly
      if (deltaX > threshold || deltaY > threshold) {
        clearLongPressTimer();

        // Set swipe active if we've moved enough
        if (deltaX > minDistance || deltaY > minDistance) {
          setIsSwipeActive(true);
        }
      }

      // Prevent scrolling during horizontal swipes
      if (deltaX > deltaY && deltaX > threshold) {
        e.preventDefault();
      }
    },
    [minDistance, threshold, clearLongPressTimer],
  );

  const handleTouchEnd = useCallback(
    (_e: React.TouchEvent) => {
      clearLongPressTimer();

      if (!touchStart.current || !touchCurrent.current) {
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      const startPoint = touchStart.current;
      const endPoint = touchCurrent.current;
      const deltaTime = endPoint.timestamp - startPoint.timestamp;

      // Check if gesture was too slow
      if (deltaTime > maxTime) {
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      const deltaX = endPoint.x - startPoint.x;
      const deltaY = endPoint.y - startPoint.y;

      // Determine if it's a tap (small movement)
      if (isTapGesture(deltaX, deltaY, threshold)) {
        handlers.onTap?.();
        resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
        return;
      }

      // Handle swipe gestures
      handleSwipeGesture(deltaX, deltaY, minDistance, handlers);
      resetTouchState(touchStart, touchCurrent, setIsSwipeActive);
    },
    [handlers, maxTime, minDistance, threshold, clearLongPressTimer],
  );

  return {
    onTouchStart: handleTouchStart,
    onTouchMove: handleTouchMove,
    onTouchEnd: handleTouchEnd,
    isSwipeActive,
  };
};

/**
 * Simplified swipe hook for common use cases
 */
export const useSwipeGestures = (
  onSwipeLeft?: () => void,
  onSwipeRight?: () => void,
  options?: SwipeOptions,
) => {
  return useTouchGestures(
    {
      onSwipeLeft,
      onSwipeRight,
    },
    options,
  );
};

export default useTouchGestures;

/**
 * Viewport Hook
 * Provides viewport dimensions and mobile-specific viewport utilities
 */
import { useState, useEffect, useCallback } from "react";

interface ViewportSize {
  width: number;
  height: number;
  innerWidth: number;
  innerHeight: number;
}

interface ViewportInfo extends ViewportSize {
  isMobile: boolean;
  isTablet: boolean;
  isDesktop: boolean;
  isLandscape: boolean;
  isPortrait: boolean;
  safeAreaInsets: {
    top: number;
    bottom: number;
    left: number;
    right: number;
  };
}

// Breakpoints
const MOBILE_BREAKPOINT = 768;
const TABLET_BREAKPOINT = 1024;

export const useViewport = (): ViewportInfo => {
  const [viewport, setViewport] = useState<ViewportSize>(() => {
    if (typeof window === "undefined") {
      return {
        width: 0,
        height: 0,
        innerWidth: 0,
        innerHeight: 0,
      };
    }

    return {
      width: window.screen.width,
      height: window.screen.height,
      innerWidth: window.innerWidth,
      innerHeight: window.innerHeight,
    };
  });

  const [safeAreaInsets, setSafeAreaInsets] = useState({
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
  });

  const updateViewport = useCallback(() => {
    setViewport({
      width: window.screen.width,
      height: window.screen.height,
      innerWidth: window.innerWidth,
      innerHeight: window.innerHeight,
    });
  }, []);

  const updateSafeAreaInsets = useCallback(() => {
    if (typeof window === "undefined" || !window.getComputedStyle) return;

    const computedStyle = window.getComputedStyle(document.documentElement);

    setSafeAreaInsets({
      top: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-top)") || "0",
      ),
      bottom: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-bottom)") || "0",
      ),
      left: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-left)") || "0",
      ),
      right: parseInt(
        computedStyle.getPropertyValue("env(safe-area-inset-right)") || "0",
      ),
    });
  }, []);

  useEffect(() => {
    if (typeof window === "undefined") return;

    updateViewport();
    updateSafeAreaInsets();

    // Handle resize events with throttling
    let timeoutId: ReturnType<typeof setTimeout>;
    const handleResize = () => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        updateViewport();
        updateSafeAreaInsets();
      }, 150);
    };

    // Handle orientation change
    const handleOrientationChange = () => {
      // Delay to account for viewport changes after orientation change
      setTimeout(() => {
        updateViewport();
        updateSafeAreaInsets();
      }, 500);
    };

    window.addEventListener("resize", handleResize, { passive: true });
    window.addEventListener("orientationchange", handleOrientationChange, {
      passive: true,
    });

    return () => {
      clearTimeout(timeoutId);
      window.removeEventListener("resize", handleResize);
      window.removeEventListener("orientationchange", handleOrientationChange);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Determine device type based on viewport width
  const isMobile = viewport.innerWidth < MOBILE_BREAKPOINT;
  const isTablet =
    viewport.innerWidth >= MOBILE_BREAKPOINT &&
    viewport.innerWidth < TABLET_BREAKPOINT;
  const isDesktop = viewport.innerWidth >= TABLET_BREAKPOINT;

  // Determine orientation
  const isLandscape = viewport.innerWidth > viewport.innerHeight;
  const isPortrait = viewport.innerHeight >= viewport.innerWidth;

  return {
    ...viewport,
    isMobile,
    isTablet,
    isDesktop,
    isLandscape,
    isPortrait,
    safeAreaInsets,
  };
};

/**
 * React Hook for Relationship Status Management
 * Handles session management and status updates
 */
import { useState, useEffect, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { relationshipChastityService } from "@/services/database/RelationshipChastityService";
import {
  RelationshipChastityData,
  RelationshipSession,
} from "@/types/relationships";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";

interface RelationshipStatusState extends BaseHookState {
  chastityData: RelationshipChastityData | null;
  sessions: RelationshipSession[];
}

interface RelationshipStatusActions extends BaseHookActions {
  startSession: (
    relationshipId: string,
    options?: {
      goalDuration?: number;
      isHardcoreMode?: boolean;
      notes?: string;
    },
  ) => Promise<void>;
  endSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  pauseSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  resumeSession: (relationshipId: string, sessionId: string) => Promise<void>;
  loadRelationshipData: (relationshipId: string) => Promise<void>;
}

export function useRelationshipStatus(): RelationshipStatusState &
  RelationshipStatusActions {
  const { user } = useAuthState();
  const userId = user?.uid;

  const [state, setState] = useState<RelationshipStatusState>({
    chastityData: null,
    sessions: [],
    isLoading: false,
    error: null,
  });

  const { clearError: clearErrorFn } = createBaseActions();

  const loadChastityData = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const chastityData =
          await relationshipChastityService.getChastityData(relationshipId);
        setState((prev) => ({ ...prev, chastityData }));
      },
      "load chastity data",
      setState,
    );
  }, []);

  const loadSessions = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const sessions =
          await relationshipChastityService.getSessionHistory(relationshipId);
        setState((prev) => ({ ...prev, sessions }));
      },
      "load sessions",
      setState,
    );
  }, []);

  const loadRelationshipData = useCallback(
    async (relationshipId: string) => {
      await Promise.all([
        loadChastityData(relationshipId),
        loadSessions(relationshipId),
      ]);
    },
    [loadChastityData, loadSessions],
  );

  const startSession = useCallback(
    async (
      relationshipId: string,
      options?: {
        goalDuration?: number;
        isHardcoreMode?: boolean;
        notes?: string;
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.startSession(
            relationshipId,
            userId,
            options || {},
          );
          await loadRelationshipData(relationshipId);
        },
        "start session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const endSession = useCallback(
    async (relationshipId: string, sessionId: string, reason?: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.endSession(
            relationshipId,
            sessionId,
            userId,
            reason,
          );
          await loadRelationshipData(relationshipId);
        },
        "end session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const pauseSession = useCallback(
    async (relationshipId: string, sessionId: string, reason?: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.pauseSession(
            relationshipId,
            sessionId,
            userId,
            reason,
          );
          await loadRelationshipData(relationshipId);
        },
        "pause session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const resumeSession = useCallback(
    async (relationshipId: string, sessionId: string) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.resumeSession(
            relationshipId,
            sessionId,
            userId,
          );
          await loadRelationshipData(relationshipId);
        },
        "resume session",
        setState,
      );
    },
    [userId, loadRelationshipData],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  // Set up real-time listeners for active relationship data
  useEffect(() => {
    // This would be called externally when activeRelationship changes
    // The parent hook will need to manage this subscription
  }, []);

  return {
    ...state,
    startSession,
    endSession,
    pauseSession,
    resumeSession,
    loadRelationshipData,
    clearError,
  };
}

/**
 * React Hook for Relationship Task and Event Management
 * Handles creating, updating tasks and logging events
 */
import { useState, useEffect, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { relationshipChastityService } from "@/services/database/RelationshipChastityService";
import { RelationshipTask, RelationshipEvent } from "@/types/relationships";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";

interface RelationshipTasksState extends BaseHookState {
  tasks: RelationshipTask[];
  events: RelationshipEvent[];
}

interface RelationshipTasksActions extends BaseHookActions {
  createTask: (
    relationshipId: string,
    taskData: {
      text: string;
      dueDate?: Date;
      consequence?: RelationshipTask["consequence"];
    },
  ) => Promise<void>;
  updateTaskStatus: (
    relationshipId: string,
    taskId: string,
    status: RelationshipTask["status"],
    note?: string,
  ) => Promise<void>;
  logEvent: (
    relationshipId: string,
    eventData: {
      type: RelationshipEvent["type"];
      details: RelationshipEvent["details"];
      isPrivate?: boolean;
      tags?: string[];
    },
