  const loadEvents = useCallback(async (relationshipId: string) => {
    return withErrorHandling(
      async () => {
        const events =
          await relationshipChastityService.getEvents(relationshipId);
        setState((prev) => ({ ...prev, events }));
      },
      "load events",
      setState,
    );
  }, []);

  const loadRelationshipData = useCallback(
    async (relationshipId: string) => {
      await Promise.all([
        loadTasks(relationshipId),
        loadEvents(relationshipId),
      ]);
    },
    [loadTasks, loadEvents],
  );

  const createTask = useCallback(
    async (
      relationshipId: string,
      taskData: {
        text: string;
        dueDate?: Date;
        consequence?: RelationshipTask["consequence"];
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.createTask(
            relationshipId,
            taskData,
            userId,
          );
          await loadTasks(relationshipId);
        },
        "create task",
        setState,
      );
    },
    [userId, loadTasks],
  );

  const updateTaskStatus = useCallback(
    async (
      relationshipId: string,
      taskId: string,
      status: RelationshipTask["status"],
      note?: string,
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.updateTaskStatus(
            relationshipId,
            taskId,
            status,
            userId,
            note,
          );
          await loadTasks(relationshipId);
        },
        "update task status",
        setState,
      );
    },
    [userId, loadTasks],
  );

  const logEvent = useCallback(
    async (
      relationshipId: string,
      eventData: {
        type: RelationshipEvent["type"];
        details: RelationshipEvent["details"];
        isPrivate?: boolean;
        tags?: string[];
      },
    ) => {
      if (!userId) throw new Error("User not authenticated");

      return withErrorHandling(
        async () => {
          await relationshipChastityService.logEvent(
            relationshipId,
            eventData,
            userId,
          );
          await loadEvents(relationshipId);
        },
        "log event",
        setState,
      );
    },
    [userId, loadEvents],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  // Set up real-time listeners for tasks
  useEffect(() => {
    // This would be called externally when activeRelationship changes
    // The parent hook will need to manage this subscription
  }, []);

  return {
    ...state,
    createTask,
    updateTaskStatus,
    logEvent,
    loadRelationshipData,
    clearError,
  };
}

/**
 * React Hook for Relationship Form Validation
 * Handles validation logic for relationship forms and rules
 */
import { useState, useCallback } from "react";
import { useAuthState } from "@/contexts/AuthContext";
import { dataMigrationService } from "@/services/migration/DataMigrationService";
import { BaseHookState, BaseHookActions } from "./types";
import { withErrorHandling, createBaseActions } from "./utils";
import type { KeyholderPermissions } from "@/types/core";

interface RelationshipValidationState extends BaseHookState {
  needsMigration: boolean;
}

interface RelationshipValidationActions extends BaseHookActions {
  migrateSingleUserData: () => Promise<void>;
  checkMigrationStatus: () => Promise<void>;
  validateRequestForm: (formData: {
    email: string;
    role: "submissive" | "keyholder";
    message?: string;
  }) => { isValid: boolean; errors: string[] };
  validatePermissionsForm: (permissions: KeyholderPermissions) => {
    isValid: boolean;
    errors: string[];
  };
}

export function useRelationshipValidation(): RelationshipValidationState &
  RelationshipValidationActions {
  const { user } = useAuthState();
  const userId = user?.uid;

  const [state, setState] = useState<RelationshipValidationState>({
    needsMigration: false,
    isLoading: false,
    error: null,
  });

  const { clearError: clearErrorFn } = createBaseActions();

  const checkMigrationStatus = useCallback(async () => {
    if (!userId) return;

    try {
      const needsMigration = await dataMigrationService.needsMigration(userId);
      setState((prev) => ({ ...prev, needsMigration }));
    } catch {
      // Silent fail for migration check - using logger instead of console
      // logger.warn("Failed to check migration status", error);
    }
  }, [userId]);

  const migrateSingleUserData = useCallback(async () => {
    if (!userId) throw new Error("User not authenticated");

    return withErrorHandling(
      async () => {
        const result = await dataMigrationService.migrateSingleUserData(userId);
        if (!result.success) {
          throw new Error(result.errors.join(", "));
        }
        setState((prev) => ({ ...prev, needsMigration: false }));
      },
      "migrate single user data",
      setState,
    );
  }, [userId]);

  const validateRequestForm = useCallback(
    (formData: {
      email: string;
      role: "submissive" | "keyholder";
      message?: string;
    }) => {
      const errors: string[] = [];

      if (!formData.email.trim()) {
        errors.push("Email is required");
      } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        errors.push("Email format is invalid");
      }

      if (!formData.role) {
        errors.push("Role is required");
      }

      if (formData.message && formData.message.length > 500) {
        errors.push("Message cannot exceed 500 characters");
      }

      return {
        isValid: errors.length === 0,
        errors,
      };
    },
    [],
  );

  const validatePermissionsForm = useCallback(
    (permissions: KeyholderPermissions) => {
      const errors: string[] = [];

      // Add specific validation rules for permissions
      if (typeof permissions !== "object" || permissions === null) {
        errors.push("Permissions must be an object");
      }

      // Add more specific validation as needed based on RelationshipPermissions type

      return {
        isValid: errors.length === 0,
        errors,
      };
    },
    [],
  );

  const clearError = useCallback(() => {
    clearErrorFn(setState);
  }, [clearErrorFn]);

  return {
    ...state,
    migrateSingleUserData,
    checkMigrationStatus,
    validateRequestForm,
    validatePermissionsForm,
    clearError,
  };
}

import React, { useState } from "react";
import type { DBTask, TaskStatus } from "../../types/database";
import {
  FaCheckCircle,
  FaTimesCircle,
  FaTrophy,
  FaClock,
} from "../../utils/iconImport";

// Task status configuration type
interface TaskStatusConfig {
  icon: React.ReactNode;
  text: string;
  borderColor: string;
}

// Task priority styles type
interface TaskPriorityStyles {
  bgColor: string;
  textColor: string;
}

// Custom hook for task item logic
export const useTaskItem = (
  task: DBTask,
  onSubmit: (taskId: string, note: string) => void,
) => {
  const [note, setNote] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Task status configuration logic
  const getStatusConfig = (status: TaskStatus): TaskStatusConfig => {
    switch (status) {
      case "pending":
        return {
          icon: <FaClock className="text-nightly-aquamarine" />,
          text: "Pending",
          borderColor: "border-nightly-aquamarine",
        };
      case "submitted":
        return {
          icon: <FaClock className="text-yellow-400" />,
          text: "Submitted",
          borderColor: "border-yellow-400",
        };
      case "approved":
        return {
          icon: <FaCheckCircle className="text-green-400" />,
          text: "Approved",
          borderColor: "border-green-400",
        };
      case "rejected":
        return {
          icon: <FaTimesCircle className="text-red-400" />,
          text: "Rejected",
          borderColor: "border-red-400",
        };
      case "completed":
        return {
          icon: <FaTrophy className="text-nightly-lavender-floral" />,
          text: "Completed",
          borderColor: "border-nightly-lavender-floral",
        };
      default:
        return {
          icon: <FaClock className="text-gray-400" />,
          text: "Unknown",
          borderColor: "border-gray-400",
        };
    }
  };

  // Priority styling logic
  const getPriorityStyles = (priority: string): TaskPriorityStyles => {
    switch (priority) {
      case "critical":
        return {
          bgColor: "bg-red-500/20",
          textColor: "text-red-300",
        };
      case "high":
        return {
          bgColor: "bg-orange-500/20",
          textColor: "text-orange-300",
        };
      case "medium":
        return {
          bgColor: "bg-yellow-500/20",
          textColor: "text-yellow-300",
        };
      default:
        return {
          bgColor: "bg-gray-500/20",
          textColor: "text-gray-300",
        };
    }
  };

  // Submit handler logic
  const handleSubmit = async () => {
    setIsSubmitting(true);
    try {
      await onSubmit(task.id, note);
      setNote("");
    } finally {
      setIsSubmitting(false);
    }
  };

  // Derived values
  const statusConfig = getStatusConfig(task.status);
  const priorityStyles = task.priority
    ? getPriorityStyles(task.priority)
    : null;
  const isOverdue = task.dueDate && new Date() > task.dueDate;

  return {
    // State
    note,
    isSubmitting,

    // Actions
    setNote,
    handleSubmit,

    // Computed values
    statusConfig,
    priorityStyles,
    isOverdue: Boolean(isOverdue),

    // Helper functions (exposed for flexibility)
    getStatusConfig,
    getPriorityStyles,
  };
};

