  const [showPermissions, setShowPermissions] = useState<string | null>(null);
  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [keyholderNameInput, setKeyholderNameInput] = useState("");

  // Message state
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState<"success" | "error" | "info">(
    "info",
  );

  // Generate mock data based on scenario
  const getMockData = () => {
    const activeKeyholder =
      scenario === "submissive-with-keyholder" ? mockActiveKeyholder : null;

    const activeInviteCodes =
      scenario === "submissive-no-keyholder" ||
      scenario === "submissive-with-keyholder"
        ? mockInviteCodes
        : [];

    const relationshipSummary = {
      totalAsSubmissive: scenario === "submissive-with-keyholder" ? 1 : 0,
      totalAsKeyholder: scenario === "keyholder-mode" ? 2 : 0,
    };

    const relationships =
      scenario === "keyholder-mode"
        ? {
            asSubmissive: [],
            asKeyholder: [
              {
                id: "rel-2",
                submissiveUserId: "sub-1",
                keyholderUserId: "demo-user-123",
                status: "active" as const,
                permissions: mockActiveKeyholder.permissions,
                createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 3),
                acceptedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 3),
              },
              {
                id: "rel-3",
                submissiveUserId: "sub-2",
                keyholderUserId: "demo-user-123",
                status: "active" as const,
                permissions: mockActiveKeyholder.permissions,
                createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5),
                acceptedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5),
              },
            ],
          }
        : { asSubmissive: [], asKeyholder: [] };

    return {
      activeKeyholder,
      activeInviteCodes,
      relationshipSummary,
      relationships,
    };
  };

  const mockData = getMockData();

  // Message handlers
  const showMessage = (
    text: string,
    type: "success" | "error" | "info" = "info",
  ) => {
    setMessage(text);
    setMessageType(type);
  };

  const clearMessage = () => {
    setMessage("");
    setMessageType("info");
  };

  // Action handlers
  const handleCreateInvite = () => {
    showMessage("Demo: Invite code ABC123 created successfully!", "success");
    setShowCreateInvite(false);
  };

  const handleAcceptInvite = () => {
    if (inviteCodeInput === "ABC123") {
      showMessage("Demo: Successfully accepted invite code!", "success");
    } else {
      showMessage("Demo: Invalid invite code. Try 'ABC123'", "error");
    }
    setInviteCodeInput("");
    setKeyholderNameInput("");
    setShowAcceptInvite(false);
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showMessage(`Copied "${text}" to clipboard`, "success");
    } catch {
      showMessage("Failed to copy to clipboard", "error");
    }
  };

  return {
    // State
    showCreateInvite,
    setShowCreateInvite,
    showAcceptInvite,
    setShowAcceptInvite,
    showPermissions,
    setShowPermissions,
    inviteCodeInput,
    setInviteCodeInput,
    keyholderNameInput,
    setKeyholderNameInput,
    message,
    messageType,

    // Actions
    showMessage,
    clearMessage,
    handleCreateInvite,
    handleAcceptInvite,
    copyToClipboard,

    // Mock data
    ...mockData,
  };
};

/**
 * Custom hook for Achievement Gallery state and logic
 */

import { useState, useMemo } from "react";
import {
  DBAchievement,
  DBUserAchievement,
  AchievementDifficulty,
} from "../types";
import { AchievementCategory } from "../types/achievements";

interface AchievementWithProgress {
  achievement: DBAchievement;
  userAchievement?: DBUserAchievement;
  progress: {
    currentValue: number;
    targetValue: number;
    percentage: number;
    isCompleted: boolean;
  } | null;
  isEarned: boolean;
  isVisible: boolean;
}

export const useAchievementGallery = (
  achievementsWithProgress: AchievementWithProgress[],
) => {
  const [selectedCategory, setSelectedCategory] = useState<
    AchievementCategory | "all"
  >("all");
  const [selectedDifficulty, setSelectedDifficulty] = useState<
    AchievementDifficulty | "all"
  >("all");
  const [showOnlyEarned, setShowOnlyEarned] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  // Calculate stats
  const stats = useMemo(() => {
    const totalEarned = achievementsWithProgress.filter(
      (a) => a.isEarned,
    ).length;
    const totalVisible = achievementsWithProgress.filter(
      (a) => !a.achievement.isHidden,
    ).length;
    const totalPoints = achievementsWithProgress
      .filter((a) => a.isEarned)
      .reduce((sum, a) => sum + a.achievement.points, 0);

    return {
      totalEarned,
      totalVisible,
      totalPoints,
      completionPercentage:
        totalVisible > 0 ? (totalEarned / totalVisible) * 100 : 0,
    };
  }, [achievementsWithProgress]);

  // Filter achievements
  const filteredAchievements = useMemo(() => {
    return achievementsWithProgress.filter((item) => {
      const { achievement, isEarned } = item;

      // Category filter
      if (
        selectedCategory !== "all" &&
        achievement.category !== selectedCategory
      ) {
        return false;
      }

      // Difficulty filter
      if (
        selectedDifficulty !== "all" &&
        achievement.difficulty !== selectedDifficulty
      ) {
        return false;
      }

      // Earned filter
      if (showOnlyEarned && !isEarned) {
        return false;
      }

      // Search filter
      if (
        searchTerm &&
        !achievement.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        !achievement.description
          .toLowerCase()
          .includes(searchTerm.toLowerCase())
      ) {
        return false;
      }

      // Hide hidden achievements if not earned
      if (achievement.isHidden && !isEarned) {
        return false;
      }

      return true;
    });
  }, [
    achievementsWithProgress,
    selectedCategory,
    selectedDifficulty,
    showOnlyEarned,
    searchTerm,
  ]);

  // Group by category
  const groupedAchievements = useMemo(() => {
    const groups: Record<string, AchievementWithProgress[]> = {};

    filteredAchievements.forEach((item) => {
      const categoryName = getCategoryName(
        item.achievement.category as AchievementCategory,
      );
      if (!groups[categoryName]) {
        groups[categoryName] = [];
      }
      groups[categoryName].push(item);
    });

    return groups;
  }, [filteredAchievements]);

  return {
    // State
    selectedCategory,
    selectedDifficulty,
    showOnlyEarned,
    searchTerm,

    // Setters
    setSelectedCategory,
    setSelectedDifficulty,
    setShowOnlyEarned,
    setSearchTerm,

    // Computed values
    stats,
    filteredAchievements,
    groupedAchievements,
  };
};

// Helper function to get category display name
export const getCategoryName = (category: AchievementCategory): string => {
  switch (category) {
    case AchievementCategory.SESSION_MILESTONES:
      return "Session Milestones";
    case AchievementCategory.CONSISTENCY_BADGES:
      return "Consistency";
    case AchievementCategory.STREAK_ACHIEVEMENTS:
      return "Streaks";
    case AchievementCategory.GOAL_BASED:
      return "Goals";
    case AchievementCategory.TASK_COMPLETION:
      return "Tasks";
    case AchievementCategory.SPECIAL_ACHIEVEMENTS:
      return "Special";
    default:
      return "Unknown";
  }
};

// Helper function to get difficulty color styling
export const getDifficultyColor = (
  difficulty: AchievementDifficulty,
): string => {
  switch (difficulty) {
    case AchievementDifficulty.COMMON:
      return "border-gray-400 bg-gray-50";
    case AchievementDifficulty.UNCOMMON:
      return "border-green-400 bg-green-50";
    case AchievementDifficulty.RARE:
      return "border-blue-400 bg-blue-50";
    case AchievementDifficulty.EPIC:
      return "border-purple-400 bg-purple-50";
    case AchievementDifficulty.LEGENDARY:
      return "border-yellow-400 bg-yellow-50";
    default:
      return "border-gray-400 bg-gray-50";
  }
};

/**
 * useAchievements Hook
 * React hook for managing achievements and progress
 */

import { useEffect, useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { achievementDBService, achievementEngine } from "../services";
import {
  DBAchievement,
  DBUserAchievement,
  DBAchievementProgress,
  AchievementCategory,
} from "../types";
import { logger } from "../utils/logging";

export interface AchievementStats {
  totalEarned: number;
  totalPoints: number;
  completionPercentage: number;
  categoryCounts: Record<AchievementCategory, number>;
  recentAchievements: DBUserAchievement[];
}

export const useAchievements = (userId?: string) => {
  const queryClient = useQueryClient();

  // ==================== QUERIES ====================

  /**
   * Get all available achievements
   */
  const { data: allAchievements = [], isLoading: isLoadingAchievements } =
    useQuery({
      queryKey: ["achievements"],
      queryFn: () => achievementDBService.getAllAchievements(),
      enabled: true,
      staleTime: 5 * 60 * 1000, // 5 minutes
    });

  /**
   * Get user's earned achievements
   */
  const { data: userAchievements = [], isLoading: isLoadingUserAchievements } =
    useQuery({
      queryKey: ["achievements", "user", userId],
      queryFn: () => achievementDBService.getUserAchievements(userId!),
      enabled: Boolean(userId),
      staleTime: 30 * 1000, // 30 seconds
    });

  /**
   * Get user's visible achievements (for public profile)
   */
  const { data: visibleAchievements = [] } = useQuery({
    queryKey: ["achievements", "visible", userId],
    queryFn: () => achievementDBService.getUserVisibleAchievements(userId!),
    enabled: Boolean(userId),
    staleTime: 60 * 1000, // 1 minute
  });

  /**
   * Get user's achievement progress
   */
  const { data: achievementProgress = [], isLoading: isLoadingProgress } =
    useQuery({
      queryKey: ["achievements", "progress", userId],
      queryFn: () => achievementDBService.getUserAchievementProgress(userId!),
      enabled: Boolean(userId),
      staleTime: 30 * 1000, // 30 seconds
    });

  /**
   * Get unread notifications
   */
  const { data: unreadNotifications = [], isLoading: isLoadingNotifications } =
    useQuery({
      queryKey: ["achievements", "notifications", userId],
      queryFn: () => achievementDBService.getUserUnreadNotifications(userId!),
      enabled: Boolean(userId),
      refetchInterval: 30 * 1000, // Check every 30 seconds
    });

  /**
   * Get achievement statistics
   */
  const { data: achievementStats, isLoading: isLoadingStats } = useQuery({
    queryKey: ["achievements", "stats", userId],
    queryFn: async (): Promise<AchievementStats> => {
      if (!userId) throw new Error("User ID required");

      const [stats, achievements] = await Promise.all([
        achievementDBService.getUserAchievementStats(userId),
        achievementDBService.getUserAchievements(userId),
      ]);

      // Get recent achievements (last 5)
      const recentAchievements = achievements
        .sort(
          (a: DBUserAchievement, b: DBUserAchievement) =>
            b.earnedAt.getTime() - a.earnedAt.getTime(),
        )
        .slice(0, 5);

      return {
        ...stats,
        recentAchievements,
      };
    },
    enabled: Boolean(userId),
    staleTime: 2 * 60 * 1000, // 2 minutes
  });

  // ==================== MUTATIONS ====================

  /**
   * Toggle achievement visibility
   */
  const toggleVisibilityMutation = useMutation({
    mutationFn: ({ achievementId }: { achievementId: string }) =>
      achievementDBService.toggleAchievementVisibility(userId!, achievementId),
    onSuccess: () => {
      queryClient.invalidateQueries({
        queryKey: ["achievements", "visible", userId],
      });
      queryClient.invalidateQueries({
        queryKey: ["achievements", "user", userId],
      });
    },
    onError: (error: Error) => {
      logger.error(
        "Failed to toggle achievement visibility",
        error,
        "useAchievements",
      );
    },
  });

  /**
   * Mark notification as read
   */
  const markNotificationReadMutation = useMutation({
    mutationFn: (notificationId: string) =>
      achievementDBService.markNotificationRead(notificationId),
    onSuccess: () => {
      queryClient.invalidateQueries({
        queryKey: ["achievements", "notifications", userId],
      });
    },
    onError: (error: Error) => {
      logger.error(
        "Failed to mark notification as read",
        error,
        "useAchievements",
      );
    },
  });

  /**
   * Perform full achievement check
   */
  const performFullCheckMutation = useMutation({
    mutationFn: () => achievementEngine.performFullCheck(userId!),
    onSuccess: () => {
      // Invalidate all achievement-related queries
      queryClient.invalidateQueries({ queryKey: ["achievements"] });
    },
    onError: (error) => {
      logger.error(
        "Failed to perform full achievement check",
        error,
        "useAchievements",
      );
    },
  });

  // ==================== HELPER FUNCTIONS ====================

  /**
   * Get achievement by ID
   */
  const getAchievementById = useCallback(
    (achievementId: string): DBAchievement | undefined => {
      return allAchievements.find((a: DBAchievement) => a.id === achievementId);
    },
    [allAchievements],
  );

  /**
   * Check if user has specific achievement
   */
  const hasAchievement = useCallback(
    (achievementId: string): boolean => {
      return userAchievements.some(
        (ua: DBUserAchievement) => ua.achievementId === achievementId,
      );
    },
    [userAchievements],
  );

  /**
   * Get progress for specific achievement
   */
  const getProgressForAchievement = useCallback(
    (achievementId: string): DBAchievementProgress | undefined => {
      return achievementProgress.find(
        (ap: DBAchievementProgress) => ap.achievementId === achievementId,
      );
    },
    [achievementProgress],
  );

  /**
   * Get achievements by category
   */
  const getAchievementsByCategory = useCallback(
    (category: AchievementCategory): DBAchievement[] => {
      return allAchievements.filter(
        (a: DBAchievement) => a.category === category,
      );
    },
    [allAchievements],
  );

  /**
   * Get user's achievements by category
   */
  const getUserAchievementsByCategory = useCallback(
    (category: AchievementCategory): DBUserAchievement[] => {
      const categoryAchievementIds = allAchievements
        .filter((a: DBAchievement) => a.category === category)
        .map((a: DBAchievement) => a.id);

      return userAchievements.filter((ua: DBUserAchievement) =>
        categoryAchievementIds.includes(ua.achievementId),
      );
    },
    [allAchievements, userAchievements],
  );

  /**
   * Get achievements with progress information
   */
  const getAchievementsWithProgress = useCallback(() => {
    return allAchievements.map((achievement: DBAchievement) => {
      const userAchievement = userAchievements.find(
        (ua: DBUserAchievement) => ua.achievementId === achievement.id,
      );
      const progress = achievementProgress.find(
        (ap: DBAchievementProgress) => ap.achievementId === achievement.id,
      );

      return {
        achievement,
        userAchievement,
        progress: progress
          ? {
              currentValue: progress.currentValue,
              targetValue: progress.targetValue,
              percentage: Math.min(
                (progress.currentValue / progress.targetValue) * 100,
                100,
              ),
              isCompleted: progress.isCompleted,
            }
          : null,
        isEarned: Boolean(userAchievement),
        isVisible: userAchievement?.isVisible ?? true,
      };
    });
  }, [allAchievements, userAchievements, achievementProgress]);

  // ==================== ACTIONS ====================

  const toggleAchievementVisibility = useCallback(
    (achievementId: string) => {
      if (!userId) return;
      toggleVisibilityMutation.mutate({ achievementId });
    },
    [userId, toggleVisibilityMutation],
  );

  const markNotificationRead = useCallback(
    (notificationId: string) => {
      markNotificationReadMutation.mutate(notificationId);
    },
    [markNotificationReadMutation],
  );

  const performFullCheck = useCallback(() => {
    if (!userId) return;
    performFullCheckMutation.mutate();
  }, [userId, performFullCheckMutation]);

  // ==================== EFFECTS ====================

  /**
   * Initialize achievement engine on mount
   */
  useEffect(() => {
    achievementEngine.initialize().catch((error) => {
      logger.error(
        "Failed to initialize achievement engine",
        error,
        "useAchievements",
      );
    });
  }, []);

  // ==================== RETURN ====================

  return {
    // Data
    allAchievements,
    userAchievements,
    visibleAchievements,
    achievementProgress,
    unreadNotifications,
    achievementStats,

    // Loading states
    isLoading: isLoadingAchievements || isLoadingUserAchievements,
    isLoadingProgress,
    isLoadingNotifications,
    isLoadingStats,

    // Helper functions
    getAchievementById,
    hasAchievement,
    getProgressForAchievement,
    getAchievementsByCategory,
    getUserAchievementsByCategory,
    getAchievementsWithProgress,

    // Actions
    toggleAchievementVisibility,
    markNotificationRead,
    performFullCheck,

    // Mutation states
    isTogglingVisibility: toggleVisibilityMutation.isPending,
    isMarkingRead: markNotificationReadMutation.isPending,
    isPerformingCheck: performFullCheckMutation.isPending,
  };
};

/**
 * Hook for achievement notifications (can be used globally)
 */
export const useAchievementNotifications = (userId?: string) => {
  const { unreadNotifications, markNotificationRead, isLoadingNotifications } =
    useAchievements(userId);

  return {
    notifications: unreadNotifications,
    isLoading: isLoadingNotifications,
    markAsRead: markNotificationRead,
    hasUnread: unreadNotifications.length > 0,
    unreadCount: unreadNotifications.length,
  };
};

/**
 * Dexie Sync Hook
 * Provides easy access to Dexie services with automatic sync management
 */
import { useCallback, useMemo } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useApp } from "@/contexts/AppContext";
import {
  sessionDBService,
  eventDBService,
  taskDBService,
  goalDBService,
  settingsDBService,
} from "@/services/database";
import { firebaseSync } from "@/services/sync";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useDexieSync");

export const useDexieSync = () => {
  const { state: appState, actions: appActions } = useApp();
  const { user } = useAuth();

  /**
   * Get all Dexie services
   */
  const services = useMemo(
    () => ({
      sessions: sessionDBService,
      events: eventDBService,
      tasks: taskDBService,
      goals: goalDBService,
      settings: settingsDBService,
    }),
    [],
  );

  /**
   * Trigger manual sync
   */
  const triggerSync = useCallback(async () => {
    if (!user?.uid) {
      logger.warn("Cannot sync: no authenticated user");
      return;
    }

    try {
      await appActions.triggerSync(user.uid);
    } catch (error) {
      logger.error("Sync failed", { error: error as Error });
      throw error;
    }
  }, [user?.uid, appActions]);

  /**
   * Create a record with automatic sync queuing
   */
  const createWithSync = useCallback(
    async <T extends Record<string, unknown>>(
      service: keyof typeof services,
      data: T,
    ): Promise<string> => {
      if (!user?.uid) {
        throw new Error("No authenticated user");
      }

      const id = await services[service].create(data);

      // Trigger background sync if online
      if (appState.isOnline) {
        try {
          await firebaseSync.sync();
        } catch (error) {
          logger.warn("Background sync failed", { error: error as Error });
          // Don't throw - local data is saved
        }
      }

      return id;
    },
    [user?.uid, appState.isOnline, services],
  );

  /**
   * Update a record with automatic sync queuing
   */
  const updateWithSync = useCallback(
    async <T extends Record<string, unknown>>(
      service: keyof typeof services,
      id: string,
      updates: T,
    ): Promise<void> => {
      if (!user?.uid) {
        throw new Error("No authenticated user");
      }

      await services[service].update(id, updates);

      // Trigger background sync if online
      if (appState.isOnline) {
        try {
          await firebaseSync.sync();
        } catch (error) {
          logger.warn("Background sync failed", { error: error as Error });
          // Don't throw - local data is saved
        }
      }
    },
    [user?.uid, appState.isOnline, services],
  );

  /**
   * Delete a record with automatic sync queuing
   */
  const deleteWithSync = useCallback(
    async (service: keyof typeof services, id: string): Promise<void> => {
      if (!user?.uid) {
        throw new Error("No authenticated user");
      }

      await services[service].delete(id);

      // Trigger background sync if online
      if (appState.isOnline) {
        try {
          await firebaseSync.sync();
        } catch (error) {
          logger.warn("Background sync failed", { error: error as Error });
          // Don't throw - local data is saved
        }
      }
    },
    [user?.uid, appState.isOnline, services],
  );

  return {
    // Services
    services,

    // Sync control
    triggerSync,
    syncStatus: appState.syncStatus,
    lastSyncTime: appState.lastSyncTime,
    isOnline: appState.isOnline,

    // CRUD operations with sync
    createWithSync,
    updateWithSync,
    deleteWithSync,

    // Direct service access for read operations
    findById: useCallback(
      async (service: keyof typeof services, id: string) => {
        return services[service].findById(id);
      },
      [services],
    ),

    findByUserId: useCallback(
      async (service: keyof typeof services, userId: string) => {
        return services[service].findByUserId(userId);
      },
      [services],
    ),

    paginate: useCallback(
      async (
        service: keyof typeof services,
        userId: string,
        offset: number = 0,
        limit: number = 50,
      ) => {
        return services[service].paginate(userId, offset, limit);
      },
      [services],
    ),
  };
};

