  ) => Promise<boolean>;
}

const initialState: KeyholderRelationshipState = {
  relationships: {
    asSubmissive: [],
    asKeyholder: [],
  },
  activeKeyholder: null,
  activeInviteCodes: [],
  isLoading: false,
  isCreatingInvite: false,
  isAcceptingInvite: false,
  isUpdatingPermissions: false,
  inviteCodeInput: "",
  keyholderNameInput: "",
  message: "",
  messageType: "info",
  relationshipSummary: null,
};

export function useKeyholderRelationships(): KeyholderRelationshipState &
  KeyholderRelationshipActions {
  const [state, setState] = useState<KeyholderRelationshipState>(initialState);
  const { user } = useAuthState();

  // Load relationships
  const loadRelationships = useCallback(async () => {
    if (!user?.uid) return;

    setState((prev) => ({ ...prev, isLoading: true }));

    try {
      const relationships =
        await KeyholderRelationshipService.getUserRelationships(user.uid);
      const activeKeyholder =
        await KeyholderRelationshipService.getActiveKeyholder(user.uid);

      setState((prev) => ({
        ...prev,
        relationships,
        activeKeyholder,
        isLoading: false,
      }));

      logger.debug("Relationships loaded", {
        submissiveCount: relationships.asSubmissive.length,
        keyholderCount: relationships.asKeyholder.length,
      });
    } catch (error) {
      setState((prev) => ({
        ...prev,
        isLoading: false,
        message: "Failed to load relationships",
        messageType: "error",
      }));
      logger.error("Failed to load relationships", { error: error as Error });
    }
  }, [user?.uid]);

  // Load invite codes
  const loadInviteCodes = useCallback(async () => {
    if (!user?.uid) return;

    try {
      const activeInviteCodes =
        await KeyholderRelationshipService.getActiveInviteCodes(user.uid);
      setState((prev) => ({ ...prev, activeInviteCodes }));
    } catch (error) {
      logger.error("Failed to load invite codes", { error: error as Error });
    }
  }, [user?.uid]);

  // Load relationship summary
  const loadRelationshipSummary = useCallback(async () => {
    if (!user?.uid) return;

    try {
      const relationshipSummary =
        await KeyholderRelationshipService.getRelationshipSummary(user.uid);
      setState((prev) => ({ ...prev, relationshipSummary }));
    } catch (error) {
      logger.error("Failed to load relationship summary", {
        error: error as Error,
      });
    }
  }, [user?.uid]);

  // Create invite code
  const createInviteCode = useCallback(
    async (expirationHours = 24): Promise<InviteCode | null> => {
      if (!user?.uid || !user.displayName) return null;

      setState((prev) => ({ ...prev, isCreatingInvite: true, message: "" }));

      try {
        const canCreate =
          await KeyholderRelationshipService.canCreateInviteCode(user.uid);
        if (!canCreate) {
          setState((prev) => ({
            ...prev,
            isCreatingInvite: false,
            message: "You already have an active keyholder relationship",
            messageType: "error",
          }));
          return null;
        }

        const inviteCode = await KeyholderRelationshipService.createInviteCode(
          user.uid,
          user.displayName,
          expirationHours,
        );

        setState((prev) => ({
          ...prev,
          isCreatingInvite: false,
          message: `Invite code created: ${inviteCode.code}`,
          messageType: "success",
        }));

        logger.info("Invite code created successfully", {
          code: inviteCode.code,
        });

        // Reload invite codes
        await loadInviteCodes();

        return inviteCode;
      } catch (error) {
        const errorMessage =
          (error as Error).message || "Failed to create invite code";
        setState((prev) => ({
          ...prev,
          isCreatingInvite: false,
          message: errorMessage,
          messageType: "error",
        }));
        logger.error("Failed to create invite code", { error: error as Error });
        return null;
      }
    },
    [user?.uid, user?.displayName, loadInviteCodes],
  );

  // Accept invite code
  const acceptInviteCode = useCallback(
    async (code: string, keyholderName?: string): Promise<boolean> => {
      if (!user?.uid) return false;

      setState((prev) => ({ ...prev, isAcceptingInvite: true, message: "" }));

      try {
        const _relationship =
          await KeyholderRelationshipService.acceptInviteCode(
            code,
            user.uid,
            keyholderName || user.displayName,
          );

        setState((prev) => ({
          ...prev,
          isAcceptingInvite: false,
          message: "Successfully linked with submissive!",
          messageType: "success",
          inviteCodeInput: "",
          keyholderNameInput: "",
        }));

        logger.info("Invite code accepted successfully");

        // Reload relationships
        await loadRelationships();
        await loadRelationshipSummary();

        return true;
      } catch (error) {
        const errorMessage =
          (error as Error).message || "Failed to accept invite code";
        setState((prev) => ({
          ...prev,
          isAcceptingInvite: false,
          message: errorMessage,
          messageType: "error",
        }));
        logger.error("Failed to accept invite code", { error: error as Error });
        return false;
      }
    },
    [user?.uid, user?.displayName, loadRelationships, loadRelationshipSummary],
  );

  // Revoke invite code
  const revokeInviteCode = useCallback(
    async (codeId: string) => {
      if (!user?.uid) return;

      try {
        await KeyholderRelationshipService.revokeInviteCode(codeId, user.uid);
        logger.info("Invite code revoked successfully", { codeId });
        await loadInviteCodes();
      } catch (error) {
        const _errorMessage =
          (error as Error).message || "Failed to revoke invite code";
        logger.error("Failed to revoke invite code", { error: error as Error });
      }
    },
    [user?.uid, loadInviteCodes],
  );

  // Update permissions
  const updatePermissions = useCallback(
    async (relationshipId: string, permissions: KeyholderPermissions) => {
      if (!user?.uid) return;

      setState((prev) => ({ ...prev, isUpdatingPermissions: true }));

      try {
        await KeyholderRelationshipService.updatePermissions(
          relationshipId,
          permissions,
          user.uid,
        );
        setState((prev) => ({
          ...prev,
          isUpdatingPermissions: false,
          message: "Permissions updated successfully",
          messageType: "success",
        }));
        logger.info("Permissions updated successfully");
        await loadRelationships();
      } catch (error) {
        const errorMessage =
          (error as Error).message || "Failed to update permissions";
        setState((prev) => ({
          ...prev,
          isUpdatingPermissions: false,
          message: errorMessage,
          messageType: "error",
        }));
        logger.error("Failed to update permissions", { error: error as Error });
      }
    },
    [user?.uid, loadRelationships],
  );

  // End relationship
  const endRelationship = useCallback(
    async (relationshipId: string) => {
      if (!user?.uid) return;

      try {
        await KeyholderRelationshipService.endRelationship(
          relationshipId,
          user.uid,
        );
        logger.info("Relationship ended successfully");
        await loadRelationships();
        await loadRelationshipSummary();
      } catch (error) {
        const _errorMessage =
          (error as Error).message || "Failed to end relationship";
        logger.error("Failed to end relationship", { error: error as Error });
      }
    },
    [user?.uid, loadRelationships, loadRelationshipSummary],
  );

  // Form actions
  const setInviteCodeInput = useCallback((code: string) => {
    setState((prev) => ({ ...prev, inviteCodeInput: code.toUpperCase() }));
  }, []);

  const setKeyholderNameInput = useCallback((name: string) => {
    setState((prev) => ({ ...prev, keyholderNameInput: name }));
  }, []);

  const clearMessage = useCallback(() => {
    setState((prev) => ({ ...prev, message: "", messageType: "info" }));
  }, []);

  const clearForm = useCallback(() => {
    setState((prev) => ({
      ...prev,
      inviteCodeInput: "",
      keyholderNameInput: "",
      message: "",
      messageType: "info",
    }));
  }, []);

  // Utilities
  const validateInviteCode = useCallback((code: string): boolean => {
    return KeyholderRelationshipService.validateInviteCodeFormat(code);
  }, []);

  const canCreateInviteCode = useCallback(async (): Promise<boolean> => {
    if (!user?.uid) return false;
    return await KeyholderRelationshipService.canCreateInviteCode(user.uid);
  }, [user?.uid]);

  const hasPermission = useCallback(
    async (
      submissiveUserId: string,
      permission: keyof KeyholderPermissions,
    ): Promise<boolean> => {
      if (!user?.uid) return false;
      return await KeyholderRelationshipService.hasPermission(
        user.uid,
        submissiveUserId,
        permission,
      );
    },
    [user?.uid],
  );

  // Load data on mount and user change
  useEffect(() => {
    if (user?.uid) {
      loadRelationships();
      loadInviteCodes();
      loadRelationshipSummary();
    } else {
      setState(initialState);
    }
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [user?.uid, loadRelationships, loadInviteCodes, loadRelationshipSummary]);

  return {
    ...state,
    loadRelationships,
    loadInviteCodes,
    loadRelationshipSummary,
    createInviteCode,
    acceptInviteCode,
    revokeInviteCode,
    updatePermissions,
    endRelationship,
    setInviteCodeInput,
    setKeyholderNameInput,
    clearMessage,
    clearForm,
    validateInviteCode,
    canCreateInviteCode,
    hasPermission,
  };
}

/**
 * useLeaderboards Hook
 * React hook for managing leaderboard data and user participation
 */

import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { achievementDBService } from "../services";
import {
  LeaderboardCategory,
  LeaderboardPeriod,
  DBLeaderboardEntry,
} from "../types";
import { logger } from "../utils/logging";

export interface LeaderboardEntry {
  id: string;
  displayName: string;
  value: number;
  rank: number;
  isCurrentUser: boolean;
}

export interface UserRankInfo {
  rank: number;
  value: number;
  totalParticipants: number;
}

export interface LeaderboardPrivacySettings {
  participateInGlobal: boolean;
  participateInMonthly: boolean;
  shareSessionTime: boolean;
  shareStreakData: boolean;
  shareAchievements: boolean;
  displayName: "real" | "username" | "anonymous";
  showOnPublicProfile: boolean;
}

export const useLeaderboards = (
  userId?: string,
  category: LeaderboardCategory = LeaderboardCategory.ACHIEVEMENT_POINTS,
  period: LeaderboardPeriod = LeaderboardPeriod.ALL_TIME,
) => {
  const queryClient = useQueryClient();
  const [privacySettings, setPrivacySettings] =
    useState<LeaderboardPrivacySettings>({
      participateInGlobal: false,
      participateInMonthly: false,
      shareSessionTime: false,
      shareStreakData: false,
      shareAchievements: true,
      displayName: "anonymous",
      showOnPublicProfile: false,
    });

  // ==================== QUERIES ====================

  /**
   * Get leaderboard data for a specific category and period
   */
  const {
    data: rawLeaderboardData = [],
    isLoading: isLoadingLeaderboard,
    error: leaderboardError,
  } = useQuery({
    queryKey: ["leaderboards", category, period],
    queryFn: () => achievementDBService.getLeaderboard(category, period),
    enabled: true,
    staleTime: 60 * 1000, // 1 minute
  });

  /**
   * Get user's rank in the leaderboard
   */
  const { data: userRank, isLoading: isLoadingUserRank } = useQuery({
    queryKey: ["leaderboards", "rank", userId, category, period],
    queryFn: () =>
      achievementDBService.getUserLeaderboardRank(userId!, category, period),
    enabled: Boolean(userId),
    staleTime: 60 * 1000, // 1 minute
  });

  /**
   * Get user's leaderboard privacy settings
   */
  const { data: userPrivacySettings } = useQuery({
    queryKey: ["leaderboards", "privacy", userId],
    queryFn: () => achievementDBService.getLeaderboardPrivacy(userId!),
    enabled: Boolean(userId),
  });

  // Update privacy settings when data changes
  useEffect(() => {
    if (userPrivacySettings) {
      setPrivacySettings(userPrivacySettings);
    }
  }, [userPrivacySettings]);

  // ==================== MUTATIONS ====================

  /**
   * Opt into leaderboards
   */
  const optInMutation = useMutation({
    mutationFn: async () => {
      if (!userId) throw new Error("User ID required");

      const defaultSettings: LeaderboardPrivacySettings = {
        participateInGlobal: true,
        participateInMonthly: true,
        shareSessionTime: true,
        shareStreakData: true,
        shareAchievements: true,
        displayName: "anonymous",
        showOnPublicProfile: false,
      };

      await achievementDBService.updateLeaderboardPrivacy(
        userId,
        defaultSettings,
      );
      return defaultSettings;
    },
    onSuccess: (data) => {
      setPrivacySettings(data);
      queryClient.invalidateQueries({ queryKey: ["leaderboards"] });
      logger.info("User opted into leaderboards", "useLeaderboards");
    },
    onError: (error) => {
      logger.error("Failed to opt into leaderboards", error, "useLeaderboards");
    },
  });

  /**
   * Opt out of leaderboards
   */
  const optOutMutation = useMutation({
    mutationFn: async () => {
      if (!userId) throw new Error("User ID required");

      const optOutSettings: LeaderboardPrivacySettings = {
        participateInGlobal: false,
        participateInMonthly: false,
        shareSessionTime: false,
        shareStreakData: false,
        shareAchievements: false,
        displayName: "anonymous",
        showOnPublicProfile: false,
      };

      await achievementDBService.updateLeaderboardPrivacy(
        userId,
        optOutSettings,
      );
      return optOutSettings;
    },
    onSuccess: (data) => {
      setPrivacySettings(data);
      queryClient.invalidateQueries({ queryKey: ["leaderboards"] });
      logger.info("User opted out of leaderboards", "useLeaderboards");
    },
    onError: (error) => {
      logger.error(
        "Failed to opt out of leaderboards",
        error,
        "useLeaderboards",
      );
    },
  });

  /**
   * Update leaderboard privacy settings
   */
  const updatePrivacyMutation = useMutation({
    mutationFn: async (settings: Partial<LeaderboardPrivacySettings>) => {
      if (!userId) throw new Error("User ID required");

      const newSettings = { ...privacySettings, ...settings };
      await achievementDBService.updateLeaderboardPrivacy(userId, newSettings);
      return newSettings;
    },
    onSuccess: (data) => {
      setPrivacySettings(data);
      queryClient.invalidateQueries({ queryKey: ["leaderboards"] });
      logger.info("Updated leaderboard privacy settings", "useLeaderboards");
    },
    onError: (error) => {
      logger.error(
        "Failed to update privacy settings",
        error,
        "useLeaderboards",
      );
    },
  });

  // ==================== PROCESSED DATA ====================

  /**
   * Process raw leaderboard data into displayable format
   */
  const leaderboardData: LeaderboardEntry[] = rawLeaderboardData.map(
    (entry: DBLeaderboardEntry, index: number) => ({
      id: entry.id,
      displayName: getDisplayName(entry),
      value: entry.value,
      rank: index + 1,
      isCurrentUser: entry.userId === userId,
    }),
  );

  /**
   * Generate display name based on privacy settings
   */
  function getDisplayName(entry: DBLeaderboardEntry): string {
    if (entry.userId === userId) {
      return "You";
    }

    switch (entry.displayNameType) {
      case "real":
        return entry.displayName || "Unknown User";
      case "username":
        return entry.displayName || `User_${entry.userId.slice(-6)}`;
      case "anonymous":
      default:
        return `ChastityUser_${entry.userId.slice(-4)}`;
    }
  }

  // ==================== PUBLIC API ====================

  return {
    // Data
    leaderboardData,
    userRank,
    privacySettings,

    // Loading states
    isLoading: isLoadingLeaderboard || isLoadingUserRank,
    error: leaderboardError,

    // Actions
    optInToLeaderboards: optInMutation.mutateAsync,
    optOutFromLeaderboards: optOutMutation.mutateAsync,
    updateLeaderboardPrivacy: updatePrivacyMutation.mutateAsync,

    // Status
    isOptedIn:
      privacySettings.participateInGlobal ||
      privacySettings.participateInMonthly,
    isOptingIn: optInMutation.isPending,
    isOptingOut: optOutMutation.isPending,
    isUpdatingPrivacy: updatePrivacyMutation.isPending,
  };
};

// Named export only - hook files should only export hooks starting with 'use'

/**
 * React Hook for Relationship Management (Composed)
 * Provides state management and actions for the dual-account keyholder system
 * Refactored to use focused hooks for better maintainability
 */
import { useEffect, useCallback, useMemo } from "react";
import { relationshipChastityService } from "@/services/database/RelationshipChastityService";
import {
  RelationshipTask,
  RelationshipEvent,
  RelationshipPermissions,
} from "@/types/relationships";
import type { KeyholderRelationship } from "@/types/core";
import {
  useRelationshipList,
  useRelationshipInvites,
  useRelationshipActions,
  useRelationshipStatus,
  useRelationshipPermissions,
  useRelationshipTasks,
  useRelationshipValidation,
} from "./relationships";

// Re-export types from the shared types file
export type {
  RelationshipState,
  BaseHookState,
  BaseHookActions,
} from "./relationships/types";

// Maintain backward compatibility with the original interface
export interface RelationshipActions {
  // Relationship management
  sendRelationshipRequest: (
    targetUserId: string,
    role: "submissive" | "keyholder",
    message?: string,
  ) => Promise<void>;
  acceptRelationshipRequest: (requestId: string) => Promise<void>;
  rejectRelationshipRequest: (requestId: string) => Promise<void>;
  endRelationship: (relationshipId: string) => Promise<void>;
  updatePermissions: (
    relationshipId: string,
    permissions: RelationshipPermissions,
  ) => Promise<void>;

  // Session management
  startSession: (
    relationshipId: string,
    options?: {
      goalDuration?: number;
      isHardcoreMode?: boolean;
      notes?: string;
    },
  ) => Promise<void>;
  endSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  pauseSession: (
    relationshipId: string,
    sessionId: string,
    reason?: string,
  ) => Promise<void>;
  resumeSession: (relationshipId: string, sessionId: string) => Promise<void>;

  // Task management
  createTask: (
    relationshipId: string,
    taskData: {
      text: string;
      dueDate?: Date;
      consequence?: RelationshipTask["consequence"];
    },
  ) => Promise<void>;
  updateTaskStatus: (
    relationshipId: string,
    taskId: string,
    status: RelationshipTask["status"],
    note?: string,
  ) => Promise<void>;

  // Event logging
  logEvent: (
    relationshipId: string,
    eventData: {
      type: RelationshipEvent["type"];
      details: RelationshipEvent["details"];
      isPrivate?: boolean;
      tags?: string[];
    },
  ) => Promise<void>;

  // Migration
  migrateSingleUserData: () => Promise<void>;
  checkMigrationStatus: () => Promise<void>;

  // Utility
  setActiveRelationship: (relationship: KeyholderRelationship | null) => void;
  refreshData: () => Promise<void>;
  clearError: () => void;
}

export function useRelationships() {
  // Use focused hooks for specific functionalities
  const relationshipList = useRelationshipList();
  const relationshipInvites = useRelationshipInvites();
  const relationshipActions = useRelationshipActions();
  const relationshipStatus = useRelationshipStatus();
  const relationshipPermissions = useRelationshipPermissions();
  const relationshipTasks = useRelationshipTasks();
  const relationshipValidation = useRelationshipValidation();

  // Load data for active relationship when it changes
  useEffect(() => {
    if (relationshipList.activeRelationship) {
      const relationshipId = relationshipList.activeRelationship.id;
      relationshipStatus.loadRelationshipData(relationshipId);
      relationshipTasks.loadRelationshipData(relationshipId);
    }
  }, [
    relationshipList.activeRelationship,
    relationshipStatus,
    relationshipTasks,
  ]);

  // Set up real-time listeners for active relationship data
  useEffect(() => {
    if (!relationshipList.activeRelationship) return;

    const relationshipId = relationshipList.activeRelationship.id;

    const unsubscribeChastityData =
      relationshipChastityService.subscribeToChastityData(
        relationshipId,
        (_chastityData) => {
          // Update the status hook with new data
          relationshipStatus.loadRelationshipData(relationshipId);
        },
      );

    const unsubscribeTasks = relationshipChastityService.subscribeToTasks(
      relationshipId,
      (_tasks) => {
        // Update the tasks hook with new data
        relationshipTasks.loadRelationshipData(relationshipId);
      },
    );

    return () => {
      unsubscribeChastityData();
      unsubscribeTasks();
    };
  }, [
    relationshipList.activeRelationship,
    relationshipStatus,
    relationshipTasks,
  ]);

  // Enhanced refresh function that coordinates all hooks
  const refreshData = useCallback(async () => {
    await Promise.all([
      relationshipList.refreshRelationships(),
      relationshipInvites.refreshPendingRequests(),
      relationshipValidation.checkMigrationStatus(),
    ]);

    if (relationshipList.activeRelationship) {
      const relationshipId = relationshipList.activeRelationship.id;
      await Promise.all([
        relationshipStatus.loadRelationshipData(relationshipId),
        relationshipTasks.loadRelationshipData(relationshipId),
      ]);
    }
  }, [
    relationshipList,
    relationshipInvites,
    relationshipValidation,
    relationshipStatus,
    relationshipTasks,
  ]);

  // Unified error handling - combine errors from all hooks
  const _hasError = useMemo(() => {
    return !!(
      relationshipList.error ||
      relationshipInvites.error ||
      relationshipActions.error ||
      relationshipStatus.error ||
      relationshipPermissions.error ||
      relationshipTasks.error ||
      relationshipValidation.error
    );
  }, [
    relationshipList.error,
    relationshipInvites.error,
    relationshipActions.error,
    relationshipStatus.error,
    relationshipPermissions.error,
    relationshipTasks.error,
    relationshipValidation.error,
  ]);

  const combinedError = useMemo(() => {
    const errors = [
      relationshipList.error,
      relationshipInvites.error,
      relationshipActions.error,
      relationshipStatus.error,
      relationshipPermissions.error,
      relationshipTasks.error,
      relationshipValidation.error,
    ].filter(Boolean);

    return errors.length > 0 ? errors[0] : null;
  }, [
    relationshipList.error,
    relationshipInvites.error,
    relationshipActions.error,
    relationshipStatus.error,
    relationshipPermissions.error,
    relationshipTasks.error,
    relationshipValidation.error,
  ]);

  // Unified loading state - true if any hook is loading
  const isLoading = useMemo(() => {
    return !!(
      relationshipList.isLoading ||
      relationshipInvites.isLoading ||
      relationshipActions.isLoading ||
      relationshipStatus.isLoading ||
      relationshipPermissions.isLoading ||
      relationshipTasks.isLoading ||
      relationshipValidation.isLoading
    );
  }, [
    relationshipList.isLoading,
    relationshipInvites.isLoading,
    relationshipActions.isLoading,
    relationshipStatus.isLoading,
    relationshipPermissions.isLoading,
    relationshipTasks.isLoading,
    relationshipValidation.isLoading,
  ]);

  // Unified clear error function
  const clearError = useCallback(() => {
    relationshipList.clearError();
    relationshipInvites.clearError();
    relationshipActions.clearError();
    relationshipStatus.clearError();
    relationshipPermissions.clearError();
    relationshipTasks.clearError();
    relationshipValidation.clearError();
  }, [
    relationshipList,
    relationshipInvites,
    relationshipActions,
    relationshipStatus,
    relationshipPermissions,
    relationshipTasks,
    relationshipValidation,
  ]);

  // Enhanced setActiveRelationship that clears data when switching
  const setActiveRelationship = useCallback(
    (relationship: KeyholderRelationship | null) => {
      relationshipList.setActiveRelationship(relationship);

      // Clear old data when switching relationships
      if (!relationship) {
        // Data will be automatically cleared since hooks are reactive
      }
    },
    [relationshipList],
  );

  // Return combined state and actions for backward compatibility
  return {
    // Combined state - maintaining backward compatibility
    relationships: relationshipList.relationships,
    pendingRequests: relationshipInvites.pendingRequests,
    activeRelationship: relationshipList.activeRelationship,
    chastityData: relationshipStatus.chastityData,
    tasks: relationshipTasks.tasks,
    events: relationshipTasks.events,
    sessions: relationshipStatus.sessions,
    isLoading,
    error: combinedError,
    needsMigration: relationshipValidation.needsMigration,

    // Combined actions - maintaining backward compatibility
    sendRelationshipRequest: relationshipInvites.sendRelationshipRequest,
    acceptRelationshipRequest: relationshipActions.acceptRelationshipRequest,
    rejectRelationshipRequest: relationshipActions.rejectRelationshipRequest,
    endRelationship: relationshipActions.endRelationship,
    updatePermissions: relationshipPermissions.updatePermissions,
    startSession: relationshipStatus.startSession,
    endSession: relationshipStatus.endSession,
    pauseSession: relationshipStatus.pauseSession,
    resumeSession: relationshipStatus.resumeSession,
    createTask: relationshipTasks.createTask,
    updateTaskStatus: relationshipTasks.updateTaskStatus,
    logEvent: relationshipTasks.logEvent,
    migrateSingleUserData: relationshipValidation.migrateSingleUserData,
    checkMigrationStatus: relationshipValidation.checkMigrationStatus,
    setActiveRelationship,
    refreshData,
    clearError,
  };
}

/**
 * Session Persistence Hook
 * Provides React integration for session persistence functionality
 */
import { useState, useEffect, useCallback } from "react";
import { sessionPersistenceService } from "../services";
import type {
  SessionRestorationResult,
  SessionPersistenceState,
} from "../services/SessionPersistenceService";
import type { DBSession } from "../types/database";
import { serviceLogger } from "../utils/logging";

const logger = serviceLogger("useSessionPersistence");

export interface UseSessionPersistenceOptions {
  userId?: string;
  autoInitialize?: boolean;
}

export interface UseSessionPersistenceReturn {
  isInitializing: boolean;
  restorationResult: SessionRestorationResult | null;
  error: string | null;
  isSessionRestored: boolean;

  // Actions
  initializeSession: (userId: string) => Promise<SessionRestorationResult>;
  backupSession: (session: DBSession) => Promise<void>;
  startHeartbeat: (sessionId: string) => void;
  stopHeartbeat: () => void;
  detectAndRecover: (userId: string) => Promise<SessionRestorationResult>;

  // State getters
  getBackupState: () => SessionPersistenceState | null;
}

export function useSessionPersistence(
  options: UseSessionPersistenceOptions = {},
): UseSessionPersistenceReturn {
  const { userId, autoInitialize = true } = options;

  const [isInitializing, setIsInitializing] = useState(false);
  const [restorationResult, setRestorationResult] =
    useState<SessionRestorationResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isSessionRestored, setIsSessionRestored] = useState(false);

  // Initialize session persistence when userId is available
  const initializeSession = useCallback(
    async (targetUserId: string): Promise<SessionRestorationResult> => {
      try {
        setIsInitializing(true);
        setError(null);

        logger.debug("Initializing session persistence", {
          userId: targetUserId,
        });

        const result =
          await sessionPersistenceService.initializeSessionState(targetUserId);
        setRestorationResult(result);
        setIsSessionRestored(result.wasRestored);

        if (!result.success && result.error) {
          setError(result.error);
        }

        logger.debug("Session persistence initialized", {
          userId: targetUserId,
          wasRestored: result.wasRestored,
          success: result.success,
        });

        return result;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : "Unknown error";
        setError(errorMessage);
        logger.error("Failed to initialize session persistence", {
          error: err as Error,
          userId: targetUserId,
        });

        return {
          success: false,
          error: errorMessage,
          wasRestored: false,
        };
      } finally {
        setIsInitializing(false);
      }
    },
    [],
  );

  // Backup session state
  const backupSession = useCallback(
    async (session: DBSession): Promise<void> => {
      try {
        await sessionPersistenceService.backupSessionState(session);
        logger.debug("Session backed up", { sessionId: session.id });
      } catch (err) {
        logger.error("Failed to backup session", {
          error: err as Error,
          sessionId: session.id,
        });
        throw err;
      }
    },
    [],
  );

  // Start heartbeat
  const startHeartbeat = useCallback((sessionId: string): void => {
    sessionPersistenceService.startHeartbeat(sessionId);
    logger.debug("Heartbeat started", { sessionId });
  }, []);

  // Stop heartbeat
  const stopHeartbeat = useCallback((): void => {
    sessionPersistenceService.stopHeartbeat();
    logger.debug("Heartbeat stopped");
  }, []);

  // Detect and recover from interruptions
  const detectAndRecover = useCallback(
    async (targetUserId: string): Promise<SessionRestorationResult> => {
      try {
        logger.debug("Detecting and recovering session", {
          userId: targetUserId,
        });

        const result =
          await sessionPersistenceService.detectAndRecover(targetUserId);

        if (result.wasRestored) {
          setRestorationResult(result);
          setIsSessionRestored(true);
        }

        return result;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : "Unknown error";
        logger.error("Failed to detect and recover session", {
          error: err as Error,
          userId: targetUserId,
        });

        return {
          success: false,
          error: errorMessage,
          wasRestored: false,
        };
      }
    },
    [],
  );

  // Get current backup state
  const getBackupState = useCallback((): SessionPersistenceState | null => {
    try {
      const backup = localStorage.getItem("chastity_session_backup");
      return backup ? JSON.parse(backup) : null;
    } catch (err) {
      logger.error("Failed to get backup state", { error: err as Error });
      return null;
    }
  }, []);

  // Auto-initialize when userId is provided
  useEffect(() => {
    if (autoInitialize && userId && !isInitializing && !restorationResult) {
      initializeSession(userId);
    }
  }, [
    userId,
    autoInitialize,
    isInitializing,
    restorationResult,
    initializeSession,
  ]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopHeartbeat();
    };
  }, [stopHeartbeat]);

  return {
    isInitializing,
    restorationResult,
    error,
    isSessionRestored,

    // Actions
    initializeSession,
    backupSession,
    startHeartbeat,
    stopHeartbeat,
    detectAndRecover,

    // State getters
    getBackupState,
  };
}

/**
 * Session Timer Hook
 * Provides real-time timer updates for active chastity sessions
 */
import { useState, useEffect, useMemo, useRef } from "react";
import type { DBSession } from "../types/database";
import { TimerService } from "../services/TimerService";

export interface SessionTimerData {
  // Core timer values
  effectiveTime: number; // Time in seconds excluding pauses
  totalElapsedTime: number; // Total time including pauses
  currentPauseDuration: number; // Current pause duration if paused

  // Formatted strings
  effectiveTimeFormatted: string;
  totalElapsedTimeFormatted: string;
  currentPauseDurationFormatted: string;

  // Goal-related data
  goalProgress: number; // Percentage (0-100)
  remainingGoalTime: number; // Seconds remaining to goal
  remainingGoalTimeFormatted: string;
  isGoalCompleted: boolean;

  // State flags
  isActive: boolean; // Session exists and not ended
  isPaused: boolean;

  // Current time reference
  currentTime: Date;
}

export interface UseSessionTimerOptions {
  updateInterval?: number; // Milliseconds, default 1000
  enabled?: boolean; // Enable/disable timer updates, default true
}

/**
 * Hook for real-time session timer with live updates
 */
export function useSessionTimer(
  session: DBSession | null | undefined,
  options: UseSessionTimerOptions = {},
): SessionTimerData {
  const { updateInterval = 1000, enabled = true } = options;

  const [currentTime, setCurrentTime] = useState(() => new Date());
  const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);

  // Track session state changes manually to avoid zustand warnings
  const sessionId = session?.id;
  const sessionEndTime = session?.endTime;
  const isSessionActive = enabled && sessionId && !sessionEndTime;

  // Update current time every second
  useEffect(() => {
    if (!isSessionActive) {
      // Clear interval if timer is disabled or session is not active
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
      return;
    }

    const updateCurrentTime = () => {
      setCurrentTime(new Date());
    };

    // Set up interval
    intervalRef.current = setInterval(updateCurrentTime, updateInterval);

    // Cleanup on unmount or dependency change
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [isSessionActive, updateInterval]); // eslint-disable-line zustand-safe-patterns/zustand-no-store-actions-in-deps

  // Memoized calculations to prevent unnecessary recalculations
  const timerData = useMemo((): SessionTimerData => {
    // Default values when no session or session ended
    const defaultData: SessionTimerData = {
      effectiveTime: 0,
      totalElapsedTime: 0,
      currentPauseDuration: 0,
      effectiveTimeFormatted: "0s",
      totalElapsedTimeFormatted: "0s",
      currentPauseDurationFormatted: "0s",
      goalProgress: 0,
      remainingGoalTime: 0,
      remainingGoalTimeFormatted: "0s",
      isGoalCompleted: false,
      isActive: false,
      isPaused: false,
      currentTime,
    };

    if (!session || session.endTime) {
      return defaultData;
    }

    // Calculate timer values
    const effectiveTime = TimerService.calculateEffectiveTime(
      session,
      currentTime,
    );
    const totalElapsedTime = TimerService.calculateTotalElapsedTime(
      session,
      currentTime,
    );
    const currentPauseDuration = TimerService.calculateCurrentPauseDuration(
      session,
      currentTime,
    );

    // Calculate goal-related data
    const goalProgress = TimerService.calculateGoalProgress(
      session,
      currentTime,
    );
    const remainingGoalTime = TimerService.calculateRemainingGoalTime(
      session,
      currentTime,
    );
    const isGoalCompleted = TimerService.isGoalCompleted(session, currentTime);

    return {
      effectiveTime,
      totalElapsedTime,
      currentPauseDuration,
      effectiveTimeFormatted: TimerService.formatDuration(effectiveTime),
      totalElapsedTimeFormatted: TimerService.formatDuration(totalElapsedTime),
      currentPauseDurationFormatted:
        TimerService.formatDuration(currentPauseDuration),
      goalProgress,
      remainingGoalTime,
      remainingGoalTimeFormatted:
        TimerService.formatDuration(remainingGoalTime),
      isGoalCompleted,
      isActive: true,
      isPaused: session.isPaused,
      currentTime,
    };
  }, [session, currentTime]);

  return timerData;
}

/**
 * Hook for getting a snapshot of timer data without live updates
 * Useful for components that don't need real-time updates
 */
export function useSessionTimerSnapshot(
  session: DBSession | null | undefined,
): SessionTimerData {
  return useSessionTimer(session, { enabled: false });
}

/**
 * Hook for multiple sessions timer data (useful for dashboards)
 */
export function useMultiSessionTimer(
  sessions: (DBSession | null | undefined)[],
  options: UseSessionTimerOptions = {},
): SessionTimerData[] {
  const [currentTime, setCurrentTime] = useState(() => new Date());
  const { updateInterval = 1000, enabled = true } = options;
  const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);

  // Check if we have any active sessions
  const hasActiveSessions = useMemo(() => {
    return sessions.some((session) => session && !session.endTime);
  }, [sessions]);

  // Update current time for all sessions
  useEffect(() => {
    if (!enabled || !hasActiveSessions) {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
      return;
    }

    const updateCurrentTime = () => {
      setCurrentTime(new Date());
    };

    intervalRef.current = setInterval(updateCurrentTime, updateInterval);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [enabled, hasActiveSessions, updateInterval]); // eslint-disable-line zustand-safe-patterns/zustand-no-store-actions-in-deps

  // Calculate timer data for each session
  const timerDataArray = useMemo(() => {
    return sessions.map((session) => {
      if (!session || session.endTime) {
        return {
          effectiveTime: 0,
          totalElapsedTime: 0,
          currentPauseDuration: 0,
          effectiveTimeFormatted: "0s",
          totalElapsedTimeFormatted: "0s",
          currentPauseDurationFormatted: "0s",
          goalProgress: 0,
          remainingGoalTime: 0,
          remainingGoalTimeFormatted: "0s",
          isGoalCompleted: false,
          isActive: false,
          isPaused: false,
          currentTime,
        };
      }

      const effectiveTime = TimerService.calculateEffectiveTime(
        session,
        currentTime,
      );
      const totalElapsedTime = TimerService.calculateTotalElapsedTime(
        session,
        currentTime,
      );
      const currentPauseDuration = TimerService.calculateCurrentPauseDuration(
        session,
        currentTime,
      );
      const goalProgress = TimerService.calculateGoalProgress(
        session,
        currentTime,
      );
      const remainingGoalTime = TimerService.calculateRemainingGoalTime(
        session,
        currentTime,
      );
      const isGoalCompleted = TimerService.isGoalCompleted(
        session,
        currentTime,
      );

      return {
        effectiveTime,
        totalElapsedTime,
        currentPauseDuration,
        effectiveTimeFormatted: TimerService.formatDuration(effectiveTime),
        totalElapsedTimeFormatted:
          TimerService.formatDuration(totalElapsedTime),
        currentPauseDurationFormatted:
          TimerService.formatDuration(currentPauseDuration),
        goalProgress,
        remainingGoalTime,
        remainingGoalTimeFormatted:
          TimerService.formatDuration(remainingGoalTime),
        isGoalCompleted,
        isActive: true,
        isPaused: session.isPaused,
        currentTime,
      };
    });
  }, [sessions, currentTime]);

  return timerDataArray;
}

import { useState, useEffect, useCallback } from "react";
import { goalDBService } from "@/services/database/GoalDBService";
import type { DBGoal } from "@/types/database";
import { serviceLogger } from "@/utils/logging";
import { useAchievementIntegration } from "@/constants/challengeAchievements";

const logger = serviceLogger("useSpecialChallenges");

export interface SpecialChallengeStatus {
  locktober: {
    available: boolean;
    active: boolean;
    completed: boolean;
    goal?: DBGoal;
  };
  noNutNovember: {
    available: boolean;
    active: boolean;
    completed: boolean;
    goal?: DBGoal;
  };
}

/**
 * Hook for managing special challenge goals (Locktober, No Nut November)
 */
export const useSpecialChallenges = (userId: string | null) => {
  const [challengeStatus, setChallengeStatus] =
    useState<SpecialChallengeStatus>({
      locktober: { available: false, active: false, completed: false },
      noNutNovember: { available: false, active: false, completed: false },
    });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const { checkForChallengeAchievements } = useAchievementIntegration();

  /**
   * Check if challenges are available based on current date
   */
  const checkChallengeAvailability = useCallback(() => {
    const currentDate = new Date();
    const currentMonth = currentDate.getMonth(); // 0-based

    return {
      locktober: currentMonth === 9, // October
      noNutNovember: currentMonth === 10, // November
    };
  }, []);

  /**
   * Load challenge status for the user
   */
  const loadChallengeStatus = useCallback(async () => {
    if (!userId) {
      setIsLoading(false);
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      const availability = checkChallengeAvailability();
      const specialGoals = await goalDBService.getSpecialChallengeGoals(userId);
      const currentYear = new Date().getFullYear();

      // Find current year's goals
      const locktoberGoal = specialGoals.find(
        (goal) =>
          goal.challengeType === "locktober" &&
          goal.challengeYear === currentYear,
      );
      const noNutGoal = specialGoals.find(
        (goal) =>
          goal.challengeType === "no_nut_november" &&
          goal.challengeYear === currentYear,
      );

      setChallengeStatus({
        locktober: {
          available: availability.locktober,
          active: !!locktoberGoal && !locktoberGoal.isCompleted,
          completed: !!locktoberGoal?.isCompleted,
          goal: locktoberGoal,
        },
        noNutNovember: {
          available: availability.noNutNovember,
          active: !!noNutGoal && !noNutGoal.isCompleted,
          completed: !!noNutGoal?.isCompleted,
          goal: noNutGoal,
        },
      });

      logger.debug("Loaded challenge status", {
        userId,
        hasLocktober: !!locktoberGoal,
        hasNoNut: !!noNutGoal,
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      setError(errorMessage);
      logger.error("Failed to load challenge status", { error: err, userId });
    } finally {
      setIsLoading(false);
    }
  }, [userId, checkChallengeAvailability]);

  /**
   * Join a special challenge
   */
  const joinChallenge = useCallback(
    async (challengeType: "locktober" | "no_nut_november") => {
      if (!userId) {
        throw new Error("User not authenticated");
      }

      try {
        setError(null);
        const goal = await goalDBService.getOrCreateChallengeGoal(
          userId,
          challengeType,
        );

        // Refresh status
        await loadChallengeStatus();

        logger.info("User joined challenge", {
          userId,
          challengeType,
          goalId: goal.id,
        });
        return goal;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : "Failed to join challenge";
        setError(errorMessage);
        logger.error("Failed to join challenge", {
          error: err,
          userId,
          challengeType,
        });
        throw err;
      }
    },
    [userId, loadChallengeStatus],
  );

  /**
   * Update challenge progress
   */
  const updateChallengeProgress = useCallback(
    async (
      challengeType: "locktober" | "no_nut_november",
      progressValue: number,
    ) => {
      if (!userId) {
        throw new Error("User not authenticated");
      }

      try {
        const challengeMap = {
          locktober: challengeStatus.locktober,
          no_nut_november: challengeStatus.noNutNovember,
        };
        const challenge =
          challengeMap[challengeType as keyof typeof challengeMap];
        if (!challenge.goal) {
          throw new Error("Challenge goal not found");
        }

        await goalDBService.updateGoalProgress(
          challenge.goal.id,
          progressValue,
        );

        // Check if challenge was completed and trigger achievements
        const updatedGoal = await goalDBService.findById(challenge.goal.id);
        if (updatedGoal?.isCompleted) {
          await checkForChallengeAchievements({
            challengeType: updatedGoal.challengeType,
            challengeYear: updatedGoal.challengeYear,
            isCompleted: updatedGoal.isCompleted,
          });
        }

        // Refresh status
        await loadChallengeStatus();

        logger.info("Updated challenge progress", {
          userId,
          challengeType,
          progressValue,
          goalId: challenge.goal.id,
        });
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : "Failed to update progress";
        setError(errorMessage);
        logger.error("Failed to update challenge progress", {
          error: err,
          userId,
          challengeType,
          progressValue,
        });
        throw err;
      }
    },
    [
      userId,
      challengeStatus,
      loadChallengeStatus,
      checkForChallengeAchievements,
    ],
  );

  /**
   * Get progress percentage for a challenge
   */
  const getChallengeProgress = useCallback(
    (challengeType: "locktober" | "no_nut_november") => {
      const challenge = challengeStatus[challengeType];
      if (!challenge.goal) return 0;

      return Math.min(
        100,
        (challenge.goal.currentValue / challenge.goal.targetValue) * 100,
      );
    },
    [challengeStatus],
  );

  // Load challenge status on mount and when userId changes
  useEffect(() => {
    loadChallengeStatus();
    // eslint-disable-next-line zustand-safe-patterns/zustand-no-store-actions-in-deps
  }, [userId, checkChallengeAvailability, loadChallengeStatus]);

  // Refresh status every hour to check for date changes
  useEffect(() => {
    const interval = setInterval(
      () => {
        const availability = checkChallengeAvailability();
        setChallengeStatus((prev) => ({
          ...prev,
          locktober: { ...prev.locktober, available: availability.locktober },
          noNutNovember: {
            ...prev.noNutNovember,
            available: availability.noNutNovember,
          },
        }));
      },
      60 * 60 * 1000,
    ); // Check every hour

    return () => clearInterval(interval);
  }, [checkChallengeAvailability]);

  return {
    challengeStatus,
    isLoading,
    error,
    joinChallenge,
    updateChallengeProgress,
    getChallengeProgress,
    refreshStatus: loadChallengeStatus,
  };
};

/**
 * useSync Hook
 * Manages synchronization state and conflict resolution
 */
import { useState, useEffect, useCallback } from "react";
import { firebaseSync } from "@/services/sync";
import type { SyncResult, ConflictInfo, SyncOptions } from "@/types/database";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("useSync");

interface UseSyncState {
  isSyncing: boolean;
  lastSyncResult: SyncResult | null;
  pendingConflicts: ConflictInfo[];
  error: Error | null;
}

interface UseSyncActions {
  sync: (userId: string, options?: SyncOptions) => Promise<SyncResult>;
  resolveConflicts: (
    resolutions: Record<string, "local" | "remote">,
  ) => Promise<void>;
  clearError: () => void;
}

export const useSync = (): UseSyncState & UseSyncActions => {
  const [state, setState] = useState<UseSyncState>({
    isSyncing: false,
    lastSyncResult: null,
    pendingConflicts: [],
    error: null,
  });

  // Check for pending conflicts on mount
  useEffect(() => {
    const checkPendingConflicts = () => {
      const conflicts = firebaseSync.getPendingConflicts();
      setState((prev) => ({ ...prev, pendingConflicts: conflicts }));
    };

    checkPendingConflicts();

    // Check periodically for conflicts
    const interval = setInterval(checkPendingConflicts, 30000); // Every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const sync = useCallback(
    async (userId: string, options?: SyncOptions): Promise<SyncResult> => {
      setState((prev) => ({ ...prev, isSyncing: true, error: null }));

      try {
        logger.info("Starting sync", { userId, options });
        const result = await firebaseSync.syncUserData(userId, options);

        setState((prev) => ({
          ...prev,
          isSyncing: false,
          lastSyncResult: result,
          pendingConflicts:
            result.conflicts.length > 0
              ? result.conflicts
              : prev.pendingConflicts,
        }));

        logger.info("Sync completed", {
          success: result.success,
          conflicts: result.conflicts.length,
        });

        return result;
      } catch (error) {
        const err = error as Error;
        logger.error("Sync failed", { error: err });

        setState((prev) => ({
          ...prev,
          isSyncing: false,
          error: err,
        }));

        throw error;
      }
    },
    [],
  );

  const resolveConflicts = useCallback(
    async (resolutions: Record<string, "local" | "remote">): Promise<void> => {
      try {
        logger.info("Resolving conflicts", {
          resolutionCount: Object.keys(resolutions).length,
        });

        // Apply resolutions (implementation would depend on specific conflict resolution logic)
        const resolvedIds: string[] = [];

        for (const [conflictKey, resolution] of Object.entries(resolutions)) {
          // conflictKey format: "collection-documentId-index" or similar
          const conflict = state.pendingConflicts.find(
            (c) =>
              `${c.collection}-${c.documentId}` ===
              conflictKey.split("-").slice(0, 2).join("-"),
          );

          if (conflict) {
            // Apply the chosen resolution
            const dataToApply =
              resolution === "local" ? conflict.localData : conflict.remoteData;

            // Update both local and remote with chosen data
            await firebaseSync.applyRemoteChanges(conflict.collection, [
              dataToApply,
            ]);

            resolvedIds.push(`${conflict.collection}-${conflict.documentId}`);
          }
        }

        // Clear resolved conflicts
        firebaseSync.clearResolvedConflicts(resolvedIds);

        setState((prev) => ({
          ...prev,
          pendingConflicts: prev.pendingConflicts.filter(
            (c) => !resolvedIds.includes(`${c.collection}-${c.documentId}`),
          ),
        }));

        logger.info("Conflicts resolved", {
          resolvedCount: resolvedIds.length,
        });
      } catch (error) {
        logger.error("Failed to resolve conflicts", { error: error as Error });
        setState((prev) => ({ ...prev, error: error as Error }));
        throw error;
      }
    },
    [state.pendingConflicts],
  );

  const clearError = useCallback(() => {
    setState((prev) => ({ ...prev, error: null }));
  }, []);

  return {
    ...state,
    sync,
    resolveConflicts,
    clearError,
  };
};

/**
 * Achievement Page
 * Dedicated page for viewing and managing achievements
 */

import React, { useState } from "react";
import { useAuthState } from "../contexts";
import { useAchievements } from "../hooks/useAchievements";
import {
  AchievementGallery,
  AchievementDashboard,
} from "../components/achievements";
