import { TaskItem } from "../components/tasks";

const TasksPage: React.FC = () => {
  const { user } = useAuthState();
  const [activeTab, setActiveTab] = useState<"active" | "archived">("active");

  // Use TanStack Query hooks for tasks
  const {
    data: tasks = [],
    isLoading: loading,
    error,
  } = useTasks(user?.uid || "");

  const updateTaskStatus = useUpdateTaskStatus();

  const handleSubmitTask = async (taskId: string, _note: string) => {
    if (!user) return;

    try {
      await updateTaskStatus.mutateAsync({
        taskId,
        userId: user.uid,
        status: "submitted" as TaskStatus,
        // Note: submissiveNote would be handled in task updates
      });
    } catch {
      // Error is already logged in the hook
      // TODO: Add toast notification for user feedback on error
    }
  };

  const activeTasks = tasks.filter((task) =>
    ["pending", "submitted"].includes(task.status),
  );

  const archivedTasks = tasks.filter((task) =>
    ["approved", "rejected", "completed", "cancelled"].includes(task.status),
  );

  return (
    <div className="p-6">
      {/* Enhanced Header with Glass Effect */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-white via-blue-200 to-purple-200 bg-clip-text text-transparent mb-2">
          Task Management
        </h1>
        <div className="w-16 h-1 bg-gradient-to-r from-blue-400 to-purple-400 mx-auto rounded-full"></div>
      </div>

      {/* Tab Navigation with Glass Pills */}
      <div className="flex justify-center space-x-4 mb-8">
        <button
          onClick={() => setActiveTab("active")}
          className={`glass-nav px-6 py-3 font-medium transition-all duration-300 ${
            activeTab === "active"
              ? "glass-card-primary text-blue-200 shadow-liquid transform scale-105"
              : "text-gray-300 hover:text-white glass-hover"
          }`}
        >
          Active Tasks ({activeTasks.length})
        </button>
        <button
          onClick={() => setActiveTab("archived")}
          className={`glass-nav px-6 py-3 font-medium transition-all duration-300 ${
            activeTab === "archived"
              ? "glass-card-primary text-blue-200 shadow-liquid transform scale-105"
              : "text-gray-300 hover:text-white glass-hover"
          }`}
        >
          Archived ({archivedTasks.length})
        </button>
      </div>

      {/* Content with Glass Container */}
      <div className="max-w-4xl mx-auto">
        {loading ? (
          <div className="glass-card text-center py-12">
            <div className="glass-float">
              <div className="inline-flex items-center space-x-2">
                <div className="w-6 h-6 border-2 border-blue-400 border-t-transparent rounded-full animate-spin"></div>
                <span className="text-blue-200 text-lg">Loading tasks...</span>
              </div>
            </div>
          </div>
        ) : error ? (
          <div className="text-center py-8">
            <div className="text-red-400">
              Error loading tasks. Please try again.
            </div>
          </div>
        ) : (
          <>
            {activeTab === "active" ? (
              activeTasks.length > 0 ? (
                <div className="space-y-6">
                  {activeTasks.map((task) => (
                    <div
                      key={task.id}
                      className="glass-card glass-hover transform transition-all duration-300 hover:scale-[1.02]"
                    >
                      <TaskItem task={task} onSubmit={handleSubmitTask} />
                    </div>
                  ))}
                </div>
              ) : (
                <div className="glass-card text-center py-12">
                  <div className="glass-float">
                    <div className="text-6xl mb-4"></div>
                    <h3 className="text-xl font-semibold text-gray-200 mb-2">
                      No Active Tasks
                    </h3>
                    <p className="text-gray-400">
                      You're all caught up! New tasks will appear here when
                      assigned.
                    </p>
                  </div>
                </div>
              )
            ) : archivedTasks.length > 0 ? (
              <div className="space-y-6">
                {archivedTasks.map((task) => (
                  <div
                    key={task.id}
                    className="glass-card opacity-75 hover:opacity-100 transition-opacity duration-300"
                  >
                    <TaskItem
                      task={task}
                      onSubmit={() => {}} // Archived tasks can't be submitted
                    />
                  </div>
                ))}
              </div>
            ) : (
              <div className="glass-card text-center py-12">
                <div className="glass-float">
                  <div className="text-6xl mb-4"></div>
                  <h3 className="text-xl font-semibold text-gray-200 mb-2">
                    No Archived Tasks
                  </h3>
                  <p className="text-gray-400">
                    Completed and reviewed tasks will appear here.
                  </p>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default TasksPage;

/**
 * Relationship Chastity Service
 * REFACTORED: Now uses domain-focused services for better maintainability
 *
 * This file maintains backward compatibility by re-exporting the facade service
 * that delegates to the appropriate domain services.
 */
import {
  collection,
  doc,
  addDoc,
  updateDoc,
  getDoc,
  getDocs,
  query,
  orderBy,
  limit,
  serverTimestamp,
  writeBatch,
  onSnapshot,
  Unsubscribe,
  Firestore,
} from "firebase/firestore";
import { getFirestore } from "../firebase";
import {
  RelationshipChastityData,
  RelationshipSession,
  RelationshipTask,
  RelationshipEvent,
  SessionEvent,
  RelationshipTaskStatus,
} from "../../types/relationships";
import { relationshipService } from "./relationships/RelationshipService";
import { serviceLogger } from "../../utils/logging";
import { generateUUID } from "../../utils";

const logger = serviceLogger("RelationshipChastityService");

class RelationshipChastityService {
  private db: Firestore | null = null;

  constructor() {
    this.initializeDb();
  }

  private async initializeDb() {
    this.db = await getFirestore();
  }

  private async ensureDb(): Promise<Firestore> {
    if (!this.db) {
      await this.initializeDb();
    }
    if (!this.db) {
      throw new Error("Failed to initialize Firestore database");
    }
    return this.db;
  }

  // ==================== CHASTITY DATA MANAGEMENT ====================

  /**
   * Get chastity data for a relationship
   */
  async getChastityData(
    relationshipId: string,
  ): Promise<RelationshipChastityData | null> {
    try {
      const db = await this.ensureDb();
      const docSnapshot = await getDoc(doc(db, "chastityData", relationshipId));

      if (!docSnapshot.exists()) {
        return null;
      }

      const data = {
        ...docSnapshot.data(),
        relationshipId: docSnapshot.id,
      } as RelationshipChastityData;

      logger.debug("Retrieved chastity data", { relationshipId });
      return data;
    } catch (error) {
      logger.error("Failed to get chastity data", {
        error: error as Error,
        relationshipId,
      });
      throw error;
    }
  }

  /**
   * Update chastity data settings
   */
  async updateChastitySettings(
    relationshipId: string,
    settings: Partial<RelationshipChastityData["settings"]>,
    userId: string,
  ): Promise<void> {
    try {
      const db = await this.ensureDb();

      // Check permissions
      const hasPermission = await relationshipService.checkPermission(
        relationshipId,
        userId,
        "settings",
      );

      if (!hasPermission) {
        throw new Error("Insufficient permissions to update settings");
      }

      await updateDoc(doc(db, "chastityData", relationshipId), {
        settings,
        updatedAt: serverTimestamp(),
      });

      logger.info("Updated chastity settings", {
        relationshipId,
        userId,
        settings,
      });
    } catch (error) {
      logger.error("Failed to update chastity settings", {
        error: error as Error,
        relationshipId,
        userId,
      });
      throw error;
    }
  }

  // ==================== SESSION MANAGEMENT ====================

  /**
   * Start a new chastity session
   */
  async startSession(
    relationshipId: string,
    userId: string,
    options: {
      goalDuration?: number;
      isHardcoreMode?: boolean;
      notes?: string;
    } = {},
  ): Promise<string> {
    try {
      const db = await this.ensureDb();
      if (!db) {
        throw new Error("Database connection not available");
      }

      // Check if user has permission to start session
      const hasPermission = await relationshipService.checkPermission(
        relationshipId,
        userId,
        "sessions",
      );

      if (!hasPermission) {
        throw new Error("Insufficient permissions to start session");
      }

      // Check if there's already an active session
      const chastityData = await this.getChastityData(relationshipId);
      if (chastityData?.currentSession.isActive) {
        throw new Error("A session is already active for this relationship");
      }

      const sessionId = generateUUID();
      const batch = writeBatch(db);

      // Create session record
      const sessionData: Omit<RelationshipSession, "createdAt" | "updatedAt"> =
        {
          id: sessionId,
          relationshipId,
          startTime: serverTimestamp() as Timestamp,
          duration: 0,
          effectiveDuration: 0,
          events: [
            {
              type: "start",
              timestamp: serverTimestamp() as Timestamp,
              initiatedBy:
                userId === chastityData?.keyholderId
                  ? "keyholder"
                  : "submissive",
              reason: options.notes,
            },
          ],
          goalMet: false,
          keyholderApproval: {
            required: false,
          },
        };

      batch.set(
        doc(db, "chastityData", relationshipId, "sessions", sessionId),
        {
          ...sessionData,
          createdAt: serverTimestamp(),
          updatedAt: serverTimestamp(),
        },
      );

      // Update current session in chastity data
      batch.update(doc(db, "chastityData", relationshipId), {
        currentSession: {
          id: sessionId,
          isActive: true,
          startTime: serverTimestamp(),
          accumulatedPauseTime: 0,
          keyholderApprovalRequired:
            chastityData?.settings.requireReasonForEnd || false,
        },
        updatedAt: serverTimestamp(),
      });

      await batch.commit();

      logger.info("Started new session", {
        sessionId,
        relationshipId,
        userId,
        options,
      });

      return sessionId;
    } catch (error) {
      logger.error("Failed to start session", {
        error: error as Error,
        relationshipId,
        userId,
        options,
      });
      throw error;
    }
  }

  /**
   * End a chastity session
   */
  async endSession(
    relationshipId: string,
    sessionId: string,
    userId: string,
    endReason?: string,
  ): Promise<void> {
    try {
      const db = await this.ensureDb();
      if (!db) {
        throw new Error("Database connection not available");
      }

      // Check permissions
      const hasPermission = await relationshipService.checkPermission(
        relationshipId,
        userId,
        "sessions",
      );

      if (!hasPermission) {
        throw new Error("Insufficient permissions to end session");
      }

      const batch = writeBatch(db);

      // Update session record
      const sessionRef = doc(
        db,
        "chastityData",
        relationshipId,
        "sessions",
        sessionId,
      );
      const sessionDoc = await getDoc(sessionRef);

      if (!sessionDoc.exists()) {
        throw new Error("Session not found");
      }

      const sessionData = sessionDoc.data() as RelationshipSession;
      const endTime = new Date();
      const totalDuration =
        endTime.getTime() - sessionData.startTime.toDate().getTime();

      // Add end event
      const endEvent: SessionEvent = {
        type: "end",
        timestamp: serverTimestamp() as Timestamp,
        initiatedBy:
          userId === (await this.getKeyholderId(relationshipId))
            ? "keyholder"
            : "submissive",
        reason: endReason,
      };

      batch.update(sessionRef, {
        endTime: serverTimestamp(),
        duration: Math.floor(totalDuration / 1000),
        events: [...sessionData.events, endEvent],
        updatedAt: serverTimestamp(),
      });

      // Update current session in chastity data
      batch.update(doc(db, "chastityData", relationshipId), {
        currentSession: {
          id: "",
          isActive: false,
          startTime: serverTimestamp(),
          accumulatedPauseTime: 0,
          keyholderApprovalRequired: false,
        },
        updatedAt: serverTimestamp(),
      });

      await batch.commit();

      logger.info("Ended session", {
        sessionId,
        relationshipId,
        userId,
        endReason,
      });
    } catch (error) {
      logger.error("Failed to end session", {
        error: error as Error,
        sessionId,
        relationshipId,
        userId,
      });
      throw error;
    }
  }

  /**
   * Pause a session
   */
  async pauseSession(
    relationshipId: string,
    sessionId: string,
    userId: string,
    pauseReason?: string,
  ): Promise<void> {
    try {
      const db = await this.ensureDb();
      if (!db) {
        throw new Error("Database connection not available");
      }

      // Check if submissive can pause
      const canPause = await relationshipService.checkPermission(
        relationshipId,
        userId,
        "pauseSession",
      );

      if (!canPause) {
        throw new Error("Insufficient permissions to pause session");
      }

      const batch = writeBatch(db);

      // Update session with pause event
      const sessionRef = doc(
        db,
        "chastityData",
        relationshipId,
        "sessions",
        sessionId,
      );
      const sessionDoc = await getDoc(sessionRef);

      if (!sessionDoc.exists()) {
        throw new Error("Session not found");
      }

      const sessionData = sessionDoc.data() as RelationshipSession;

      const pauseEvent: SessionEvent = {
        type: "pause",
        timestamp: serverTimestamp() as Timestamp,
        initiatedBy: "submissive",
        reason: pauseReason,
      };

      batch.update(sessionRef, {
        events: [...sessionData.events, pauseEvent],
        updatedAt: serverTimestamp(),
      });

      // Update current session
      batch.update(doc(db, "chastityData", relationshipId), {
        "currentSession.pausedAt": serverTimestamp(),
        updatedAt: serverTimestamp(),
      });

      await batch.commit();

      logger.info("Paused session", {
        sessionId,
        relationshipId,
        userId,
        pauseReason,
      });
    } catch (error) {
      logger.error("Failed to pause session", {
        error: error as Error,
        sessionId,
        relationshipId,
        userId,
      });
      throw error;
    }
  }

  /**
   * Resume a paused session
   */
  async resumeSession(
    relationshipId: string,
    sessionId: string,
    userId: string,
  ): Promise<void> {
    try {
      const db = await this.ensureDb();
      if (!db) {
        throw new Error("Database connection not available");
      }

      // Get current chastity data to calculate pause time
      const chastityData = await this.getChastityData(relationshipId);
      if (!chastityData?.currentSession.pausedAt) {
        throw new Error("Session is not paused");
      }

      const batch = writeBatch(db);

      // Calculate pause duration
      const pauseEnd = new Date();
      const pauseStart = chastityData.currentSession.pausedAt.toDate();
      const pauseDuration = Math.floor(
        (pauseEnd.getTime() - pauseStart.getTime()) / 1000,
      );

      // Update session with resume event
      const sessionRef = doc(
        db,
        "chastityData",
        relationshipId,
        "sessions",
        sessionId,
      );
      const sessionDoc = await getDoc(sessionRef);

      if (!sessionDoc.exists()) {
        throw new Error("Session not found");
      }

      const sessionData = sessionDoc.data() as RelationshipSession;

      const resumeEvent: SessionEvent = {
        type: "resume",
        timestamp: serverTimestamp() as Timestamp,
        initiatedBy: "submissive",
      };

      batch.update(sessionRef, {
        events: [...sessionData.events, resumeEvent],
        updatedAt: serverTimestamp(),
      });

      // Update current session
      batch.update(doc(db, "chastityData", relationshipId), {
        "currentSession.pausedAt": null,
        "currentSession.accumulatedPauseTime":
          chastityData.currentSession.accumulatedPauseTime + pauseDuration,
        updatedAt: serverTimestamp(),
      });

      await batch.commit();

      logger.info("Resumed session", {
        sessionId,
        relationshipId,
        userId,
        pauseDuration,
      });
    } catch (error) {
      logger.error("Failed to resume session", {
        error: error as Error,
        sessionId,
        relationshipId,
        userId,
      });
      throw error;
    }
  }

  // ==================== TASK MANAGEMENT ====================

  /**
   * Create a new task
   */
  async createTask(
    relationshipId: string,
    taskData: {
      text: string;
      dueDate?: Date;
      consequence?: RelationshipTask["consequence"];
    },
    userId: string,
  ): Promise<string> {
    try {
      const db = await this.ensureDb();

      // Check permissions
      const hasPermission = await relationshipService.checkPermission(
        relationshipId,
        userId,
        "tasks",
      );

      if (!hasPermission) {
        throw new Error("Insufficient permissions to create tasks");
      }

      const taskId = generateUUID();
      const isKeyholder =
        userId === (await this.getKeyholderId(relationshipId));

      const task: Omit<RelationshipTask, "createdAt" | "updatedAt"> = {
        id: taskId,
        relationshipId,
        text: taskData.text,
        assignedBy: isKeyholder ? "keyholder" : "submissive",
        assignedTo: "submissive",
        dueDate: taskData.dueDate ? (taskData.dueDate as Timestamp) : undefined,
        status: RelationshipTaskStatus.PENDING,
        consequence: taskData.consequence,
      };

      await addDoc(collection(db, "chastityData", relationshipId, "tasks"), {
        ...task,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
      });

      logger.info("Created task", {
        taskId,
        relationshipId,
        userId,
        isKeyholder,
      });

      return taskId;
    } catch (error) {
      logger.error("Failed to create task", {
        error: error as Error,
        relationshipId,
        userId,
      });
      throw error;
    }
  }

  /**
   * Update task status
   */
  async updateTaskStatus(
    relationshipId: string,
    taskId: string,
    status: RelationshipTaskStatus,
    userId: string,
    note?: string,
  ): Promise<void> {
    try {
      const db = await this.ensureDb();

      const updateData: Record<string, unknown> = {
        status,
        updatedAt: serverTimestamp(),
      };

      // Add role-specific fields
      const isKeyholder =
        userId === (await this.getKeyholderId(relationshipId));

      if (status === RelationshipTaskStatus.SUBMITTED && !isKeyholder) {
        updateData.submittedAt = serverTimestamp();
        updateData.submissiveNote = note;
      } else if (
        (status === RelationshipTaskStatus.APPROVED ||
          status === RelationshipTaskStatus.REJECTED) &&
        isKeyholder
      ) {
        updateData.approvedAt = serverTimestamp();
        updateData.keyholderFeedback = note;
      } else if (status === RelationshipTaskStatus.COMPLETED) {
        updateData.completedAt = serverTimestamp();
      }

      await updateDoc(
        doc(db, "chastityData", relationshipId, "tasks", taskId),
        updateData,
      );

      logger.info("Updated task status", {
        taskId,
        relationshipId,
        status,
        userId,
        isKeyholder,
      });
    } catch (error) {
      logger.error("Failed to update task status", {
        error: error as Error,
        taskId,
        relationshipId,
        status,
        userId,
      });
      throw error;
    }
  }

  /**
   * Get tasks for a relationship
   */
  async getTasks(
    relationshipId: string,
    limitCount: number = 50,
  ): Promise<RelationshipTask[]> {
    try {
      const db = await this.ensureDb();

      const tasksSnapshot = await getDocs(
        query(
          collection(db, "chastityData", relationshipId, "tasks"),
          orderBy("createdAt", "desc"),
          limit(limitCount),
        ),
      );

      const tasks = tasksSnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      })) as RelationshipTask[];

      logger.debug("Retrieved tasks", {
        relationshipId,
        count: tasks.length,
      });

      return tasks;
    } catch (error) {
      logger.error("Failed to get tasks", {
        error: error as Error,
        relationshipId,
      });
      throw error;
    }
  }

  // ==================== EVENT LOGGING ====================

  /**
   * Log an event
   */
  async logEvent(
    relationshipId: string,
