      throw error;
    }
  }

  // ==================== HELPER METHODS ====================

  /**
   * Get keyholder ID for a relationship
   */
  private async getKeyholderId(relationshipId: string): Promise<string> {
    const relationship =
      await relationshipService.getRelationship(relationshipId);
    if (!relationship) {
      throw new Error("Relationship not found");
    }
    return relationship.keyholderId;
  }

  /**
   * Get session history for a relationship
   */
  async getSessionHistory(
    relationshipId: string,
    limitCount: number = 50,
  ): Promise<RelationshipSession[]> {
    try {
      const db = await this.ensureDb();

      const sessionsSnapshot = await getDocs(
        query(
          collection(db, "chastityData", relationshipId, "sessions"),
          orderBy("startTime", "desc"),
          limit(limitCount),
        ),
      );

      const sessions = sessionsSnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      })) as RelationshipSession[];

      logger.debug("Retrieved session history", {
        relationshipId,
        count: sessions.length,
      });

      return sessions;
    } catch (error) {
      logger.error("Failed to get session history", {
        error: error as Error,
        relationshipId,
      });
      throw error;
    }
  }

  // ==================== REAL-TIME LISTENERS ====================

  /**
   * Subscribe to chastity data changes
   */
  subscribeToChastityData(
    relationshipId: string,
    callback: (data: RelationshipChastityData | null) => void,
  ): Promise<Unsubscribe> {
    return this.ensureDb().then((db) => {
      return onSnapshot(
        doc(db, "chastityData", relationshipId),
        (doc) => {
          const data = doc.exists()
            ? ({
                ...doc.data(),
                relationshipId: doc.id,
              } as RelationshipChastityData)
            : null;
          callback(data);
        },
        (error) => {
          logger.error("Error in chastity data subscription", {
            error,
            relationshipId,
          });
        },
      );
    });
  }

  /**
   * Subscribe to task changes
   */
  subscribeToTasks(
    relationshipId: string,
    callback: (tasks: RelationshipTask[]) => void,
  ): Promise<Unsubscribe> {
    return this.ensureDb().then((db) => {
      return onSnapshot(
        query(
          collection(db, "chastityData", relationshipId, "tasks"),
          orderBy("createdAt", "desc"),
        ),
        (snapshot) => {
          const tasks = snapshot.docs.map((doc) => ({
            ...doc.data(),
            id: doc.id,
          })) as RelationshipTask[];
          callback(tasks);
        },
        (error) => {
          logger.error("Error in tasks subscription", {
            error,
            relationshipId,
          });
        },
      );
    });
  }
}

export const relationshipChastityService = new RelationshipChastityService();

/**
 * Form Store - Form State Management
 * Manages form state, validation, and dirty tracking for complex forms
 */
import React from "react";
import { create } from "zustand";
import { devtools } from "zustand/middleware";

// Define common form field value types
export type FormFieldValue =
  | string
  | number
  | boolean
  | Date
  | string[]
  | null
  | undefined;

export interface FormField {
  value: FormFieldValue;
  error?: string;
  touched: boolean;
  dirty: boolean;
}

export interface FormState {
  // Form registry - stores forms by ID
  forms: Record<
    string,
    {
      fields: Record<string, FormField>;
      isSubmitting: boolean;
      isValid: boolean;
      isDirty: boolean;
      submitCount: number;
      lastSubmitted?: Date;
    }
  >;

  // Actions
  createForm: (
    formId: string,
    initialValues?: Record<string, FormFieldValue>,
  ) => void;
  destroyForm: (formId: string) => void;
  setFieldValue: (
    formId: string,
    fieldName: string,
    value: FormFieldValue,
  ) => void;
  setFieldError: (formId: string, fieldName: string, error?: string) => void;
  touchField: (formId: string, fieldName: string) => void;
  resetForm: (
    formId: string,
    newValues?: Record<string, FormFieldValue>,
  ) => void;
  setSubmitting: (formId: string, isSubmitting: boolean) => void;
  incrementSubmitCount: (formId: string) => void;

  // Validation
  validateField: (
    formId: string,
    fieldName: string,
    validator: (value: FormFieldValue) => string | undefined,
  ) => void;
  validateForm: (
    formId: string,
    validators: Record<string, (value: FormFieldValue) => string | undefined>,
  ) => boolean;

  // Utility getters
  getForm: (formId: string) => FormState["forms"][string] | undefined;
  getFieldValue: (formId: string, fieldName: string) => FormFieldValue;
  getFieldError: (formId: string, fieldName: string) => string | undefined;
  isFieldTouched: (formId: string, fieldName: string) => boolean;
  isFieldDirty: (formId: string, fieldName: string) => boolean;
  isFormValid: (formId: string) => boolean;
  isFormDirty: (formId: string) => boolean;
  isFormSubmitting: (formId: string) => boolean;
}

// Helper functions to break down complexity
const createFieldsFromValues = (
  initialValues: Record<string, FormFieldValue>,
): Record<string, FormField> => {
  const fields: Record<string, FormField> = {};
  Object.entries(initialValues).forEach(([key, value]) => {
    fields[key] = {
      value,
      touched: false,
      dirty: false,
    };
  });
  return fields;
};

const createFormInstance = (initialValues: Record<string, FormFieldValue>) => ({
  fields: createFieldsFromValues(initialValues),
  isSubmitting: false,
  isValid: true,
  isDirty: false,
  submitCount: 0,
});

const updateFieldInForm = (
  form: FormState["forms"][string],
  fieldName: string,
  updates: Partial<FormField>,
) => {
  const field = form.fields[fieldName] || {
    value: undefined,
    touched: false,
    dirty: false,
  };

  return {
    ...form,
    fields: {
      ...form.fields,
      [fieldName]: {
        ...field,
        ...updates,
      },
    },
  };
};

const calculateFormDirty = (fields: Record<string, FormField>): boolean =>
  Object.values(fields).some((f) => f.dirty);

const calculateFormValid = (fields: Record<string, FormField>): boolean =>
  !Object.values(fields).some((f) => f.error);

// Validation functions
const validateSingleField = (
  get: () => FormState,
  formId: string,
  fieldName: string,
  validator: (value: FormFieldValue) => string | undefined,
) => {
  const form = get().forms[formId];
  if (!form) return;

  const field = form.fields[fieldName];
  if (!field) return;

  const error = validator(field.value);
  get().setFieldError(formId, fieldName, error);
};

const validateMultipleFields = (
  get: () => FormState,
  formId: string,
  validators: Record<string, (value: FormFieldValue) => string | undefined>,
): boolean => {
  const form = get().forms[formId];
  if (!form) return false;

  let isValid = true;
  Object.entries(validators).forEach(([fieldName, validator]) => {
    const field = form.fields[fieldName];
    if (!field) return;

    const error = validator(field.value);
    get().setFieldError(formId, fieldName, error);
    if (error) isValid = false;
  });

  return isValid;
};

// Form lifecycle actions
const createFormLifecycleActions = (set: any) => ({
  createForm: (formId: string, initialValues = {}) =>
    set(
      (state: FormState) => {
        if (state.forms[formId]) return state;
        return {
          forms: {
            ...state.forms,
            [formId]: createFormInstance(initialValues),
          },
        };
      },
      false,
      `createForm:${formId}`,
    ),

  destroyForm: (formId: string) =>
    set(
      (state: FormState) => {
        const { [formId]: _removed, ...rest } = state.forms;
        return { forms: rest };
      },
      false,
      `destroyForm:${formId}`,
    ),
});

// Field manipulation actions
const createFieldActions = (set: any) => ({
  setFieldValue: (formId: string, fieldName: string, value: FormFieldValue) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        const currentField = form.fields[fieldName];
        const isDirty = currentField ? currentField.value !== value : true;

        const updatedForm = updateFieldInForm(form, fieldName, {
          value,
          dirty: isDirty,
        });
        const formIsDirty = calculateFormDirty(updatedForm.fields);

        return {
          forms: {
            ...state.forms,
            [formId]: {
              ...updatedForm,
              isDirty: formIsDirty,
            },
          },
        };
      },
      false,
      `setFieldValue:${formId}.${fieldName}`,
    ),

  setFieldError: (formId: string, fieldName: string, error?: string) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        const updatedForm = updateFieldInForm(form, fieldName, { error });
        const isValid = calculateFormValid(updatedForm.fields);

        return {
          forms: {
            ...state.forms,
            [formId]: {
              ...updatedForm,
              isValid,
            },
          },
        };
      },
      false,
      `setFieldError:${formId}.${fieldName}`,
    ),

  touchField: (formId: string, fieldName: string) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        const updatedForm = updateFieldInForm(form, fieldName, {
          touched: true,
        });

        return {
          forms: {
            ...state.forms,
            [formId]: updatedForm,
          },
        };
      },
      false,
      `touchField:${formId}.${fieldName}`,
    ),
});

// Form state actions
const createFormStateActions = (set: any) => ({
  resetForm: (formId: string, newValues = {}) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        return {
          forms: {
            ...state.forms,
            [formId]: {
              ...form,
              fields: createFieldsFromValues(newValues),
              isDirty: false,
              isValid: true,
            },
          },
        };
      },
      false,
      `resetForm:${formId}`,
    ),

  setSubmitting: (formId: string, isSubmitting: boolean) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        return {
          forms: {
            ...state.forms,
            [formId]: {
              ...form,
              isSubmitting,
              ...(isSubmitting ? {} : { lastSubmitted: new Date() }),
            },
          },
        };
      },
      false,
      `setSubmitting:${formId}`,
    ),

  incrementSubmitCount: (formId: string) =>
    set(
      (state: FormState) => {
        const form = state.forms[formId];
        if (!form) return state;

        return {
          forms: {
            ...state.forms,
            [formId]: {
              ...form,
              submitCount: form.submitCount + 1,
            },
          },
        };
      },
      false,
      `incrementSubmitCount:${formId}`,
    ),
});

// Form validation actions
const createValidationActions = (get: () => FormState) => ({
  validateField: (
    formId: string,
    fieldName: string,
    validator: (value: FormFieldValue) => string | undefined,
  ) => validateSingleField(get, formId, fieldName, validator),

  validateForm: (
    formId: string,
    validators: Record<string, (value: FormFieldValue) => string | undefined>,
  ) => validateMultipleFields(get, formId, validators),
});

// Form state actions
const createFormActions = (set: any, get: () => FormState) => ({
  ...createFormLifecycleActions(set),
  ...createFieldActions(set),
  ...createFormStateActions(set),
  ...createValidationActions(get),
});

export const useFormStore = create<FormState>()(
  devtools(
    (set, get) => ({
      // Initial state
      forms: {},

      // All actions
      ...createFormActions(set, get),

      // Utility getters
      getForm: (formId: string) => get().forms[formId],
      getFieldValue: (formId: string, fieldName: string) =>
        get().forms[formId]?.fields[fieldName]?.value,
      getFieldError: (formId: string, fieldName: string) =>
        get().forms[formId]?.fields[fieldName]?.error,
      isFieldTouched: (formId: string, fieldName: string) =>
        get().forms[formId]?.fields[fieldName]?.touched ?? false,
      isFieldDirty: (formId: string, fieldName: string) =>
        get().forms[formId]?.fields[fieldName]?.dirty ?? false,
      isFormValid: (formId: string) => get().forms[formId]?.isValid ?? false,
      isFormDirty: (formId: string) => get().forms[formId]?.isDirty ?? false,
      isFormSubmitting: (formId: string) =>
        get().forms[formId]?.isSubmitting ?? false,
    }),
    {
      name: "form-store",
    },
  ),
);

// Selector hooks for better performance
export const useForm = (formId: string) =>
  useFormStore((state) => state.forms[formId]);

export const useFormField = (formId: string, fieldName: string) =>
  useFormStore((state) => state.forms[formId]?.fields[fieldName]);

export const useFormActions = () =>
  useFormStore((state) => ({
    createForm: state.createForm,
    destroyForm: state.destroyForm,
    setFieldValue: state.setFieldValue,
    setFieldError: state.setFieldError,
    touchField: state.touchField,
    resetForm: state.resetForm,
    setSubmitting: state.setSubmitting,
    validateField: state.validateField,
    validateForm: state.validateForm,
  }));

// Utility hook for easier form management
export const useFormManager = (formId: string, initialValues = {}) => {
  const form = useForm(formId);
  const actions = useFormActions();

  // Auto-create form on mount
  React.useEffect(() => {
    if (!form) {
      actions.createForm(formId, initialValues);
    }
  }, [formId, form, actions, initialValues]);

  // Auto-destroy form on unmount
  React.useEffect(() => {
    return () => {
      actions.destroyForm(formId);
    };
  }, [formId, actions]);

  return {
    form,
    ...actions,
  };
};

/**
 * Keyholder Store
 * UI state management for keyholder mode functionality
 * Zustand store - handles keyholder UI state only
 */
import { create } from "zustand";
import { devtools } from "zustand/middleware";
import { sha256 } from "@/utils/helpers/hash";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("KeyholderStore");

export interface KeyholderState {
  // UI State
  isKeyholderModeUnlocked: boolean;
  keyholderMessage: string;
  isPasswordDialogOpen: boolean;
  isPasswordSettingMode: boolean;

  // Form State
  passwordAttempt: string;
  newPassword: string;
  confirmPassword: string;

  // Loading States
  isCheckingPassword: boolean;
  isSavingPassword: boolean;
}

export interface KeyholderActions {
  // Password Management
  checkPassword: (passwordAttempt: string, storedHash: string) => Promise<void>;
  setTempPassword: (keyholderName: string) => Promise<string>;
  setPermanentPassword: (
    newPassword: string,
    onSave: (hash: string) => Promise<void>,
  ) => Promise<void>;

  // UI Actions
  unlockKeyholderMode: () => void;
  lockKeyholderControls: () => void;
  setMessage: (message: string) => void;
  clearMessage: () => void;

  // Dialog Management
  openPasswordDialog: () => void;
  closePasswordDialog: () => void;
  setPasswordSettingMode: (isSettingMode: boolean) => void;

  // Form Actions
  setPasswordAttempt: (password: string) => void;
  setNewPassword: (password: string) => void;
  setConfirmPassword: (password: string) => void;
  clearForm: () => void;

  // Reset
  resetStore: () => void;
}

export interface KeyholderStore extends KeyholderState, KeyholderActions {}

const initialState: KeyholderState = {
  isKeyholderModeUnlocked: false,
  keyholderMessage: "",
  isPasswordDialogOpen: false,
  isPasswordSettingMode: false,
  passwordAttempt: "",
  newPassword: "",
  confirmPassword: "",
  isCheckingPassword: false,
  isSavingPassword: false,
};

export const useKeyholderStore = create<KeyholderStore>()(
  devtools(
    (set, get) => ({
      ...initialState,

      // Password Management
      checkPassword: async (passwordAttempt: string, storedHash: string) => {
        const state = get();

        if (state.isCheckingPassword) {
          logger.debug("Password check already in progress");
          return;
        }

        set({ isCheckingPassword: true, keyholderMessage: "" });

        try {
          logger.debug("Checking keyholder password");

          if (!storedHash) {
            const message =
              "Error: No keyholder password is set in the database.";
            set({
              keyholderMessage: message,
              isCheckingPassword: false,
            });
            logger.warn("No keyholder password hash found");
            return;
          }

          const attemptHash = await sha256(passwordAttempt);

          if (attemptHash === storedHash) {
            set({
              isKeyholderModeUnlocked: true,
              keyholderMessage: "Controls are now unlocked.",
              isPasswordDialogOpen: false,
              passwordAttempt: "",
              isCheckingPassword: false,
            });
            logger.info("Keyholder password correct, mode unlocked");
          } else {
            set({
              keyholderMessage: "Incorrect password. Please try again.",
              passwordAttempt: "",
              isCheckingPassword: false,
            });
            logger.warn("Incorrect keyholder password attempt");
          }
        } catch (error) {
          const message = "Failed to check password. Please try again.";
          set({
            keyholderMessage: message,
            isCheckingPassword: false,
          });
          logger.error("Error checking keyholder password", {
            error: error as Error,
          });
        }
      },

      setTempPassword: async (keyholderName: string): Promise<string> => {
        try {
          logger.debug("Generating temporary keyholder password", {
            keyholderName,
          });

          const tempPassword = Math.random()
            .toString(36)
            .substring(2, 8)
            .toUpperCase();
          const message = `Your keyholder password is: ${tempPassword}. This is now the permanent password unless you set a custom one.`;

          set({ keyholderMessage: message });

          logger.info("Temporary keyholder password generated");
          return tempPassword;
        } catch (error) {
          const message = "Failed to generate temporary password.";
          set({ keyholderMessage: message });
          logger.error("Error generating temporary password", {
            error: error as Error,
          });
          throw error;
        }
      },

      setPermanentPassword: async (
        newPassword: string,
        onSave: (hash: string) => Promise<void>,
      ) => {
        const state = get();

        if (state.isSavingPassword) {
          logger.debug("Password save already in progress");
          return;
        }

        if (!newPassword || newPassword.length < 6) {
          set({
            keyholderMessage: "Password must be at least 6 characters long.",
          });
          return;
        }

        set({ isSavingPassword: true, keyholderMessage: "" });

        try {
          logger.debug("Setting permanent keyholder password");

          const newHash = await sha256(newPassword);
          await onSave(newHash);

          set({
            keyholderMessage:
              "Permanent password has been updated successfully!",
            newPassword: "",
            confirmPassword: "",
            isPasswordSettingMode: false,
            isPasswordDialogOpen: false,
            isSavingPassword: false,
          });

          logger.info("Permanent keyholder password updated successfully");
        } catch (error) {
          const message = "Failed to update password. Please try again.";
          set({
            keyholderMessage: message,
            isSavingPassword: false,
          });
          logger.error("Error setting permanent password", {
            error: error as Error,
          });
        }
      },

      // UI Actions
      unlockKeyholderMode: () => {
        set({
          isKeyholderModeUnlocked: true,
          keyholderMessage: "Controls are now unlocked.",
          isPasswordDialogOpen: false,
        });
        logger.info("Keyholder mode unlocked manually");
      },

      lockKeyholderControls: () => {
        set({
          isKeyholderModeUnlocked: false,
          keyholderMessage: "",
          isPasswordDialogOpen: false,
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
        });
        logger.info("Keyholder controls locked");
      },

      setMessage: (message: string) => {
        set({ keyholderMessage: message });
      },

      clearMessage: () => {
        set({ keyholderMessage: "" });
      },

      // Dialog Management
      openPasswordDialog: () => {
        set({
          isPasswordDialogOpen: true,
          keyholderMessage: "",
          passwordAttempt: "",
        });
      },

      closePasswordDialog: () => {
        set({
          isPasswordDialogOpen: false,
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
          isPasswordSettingMode: false,
        });
      },

      setPasswordSettingMode: (isSettingMode: boolean) => {
        set({
          isPasswordSettingMode: isSettingMode,
          keyholderMessage: "",
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
        });
      },

      // Form Actions
      setPasswordAttempt: (password: string) => {
        set({ passwordAttempt: password });
      },

      setNewPassword: (password: string) => {
        set({ newPassword: password });
      },

      setConfirmPassword: (password: string) => {
        set({ confirmPassword: password });
      },

      clearForm: () => {
        set({
          passwordAttempt: "",
          newPassword: "",
          confirmPassword: "",
          keyholderMessage: "",
        });
      },

      // Reset
      resetStore: () => {
        set(initialState);
        logger.debug("Keyholder store reset to initial state");
      },
    }),
    {
      name: "keyholder-store",
      // Only enable devtools in development
      enabled:
        import.meta.env.MODE === "development" ||
        import.meta.env.MODE === "nightly",
    },
  ),
);

/**
 * Notification Store - Temporary UI Feedback
 * Manages toast messages, alerts, and temporary UI feedback
 */
import { create } from "zustand";
import { devtools } from "zustand/middleware";

export interface Notification {
  id: string;
  type: "success" | "error" | "warning" | "info";
  title?: string;
  message: string;
  duration?: number; // in milliseconds, 0 means persistent
  dismissible?: boolean;
  position?:
    | "top-left"
    | "top-right"
    | "bottom-left"
    | "bottom-right"
    | "top-center"
    | "bottom-center";
  action?: {
    label: string;
    onClick: () => void;
  };
  timestamp: Date;
}

export interface NotificationState {
  // Notifications list
  notifications: Notification[];

  // Configuration
  pauseOnHover?: boolean;

  // Actions
  addNotification: (
    notification: Omit<Notification, "id" | "timestamp">,
  ) => string;
  removeNotification: (id: string) => void;
  clearAllNotifications: () => void;
  updateNotification: (id: string, updates: Partial<Notification>) => void;

  // Convenience methods
  showSuccess: (message: string, title?: string, duration?: number) => string;
  showError: (message: string, title?: string, duration?: number) => string;
  showWarning: (message: string, title?: string, duration?: number) => string;
  showInfo: (message: string, title?: string, duration?: number) => string;

  // Reset function for testing
  resetStore: () => void;
}

// Additional type exports for compatibility with index.ts
export interface NotificationActions {
  addNotification: (
    notification: Omit<Notification, "id" | "timestamp">,
  ) => string;
  removeNotification: (id: string) => void;
  clearAllNotifications: () => void;
  showSuccess: (message: string, title?: string, duration?: number) => string;
  showError: (message: string, title?: string, duration?: number) => string;
  showWarning: (message: string, title?: string, duration?: number) => string;
  showInfo: (message: string, title?: string, duration?: number) => string;
}

export type NotificationStore = NotificationState;
export type NotificationConfig = Omit<Notification, "id" | "timestamp">;
export type NotificationType = "success" | "error" | "warning" | "info";
export type NotificationAction = {
  label: string;
  onClick: () => void;
};

// Default durations for different notification types
const DEFAULT_DURATIONS = {
  success: 4000,
  error: 0, // Persistent for errors
  warning: 6000,
  info: 4000,
};

const initialState = {
  notifications: [],
};

export const useNotificationStore = create<NotificationState>()(
  devtools(
    (set, get) => ({
      // Initial state
      ...initialState,
      pauseOnHover: true,

      // Actions
      addNotification: (notification) => {
        const id = `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
        const newNotification: Notification = {
          id,
          timestamp: new Date(),
          duration: DEFAULT_DURATIONS[notification.type],
          dismissible: true,
          ...notification,
        };

        set(
          (state) => ({
            notifications: [...state.notifications, newNotification],
          }),
          false,
          `addNotification:${notification.type}`,
        );

        // Auto-remove notification after duration if specified
        if (newNotification.duration && newNotification.duration > 0) {
          setTimeout(() => {
            useNotificationStore.getState().removeNotification(id);
          }, newNotification.duration);
        }

        return id;
      },

      removeNotification: (id: string) =>
        set(
          (state) => ({
            notifications: state.notifications.filter((n) => n.id !== id),
          }),
          false,
          `removeNotification:${id}`,
        ),

      clearAllNotifications: () =>
        set({ notifications: [] }, false, "clearAllNotifications"),

      updateNotification: (id: string, updates: Partial<Notification>) =>
        set(
          (state) => ({
            notifications: state.notifications.map((n) =>
              n.id === id ? { ...n, ...updates } : n,
            ),
          }),
          false,
          `updateNotification:${id}`,
        ),

      // Convenience methods
      showSuccess: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "success",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showError: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "error",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showWarning: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "warning",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      showInfo: (message: string, title?: string, duration?: number) => {
        const notificationData: Omit<Notification, "id" | "timestamp"> = {
          type: "info",
          message,
          title,
        };
        if (duration !== undefined) {
          notificationData.duration = duration;
        }
        return get().addNotification(notificationData);
      },

      // Reset function for testing
      resetStore: () => set(initialState, false, "resetStore"),
    }),
    {
      name: "notification-store",
    },
  ),
);

// Selector hooks for better performance
export const useNotifications = () =>
  useNotificationStore((state) => state.notifications);

export const useNotificationActions = () =>
  useNotificationStore((state) => ({
    addNotification: state.addNotification,
    removeNotification: state.removeNotification,
    clearAllNotifications: state.clearAllNotifications,
    showSuccess: state.showSuccess,
    showError: state.showError,
    showWarning: state.showWarning,
    showInfo: state.showInfo,
  }));

// Utility hooks for common notification patterns
export const useErrorHandler = () => {
  const { showError } = useNotificationActions();

  const handleError = (error: Error | string, title = "Error") => {
    const message = typeof error === "string" ? error : error.message;
    return showError(message, title);
  };

  return { handleError };
};

export const useSuccessHandler = () => {
  const { showSuccess } = useNotificationActions();

  const handleSuccess = (message: string, title = "Success") => {
    return showSuccess(message, title);
  };

  return { handleSuccess };
};

/**
 * Theme Store
 * UI state management for theme preferences, animations, and layout settings
 * Zustand store - handles theme and UI preference state
 */
import { create } from "zustand";
import { devtools, persist } from "zustand/middleware";
import { serviceLogger } from "@/utils/logging";

const logger = serviceLogger("ThemeStore");

export type ThemeMode = "light" | "dark" | "system";
export type ColorScheme = "nightly" | "classic" | "high-contrast";
export type FontSize = "sm" | "md" | "lg" | "xl";
export type AnimationSpeed = "none" | "reduced" | "normal" | "fast";

export interface ThemeState {
  // Theme settings
  mode: ThemeMode;
  colorScheme: ColorScheme;

  // Typography
  fontSize: FontSize;

  // Animations and motion
  animationSpeed: AnimationSpeed;
  reduceMotion: boolean;

  // Layout preferences
  compactMode: boolean;
  showSidebar: boolean;
  sidebarCollapsed: boolean;

  // Accessibility
  highContrast: boolean;
  focusIndicators: boolean;

  // UI preferences
  showTooltips: boolean;
  showHints: boolean;
  autoSave: boolean;

  // System detection
  systemPrefersDark: boolean;
  systemPrefersReducedMotion: boolean;
}

export interface ThemeActions {
  // Theme management
  setMode: (mode: ThemeMode) => void;
  setColorScheme: (scheme: ColorScheme) => void;
  toggleMode: () => void;

  // Typography
  setFontSize: (size: FontSize) => void;
  increaseFontSize: () => void;
  decreaseFontSize: () => void;

  // Animations and motion
  setAnimationSpeed: (speed: AnimationSpeed) => void;
  setReduceMotion: (reduce: boolean) => void;
  toggleReduceMotion: () => void;

  // Layout preferences
  setCompactMode: (compact: boolean) => void;
  toggleCompactMode: () => void;
  setShowSidebar: (show: boolean) => void;
  toggleSidebar: () => void;
  setSidebarCollapsed: (collapsed: boolean) => void;
  toggleSidebarCollapse: () => void;

  // Accessibility
  setHighContrast: (enabled: boolean) => void;
  toggleHighContrast: () => void;
  setFocusIndicators: (enabled: boolean) => void;
  toggleFocusIndicators: () => void;

  // UI preferences
  setShowTooltips: (show: boolean) => void;
  toggleTooltips: () => void;
  setShowHints: (show: boolean) => void;
  toggleHints: () => void;
  setAutoSave: (enabled: boolean) => void;
  toggleAutoSave: () => void;

  // System detection
  updateSystemPreferences: () => void;

  // Computed getters
  getEffectiveMode: () => "light" | "dark";
  getEffectiveAnimationSpeed: () => AnimationSpeed;
  shouldReduceMotion: () => boolean;

  // Presets
  applyPreset: (
    preset: "default" | "minimal" | "accessible" | "performance",
  ) => void;

  // Reset
  resetToDefaults: () => void;
  resetStore: () => void;
}

export interface ThemeStore extends ThemeState, ThemeActions {}

const defaultState: ThemeState = {
  mode: "system",
  colorScheme: "nightly",
  fontSize: "md",
  animationSpeed: "normal",
  reduceMotion: false,
  compactMode: false,
  showSidebar: true,
  sidebarCollapsed: false,
  highContrast: false,
  focusIndicators: true,
  showTooltips: true,
  showHints: true,
  autoSave: true,
  systemPrefersDark: false,
  systemPrefersReducedMotion: false,
};

// Helper functions
const detectSystemPreferences = () => {
  if (typeof window === "undefined") {
    return { dark: false, reducedMotion: false };
  }

  const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
  const prefersReducedMotion = window.matchMedia(
    "(prefers-reduced-motion: reduce)",
  ).matches;

  return { dark: prefersDark, reducedMotion: prefersReducedMotion };
};

const fontSizeOrder: FontSize[] = ["sm", "md", "lg", "xl"];

// Helper functions for different action categories
const createThemeActions = (set: any, get: () => ThemeStore) => ({
  setMode: (mode: ThemeMode) => {
    set({ mode });
    logger.debug("Theme mode set", { mode });

    // Apply theme class to document
    if (typeof document !== "undefined") {
      const effectiveMode = get().getEffectiveMode();
      document.documentElement.classList.toggle(
        "dark",
        effectiveMode === "dark",
      );
    }
  },

  setColorScheme: (scheme: ColorScheme) => {
    set({ colorScheme: scheme });
    logger.debug("Color scheme set", { scheme });

    // Apply color scheme class to document
    if (typeof document !== "undefined") {
      document.documentElement.className = document.documentElement.className
        .replace(/scheme-\w+/g, "")
        .concat(` scheme-${scheme}`);
    }
  },

  toggleMode: () => {
    const { mode } = get();
    const newMode: ThemeMode =
      mode === "light" ? "dark" : mode === "dark" ? "system" : "light";
    get().setMode(newMode);
  },
});

const createTypographyActions = (set: any, get: () => ThemeStore) => ({
  setFontSize: (size: FontSize) => {
    set({ fontSize: size });
    logger.debug("Font size set", { size });

    // Apply font size class to document
    if (typeof document !== "undefined") {
      document.documentElement.className = document.documentElement.className
        .replace(/text-size-\w+/g, "")
        .concat(` text-size-${size}`);
    }
  },

  increaseFontSize: () => {
    const { fontSize } = get();
    const currentIndex = fontSizeOrder.indexOf(fontSize);
    if (currentIndex < fontSizeOrder.length - 1) {
      const newSize = fontSizeOrder[currentIndex + 1];
      if (newSize) {
        get().setFontSize(newSize);
      }
    }
  },

  decreaseFontSize: () => {
    const { fontSize } = get();
    const currentIndex = fontSizeOrder.indexOf(fontSize);
    if (currentIndex > 0) {
      const newSize = fontSizeOrder[currentIndex - 1];
      if (newSize) {
        get().setFontSize(newSize);
      }
    }
  },
});

const createAnimationActions = (set: any, get: () => ThemeStore) => ({
  setAnimationSpeed: (speed: AnimationSpeed) => {
    set({ animationSpeed: speed });
    logger.debug("Animation speed set", { speed });
  },

  setReduceMotion: (reduce: boolean) => {
    set({ reduceMotion: reduce });
    logger.debug("Reduce motion set", { reduce });
  },

  toggleReduceMotion: () => {
    const { reduceMotion } = get();
    get().setReduceMotion(!reduceMotion);
  },
});

const createLayoutActions = (set: any, get: () => ThemeStore) => ({
  setCompactMode: (compact: boolean) => {
    set({ compactMode: compact });
    logger.debug("Compact mode set", { compact });
  },

  toggleCompactMode: () => {
    const { compactMode } = get();
    get().setCompactMode(!compactMode);
  },

  setShowSidebar: (show: boolean) => {
    set({ showSidebar: show });
    logger.debug("Show sidebar set", { show });
  },

  toggleSidebar: () => {
    const { showSidebar } = get();
    get().setShowSidebar(!showSidebar);
  },

  setSidebarCollapsed: (collapsed: boolean) => {
    set({ sidebarCollapsed: collapsed });
    logger.debug("Sidebar collapsed set", { collapsed });
  },

  toggleSidebarCollapse: () => {
    const { sidebarCollapsed } = get();
    get().setSidebarCollapsed(!sidebarCollapsed);
  },
});

const createAccessibilityActions = (set: any, get: () => ThemeStore) => ({
  setHighContrast: (enabled: boolean) => {
    set({ highContrast: enabled });
    logger.debug("High contrast set", { enabled });

    // Apply high contrast class to document
    if (typeof document !== "undefined") {
      document.documentElement.classList.toggle("high-contrast", enabled);
    }
  },

  toggleHighContrast: () => {
    const { highContrast } = get();
    get().setHighContrast(!highContrast);
  },

  setFocusIndicators: (enabled: boolean) => {
    set({ focusIndicators: enabled });
    logger.debug("Focus indicators set", { enabled });
  },

  toggleFocusIndicators: () => {
    const { focusIndicators } = get();
    get().setFocusIndicators(!focusIndicators);
  },
});

const createUIPreferenceActions = (set: any, get: () => ThemeStore) => ({
  setShowTooltips: (show: boolean) => {
    set({ showTooltips: show });
    logger.debug("Show tooltips set", { show });
  },

  toggleTooltips: () => {
    const { showTooltips } = get();
    get().setShowTooltips(!showTooltips);
  },

  setShowHints: (show: boolean) => {
    set({ showHints: show });
    logger.debug("Show hints set", { show });
  },

  toggleHints: () => {
    const { showHints } = get();
    get().setShowHints(!showHints);
  },

  setAutoSave: (enabled: boolean) => {
    set({ autoSave: enabled });
    logger.debug("Auto save set", { enabled });
  },

  toggleAutoSave: () => {
    const { autoSave } = get();
    get().setAutoSave(!autoSave);
  },
});

const createSystemActions = (set: any, get: () => ThemeStore) => ({
  updateSystemPreferences: () => {
    const { dark, reducedMotion } = detectSystemPreferences();

    set({
      systemPrefersDark: dark,
      systemPrefersReducedMotion: reducedMotion,
    });

    logger.debug("System preferences updated", { dark, reducedMotion });

    // Update effective mode if using system preference
    const { mode } = get();
    if (mode === "system") {
      get().setMode("system"); // Triggers re-application of theme
    }
  },
});

const createComputedGetters = (get: () => ThemeStore) => ({
  getEffectiveMode: () => {
    const { mode, systemPrefersDark } = get();
    if (mode === "system") {
      return systemPrefersDark ? "dark" : "light";
    }
    return mode;
  },

  getEffectiveAnimationSpeed: () => {
    const { animationSpeed, systemPrefersReducedMotion } = get();
    if (systemPrefersReducedMotion) {
      return "none";
    }
    return animationSpeed;
  },

  shouldReduceMotion: () => {
    const { reduceMotion, systemPrefersReducedMotion } = get();
    return reduceMotion || systemPrefersReducedMotion;
  },
});

const createPresetActions = (set: any) => ({
  applyPreset: (
    preset: "default" | "minimal" | "accessible" | "performance",
  ) => {
    const presets = {
      default: { ...defaultState },
      minimal: {
        ...defaultState,
        showTooltips: false,
        showHints: false,
        animationSpeed: "reduced" as AnimationSpeed,
        compactMode: true,
      },
      accessible: {
        ...defaultState,
        highContrast: true,
        focusIndicators: true,
        fontSize: "lg" as FontSize,
        reduceMotion: true,
        animationSpeed: "none" as AnimationSpeed,
      },
      performance: {
        ...defaultState,
        animationSpeed: "none" as AnimationSpeed,
        reduceMotion: true,
        showTooltips: false,
        autoSave: false,
      },
    };

    const presetConfig = presets[preset];
    set(presetConfig);
    logger.info("Theme preset applied", { preset });
  },

  resetToDefaults: () => {
    set(defaultState);
    logger.info("Theme reset to defaults");
  },

  resetStore: () => {
    set(defaultState);
    logger.debug("Theme store reset to initial state");
  },
});

export const useThemeStore = create<ThemeStore>()(
  devtools(
    persist(
      (set, get) => ({
        ...defaultState,
        ...createThemeActions(set, get),
        ...createTypographyActions(set, get),
        ...createAnimationActions(set, get),
        ...createLayoutActions(set, get),
        ...createAccessibilityActions(set, get),
        ...createUIPreferenceActions(set, get),
        ...createSystemActions(set, get),
        ...createComputedGetters(get),
        ...createPresetActions(set),
      }),
      {
        name: "theme-store",
        // Only persist certain settings
        partialize: (state) => ({
          mode: state.mode,
          colorScheme: state.colorScheme,
          fontSize: state.fontSize,
          animationSpeed: state.animationSpeed,
          reduceMotion: state.reduceMotion,
          compactMode: state.compactMode,
          showSidebar: state.showSidebar,
          sidebarCollapsed: state.sidebarCollapsed,
          highContrast: state.highContrast,
          focusIndicators: state.focusIndicators,
          showTooltips: state.showTooltips,
          showHints: state.showHints,
          autoSave: state.autoSave,
        }),
      },
    ),
    {
      name: "theme-store",
      // Only enable devtools in development
      enabled:
        import.meta.env.MODE === "development" ||
        import.meta.env.MODE === "nightly",
    },
  ),
);

// Initialize system preferences and listen for changes
if (typeof window !== "undefined") {
  // Initial detection
  useThemeStore.getState().updateSystemPreferences();

  // Listen for system preference changes
  const darkModeQuery = window.matchMedia("(prefers-color-scheme: dark)");
  const reducedMotionQuery = window.matchMedia(
    "(prefers-reduced-motion: reduce)",
  );

  darkModeQuery.addEventListener("change", () => {
    useThemeStore.getState().updateSystemPreferences();
  });

  reducedMotionQuery.addEventListener("change", () => {
    useThemeStore.getState().updateSystemPreferences();
  });
}
